import { useEffect, useMemo, useRef, useState } from "react";
import { cn } from "@/lib/utils";

export interface StickySectionNavItem {
  id: string;
  title: string;
  onSelect?: () => void;
}

export interface StickySectionNavProps {
  items: StickySectionNavItem[];
  stickyTopOffset?: number;
  align?: "start" | "center" | "end";
  ariaLabel?: string;
  className?: string;
  navClassName?: string;
  observeIds?: string[];
  onActiveChange?: (id: string) => void;
  fallbackActiveId?: string;
  scrollBehavior?: ScrollBehavior;
  disableSticky?: boolean;
}

const ALIGN_CLASSNAMES: Record<NonNullable<StickySectionNavProps["align"]>, string> = {
  start: "justify-start",
  center: "justify-center",
  end: "justify-end"
};

const BASE_NAV_BUTTON_CLASSES = [
  "flex-shrink-0",
  "whitespace-nowrap",
  "rounded-full",
  "border",
  "px-3",
  "py-1.5",
  "text-sm",
  "font-medium",
  "transition-colors",
  "focus-visible:outline-none",
  "focus-visible:ring-2",
  "focus-visible:ring-offset-2",
  "focus-visible:ring-primary"
].join(" ");

const ACTIVE_NAV_CLASSES = "border-primary/50 bg-primary/10 text-primary shadow-sm";
const INACTIVE_NAV_CLASSES =
  "border-transparent bg-muted/20 text-muted-foreground hover:bg-muted/30 hover:text-foreground";

export const STICKY_NAV_CLASSES = {
  baseButton: BASE_NAV_BUTTON_CLASSES,
  active: ACTIVE_NAV_CLASSES,
  inactive: INACTIVE_NAV_CLASSES
};

export function StickySectionNav({
  items,
  stickyTopOffset = 0,
  align = "end",
  ariaLabel = "Section navigation",
  className,
  navClassName,
  observeIds,
  onActiveChange,
  fallbackActiveId,
  scrollBehavior = "smooth",
  disableSticky = false
}: StickySectionNavProps) {
  const [activeId, setActiveId] = useState<string>(() => fallbackActiveId ?? items[0]?.id ?? "");
  const observer = useRef<IntersectionObserver | null>(null);

  const idsToObserve = useMemo(() => {
    const ids = observeIds && observeIds.length > 0 ? observeIds : items.map((item) => item.id);
    return Array.from(new Set(ids)).filter(Boolean);
  }, [observeIds, items]);

  useEffect(() => {
    if (idsToObserve.length === 0 || typeof window === "undefined") {
      return undefined;
    }

    const headings = idsToObserve
      .map((id) => document.getElementById(id))
      .filter((el): el is HTMLElement => Boolean(el));

    observer.current?.disconnect();
    observer.current = new IntersectionObserver(
      (entries) => {
        const visible = entries
          .filter((entry) => entry.isIntersecting)
          .sort((a, b) => b.intersectionRatio - a.intersectionRatio);

        if (visible[0]?.target?.id) {
          setActiveId(visible[0].target.id);
        }
      },
      { rootMargin: "-100px 0px -60% 0px", threshold: [0, 0.5, 1] }
    );

    headings.forEach((el) => observer.current?.observe(el));

    return () => observer.current?.disconnect();
  }, [idsToObserve]);

  useEffect(() => {
    if (idsToObserve.length === 0) {
      setActiveId("");
      return;
    }

    setActiveId((prev) => {
      if (prev && idsToObserve.includes(prev)) {
        return prev;
      }
      const preferred =
        fallbackActiveId && idsToObserve.includes(fallbackActiveId)
          ? fallbackActiveId
          : idsToObserve[0];
      return preferred ?? "";
    });
  }, [idsToObserve, fallbackActiveId]);

  useEffect(() => {
    if (activeId && onActiveChange) {
      onActiveChange(activeId);
    }
  }, [activeId, onActiveChange]);

  const handleSelect = (item: StickySectionNavItem) => {
    setActiveId(item.id);

    if (item.onSelect) {
      item.onSelect();
      return;
    }

    if (typeof window === "undefined") {
      return;
    }

    const target = document.getElementById(item.id);
    if (target) {
      target.scrollIntoView({ behavior: scrollBehavior, block: "start" });
    }
  };

  if (items.length === 0) {
    return null;
  }

  const containerClasses = disableSticky
    ? "border-b border-border/20 bg-background/70 px-2 py-3 backdrop-blur supports-[backdrop-filter]:bg-background/50"
    : "sticky z-30 -mx-2 mb-6 border-b border-border/20 bg-background/70 px-2 py-3 backdrop-blur supports-[backdrop-filter]:bg-background/50";

  const containerStyle = disableSticky ? undefined : { top: stickyTopOffset };

  return (
    <div
      className={cn(containerClasses, className)}
      style={containerStyle}
    >
      <nav
        className={cn(
          "flex w-full max-w-full items-center gap-2 overflow-x-auto",
          ALIGN_CLASSNAMES[align],
          navClassName
        )}
        aria-label={ariaLabel}
      >
        {items.map((item) => {
          const buttonClasses = [
            BASE_NAV_BUTTON_CLASSES,
            item.id === activeId ? ACTIVE_NAV_CLASSES : INACTIVE_NAV_CLASSES
          ].join(" ");

          return (
            <button
              key={item.id}
              type="button"
              className={buttonClasses}
              onClick={() => handleSelect(item)}
              aria-current={item.id === activeId ? "page" : undefined}
            >
              {item.title}
            </button>
          );
        })}
      </nav>
    </div>
  );
}

export default StickySectionNav;

export {
  StickySectionNav as default,
  STICKY_NAV_CLASSES
} from "@/components/navigation/StickySectionNav";

export type {
  StickySectionNavProps,
  StickySectionNavItem
} from "@/components/navigation/StickySectionNav";

import React, { useState, useEffect } from 'react';
import { AlertTriangle, HardDrive } from 'lucide-react';
import { Progress } from '@/components/ui/progress';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { supabase } from '@/integrations/supabase/client';
import { useOrganization } from '@/contexts/OrganizationContext';

interface StorageUsage {
  total_images: number;
  total_storage_bytes: number;
}

const STORAGE_LIMITS = {
  MAX_IMAGES: 50,
  MAX_STORAGE_BYTES: 50 * 1024 * 1024, // 50MB
};

interface CompactStorageIndicatorProps {
  onManageImages?: () => void;
}

export function CompactStorageIndicator({ onManageImages }: CompactStorageIndicatorProps) {
  const [storageUsage, setStorageUsage] = useState<StorageUsage>({ total_images: 0, total_storage_bytes: 0 });
  const [loading, setLoading] = useState(true);
  const { activeOrganization } = useOrganization();

  const fetchStorageUsage = async () => {
    if (!activeOrganization?.id) return;

    try {
      const { data, error } = await supabase
        .from('template_image_usage')
        .select('total_images, total_storage_bytes')
        .eq('organization_id', activeOrganization.id)
        .single();

      if (error && error.code !== 'PGRST116') {
        console.error('Error fetching storage usage:', error);
        return;
      }

      setStorageUsage(data || { total_images: 0, total_storage_bytes: 0 });
    } catch (error) {
      console.error('Error fetching storage usage:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchStorageUsage();
  }, [activeOrganization?.id]);

  if (loading) {
    return <div className="h-4 w-32 bg-muted animate-pulse rounded" />;
  }

  const imageProgress = (storageUsage.total_images / STORAGE_LIMITS.MAX_IMAGES) * 100;
  const storageProgress = (storageUsage.total_storage_bytes / STORAGE_LIMITS.MAX_STORAGE_BYTES) * 100;
  const isNearLimit = imageProgress > 80 || storageProgress > 80;
  const isAtLimit = imageProgress >= 100 || storageProgress >= 100;

  const formatFileSize = (bytes: number) => {
    if (bytes === 0) return '0 B';
    const k = 1024;
    const sizes = ['B', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return `${parseFloat((bytes / Math.pow(k, i)).toFixed(1))} ${sizes[i]}`;
  };

  return (
    <div className="flex items-center gap-3 p-3 bg-muted/50 rounded-lg">
      <div className="flex items-center gap-2">
        <HardDrive className="h-4 w-4 text-muted-foreground" />
        <div className="space-y-1">
          <div className="flex items-center gap-2">
            <span className="text-xs text-muted-foreground">
              {storageUsage.total_images}/{STORAGE_LIMITS.MAX_IMAGES} images
            </span>
            {isAtLimit && <AlertTriangle className="h-3 w-3 text-destructive" />}
            {isNearLimit && !isAtLimit && <AlertTriangle className="h-3 w-3 text-warning" />}
          </div>
          <div className="flex items-center gap-2">
            <Progress value={Math.min(imageProgress, 100)} className="w-20 h-1" />
            <span className="text-xs text-muted-foreground">
              {formatFileSize(storageUsage.total_storage_bytes)}/50MB
            </span>
          </div>
        </div>
      </div>

      {onManageImages && (
        <Button variant="ghost" size="sm" onClick={onManageImages} className="text-xs">
          Manage
        </Button>
      )}

      {isAtLimit && (
        <Badge variant="destructive" className="text-xs">
          Limit Reached
        </Badge>
      )}
      {isNearLimit && !isAtLimit && (
        <Badge variant="secondary" className="text-xs">
          Near Limit
        </Badge>
      )}
    </div>
  );
}

import React, { useState, useEffect } from 'react';
import { Sheet, SheetContent, SheetDescription, SheetHeader, SheetTitle } from '@/components/ui/sheet';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Input } from '@/components/ui/input';
import { AlertDialog, AlertDialogAction, AlertDialogCancel, AlertDialogContent, AlertDialogDescription, AlertDialogFooter, AlertDialogHeader, AlertDialogTitle, AlertDialogTrigger } from '@/components/ui/alert-dialog';
import { Copy, Trash2, Edit3, Check, X } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import { supabase } from '@/integrations/supabase/client';
import { useOrganization } from '@/contexts/OrganizationContext';
import { CompactStorageIndicator } from './CompactStorageIndicator';
import { useTranslation } from 'react-i18next';
import { useMessagesTranslation } from '@/hooks/useTypedTranslation';

interface TemplateAsset {
  id: string;
  file_name: string;
  file_path: string;
  file_size: number;
  content_type: string;
  alt_text?: string;
  created_at: string;
}

interface ImageLibrarySheetProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  onImageSelect?: (imageUrl: string, altText?: string) => void;
  templateId?: string;
}

export function ImageLibrarySheet({ open, onOpenChange, onImageSelect, templateId }: ImageLibrarySheetProps) {
  const [assets, setAssets] = useState<TemplateAsset[]>([]);
  const [loading, setLoading] = useState(true);
  const [editingAltText, setEditingAltText] = useState<string | null>(null);
  const [tempAltText, setTempAltText] = useState('');
  const { toast } = useToast();
  const { t } = useTranslation();
  const { t: tMessages } = useMessagesTranslation();
  const { activeOrganization } = useOrganization();

  const loadAssets = async () => {
    if (!activeOrganization?.id) return;

    try {
      setLoading(true);
      const { data, error } = await supabase
        .from('template_assets')
        .select('*')
        .eq('organization_id', activeOrganization.id)
        .order('created_at', { ascending: false });

      if (error) throw error;
      setAssets(data || []);
    } catch (error) {
      console.error('Error loading assets:', error);
      toast({
        title: 'Error',
        description: 'Failed to load images',
        variant: 'destructive',
      });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (open) {
      loadAssets();
    }
  }, [open, activeOrganization?.id]);

  const getImageUrl = (filePath: string) => {
    const { data: { publicUrl } } = supabase.storage
      .from('template-images')
      .getPublicUrl(filePath);
    return publicUrl;
  };

  const copyImageUrl = async (filePath: string) => {
    const imageUrl = getImageUrl(filePath);
    try {
      await navigator.clipboard.writeText(imageUrl);
      toast({
        title: 'Success',
        description: 'Image URL copied to clipboard',
      });
    } catch (error) {
      toast({
        title: 'Error',
        description: 'Failed to copy URL',
        variant: 'destructive',
      });
    }
  };

  const deleteAsset = async (asset: TemplateAsset) => {
    try {
      // Delete from storage
      const { error: storageError } = await supabase.storage
        .from('template-images')
        .remove([asset.file_path]);

      if (storageError) throw storageError;

      // Delete from database
      const { error: dbError } = await supabase
        .from('template_assets')
        .delete()
        .eq('id', asset.id);

      if (dbError) throw dbError;

      toast({
        title: 'Success',
        description: 'Image deleted successfully',
      });

      // Refresh the list
      loadAssets();
    } catch (error) {
      console.error('Error deleting asset:', error);
      toast({
        title: 'Error',
        description: 'Failed to delete image',
        variant: 'destructive',
      });
    }
  };

  const updateAltText = async (assetId: string, newAltText: string) => {
    try {
      const { error } = await supabase
        .from('template_assets')
        .update({ alt_text: newAltText })
        .eq('id', assetId);

      if (error) throw error;

      setAssets(prev => prev.map(asset => 
        asset.id === assetId ? { ...asset, alt_text: newAltText } : asset
      ));

      toast({
        title: 'Success',
        description: 'Alt text updated',
      });
    } catch (error) {
      console.error('Error updating alt text:', error);
      toast({
        title: 'Error',
        description: 'Failed to update alt text',
        variant: 'destructive',
      });
    }
  };

  const handleEditAltText = (assetId: string, currentAltText: string) => {
    setEditingAltText(assetId);
    setTempAltText(currentAltText || '');
  };

  const handleSaveAltText = (assetId: string) => {
    updateAltText(assetId, tempAltText);
    setEditingAltText(null);
    setTempAltText('');
  };

  const handleCancelEditAltText = () => {
    setEditingAltText(null);
    setTempAltText('');
  };

  const formatFileSize = (bytes: number) => {
    if (bytes === 0) return '0 B';
    const k = 1024;
    const sizes = ['B', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return `${parseFloat((bytes / Math.pow(k, i)).toFixed(1))} ${sizes[i]}`;
  };

  return (
    <Sheet open={open} onOpenChange={onOpenChange}>
      <SheetContent className="w-[600px] sm:w-[700px] overflow-hidden flex flex-col">
        <SheetHeader>
          <SheetTitle>Image Library</SheetTitle>
          <SheetDescription>
            Manage your uploaded images. Click on an image to select it for your template.
          </SheetDescription>
        </SheetHeader>

        <div className="mt-4">
          <CompactStorageIndicator />
        </div>

        <div className="flex-1 overflow-y-auto mt-6">
          {loading ? (
            <div className="grid grid-cols-2 gap-4">
              {Array.from({ length: 6 }).map((_, i) => (
                <div key={i} className="aspect-video bg-muted animate-pulse rounded-lg" />
              ))}
            </div>
          ) : assets.length === 0 ? (
            <div className="text-center py-12">
              <p className="text-muted-foreground">No images uploaded yet</p>
              <p className="text-sm text-muted-foreground mt-1">
                Upload an image using the image block to see it here
              </p>
            </div>
          ) : (
            <div className="grid grid-cols-2 gap-4">
              {assets.map((asset) => (
                <div key={asset.id} className="group relative bg-card border rounded-lg overflow-hidden">
                  <div className="aspect-video relative">
                    <img
                      src={getImageUrl(asset.file_path)}
                      alt={asset.alt_text || asset.file_name}
                      className="w-full h-full object-cover cursor-pointer hover:scale-105 transition-transform"
                      onClick={() => {
                        if (onImageSelect) {
                          onImageSelect(getImageUrl(asset.file_path), asset.alt_text);
                          onOpenChange(false);
                        }
                      }}
                    />
                    <div className="absolute inset-0 bg-black/0 group-hover:bg-black/20 transition-colors" />
                    <div className="absolute top-2 right-2 opacity-0 group-hover:opacity-100 transition-opacity flex gap-1">
                      <Button
                        size="sm"
                        variant="secondary"
                        onClick={() => copyImageUrl(asset.file_path)}
                        className="h-8 w-8 p-0"
                        aria-label="Copy image URL"
                      >
                        <Copy className="h-3 w-3" />
                      </Button>
                      <AlertDialog>
                        <AlertDialogTrigger asChild>
                          <Button
                            size="sm"
                            variant="destructive"
                            className="h-8 w-8 p-0"
                            aria-label="Delete image"
                          >
                            <Trash2 className="h-3 w-3" />
                          </Button>
                        </AlertDialogTrigger>
                        <AlertDialogContent>
                          <AlertDialogHeader>
                            <AlertDialogTitle>Delete Image</AlertDialogTitle>
                            <AlertDialogDescription>
                              {tMessages('confirm.deleteWithName', { name: asset.file_name })} {tMessages('confirm.cannotUndo')}
                            </AlertDialogDescription>
                          </AlertDialogHeader>
                          <AlertDialogFooter>
                            <AlertDialogCancel>Cancel</AlertDialogCancel>
                            <AlertDialogAction
                              onClick={() => deleteAsset(asset)}
                              className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
                            >
                              Delete
                            </AlertDialogAction>
                          </AlertDialogFooter>
                        </AlertDialogContent>
                      </AlertDialog>
                    </div>
                  </div>

                  <div className="p-3 space-y-2">
                    <div className="flex items-center justify-between">
                      <p className="text-sm font-medium truncate">{asset.file_name}</p>
                      <Badge variant="secondary" className="text-xs">
                        {formatFileSize(asset.file_size)}
                      </Badge>
                    </div>

                    <div className="flex items-center gap-2">
                      {editingAltText === asset.id ? (
                        <div className="flex items-center gap-1 flex-1">
                          <Input
                            value={tempAltText}
                            onChange={(e) => setTempAltText(e.target.value)}
                            placeholder="Alt text"
                            className="h-7 text-xs"
                          />
                          <Button
                            size="sm"
                            variant="ghost"
                            onClick={() => handleSaveAltText(asset.id)}
                            className="h-7 w-7 p-0"
                            aria-label="Confirm alt text"
                          >
                            <Check className="h-3 w-3" />
                          </Button>
                          <Button
                            size="sm"
                            variant="ghost"
                            onClick={handleCancelEditAltText}
                            className="h-7 w-7 p-0"
                            aria-label="Cancel alt text edit"
                          >
                            <X className="h-3 w-3" />
                          </Button>
                        </div>
                      ) : (
                        <div className="flex items-center gap-1 flex-1">
                          <p className="text-xs text-muted-foreground flex-1 truncate">
                            Alt: {asset.alt_text || 'No alt text'}
                          </p>
                          <Button
                            size="sm"
                            variant="ghost"
                            onClick={() => handleEditAltText(asset.id, asset.alt_text || '')}
                            className="h-7 w-7 p-0"
                            aria-label="Edit alt text"
                          >
                            <Edit3 className="h-3 w-3" />
                          </Button>
                        </div>
                      )}
                    </div>

                    {onImageSelect && (
                      <Button
                        size="sm"
                        variant="outline"
                        className="w-full h-7 text-xs"
                        onClick={() => {
                          onImageSelect(getImageUrl(asset.file_path), asset.alt_text);
                          onOpenChange(false);
                        }}
                      >
                        Insert
                      </Button>
                    )}
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </SheetContent>
    </Sheet>
  );
}

import * as React from "react"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const badgeVariants = cva(
  "inline-flex items-center rounded-full border px-2.5 py-0.5 text-xs font-semibold transition-colors focus:outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2",
  {
    variants: {
      variant: {
        default:
          "border-transparent bg-primary text-primary-foreground hover:bg-primary/80",
        secondary:
          "border-transparent bg-secondary text-secondary-foreground hover:bg-secondary/80",
        destructive:
          "border-transparent bg-destructive text-destructive-foreground hover:bg-destructive/80",
        outline: "text-foreground",
        success:
          "border-transparent bg-[hsl(var(--success-bg))] text-[hsl(var(--success-foreground))]",
        warning:
          "border-transparent bg-[hsl(var(--warning-bg))] text-[hsl(var(--warning-foreground))]",
        info:
          "border-transparent bg-[hsl(var(--info-bg))] text-[hsl(var(--info-foreground))]",
      },
    },
    defaultVariants: {
      variant: "default",
    },
  }
)

export interface BadgeProps
  extends React.HTMLAttributes<HTMLDivElement>,
    VariantProps<typeof badgeVariants> {}

function Badge({ className, variant, ...props }: BadgeProps) {
  return (
    <div className={cn(badgeVariants({ variant }), className)} {...props} />
  )
}

export { Badge, badgeVariants }

import * as React from "react"
import { Slot } from "@radix-ui/react-slot"
import { cva, type VariantProps } from "class-variance-authority"

import { cn } from "@/lib/utils"

const buttonVariants = cva(
  "inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-md text-sm font-medium ring-offset-background transition-colors focus-visible:outline-none focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:pointer-events-none disabled:opacity-50 [&_svg]:pointer-events-none [&_svg]:size-4 [&_svg]:shrink-0",
  {
    variants: {
      variant: {
        default: "bg-primary text-primary-foreground hover:bg-primary/90",
        destructive:
          "bg-destructive text-destructive-foreground hover:bg-destructive/90",
        outline:
          "border border-input bg-background hover:bg-accent hover:text-accent-foreground",
        secondary:
          "bg-secondary text-secondary-foreground hover:bg-secondary/80",
        ghost: "hover:bg-accent hover:text-accent-foreground hover:text-white",
        link: "text-accent underline-offset-4 hover:underline",
        cta: "bg-accent text-accent-foreground hover:bg-accent/90 h-11 px-6 rounded-md border border-input shadow-sm transition-colors",
        dangerOutline: "border border-destructive text-destructive hover:bg-destructive/10",
        chip:
          "bg-transparent text-current hover:bg-transparent hover:text-current shadow-none",
      },
      size: {
        default: "h-10 px-4 py-2",
        sm: "h-9 rounded-md px-3",
        lg: "h-11 rounded-md px-8",
        icon: "h-10 w-10",
      },
    },
    defaultVariants: {
      variant: "default",
      size: "default",
    },
  }
)

export interface ButtonProps
  extends React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {
  asChild?: boolean
}

const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  ({ className, variant, size, asChild = false, ...props }, ref) => {
    const Comp = asChild ? Slot : "button"
    return (
      <Comp
        className={cn(buttonVariants({ variant, size, className }))}
        ref={ref}
        {...props}
      />
    )
  }
)
Button.displayName = "Button"

export { Button, buttonVariants }

import * as React from "react"
import * as LabelPrimitive from "@radix-ui/react-label"
import { Slot } from "@radix-ui/react-slot"
import {
  Controller,
  ControllerProps,
  FieldPath,
  FieldValues,
  FormProvider,
  useFormContext,
} from "react-hook-form"

import { cn } from "@/lib/utils"
import { Label } from "@/components/ui/label"

const Form = FormProvider

type FormFieldContextValue<
  TFieldValues extends FieldValues = FieldValues,
  TName extends FieldPath<TFieldValues> = FieldPath<TFieldValues>
> = {
  name: TName
}

const FormFieldContext = React.createContext<FormFieldContextValue>(
  {} as FormFieldContextValue
)

const FormField = <
  TFieldValues extends FieldValues = FieldValues,
  TName extends FieldPath<TFieldValues> = FieldPath<TFieldValues>
>({
  ...props
}: ControllerProps<TFieldValues, TName>) => {
  return (
    <FormFieldContext.Provider value={{ name: props.name }}>
      <Controller {...props} />
    </FormFieldContext.Provider>
  )
}

const useFormField = () => {
  const fieldContext = React.useContext(FormFieldContext)
  const itemContext = React.useContext(FormItemContext)
  const { getFieldState, formState } = useFormContext()

  const fieldState = getFieldState(fieldContext.name, formState)

  if (!fieldContext) {
    throw new Error("useFormField should be used within <FormField>")
  }

  const { id } = itemContext

  return {
    id,
    name: fieldContext.name,
    formItemId: `${id}-form-item`,
    formDescriptionId: `${id}-form-item-description`,
    formMessageId: `${id}-form-item-message`,
    ...fieldState,
  }
}

type FormItemContextValue = {
  id: string
}

const FormItemContext = React.createContext<FormItemContextValue>(
  {} as FormItemContextValue
)

const FormItem = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => {
  const id = React.useId()

  return (
    <FormItemContext.Provider value={{ id }}>
      <div ref={ref} className={cn("space-y-2", className)} {...props} />
    </FormItemContext.Provider>
  )
})
FormItem.displayName = "FormItem"

const FormLabel = React.forwardRef<
  React.ElementRef<typeof LabelPrimitive.Root>,
  React.ComponentPropsWithoutRef<typeof LabelPrimitive.Root>
>(({ className, ...props }, ref) => {
  const { error, formItemId } = useFormField()

  return (
    <Label
      ref={ref}
      className={cn(error && "text-destructive", className)}
      htmlFor={formItemId}
      {...props}
    />
  )
})
FormLabel.displayName = "FormLabel"

const FormControl = React.forwardRef<
  React.ElementRef<typeof Slot>,
  React.ComponentPropsWithoutRef<typeof Slot>
>(({ ...props }, ref) => {
  const { error, formItemId, formDescriptionId, formMessageId } = useFormField()

  return (
    <Slot
      ref={ref}
      id={formItemId}
      aria-describedby={
        !error
          ? `${formDescriptionId}`
          : `${formDescriptionId} ${formMessageId}`
      }
      aria-invalid={!!error}
      {...props}
    />
  )
})
FormControl.displayName = "FormControl"

const FormDescription = React.forwardRef<
  HTMLParagraphElement,
  React.HTMLAttributes<HTMLParagraphElement>
>(({ className, ...props }, ref) => {
  const { formDescriptionId } = useFormField()

  return (
    <p
      ref={ref}
      id={formDescriptionId}
      className={cn("text-sm text-muted-foreground", className)}
      {...props}
    />
  )
})
FormDescription.displayName = "FormDescription"

const FormMessage = React.forwardRef<
  HTMLParagraphElement,
  React.HTMLAttributes<HTMLParagraphElement>
>(({ className, children, ...props }, ref) => {
  const { error, formMessageId } = useFormField()
  const body = error ? String(error?.message) : children

  if (!body) {
    return null
  }

  return (
    <p
      ref={ref}
      id={formMessageId}
      className={cn("text-sm font-medium text-destructive", className)}
      {...props}
    >
      {body}
    </p>
  )
})
FormMessage.displayName = "FormMessage"

