import { useState, useEffect } from 'react';
import { supabase } from '@/integrations/supabase/client';
import { getUserOrganizationId } from '@/lib/organizationUtils';
import { useToast } from '@/hooks/use-toast';

export interface LeadWithCustomFields {
  id: string;
  name: string;
  email: string;
  phone: string;
  status: string;
  status_id: string;
  updated_at: string;
  created_at: string;
  due_date?: string;
  notes?: string;
  custom_fields: Record<string, string | null>;
  lead_statuses?: {
    id: string;
    name: string;
    color: string;
    is_system_final: boolean;
  };
}

export function useLeadsWithCustomFields() {
  const [leads, setLeads] = useState<LeadWithCustomFields[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const { toast } = useToast();

  const fetchLeadsWithCustomFields = async () => {
    try {
      setLoading(true);
      setError(null);

      const organizationId = await getUserOrganizationId();
      if (!organizationId) {
        throw new Error('No active organization found');
      }

      // Fetch leads with status information
      const { data: leadsData, error: leadsError } = await supabase
        .from('leads')
        .select(`
          *,
          lead_statuses(id, name, color, is_system_final)
        `)
        .eq('organization_id', organizationId)
        .order('updated_at', { ascending: false });

      if (leadsError) throw leadsError;

      // Fetch all custom field values for these leads
      const leadIds = leadsData?.map(lead => lead.id) || [];
      const { data: fieldValues, error: fieldValuesError } = await supabase
        .from('lead_field_values')
        .select('lead_id, field_key, value')
        .in('lead_id', leadIds);

      if (fieldValuesError) throw fieldValuesError;

      // Group field values by lead_id
      const fieldValuesByLead = (fieldValues || []).reduce((acc, fv) => {
        if (!acc[fv.lead_id]) {
          acc[fv.lead_id] = {};
        }
        acc[fv.lead_id][fv.field_key] = fv.value;
        return acc;
      }, {} as Record<string, Record<string, string | null>>);

      // Combine leads with their custom field values
      const leadsWithFields: LeadWithCustomFields[] = (leadsData || []).map(lead => ({
        ...lead,
        custom_fields: fieldValuesByLead[lead.id] || {},
      }));

      setLeads(leadsWithFields);
    } catch (err) {
      console.error('Error fetching leads with custom fields:', err);
      const errorMessage = err instanceof Error ? err.message : 'Failed to fetch leads';
      setError(errorMessage);
      toast({
        title: "Error fetching leads",
        description: errorMessage,
        variant: "destructive"
      });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchLeadsWithCustomFields();
  }, []);

  return {
    leads,
    loading,
    error,
    refetch: fetchLeadsWithCustomFields
  };
}

import { useState, useEffect } from 'react';
import { supabase } from '@/integrations/supabase/client';

interface PaymentSummary {
  totalPaid: number;
  totalProject: number;
  remaining: number;
  currency: string;
}

export const useProjectPayments = (projectId: string, refreshTrigger?: number) => {
  const [paymentSummary, setPaymentSummary] = useState<PaymentSummary>({
    totalPaid: 0,
    totalProject: 0,
    remaining: 0,
    currency: 'TRY',
  });
  const [loading, setLoading] = useState(true);

  const fetchPaymentSummary = async () => {
    try {
      // Get project base price
      const { data: projectData, error: projectError } = await supabase
        .from('projects')
        .select('base_price')
        .eq('id', projectId)
        .single();

      if (projectError) throw projectError;

      // Get total cost of added services
      const { data: servicesData, error: servicesError } = await supabase
        .from('project_services')
        .select(`
          services!inner (
            selling_price,
            price
          )
        `)
        .eq('project_id', projectId);

      if (servicesError) throw servicesError;

      // Get total paid amount
      const { data: paymentsData, error: paymentsError } = await supabase
        .from('payments')
        .select('amount')
        .eq('project_id', projectId)
        .eq('status', 'paid');

      if (paymentsError) throw paymentsError;

      const basePrice = projectData?.base_price || 0;
      const servicesCost = servicesData?.reduce((sum, ps) => {
        const service = ps.services;
        const price = service.selling_price || service.price || 0;
        return sum + Number(price);
      }, 0) || 0;

      const totalProject = basePrice + servicesCost;
      const totalPaid = paymentsData?.reduce((sum, payment) => sum + Number(payment.amount), 0) || 0;
      const remaining = Math.max(0, totalProject - totalPaid);

      setPaymentSummary({
        totalPaid,
        totalProject,
        remaining,
        currency: 'TRY',
      });
    } catch (error) {
      console.error('Error fetching payment summary:', error);
      setPaymentSummary({
        totalPaid: 0,
        totalProject: 0,
        remaining: 0,
        currency: 'TRY',
      });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPaymentSummary();
  }, [projectId, refreshTrigger]);

  return { paymentSummary, loading, refetch: fetchPaymentSummary };
};

import { useState, useEffect } from 'react';
import { supabase } from '@/integrations/supabase/client';

interface TodoProgress {
  total: number;
  completed: number;
  percentage: number;
}

export const useProjectProgress = (projectId: string, refreshTrigger?: number) => {
  const [progress, setProgress] = useState<TodoProgress>({
    total: 0,
    completed: 0,
    percentage: 0,
  });
  const [loading, setLoading] = useState(true);

  const fetchTodoProgress = async () => {
    try {
      const { data, error } = await supabase
        .from('todos')
        .select('is_completed')
        .eq('project_id', projectId);

      if (error) throw error;

      const todos = data || [];
      const total = todos.length;
      const completed = todos.filter(todo => todo.is_completed).length;
      const percentage = total > 0 ? Math.round((completed / total) * 100) : 0;

      setProgress({ total, completed, percentage });
    } catch (error) {
      console.error('Error fetching todo progress:', error);
      setProgress({ total: 0, completed: 0, percentage: 0 });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTodoProgress();
  }, [projectId, refreshTrigger]);

  return { progress, loading, refetch: fetchTodoProgress };
};

