  useEffect(() => {
    setFormData(prev => {
      const newData = {
        session_name: initialData.session_name || "",
        session_date: initialData.session_date,
        session_time: initialData.session_time,
        notes: initialData.notes || "",
        location: initialData.location || "",
        project_id: initialData.project_id || ""
      };

      // Only update if data actually changed to prevent unnecessary rerenders
      if (JSON.stringify(prev) !== JSON.stringify(newData)) {
        return newData;
      }
      return prev;
    });
  }, [
    initialData.session_name,
    initialData.session_date,
    initialData.session_time,
    initialData.notes,
    initialData.location,
    initialData.project_id
  ]);


  const handleInputChange = (field: keyof SessionEditFormData, value: string) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));

    // Clear field-specific errors when user starts typing
    if (errors[field]) {
      setErrors(prev => {
        const newErrors = { ...prev };
        delete newErrors[field];
        return newErrors;
      });
    }
  };

  const resetForm = () => {
    setFormData({
      session_name: initialData.session_name || "",
      session_date: initialData.session_date,
      session_time: initialData.session_time,
      notes: initialData.notes || "",
      location: initialData.location || "",
      project_id: initialData.project_id || ""
    });
    setErrors({});
  };

  const isDirty = Boolean(
    formData.session_name !== (initialData.session_name || "") ||
    formData.session_date !== initialData.session_date ||
    formData.session_time !== initialData.session_time ||
    formData.notes !== (initialData.notes || "") ||
    formData.location !== (initialData.location || "") ||
    formData.project_id !== (initialData.project_id || "")
  );

  const isValid = Boolean(
    formData.session_date &&
    formData.session_time &&
    Object.keys(errors).length === 0
  );

  const validateForm = async () => {
    setErrors({});

    try {
      await sessionSchema.parseAsync({
        session_date: sanitizeInput(formData.session_date),
        session_time: sanitizeInput(formData.session_time),
        notes: formData.notes ? await sanitizeHtml(formData.notes) : undefined
      });
      return true;
    } catch (error) {
      if (error instanceof ZodError) {
        const newErrors: Record<string, string> = {};
        error.issues.forEach((err) => {
          const field = err.path[0] as string;
          newErrors[field] = err.message;
        });
        setErrors(newErrors);
      }
      return false;
    }
  };

  const submitForm = async () => {
    if (!(await validateForm())) {
      toast({
        title: "Validation error",
        description: "Please fix the validation errors before saving.",
        variant: "destructive"
      });
      return false;
    }

    if (!isValid) {
      toast({
        title: "Validation error",
        description: "Date and time are required.",
        variant: "destructive"
      });
      return false;
    }

    setLoading(true);
    try {
      // Check if date/time changed for workflow trigger
      const dateTimeChanged = 
        formData.session_date !== initialData.session_date || 
        formData.session_time !== initialData.session_time;
      const oldDateTime = `${initialData.session_date} ${initialData.session_time}`;
      const newDateTime = `${formData.session_date} ${formData.session_time}`;

      // Generate session name if empty
      const finalSessionName = formData.session_name.trim() || 
        generateSessionName(leadName || "Client");

      const { error } = await supabase
        .from('sessions')
        .update({
          session_name: sanitizeInput(finalSessionName),
          session_date: sanitizeInput(formData.session_date),
          session_time: sanitizeInput(formData.session_time),
          notes: formData.notes ? await sanitizeHtml(formData.notes) : null,
          location: formData.location.trim() || null,
          project_id: formData.project_id || null
        })
        .eq('id', sessionId);

      if (error) throw error;

      // Trigger workflow for session rescheduled if date/time changed
      if (dateTimeChanged) {
        try {
          const organizationId = await getUserOrganizationId();
          if (organizationId) {
            await triggerSessionRescheduled(sessionId, organizationId, oldDateTime, newDateTime, {
              session_date: formData.session_date,
              session_time: formData.session_time,
              location: formData.location,
              client_name: leadName,
              lead_id: leadId,
              project_id: formData.project_id || null
            });
          }
        } catch (workflowError) {
          console.error('Error triggering rescheduled workflow:', workflowError);
          // Don't block session update if workflow fails
        }
      }

      // Reschedule session reminders if date/time changed
      if (dateTimeChanged) {
        try {
          await rescheduleSessionReminders(sessionId);
        } catch (reminderError) {
          console.error('Error rescheduling session reminders:', reminderError);
          // Don't block session update if reminder scheduling fails
        }
      }

      toast({
        title: "Success",
        description: "Session updated successfully."
      });

      onSuccess?.();
      return true;
    } catch (error: any) {
      toast({
        title: "Error updating session",
        description: error.message,
        variant: "destructive"
      });
      return false;
    } finally {
      setLoading(false);
    }
  };

  return {
    formData,
    loading,
    errors,
    isDirty,
    isValid,
    handleInputChange,
    resetForm,
    submitForm
  };
}

import { useState } from "react";
import { toast } from "@/hooks/use-toast";
import { useWorkflowTriggers } from "@/hooks/useWorkflowTriggers";
import { useSessionReminderScheduling } from "@/hooks/useSessionReminderScheduling";
import { createSession } from "@/features/session-planning/api/sessionCreation";

interface SessionFormData {
  session_name: string;
  session_date: string;
  session_time: string;
  notes: string;
  location: string;
  project_id?: string;
}

interface UseSessionFormProps {
  leadId: string;
  leadName: string;
  projectId?: string;
  onSuccess?: () => void;
}

export function useSessionForm({ leadId, leadName, projectId, onSuccess }: UseSessionFormProps) {
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState<SessionFormData>({
    session_name: "",
    session_date: "",
    session_time: "",
    notes: "",
    location: "",
    project_id: projectId || ""
  });

  const { triggerSessionScheduled } = useWorkflowTriggers();
  const { scheduleSessionReminders } = useSessionReminderScheduling();

  const handleInputChange = (field: keyof SessionFormData, value: string) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const resetForm = () => {
    setFormData({
      session_name: "",
      session_date: "",
      session_time: "",
      notes: "",
      location: "",
      project_id: projectId || ""
    });
  };

  const isDirty = Boolean(
    formData.session_name.trim() ||
    formData.session_date.trim() ||
    formData.session_time.trim() ||
    formData.notes.trim() ||
    formData.location.trim()
  );

  const isValid = Boolean(
    formData.session_name.trim() &&
    formData.session_date &&
    formData.session_time
  );

  const submitForm = async () => {
    if (!isValid) {
      toast({
        title: "Validation error",
        description: "Session name, date and time are required.",
        variant: "destructive"
      });
      return false;
    }

    setLoading(true);
    try {
      const resolvedProjectId = formData.project_id || projectId || undefined;

      const { sessionId, organizationId } = await createSession(
        {
          leadId,
          leadName,
          sessionName: formData.session_name.trim(),
          sessionDate: formData.session_date,
          sessionTime: formData.session_time,
          notes: formData.notes,
          location: formData.location,
          projectId: resolvedProjectId
        },
        {
          updateLeadStatus: !resolvedProjectId,
          createActivity: !resolvedProjectId
        }
      );

      // Trigger workflow for session scheduled
      try {
        console.log(` Triggering session_scheduled workflow for session: ${sessionId}`);
        const workflowResult = await triggerSessionScheduled(sessionId, organizationId, {
          session_date: formData.session_date,
          session_time: formData.session_time,
          location: formData.location,
          client_name: leadName,
          lead_id: leadId,
          project_id: resolvedProjectId,
          status: 'planned'
        });
        console.log(` Session workflow result:`, workflowResult);
      } catch (workflowError) {
        console.error(' Error triggering session_scheduled workflow:', workflowError);
        toast({
          title: "Warning", 
          description: "Session created successfully, but notifications may not be sent.",
          variant: "default"
        });
      }

      // Schedule session reminders
      try {
        console.log(` Scheduling reminders for session: ${sessionId}`);
        await scheduleSessionReminders(sessionId);
      } catch (reminderError) {
        console.error(' Error scheduling session reminders:', reminderError);
      }

      toast({
        title: "Success",
        description: "Session scheduled successfully."
      });

      resetForm();
      onSuccess?.();
      return true;
    } catch (error: any) {
      toast({
        title: "Error scheduling session",
        description: error.message,
        variant: "destructive"
      });
      return false;
    } finally {
      setLoading(false);
    }
  };

  return {
    formData,
    loading,
    isDirty,
    isValid,
    handleInputChange,
    resetForm,
    submitForm
  };
}

import { useEffect } from "react";
import { useSettingsSection } from "./useSettingsSection";
import { useSettingsContext } from "@/contexts/SettingsContext";

interface SettingsSectionWithContextOptions<T> {
  sectionId: string;
  sectionName: string;
  initialValues: T;
  onSave: (values: T) => Promise<void>;
  autoSave?: boolean;
  throttleMs?: number;
}

export function useSettingsSectionWithContext<T extends Record<string, any>>(
  options: SettingsSectionWithContextOptions<T>
) {
  const { addDirtySection, removeDirtySection } = useSettingsContext();
  const section = useSettingsSection(options);

  // Track dirty state in context
  useEffect(() => {
    if (section.isDirty) {
      addDirtySection(options.sectionId);
    } else {
      removeDirtySection(options.sectionId);
    }
  }, [section.isDirty, options.sectionId, addDirtySection, removeDirtySection]);

  // Override handleSave to update context
  const handleSave = async () => {
    await section.handleSave();
    removeDirtySection(options.sectionId);
  };

  // Override handleCancel to update context
  const handleCancel = () => {
    section.handleCancel();
    removeDirtySection(options.sectionId);
  };

  return {
    ...section,
    handleSave,
    handleCancel
  };
}

import { useState, useEffect } from "react";
import { supabase } from "@/integrations/supabase/client";
import { useOrganization } from "@/contexts/OrganizationContext";
import { TemplateVariable } from "@/types/templateBuilder";

interface BusinessInfo {
  name: string;
  logo_url: string | null;
  primary_brand_color: string;
  phone?: string;
  email?: string;
  address?: string;
  website?: string;
}

export function useTemplateVariables() {
  const [variables, setVariables] = useState<TemplateVariable[]>([]);
  const [businessInfo, setBusinessInfo] = useState<BusinessInfo | null>(null);
  const [loading, setLoading] = useState(true);
  const { activeOrganization } = useOrganization();

  useEffect(() => {
    if (activeOrganization?.id) {
      fetchVariables();
    }
  }, [activeOrganization?.id]);

  const fetchVariables = async () => {
    if (!activeOrganization?.id) return;

    try {
      setLoading(true);

      // Fetch organization settings for business info
      const { data: orgSettings } = await supabase
        .from("organization_settings")
        .select("photography_business_name, logo_url, primary_brand_color")
        .eq("organization_id", activeOrganization.id)
        .single();

      // Fetch lead field definitions for dynamic variables
      const { data: leadFields } = await supabase
        .from("lead_field_definitions")
        .select("field_key, label, field_type")
        .eq("organization_id", activeOrganization.id)
        .order("sort_order");

      // Set business info
      if (orgSettings) {
        setBusinessInfo({
          name: orgSettings.photography_business_name || "Your Business",
          logo_url: orgSettings.logo_url,
          primary_brand_color: orgSettings.primary_brand_color || "#1EB29F",
        });
      }

      // Build template variables
      const templateVariables: TemplateVariable[] = [
        // Business variables (only real ones from organization_settings)
        {
          key: "business_name",
          label: "Business Name",
          category: "business"
        },

        // Lead variables from field definitions
        ...(leadFields?.map(field => ({
          key: `lead_${field.field_key}`,
          label: `Lead ${field.label}`,
          category: "lead" as const
        })) || []),

        // Static lead variables that exist in database
        {
          key: "lead_status",
          label: "Lead Status",
          category: "lead"
        },
        {
          key: "lead_due_date",
          label: "Lead Due Date", 
          category: "lead"
        },
        {
          key: "lead_created_date",
          label: "Lead Created Date",
          category: "lead"
        },
        {
          key: "lead_updated_date",
          label: "Lead Updated Date",
          category: "lead"
        },

        // Session variables (real database fields)
        {
          key: "session_date",
          label: "Session Date",
          category: "session"
        },
        {
          key: "session_time", 
          label: "Session Time",
          category: "session"
        },
        {
          key: "session_location",
          label: "Session Location",
          category: "session"
        },
        {
          key: "session_notes",
          label: "Session Notes",
          category: "session"
        },
        {
          key: "session_status",
          label: "Session Status",
          category: "session"
        },

        // Project variables (real database fields)
        {
          key: "project_name",
          label: "Project Name",
          category: "project"
        },
        {
          key: "project_type",
          label: "Project Type", 
          category: "project"
        },
        {
          key: "project_status",
          label: "Project Status",
          category: "project"
        },
        {
          key: "project_due_date",
          label: "Project Due Date",
          category: "project"
        },

        // System variables
        {
          key: "current_date",
          label: "Current Date",
          category: "custom"
        },
        {
          key: "current_time",
          label: "Current Time", 
          category: "custom"
        }
      ];

      setVariables(templateVariables);
    } catch (error) {
      console.error("Error fetching template variables:", error);
    } finally {
      setLoading(false);
    }
  };

  const getVariableValue = (key: string, mockData?: Record<string, string>): string => {
    // If mock data is provided, use it first
    if (mockData && mockData[key]) {
      return mockData[key];
    }

    // Business variables (only real ones)
    if (key === "business_name") return businessInfo?.name || "Your Business";

    // Lead variables with fallbacks
    if (key.startsWith("lead_")) {
      const fieldKey = key.replace("lead_", "");
      if (fieldKey === "name") return "John Smith";
      if (fieldKey === "email") return "john@example.com";
      if (fieldKey === "phone") return "-"; // Show dash for empty phone
      if (fieldKey === "notes") return "Sample notes";
      if (fieldKey === "status") return "New";
      if (fieldKey === "due_date") return new Date().toLocaleDateString();
      if (fieldKey === "created_date") return new Date().toLocaleDateString();
      if (fieldKey === "updated_date") return new Date().toLocaleDateString();
      // For custom fields, return placeholder
      return `Sample ${fieldKey}`;
    }

    // Session variables (real database fields)
    if (key === "session_date") return new Date().toLocaleDateString();
    if (key === "session_time") return "2:00 PM";  
    if (key === "session_location") return "Studio Location";
    if (key === "session_notes") return "Bring comfortable clothes";
    if (key === "session_status") return "Planned";

    // Project variables (real database fields)
    if (key === "project_name") return "Wedding Photography";
    if (key === "project_type") return "Wedding";
    if (key === "project_status") return "In Progress";
    if (key === "project_due_date") return new Date().toLocaleDateString();

    // System variables
    if (key === "current_date") return new Date().toLocaleDateString();
    if (key === "current_time") return new Date().toLocaleTimeString();

    return `{${key}}`;
  };

  return {
    variables,
    businessInfo,
    loading,
    getVariableValue,
    refetch: fetchVariables
  };
}

import { useState, useEffect, useCallback } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { usePortalReset } from "@/contexts/PortalResetContext";

interface UseTutorialExitOptions {
  currentStepTitle?: string;
  onExitComplete?: () => void;
}

export function useTutorialExit({ currentStepTitle, onExitComplete }: UseTutorialExitOptions = {}) {
  const [isExiting, setIsExiting] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();
  const { reset } = usePortalReset();

  const handleExitNow = useCallback(() => {
    if (isExiting) return;
    setIsExiting(true);
    console.debug("[useTutorialExit] Long-press confirmed, exiting tutorial");
    try {
      onExitComplete?.();
    } finally {
      try { reset(); } catch {}
      requestAnimationFrame(() => {
        requestAnimationFrame(() => {
          if (location.pathname !== "/getting-started") {
            navigate("/getting-started", { replace: true });
          }
          setTimeout(() => setIsExiting(false), 0);
        });
      });
    }
  }, [isExiting, onExitComplete, reset, location.pathname, navigate]);

  useEffect(() => {
    return () => setIsExiting(false);
  }, []);

  return {
    currentStepTitle,
    isExiting,
    handleExitNow
  };
}

import { useQuery, useQueryClient } from "@tanstack/react-query";
import { supabase } from "@/integrations/supabase/client";
import { useAuth } from "@/contexts/AuthContext";
import { OnboardingStage } from "@/constants/onboarding";

export interface UserPreferences {
  // User Settings
  userId: string;
  activeOrganizationId: string | null;

  // Onboarding Data
  onboardingStage: OnboardingStage;
  currentOnboardingStep: number;
  welcomeModalShown: boolean;

  // Organization Settings
  organizationId: string | null;
  businessName: string;
  logoUrl: string | null;
  primaryBrandColor: string;
  timezone: string;
  dateFormat: string;
  timeFormat: string;

  // User Profile
  displayName: string | null;
  avatarUrl: string | null;

  // Timestamps
  lastUpdated: string;
}

const PREFERENCES_CACHE_KEY = "user-preferences";
const CACHE_TTL = 12 * 60 * 60 * 1000; // 12 hours in ms

// Fetch all user preferences in a single optimized query
async function fetchUserPreferences(userId: string): Promise<UserPreferences> {
  console.log(" fetchUserPreferences: Starting unified fetch for user", userId);

  // Single query to get essential onboarding data (only columns we know exist)
  const { data, error } = await supabase
    .from('user_settings')
    .select(`
      user_id,
      onboarding_stage,
      current_onboarding_step,
      welcome_modal_shown,
      updated_at
    `)
    .eq('user_id', userId)
    .maybeSingle();

  if (error) {
    console.error(" fetchUserPreferences: Database error:", error);
    throw error;
  }

  // Create default preferences for new users
  if (!data) {
    console.log(" fetchUserPreferences: No data found, creating defaults");

    // Create minimal default user settings (only essential columns)
    const { error: insertError } = await supabase
      .from('user_settings')
      .insert({
        user_id: userId,
        onboarding_stage: 'not_started',
        current_onboarding_step: 1,
        welcome_modal_shown: false
      });

    if (insertError) {
      console.error(" fetchUserPreferences: Error creating defaults:", insertError);
      throw insertError;
    }

    // Return default preferences
    return {
      userId,
      activeOrganizationId: null,
      onboardingStage: 'not_started' as OnboardingStage,
      currentOnboardingStep: 1,
      welcomeModalShown: false,
      organizationId: null,
      businessName: '',
      logoUrl: null,
      primaryBrandColor: '#1EB29F',
      timezone: 'UTC',
      dateFormat: 'DD/MM/YYYY',
      timeFormat: '12-hour',
      displayName: null,
      avatarUrl: null,
      lastUpdated: new Date().toISOString()
    };
  }

  const preferences: UserPreferences = {
    userId: data.user_id,
    activeOrganizationId: null, // Will get from organization context
    onboardingStage: (data.onboarding_stage as OnboardingStage) || 'not_started',
    currentOnboardingStep: data.current_onboarding_step || 1,
    welcomeModalShown: data.welcome_modal_shown || false,
    organizationId: null, // Will get from organization context
    businessName: '', // Will get from organization settings
    logoUrl: null, // Will get from organization settings
    primaryBrandColor: '#1EB29F', // Will get from organization settings
    timezone: 'UTC', // Will get from organization settings
    dateFormat: 'DD/MM/YYYY', // Will get from organization settings
    timeFormat: '12-hour', // Default value
    displayName: null, // Will get from profiles table
    avatarUrl: null, // Will get from profiles table
    lastUpdated: data.updated_at || new Date().toISOString()
  };

  console.log(" fetchUserPreferences: Successfully fetched preferences", {
    onboardingStage: preferences.onboardingStage,
    welcomeModalShown: preferences.welcomeModalShown,
    currentStep: preferences.currentOnboardingStep
  });

  return preferences;
}

export function useUserPreferences() {
  const { user } = useAuth();
  const queryClient = useQueryClient();

  const query = useQuery({
    queryKey: [PREFERENCES_CACHE_KEY, user?.id],
    queryFn: () => fetchUserPreferences(user!.id),
    enabled: !!user?.id,
    staleTime: CACHE_TTL, // Consider data fresh for 12 hours
    gcTime: CACHE_TTL * 2, // Keep in cache for 24 hours
    refetchOnWindowFocus: false, // Don't refetch on window focus
    refetchOnReconnect: true, // Do refetch on reconnect
    retry: 3,
    retryDelay: (attemptIndex) => Math.min(1000 * 2 ** attemptIndex, 30000)
  });

  // Update preferences function with optimistic updates
  const updatePreferences = async (updates: Partial<UserPreferences>) => {
    if (!user?.id) return;

    console.log(" updatePreferences: Updating with optimistic update", updates);

    // Optimistic update
    queryClient.setQueryData(
      [PREFERENCES_CACHE_KEY, user.id],
      (old: UserPreferences | undefined) => {
        if (!old) return old;
        return { ...old, ...updates, lastUpdated: new Date().toISOString() };
      }
    );

    try {
      // Update database (only onboarding-related columns for now)
      const updateData: any = {};

      if (updates.onboardingStage !== undefined) updateData.onboarding_stage = updates.onboardingStage;
      if (updates.currentOnboardingStep !== undefined) updateData.current_onboarding_step = updates.currentOnboardingStep;
      if (updates.welcomeModalShown !== undefined) updateData.welcome_modal_shown = updates.welcomeModalShown;

      // Only update if we have data to update
      if (Object.keys(updateData).length === 0) {
        console.log(" updatePreferences: No onboarding data to update, skipping");
        return;
      }

      const { error } = await supabase
        .from('user_settings')
        .update(updateData)
        .eq('user_id', user.id);

      if (error) throw error;

      console.log(" updatePreferences: Database updated successfully");

      // Invalidate and refetch to ensure consistency
      queryClient.invalidateQueries({ queryKey: [PREFERENCES_CACHE_KEY, user.id] });

    } catch (error) {
      console.error(" updatePreferences: Error updating preferences:", error);

      // Revert optimistic update on error
      queryClient.invalidateQueries({ queryKey: [PREFERENCES_CACHE_KEY, user.id] });
      throw error;
    }
  };

  // Forced refresh function for login/logout/org switch
  const forceRefresh = () => {
    console.log(" forceRefresh: Forcing preferences refresh");
    queryClient.invalidateQueries({ queryKey: [PREFERENCES_CACHE_KEY] });
  };

  // Clear cache function
  const clearCache = () => {
    console.log(" clearCache: Clearing preferences cache");
    queryClient.removeQueries({ queryKey: [PREFERENCES_CACHE_KEY] });
  };

  return {
    data: query.data,
    isLoading: query.isLoading,
    error: query.error,
    updatePreferences,
    forceRefresh,
    clearCache,
    // Convenience computed values
    isReady: !query.isLoading && !!query.data,
    cacheStatus: query.isStale ? 'stale' : 'fresh'
  };
}

import { useState, useEffect } from 'react';
import { supabase } from '@/integrations/supabase/client';
import { useToast } from '@/hooks/use-toast';
import { getUserOrganizationId } from '@/lib/organizationUtils';

interface WorkflowMetrics {
  id: string;
  organization_id: string;
  date: string;
  total_executions: number;
  successful_executions: number;
  failed_executions: number;
  average_execution_time_ms: number;
}

interface WorkflowHealth {
  totalWorkflows: number;
  activeWorkflows: number;
  pausedWorkflows: number;
  recentExecutions: number;
  successRate: number;
  averageExecutionTime: number;
  failedExecutions: number;
  stuckExecutions: number;
  metrics: WorkflowMetrics[];
}

export function useWorkflowHealth() {
  const [health, setHealth] = useState<WorkflowHealth | null>(null);
  const [loading, setLoading] = useState(true);
  const { toast } = useToast();

  const fetchWorkflowHealth = async () => {
    try {
      setLoading(true);
      const organizationId = await getUserOrganizationId();
      if (!organizationId) return;

      // Get workflow counts
      const { data: workflows, error: workflowsError } = await supabase
        .from('workflows')
        .select('id, is_active')
        .eq('organization_id', organizationId);

      if (workflowsError) throw workflowsError;

      // Get execution metrics for the last 7 days
      const sevenDaysAgo = new Date();
      sevenDaysAgo.setDate(sevenDaysAgo.getDate() - 7);

      const { data: metrics, error: metricsError } = await supabase
        .from('workflow_execution_metrics')
        .select('*')
        .eq('organization_id', organizationId)
        .gte('date', sevenDaysAgo.toISOString().split('T')[0])
        .order('date', { ascending: false });

      if (metricsError) throw metricsError;

      // Get recent executions (last 24 hours)
      const yesterday = new Date();
      yesterday.setDate(yesterday.getDate() - 1);

      const { data: recentExecutions, error: executionsError } = await supabase
        .from('workflow_executions')
        .select('id, status, started_at, completed_at')
        .eq('workflows.organization_id', organizationId)
        .gte('created_at', yesterday.toISOString());

      if (executionsError) {
        console.warn('Could not fetch recent executions:', executionsError);
      }

      // Check for stuck executions (running for more than 5 minutes)
      const fiveMinutesAgo = new Date();
      fiveMinutesAgo.setMinutes(fiveMinutesAgo.getMinutes() - 5);

      const { data: stuckExecutions, error: stuckError } = await supabase
        .from('workflow_executions')
        .select('id')
        .eq('status', 'running')
        .lt('started_at', fiveMinutesAgo.toISOString());

      if (stuckError) {
        console.warn('Could not check for stuck executions:', stuckError);
      }

      // Calculate health metrics
      const totalWorkflows = workflows?.length || 0;
      const activeWorkflows = workflows?.filter(w => w.is_active).length || 0;
      const pausedWorkflows = totalWorkflows - activeWorkflows;

      const totalMetrics = metrics?.reduce((acc, m) => ({
        total_executions: acc.total_executions + m.total_executions,
        successful_executions: acc.successful_executions + m.successful_executions,
        failed_executions: acc.failed_executions + m.failed_executions,
        total_execution_time: acc.total_execution_time + (m.average_execution_time_ms * m.total_executions),
      }), {
        total_executions: 0,
        successful_executions: 0,
        failed_executions: 0,
        total_execution_time: 0,
      }) || {
        total_executions: 0,
        successful_executions: 0,
        failed_executions: 0,
        total_execution_time: 0,
      };

      const successRate = totalMetrics.total_executions > 0 
        ? (totalMetrics.successful_executions / totalMetrics.total_executions) * 100 
        : 100;

      const averageExecutionTime = totalMetrics.total_executions > 0
        ? totalMetrics.total_execution_time / totalMetrics.total_executions
        : 0;

      setHealth({
        totalWorkflows,
        activeWorkflows,
        pausedWorkflows,
        recentExecutions: recentExecutions?.length || 0,
        successRate,
        averageExecutionTime,
        failedExecutions: totalMetrics.failed_executions,
        stuckExecutions: stuckExecutions?.length || 0,
        metrics: metrics || [],
      });

    } catch (error) {
      console.error('Error fetching workflow health:', error);
      toast({
        title: 'Error',
        description: 'Failed to load workflow health metrics',
        variant: 'destructive',
      });
    } finally {
      setLoading(false);
    }
  };

  const cleanupStuckExecutions = async () => {
    try {
      const { data, error } = await supabase.rpc('cleanup_workflow_executions');

      if (error) throw error;

      toast({
        title: 'Success',
        description: `Cleaned up ${data || 0} stuck executions`,
      });

      // Refresh health metrics
      await fetchWorkflowHealth();
    } catch (error) {
      console.error('Error cleaning up executions:', error);
      toast({
        title: 'Error',
        description: 'Failed to cleanup stuck executions',
        variant: 'destructive',
      });
    }
  };

  const getHealthStatus = (): 'healthy' | 'warning' | 'critical' => {
    if (!health) return 'warning';

    if (health.stuckExecutions > 0) return 'critical';
    if (health.successRate < 80) return 'critical';
    if (health.successRate < 95) return 'warning';
    if (health.activeWorkflows === 0 && health.totalWorkflows > 0) return 'warning';

    return 'healthy';
  };

  const getHealthColor = () => {
    const status = getHealthStatus();
    switch (status) {
      case 'healthy': return 'text-green-600';
      case 'warning': return 'text-yellow-600';
      case 'critical': return 'text-red-600';
      default: return 'text-gray-600';
    }
  };

  const getHealthIcon = () => {
    const status = getHealthStatus();
    switch (status) {
      case 'healthy': return '';
      case 'warning': return '';
      case 'critical': return '';
      default: return '';
    }
  };

  useEffect(() => {
    fetchWorkflowHealth();
  }, []);

  return {
    health,
    loading,
    fetchWorkflowHealth,
    cleanupStuckExecutions,
    getHealthStatus,
    getHealthColor,
    getHealthIcon,
    refetch: fetchWorkflowHealth,
  };
}

import { useState, useEffect } from "react";
import { supabase } from "@/integrations/supabase/client";
import { useToast } from "@/hooks/use-toast";

interface WorkingHour {
  id?: string;
  day_of_week: number;
  enabled: boolean;
  start_time: string | null;
  end_time: string | null;
}

export function useWorkingHours() {
  const [workingHours, setWorkingHours] = useState<WorkingHour[]>([]);
  const [loading, setLoading] = useState(true);
  const { toast } = useToast();

  const fetchWorkingHours = async () => {
    try {
      setLoading(true);
      const { data: { user } } = await supabase.auth.getUser();

      if (!user) {
        setLoading(false);
        return;
      }

      const { data, error } = await supabase
        .from('working_hours')
        .select('*')
        .eq('user_id', user.id)
        .order('day_of_week');

      if (error) throw error;

      setWorkingHours(data || []);
    } catch (error) {
      console.error('Error fetching working hours:', error);
      toast({
        title: "Error",
        description: "Failed to load working hours",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const updateWorkingHour = async (dayOfWeek: number, updates: Partial<WorkingHour>) => {
    try {
      const { data: { user } } = await supabase.auth.getUser();

      if (!user) throw new Error("No user found");

      const { data, error } = await supabase
        .from('working_hours')
        .update(updates)
        .eq('user_id', user.id)
        .eq('day_of_week', dayOfWeek)
        .select()
        .single();

      if (error) throw error;

      setWorkingHours(prev => 
        prev.map(wh => 
          wh.day_of_week === dayOfWeek ? { ...wh, ...updates } : wh
        )
      );

      return { success: true };
    } catch (error) {
      console.error('Error updating working hour:', error);
      toast({
        title: "Error",
        description: "Failed to update working hours",
        variant: "destructive",
      });
      return { success: false, error };
    }
  };

  useEffect(() => {
    fetchWorkingHours();
  }, []);

  return {
    workingHours,
    loading,
    updateWorkingHour,
    refetch: fetchWorkingHours
  };
}

import React, { useState, useEffect, useRef, useMemo, useCallback } from 'react';
import { performanceMonitor } from '@/utils/performance';

interface VirtualizedListProps<T> {
  items: T[];
  renderItem: (item: T, index: number) => React.ReactNode;
  itemHeight: number;
  containerHeight: number;
  overscan?: number;
  className?: string;
  'aria-label'?: string;
}

/**
 * High-performance virtualized list component
 * Only renders items that are currently visible
 */
export function VirtualizedList<T>({
  items,
  renderItem,
  itemHeight,
  containerHeight,
  overscan = 5,
  className = '',
  'aria-label': ariaLabel = 'Virtual list'
}: VirtualizedListProps<T>) {
  const [scrollTop, setScrollTop] = useState(0);
  const scrollElementRef = useRef<HTMLDivElement>(null);

  // Calculate visible range with memoization
  const { startIndex, endIndex, visibleItems } = useMemo(() => {
    performanceMonitor.startTiming('VirtualizedList.calculateRange');

    const start = Math.max(0, Math.floor(scrollTop / itemHeight) - overscan);
    const end = Math.min(
      items.length - 1,
      Math.ceil((scrollTop + containerHeight) / itemHeight) + overscan
    );

    const visible = items.slice(start, end + 1);

    performanceMonitor.endTiming('VirtualizedList.calculateRange');

    return {
      startIndex: start,
      endIndex: end,
      visibleItems: visible
    };
  }, [scrollTop, itemHeight, containerHeight, overscan, items]);

  // Optimized scroll handler with requestAnimationFrame
  const handleScroll = useCallback((e: React.UIEvent<HTMLDivElement>) => {
    const target = e.currentTarget;
    requestAnimationFrame(() => {
      setScrollTop(target.scrollTop);
    });
  }, []);

  // Keyboard navigation support
  const handleKeyDown = useCallback((e: React.KeyboardEvent) => {
    if (!scrollElementRef.current) return;

    const container = scrollElementRef.current;
    let newScrollTop = scrollTop;

    switch (e.key) {
      case 'ArrowDown':
        e.preventDefault();
        newScrollTop = Math.min(
          (items.length - 1) * itemHeight,
          scrollTop + itemHeight
        );
        break;
      case 'ArrowUp':
        e.preventDefault();
        newScrollTop = Math.max(0, scrollTop - itemHeight);
        break;
      case 'PageDown':
        e.preventDefault();
        newScrollTop = Math.min(
          (items.length - 1) * itemHeight,
          scrollTop + containerHeight
        );
        break;
      case 'PageUp':
        e.preventDefault();
        newScrollTop = Math.max(0, scrollTop - containerHeight);
        break;
      case 'Home':
        if (e.ctrlKey) {
          e.preventDefault();
          newScrollTop = 0;
        }
        break;
      case 'End':
        if (e.ctrlKey) {
          e.preventDefault();
          newScrollTop = (items.length - 1) * itemHeight;
        }
        break;
    }

    if (newScrollTop !== scrollTop) {
      container.scrollTop = newScrollTop;
    }
  }, [scrollTop, itemHeight, containerHeight, items.length]);

  // Total height for scrollbar
  const totalHeight = items.length * itemHeight;

  return (
    <div
      ref={scrollElementRef}
      className={`overflow-auto ${className}`}
      style={{ height: containerHeight }}
      onScroll={handleScroll}
      onKeyDown={handleKeyDown}
      tabIndex={0}
      role="list"
      aria-label={ariaLabel}
      aria-rowcount={items.length}
    >
      <div style={{ height: totalHeight, position: 'relative' }}>
        <div
          style={{
            transform: `translateY(${startIndex * itemHeight}px)`,
            position: 'absolute',
            width: '100%'
          }}
        >
          {visibleItems.map((item, index) => (
            <div
              key={startIndex + index}
              style={{ height: itemHeight }}
              role="listitem"
              aria-rowindex={startIndex + index + 1}
            >
              {renderItem(item, startIndex + index)}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

/**
 * Hook for managing virtualized list state
 */
export function useVirtualizedList<T>(
  items: T[],
  itemHeight: number,
  containerHeight: number
) {
  const [scrollTop, setScrollTop] = useState(0);

  const scrollToIndex = useCallback((index: number) => {
    const targetScrollTop = Math.max(0, Math.min(
      index * itemHeight,
      (items.length - 1) * itemHeight
    ));
    setScrollTop(targetScrollTop);
  }, [itemHeight, items.length]);

  const scrollToTop = useCallback(() => {
    setScrollTop(0);
  }, []);

  const scrollToBottom = useCallback(() => {
    setScrollTop((items.length - 1) * itemHeight);
  }, [items.length, itemHeight]);

  return {
    scrollTop,
    scrollToIndex,
    scrollToTop,
    scrollToBottom
  };
}

import React, { useState, useEffect } from 'react';
import { Image as ImageIcon, Trash2, Edit3, Copy } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { useToast } from '@/hooks/use-toast';
import { supabase } from '@/integrations/supabase/client';
import { useOrganization } from '@/contexts/OrganizationContext';
import { useMessagesTranslation } from '@/hooks/useTypedTranslation';
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from '@/components/ui/alert-dialog';
import { useTranslation } from 'react-i18next';

interface TemplateAsset {
  id: string;
  file_name: string;
  file_path: string;
  content_type: string;
  file_size: number;
  alt_text: string | null;
  created_at: string;
}

interface ImageManagerProps {
  onImageSelect: (imageUrl: string, altText?: string) => void;
  templateId?: string;
  className?: string;
}

export function ImageManager({ onImageSelect, templateId, className }: ImageManagerProps) {
  const [assets, setAssets] = useState<TemplateAsset[]>([]);
  const [loading, setLoading] = useState(true);
  const [editingAlt, setEditingAlt] = useState<string | null>(null);
  const [altText, setAltText] = useState('');
  const { toast } = useToast();
  const { t: tMessages } = useMessagesTranslation();
  const { activeOrganization } = useOrganization();
  const { t } = useTranslation("pages");
  const { t: tCommon } = useTranslation("common");

  const loadAssets = async () => {
    if (!activeOrganization?.id) return;

    try {
      setLoading(true);
      const { data, error } = await supabase
        .from('template_assets')
        .select('*')
        .eq('organization_id', activeOrganization.id)
        .order('created_at', { ascending: false });

      if (error) throw error;
      setAssets(data || []);
    } catch (error) {
      console.error('Error loading assets:', error);
      toast({
        title: tCommon('toast.error'),
        description: t('templateBuilder.imageManager.messages.loadError'),
        variant: 'destructive',
      });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadAssets();
  }, [activeOrganization?.id]);

  const deleteAsset = async (asset: TemplateAsset) => {
    try {
      // Delete from storage
      const { error: storageError } = await supabase.storage
        .from('template-images')
        .remove([asset.file_path]);

      if (storageError) throw storageError;

      // Delete from database
      const { error: dbError } = await supabase
        .from('template_assets')
        .delete()
        .eq('id', asset.id);

      if (dbError) throw dbError;

      setAssets(prev => prev.filter(a => a.id !== asset.id));

      toast({
        title: tCommon('toast.success'),
        description: t('templateBuilder.imageManager.messages.deleteSuccess'),
      });
    } catch (error) {
      console.error('Error deleting asset:', error);
      toast({
        title: tCommon('toast.error'),
        description: t('templateBuilder.imageManager.messages.deleteError'),
        variant: 'destructive',
      });
    }
  };

  const updateAltText = async (assetId: string, newAltText: string) => {
    try {
      const { error } = await supabase
        .from('template_assets')
        .update({ alt_text: newAltText })
        .eq('id', assetId);

      if (error) throw error;

      setAssets(prev => prev.map(asset => 
        asset.id === assetId ? { ...asset, alt_text: newAltText } : asset
      ));

      toast({
        title: tCommon('toast.success'),
        description: t('templateBuilder.imageManager.messages.altSuccess'),
      });
    } catch (error) {
      console.error('Error updating alt text:', error);
      toast({
        title: tCommon('toast.error'),
        description: t('templateBuilder.imageManager.messages.altError'),
        variant: 'destructive',
      });
    }
  };

  const getImageUrl = (filePath: string) => {
    const { data: { publicUrl } } = supabase.storage
      .from('template-images')
      .getPublicUrl(filePath);
    return publicUrl;
  };

  const copyImageUrl = (filePath: string) => {
    const url = getImageUrl(filePath);
    navigator.clipboard.writeText(url);
    toast({
      title: tCommon('toast.success'),
      description: t('templateBuilder.imageManager.messages.copySuccess'),
    });
  };

  const formatFileSize = (bytes: number) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  if (loading) {
    return (
      <div className={`space-y-4 ${className}`}>
        <div className="flex items-center gap-2">
          <ImageIcon className="w-5 h-5" />
          <h3 className="font-medium">{t('templateBuilder.imageManager.loading')}</h3>
        </div>
        <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
          {[...Array(6)].map((_, i) => (
            <div key={i} className="aspect-square bg-muted animate-pulse rounded-lg" />
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className={`space-y-4 ${className}`}>
      <div className="flex items-center gap-2">
        <ImageIcon className="w-5 h-5" />
        <h3 className="font-medium">{t('templateBuilder.imageManager.titleWithCount', { count: assets.length })}</h3>
      </div>

      {assets.length === 0 ? (
        <div className="text-center py-8 text-muted-foreground">
          <ImageIcon className="w-12 h-12 mx-auto mb-3 opacity-50" />
          <p>{t('templateBuilder.imageManager.empty.title')}</p>
          <p className="text-sm">{t('templateBuilder.imageManager.empty.description')}</p>
        </div>
      ) : (
        <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
          {assets.map((asset) => (
            <div key={asset.id} className="group relative bg-card border rounded-lg overflow-hidden">
              <div className="aspect-square relative">
                <img
                  src={getImageUrl(asset.file_path)}
                  alt={asset.alt_text || asset.file_name}
                  className="w-full h-full object-cover cursor-pointer hover:scale-105 transition-transform"
                  onClick={() => onImageSelect(getImageUrl(asset.file_path), asset.alt_text || undefined)}
                />

                {/* Overlay with actions */}
                <div className="absolute inset-0 bg-black/50 opacity-0 group-hover:opacity-100 transition-opacity flex items-center justify-center gap-2">
                  <Button
                    size="sm"
                    variant="secondary"
                    onClick={() => onImageSelect(getImageUrl(asset.file_path), asset.alt_text || undefined)}
                  >
                    {t('templateBuilder.imageManager.actions.insert')}
                  </Button>
                  <Button
                    size="sm"
                    variant="outline"
                    onClick={() => copyImageUrl(asset.file_path)}
                  >
                    <Copy className="w-4 h-4" />
                  </Button>
                  <AlertDialog>
                    <AlertDialogTrigger asChild>
                      <Button size="sm" variant="destructive">
                        <Trash2 className="w-4 h-4" />
                      </Button>
                    </AlertDialogTrigger>
                    <AlertDialogContent>
                      <AlertDialogHeader>
                        <AlertDialogTitle>{t('templateBuilder.imageManager.actions.deleteTitle')}</AlertDialogTitle>
                        <AlertDialogDescription>
                          {tMessages('confirm.deleteImage')} {tMessages('confirm.cannotUndo')}
                        </AlertDialogDescription>
                      </AlertDialogHeader>
                      <AlertDialogFooter>
                        <AlertDialogCancel>{tCommon('buttons.cancel')}</AlertDialogCancel>
                        <AlertDialogAction onClick={() => deleteAsset(asset)}>
                          {tCommon('buttons.delete')}
                        </AlertDialogAction>
                      </AlertDialogFooter>
                    </AlertDialogContent>
                  </AlertDialog>
                </div>
              </div>

              {/* Image info */}
              <div className="p-3 space-y-2">
                <div className="flex items-center justify-between">
                  <p className="text-sm font-medium truncate">{asset.file_name}</p>
                  <Button
                    size="sm"
                    variant="ghost"
                    onClick={() => {
                      setEditingAlt(asset.id);
                      setAltText(asset.alt_text || '');
                    }}
                  >
                    <Edit3 className="w-3 h-3" />
                  </Button>
                </div>

                {editingAlt === asset.id ? (
                  <div className="flex gap-1">
                    <Input
                      value={altText}
                      onChange={(e) => setAltText(e.target.value)}
                      placeholder={t('templateBuilder.imageManager.actions.altPlaceholder')}
                      className="text-xs h-7"
                      onKeyDown={(e) => {
                        if (e.key === 'Enter') {
                          updateAltText(asset.id, altText);
                          setEditingAlt(null);
                        }
                        if (e.key === 'Escape') {
                          setEditingAlt(null);
                        }
                      }}
                      autoFocus
                    />
                    <Button
                      size="sm"
                      onClick={() => {
                        updateAltText(asset.id, altText);
                        setEditingAlt(null);
                      }}
                      className="h-7 px-2"
                    >
                      {tCommon('buttons.save')}
                    </Button>
                  </div>
                ) : (
                  <p className="text-xs text-muted-foreground truncate">
                    {asset.alt_text || t('templateBuilder.imageManager.messages.noAltText')}
                  </p>
                )}

                <p className="text-xs text-muted-foreground">
                  {formatFileSize(asset.file_size)}
                </p>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

