import React, { useState } from "react";
import { format } from "date-fns";
import { CalendarIcon } from "lucide-react";
import type { DateRange } from "react-day-picker";
import { cn, getUserLocale } from "@/lib/utils";
import { Button } from "@/components/ui/button";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import ReactCalendar from "react-calendar";
import "react-calendar/dist/Calendar.css";
import "@/components/react-calendar.css";
import { useTranslation } from "react-i18next";

interface DateRangePickerProps {
  dateRange?: DateRange;
  onDateRangeChange: (range: DateRange | undefined) => void;
  className?: string;
}

export const DateRangePicker = ({ dateRange, onDateRangeChange, className }: DateRangePickerProps) => {
  const { t } = useTranslation('forms');
  const [isOpen, setIsOpen] = useState(false);

  const browserLocale = getUserLocale();

  const formatDateRange = (range: DateRange | undefined) => {
    if (!range?.from) {
      return t('dateRangePicker.placeholder');
    }
    if (!range.to) {
      return format(range.from, "MMM dd, yyyy");
    }
    return `${format(range.from, "MMM dd, yyyy")} - ${format(range.to, "MMM dd, yyyy")}`;
  };

  return (
    <Popover open={isOpen} onOpenChange={setIsOpen}>
      <PopoverTrigger asChild>
        <Button
          variant="outline"
          size="sm"
          className={cn(
            "justify-start text-left font-normal whitespace-nowrap max-w-xs",
            !dateRange && "text-muted-foreground",
            className
          )}
        >
          <CalendarIcon className="mr-2 h-4 w-4" />
          {formatDateRange(dateRange)}
        </Button>
      </PopoverTrigger>
      <PopoverContent className="w-auto p-0 border shadow-lg" align="start">
        <ReactCalendar
          className="react-calendar w-full p-2 pointer-events-auto"
          locale={browserLocale}
          selectRange
          next2Label={null}
          prev2Label={null}
          onChange={(value: any) => {
            if (Array.isArray(value)) {
              const [from, to] = value as [Date | null, Date | null];
              const range: DateRange | undefined = from
                ? { from: from as Date, to: (to as Date) || undefined }
                : undefined;
              onDateRangeChange(range);
              if (from && to) setIsOpen(false);
            }
          }}
          value={dateRange?.from ? [dateRange.from, dateRange.to || dateRange.from] : null}
          formatShortWeekday={(_, date) => new Intl.DateTimeFormat(browserLocale, { weekday: 'short' }).format(date)}
        />
      </PopoverContent>
    </Popover>
  );
};

import { AlertTriangle, Clock, ChevronRight } from "lucide-react";
import { Badge } from "@/components/ui/badge";
import { formatTime, cn, getUserLocale, formatLongDate } from "@/lib/utils";
import { getRelativeDate, isOverdueSession } from "@/lib/dateUtils";
import { SessionStatusBadge } from "@/components/SessionStatusBadge";
import { useOrganizationSettings } from "@/hooks/useOrganizationSettings";
import { getDisplaySessionName } from "@/lib/sessionUtils";
import { useFormsTranslation } from '@/hooks/useTypedTranslation';
import { TruncatedTextWithTooltip } from "@/components/TruncatedTextWithTooltip";

type SessionStatus = "planned" | "completed" | "cancelled" | "no_show" | "rescheduled" | "in_post_processing" | "delivered";

interface Session {
  id: string;
  session_name?: string | null;
  session_date: string;
  session_time?: string;
  notes?: string;
  status: SessionStatus;
  project_id?: string;
  lead_id: string;
  projects?: {
    name: string;
    project_types?: {
      name: string;
    };
  };
  leads?: {
    name?: string;
  };
}

interface DeadSimpleSessionBannerProps {
  session: Session;
  onClick: (sessionId: string) => void;
}

const DeadSimpleSessionBanner = ({ session, onClick }: DeadSimpleSessionBannerProps) => {
  const { settings: orgSettings } = useOrganizationSettings();
  const userLocale = getUserLocale();
  const { t } = useFormsTranslation();

  const formatSessionTime = (timeString: string): string => {
    return formatTime(timeString, userLocale, orgSettings?.time_format || undefined);
  };

  const formatSessionDate = (dateString: string): string => {
    const relativeDate = getRelativeDate(dateString, t);
    const todayText = t('relativeDates.today');
    const tomorrowText = t('relativeDates.tomorrow');
    const yesterdayText = t('relativeDates.yesterday');

    if (relativeDate === todayText || relativeDate === tomorrowText || relativeDate === yesterdayText) {
      return relativeDate;
    }
    // Use browser locale for long date format (e.g., "22 AÄŸu 2025 Cum")
    return formatLongDate(dateString, userLocale);
  };

  const getTimeIndicator = (session: Session) => {
    const relativeDate = getRelativeDate(session.session_date, t);
    const isOverdue = isOverdueSession(session.session_date, session.status);

    if (isOverdue) {
      return {
        label: t('relativeDates.past_due'),
        leftBorder: "border-l-4 border-l-orange-500",
        labelBg: "bg-orange-100 text-orange-800"
      };
    }

    if (relativeDate === t('relativeDates.today')) {
      return {
        label: t('relativeDates.today'),
        leftBorder: "border-l-4 border-l-blue-500",
        labelBg: "bg-blue-100 text-blue-800"
      };
    }

    if (relativeDate === t('relativeDates.tomorrow')) {
      return {
        label: t('relativeDates.tomorrow'),
        leftBorder: "border-l-4 border-l-green-500",
        labelBg: "bg-green-100 text-green-800"
      };
    }

    return {
      label: null,
      leftBorder: "border-l-4 border-l-gray-200",
      labelBg: null
    };
  };

  const timeIndicator = getTimeIndicator(session);
  const isOverdue = isOverdueSession(session.session_date, session.status);

  return (
    <div
      className={cn(
        "w-full bg-white border border-gray-200 rounded-lg p-4 cursor-pointer hover:bg-gray-50 transition-colors",
        timeIndicator.leftBorder
      )}
      onClick={() => onClick(session.id)}
    >
      <div className="flex flex-col sm:flex-row sm:items-center justify-between w-full gap-3 sm:gap-4">
        {/* Left: Date and Time (2 lines) */}
        <div className="flex-shrink-0">
          <div className="text-sm font-medium text-gray-900">
            {formatSessionDate(session.session_date)}
          </div>
          {session.session_time && (
            <div className="text-xs text-gray-500 flex items-center gap-1 mt-0.5">
              <Clock className="h-3 w-3" />
              {formatSessionTime(session.session_time)}
            </div>
          )}
        </div>

        {/* Divider - only on desktop */}
        <div className="hidden sm:block w-px h-8 bg-gray-200"></div>

        {/* Middle: Session details */}
        <div className="flex-1 min-w-0 space-y-1">
          <div className="flex items-center gap-2 flex-wrap">
            <span className="text-sm font-semibold text-gray-900">
              {getDisplaySessionName(session)}
            </span>
            {timeIndicator.label && (
              <Badge variant="secondary" className={cn("text-xs px-2 py-1", timeIndicator.labelBg)}>
                {timeIndicator.label}
              </Badge>
            )}
            {isOverdue && (
              <AlertTriangle className="h-4 w-4 text-orange-500 flex-shrink-0" />
            )}
          </div>

          {session.projects?.name && (
            <div className="text-xs text-gray-500">
              {t('sessionLabels.project')}: {session.projects.name}
            </div>
          )}

          {session.notes && (
            <TruncatedTextWithTooltip
              text={session.notes}
              lines={2}
              className="text-xs text-gray-600"
              as="div"
              tooltipSide="bottom"
              tooltipAlign="start"
            />
          )}
        </div>

        {/* Right: Status and arrow */}
        <div className="flex items-center gap-3 flex-shrink-0 self-start sm:self-center">
          <SessionStatusBadge
            sessionId={session.id}
            currentStatus={session.status as any}
            editable={false}
            onStatusChange={() => {}}
            size="sm"
          />
          <ChevronRight className="h-4 w-4 text-gray-400" />
        </div>
      </div>
    </div>
  );
};

export default DeadSimpleSessionBanner;

import { useState } from "react";
import { Badge } from "@/components/ui/badge";
import DeadSimpleSessionBanner from "@/components/DeadSimpleSessionBanner";
import { sortSessionsByLifecycle, SessionWithStatus } from "@/lib/sessionSorting";
import { useFormsTranslation } from '@/hooks/useTypedTranslation';

type SessionStatus = "planned" | "completed" | "cancelled" | "no_show" | "rescheduled" | "in_post_processing" | "delivered";

interface Session extends SessionWithStatus {
  session_time?: string;
  notes?: string;
}

interface EnhancedSessionsSectionProps {
  sessions: Session[];
  loading?: boolean;
  onSessionClick: (sessionId: string) => void;
}

const EnhancedSessionsSection = ({ sessions, loading, onSessionClick }: EnhancedSessionsSectionProps) => {
  const { t } = useFormsTranslation();

  if (loading) {
    return (
      <div className="w-full bg-blue-50/50 border border-blue-100 rounded-lg p-6">
        <div className="animate-pulse space-y-3">
          <div className="h-4 bg-muted rounded w-1/4"></div>
          <div className="space-y-3">
            {[1, 2, 3, 4].map((i) => (
              <div key={i} className="h-16 bg-muted rounded-lg"></div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  if (sessions.length === 0) {
    return null;
  }

  // Sort sessions by lifecycle priority (active -> completed -> cancelled)
  const sortedSessions = sortSessionsByLifecycle(sessions);

  return (
    <div className="w-full bg-blue-50/50 border border-blue-100 rounded-lg p-6 mb-6">
      <div className="flex items-center justify-between mb-4">
        <h3 className="text-lg font-semibold">{t('sessions_form.title')}</h3>
        <Badge variant="secondary">
          {sessions.length}
        </Badge>
      </div>

      <div className="space-y-3">
        {sortedSessions.map((session) => (
          <DeadSimpleSessionBanner
            key={session.id}
            session={session as any}
            onClick={onSessionClick}
          />
        ))}
      </div>
    </div>
  );
};

export default EnhancedSessionsSection;

import { useState } from "react";
import { DragDropContext, Droppable, Draggable } from "@hello-pangea/dnd";
import {
  Edit,
  Trash2,
  GripVertical,
  Eye,
  EyeOff,
  AlertCircle,
} from "lucide-react";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from "@/components/ui/table";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
} from "@/components/ui/alert-dialog";
import { LeadFieldDefinition, FIELD_TYPE_CONFIG } from "@/types/leadFields";
import { useTranslation } from "react-i18next";
import { useIsMobile } from "@/hooks/use-mobile";
import { cn } from "@/lib/utils";
import { IconActionButton } from "@/components/ui/icon-action-button";
import { IconActionButtonGroup } from "@/components/ui/icon-action-button-group";

interface LeadFieldsListProps {
  fields: LeadFieldDefinition[];
  onEdit: (field: LeadFieldDefinition) => void;
  onDelete: (id: string) => Promise<void>;
  onReorder: (fields: LeadFieldDefinition[]) => Promise<void>;
}

export function LeadFieldsList({
  fields,
  onEdit,
  onDelete,
  onReorder,
}: LeadFieldsListProps) {
  const [deleteField, setDeleteField] = useState<LeadFieldDefinition | null>(
    null
  );
  const { t } = useTranslation("forms");
  const isMobile = useIsMobile();

  const handleDragEnd = async (result: any) => {
    if (!result.destination) return;

    const items = Array.from(fields);
    const [reorderedItem] = items.splice(result.source.index, 1);
    items.splice(result.destination.index, 0, reorderedItem);

    try {
      await onReorder(items);
    } catch (error) {
      console.error("Failed to reorder fields:", error);
    }
  };

  const handleDelete = async () => {
    if (!deleteField) return;

    try {
      await onDelete(deleteField.id);
      setDeleteField(null);
    } catch (error) {
      console.error("Failed to delete field:", error);
    }
  };

  const getFieldTypeLabel = (fieldType: string) => {
    return (
      FIELD_TYPE_CONFIG[fieldType as keyof typeof FIELD_TYPE_CONFIG]?.label ||
      fieldType
    );
  };

  if (fields.length === 0) {
    return (
      <div className="text-center py-12">
        <p className="text-muted-foreground">{t("lead_fields.no_fields")}</p>
        <p className="text-sm text-muted-foreground mt-1">
          {t("lead_fields.no_fields_description")}
        </p>
      </div>
    );
  }

  const mobileContent = (
    <DragDropContext onDragEnd={handleDragEnd}>
      <Droppable droppableId="fields-mobile">
        {(provided) => (
          <div
            ref={provided.innerRef}
            {...provided.droppableProps}
            className="space-y-3"
          >
            {fields.map((field, index) => (
              <Draggable
                key={field.id}
                draggableId={field.id}
                index={index}
                isDragDisabled={field.is_system}
              >
                {(provided, snapshot) => {
                  const dragHandleProps: Record<string, unknown> = !field.is_system
                    ? (provided.dragHandleProps ?? {})
                    : {};
                  return (
                    <div
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      className={cn(
                        "rounded-lg border border-border/60 bg-card p-4 shadow-sm transition-shadow",
                        snapshot.isDragging && "shadow-lg ring-2 ring-primary/30"
                      )}
                    >
                      <div className="flex items-start justify-between gap-3">
                        <div className="min-w-0 space-y-2">
                          <p className="text-base font-semibold leading-tight text-foreground break-words">
                            {field.label}
                          </p>
                          <div className="flex flex-wrap items-center gap-2 text-xs uppercase tracking-wide text-muted-foreground">
                            <span>{getFieldTypeLabel(field.field_type)}</span>
                            <Badge variant="secondary">
                              {field.is_system
                                ? t("lead_fields.table.system_value")
                                : t("lead_fields.table.custom_value")}
                            </Badge>
                          </div>
                        </div>
                        <button
                          type="button"
                          className={cn(
                            "rounded-md p-2 text-muted-foreground/70 transition-colors",
                            field.is_system
                              ? "cursor-not-allowed opacity-30"
                              : "cursor-grab active:cursor-grabbing hover:text-foreground"
                          )}
                          aria-label="Reorder field"
                          disabled={field.is_system}
                          {...dragHandleProps}
                        >
                          <GripVertical className="h-4 w-4" />
                        </button>
                      </div>

                      <div className="mt-3 grid grid-cols-1 gap-2 text-sm text-muted-foreground">
                        <div>
                          <span className="font-medium text-foreground">
                            {t("lead_fields.table.required")}:
                          </span>{" "}
                          {field.is_required
                            ? t("lead_fields.table.required_value")
                            : t("lead_fields.table.optional_value")}
                        </div>
                        <div className="flex flex-wrap items-center gap-2">
                          <span className="font-medium text-foreground">
                            {t("lead_fields.table.visible_in_form")}:
                          </span>
                          <span className="inline-flex items-center gap-1">
                            {field.is_visible_in_form ? (
                              <Eye className="h-4 w-4" />
                            ) : (
                              <EyeOff className="h-4 w-4" />
                            )}
                            {field.is_visible_in_form
                              ? t("lead_fields.table.visible_value")
                              : t("lead_fields.table.hidden_value")}
                          </span>
                        </div>
                      </div>

                      <IconActionButtonGroup className="mt-4">
                        <IconActionButton onClick={() => onEdit(field)}>
                          <Edit className="h-4 w-4" />
                          <span className="sr-only">Edit</span>
                        </IconActionButton>
                        {!field.is_system && (
                          <IconActionButton
                            onClick={() => setDeleteField(field)}
                            variant="danger"
                          >
                            <Trash2 className="h-4 w-4" />
                            <span className="sr-only">Delete</span>
                          </IconActionButton>
                        )}
                      </IconActionButtonGroup>
                    </div>
                  );
                }}
              </Draggable>
            ))}
            {provided.placeholder}
          </div>
        )}
      </Droppable>
    </DragDropContext>
  );

  const desktopContent = (
    <DragDropContext onDragEnd={handleDragEnd}>
      <Droppable droppableId="fields">
        {(provided) => (
          <Table>
            <TableHeader>
              <TableRow>
                <TableHead className="w-12"></TableHead>
                <TableHead>{t("lead_fields.table.field_name")}</TableHead>
                <TableHead>{t("lead_fields.table.type")}</TableHead>
                <TableHead>{t("lead_fields.table.required")}</TableHead>
                <TableHead>
                  {t("lead_fields.table.visible_in_form")}
                </TableHead>
                <TableHead>{t("lead_fields.table.status")}</TableHead>
                <TableHead className="w-24">
                  {t("lead_fields.table.actions")}
                </TableHead>
              </TableRow>
            </TableHeader>
            <TableBody {...provided.droppableProps} ref={provided.innerRef}>
              {fields.map((field, index) => (
                <Draggable
                  key={field.id}
                  draggableId={field.id}
                  index={index}
                  isDragDisabled={field.is_system}
                >
                  {(provided, snapshot) => (
                    <TableRow
                      ref={provided.innerRef}
                      {...provided.draggableProps}
                      className={snapshot.isDragging ? "bg-muted/50" : ""}
                    >
                      <TableCell>
                        <div
                          {...provided.dragHandleProps}
                          className={`flex items-center justify-center ${
                            field.is_system
                              ? "opacity-30 cursor-not-allowed"
                              : "cursor-grab"
                          }`}
                        >
                          <GripVertical className="h-4 w-4" />
                        </div>
                      </TableCell>
                      <TableCell>
                        <span className="font-medium">{field.label}</span>
                      </TableCell>
                      <TableCell>
                        <span className="text-sm text-muted-foreground">
                          {getFieldTypeLabel(field.field_type)}
                        </span>
                      </TableCell>
                      <TableCell>
                        <span className="text-sm text-muted-foreground">
                          {field.is_required
                            ? t("lead_fields.table.required_value")
                            : t("lead_fields.table.optional_value")}
                        </span>
                      </TableCell>
                      <TableCell>
                        <div className="flex items-center gap-1">
                          {field.is_visible_in_form ? (
                            <Eye className="h-4 w-4 text-muted-foreground" />
                          ) : (
                            <EyeOff className="h-4 w-4 text-muted-foreground" />
                          )}
                          <span className="text-sm text-muted-foreground">
                            {field.is_visible_in_form
                              ? t("lead_fields.table.visible_value")
                              : t("lead_fields.table.hidden_value")}
                          </span>
                        </div>
                      </TableCell>
                      <TableCell>
                        <span className="text-sm text-muted-foreground">
                          {field.is_system
                            ? t("lead_fields.table.system_value")
                            : t("lead_fields.table.custom_value")}
                        </span>
                      </TableCell>
                      <TableCell>
                        <IconActionButtonGroup>
                          <IconActionButton onClick={() => onEdit(field)}>
                            <Edit className="h-4 w-4" />
                          </IconActionButton>
                          {!field.is_system && (
                            <IconActionButton
                              onClick={() => setDeleteField(field)}
                              variant="danger"
                            >
                              <Trash2 className="h-4 w-4" />
                            </IconActionButton>
                          )}
                        </IconActionButtonGroup>
                      </TableCell>
                    </TableRow>
                  )}
                </Draggable>
              ))}
              {provided.placeholder}
            </TableBody>
          </Table>
        )}
      </Droppable>
    </DragDropContext>
  );

  return (
    <>
      {isMobile ? mobileContent : desktopContent}

      <AlertDialog
        open={!!deleteField}
        onOpenChange={() => setDeleteField(null)}
      >
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle className="flex items-center gap-2">
              <AlertCircle className="h-5 w-5 text-destructive" />
              {t("lead_fields.delete_dialog.title")}
            </AlertDialogTitle>
            <AlertDialogDescription>
              {t("lead_fields.delete_dialog.description", {
                fieldName: deleteField?.label,
              })}
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel>
              {t("lead_fields.delete_dialog.cancel")}
            </AlertDialogCancel>
            <AlertDialogAction
              onClick={handleDelete}
              className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
            >
              {t("lead_fields.delete_dialog.confirm")}
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </>
  );
}

import { useMemo } from "react";

const CONNECTOR_WORDS = new Set([
  "and",
  "ve",
  "ile",
  "with",
  "und",
  "the",
  "of",
  "for",
  "da",
  "de",
  "del",
  "della",
  "van",
  "von",
  "bin",
  "al",
  "el",
  "&"
]);

const DEFAULT_MAX_INITIALS = 3;
const DEFAULT_FALLBACK = "??";

export interface LeadInitialsProps {
  name?: string | null;
  fallback?: string;
  maxInitials?: number;
}

export const computeLeadInitials = (
  name?: string | null,
  fallback: string = DEFAULT_FALLBACK,
  maxInitials: number = DEFAULT_MAX_INITIALS
) => {
  if (maxInitials <= 0) {
    return fallback;
  }

  const trimmed = (name ?? "").trim();
  if (!trimmed) {
    return fallback;
  }

  const tokens = trimmed.match(/\p{L}+/gu) ?? [];
  const meaningfulTokens = tokens.filter(
    (token) => !CONNECTOR_WORDS.has(token.toLowerCase())
  );
  const candidates =
    meaningfulTokens.length > 0 ? meaningfulTokens : tokens;

  if (candidates.length === 0) {
    return fallback;
  }

  const initials: string[] = [];
  for (const token of candidates) {
    const chars = Array.from(token);
    const initial = chars[0]?.toUpperCase();
    if (initial) {
      initials.push(initial);
    }
    if (initials.length >= maxInitials) {
      break;
    }
  }

  if (initials.length === 0) {
    const fallbackToken = candidates[0];
    const fallbackChars = Array.from(fallbackToken)
      .slice(0, maxInitials)
      .join("")
      .toUpperCase();
    return fallbackChars || fallback;
  }

  const combined = initials.join("");
  return combined || fallback;
};

export function LeadInitials({
  name,
  fallback = DEFAULT_FALLBACK,
  maxInitials = DEFAULT_MAX_INITIALS
}: LeadInitialsProps) {
  const initials = useMemo(
    () => computeLeadInitials(name, fallback, maxInitials),
    [name, fallback, maxInitials]
  );

  return <>{initials}</>;
}

import { Card, CardContent, CardHeader } from "@/components/ui/card";
import { ActivityForm } from "@/components/shared/ActivityForm";
import { ActivityTimeline } from "@/components/shared/ActivityTimeline";
import { SegmentedControl } from "@/components/ui/segmented-control";
import { Skeleton } from "@/components/ui/skeleton";
import { useState, useEffect, useCallback } from "react";
import { supabase } from "@/integrations/supabase/client";
import { getUserOrganizationId } from "@/lib/organizationUtils";
import { toast } from "@/hooks/use-toast";
import { useFormsTranslation } from "@/hooks/useTypedTranslation";
import { formatLongDate, formatTime } from "@/lib/utils";
import type { Database, Json } from "@/integrations/supabase/types";
import type { PostgrestError } from "@supabase/supabase-js";

type ActivityRow = Database["public"]["Tables"]["activities"]["Row"];
type AuditLogRow = Database["public"]["Tables"]["audit_log"]["Row"];
type ProjectStatusRow = Database["public"]["Tables"]["project_statuses"]["Row"];
type ServiceRow = Database["public"]["Tables"]["services"]["Row"];

type ProjectActivity = {
  id: string;
  type: ActivityRow["type"];
  content: string;
  reminder_date: string | null;
  reminder_time: string | null;
  created_at: string;
  completed: boolean;
  lead_id: string;
  user_id: string;
  project_id?: string;
};

const mapActivityRow = (row: ActivityRow): ProjectActivity => ({
  id: row.id,
  type: row.type,
  content: row.content ?? "",
  reminder_date: row.reminder_date,
  reminder_time: row.reminder_time,
  created_at: row.created_at,
  completed: Boolean(row.completed),
  lead_id: row.lead_id,
  user_id: row.user_id,
  project_id: row.project_id ?? undefined,
});

type AuditLogValues = Record<string, unknown> | null;

interface AuditLogEntry {
  id: string;
  user_id: string;
  entity_type: string;
  entity_id: string;
  action: string;
  old_values: AuditLogValues;
  new_values: AuditLogValues;
  created_at: string;
}

const jsonToRecord = (value: Json | null): AuditLogValues => {
  if (value && typeof value === "object" && !Array.isArray(value)) {
    return value as Record<string, unknown>;
  }
  return null;
};

const mapAuditLogRow = (row: AuditLogRow): AuditLogEntry => ({
  id: row.id,
  user_id: row.user_id,
  entity_type: row.entity_type,
  entity_id: row.entity_id,
  action: row.action,
  old_values: jsonToRecord(row.old_values),
  new_values: jsonToRecord(row.new_values),
  created_at: row.created_at,
});

const getErrorMessage = (error: unknown): string => {
  if (error instanceof Error) {
    return error.message;
  }
  if (typeof error === "string") {
    return error;
  }
  return "An unexpected error occurred";
};

const getRecordValue = <T,>(
  record: AuditLogValues,
  key: string,
  predicate: (value: unknown) => value is T
): T | undefined => {
  const value = record?.[key];
  return predicate(value) ? value : undefined;
};

const getStringValue = (record: AuditLogValues, key: string): string | undefined =>
  getRecordValue(record, key, (value): value is string => typeof value === "string");

const getNumberValue = (record: AuditLogValues, key: string): number | undefined =>
  getRecordValue(record, key, (value): value is number => typeof value === "number");

const getBooleanValue = (record: AuditLogValues, key: string): boolean | undefined =>
  getRecordValue(record, key, (value): value is boolean => typeof value === "boolean");

interface ProjectActivitySectionProps {
  projectId: string;
  leadId: string;
  leadName: string;
  projectName: string;
  onActivityUpdated?: () => void;
}

export function ProjectActivitySection({
  projectId,
  leadId,
  leadName,
  projectName,
  onActivityUpdated
}: ProjectActivitySectionProps) {
  const { t } = useFormsTranslation();
  const [activities, setActivities] = useState<ProjectActivity[]>([]);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [auditLogs, setAuditLogs] = useState<AuditLogEntry[]>([]);
  const [statusLookup, setStatusLookup] = useState<Record<string, string>>({});
  const [serviceLookup, setServiceLookup] = useState<Record<string, string>>({});
  const [selectedSegment, setSelectedSegment] = useState<"activity" | "history">("activity");

  const fetchProjectActivities = useCallback(async () => {
    try {
      const { data, error } = await supabase
        .from<ActivityRow>("activities")
        .select("*")
        .eq("lead_id", leadId)
        .eq("project_id", projectId)
        .order("created_at", { ascending: false });

      if (error) {
        throw error;
      }

      setActivities((data ?? []).map(mapActivityRow));
    } catch (error) {
      console.error("Error fetching project activities:", error);
      setActivities([]);
    }
  }, [leadId, projectId]);

  const fetchHistoryData = useCallback(async () => {
    try {
      const logMap = new Map<string, AuditLogEntry>();
      const statusIds = new Set<string>();
      const serviceIds = new Set<string>();

      const collect = (rows: AuditLogRow[] | null | undefined) => {
        (rows ?? []).forEach((row) => {
          if (!isRelevantLog(row)) {
            return;
          }
          const mapped = mapAuditLogRow(row);
          logMap.set(mapped.id, mapped);

          if (mapped.entity_type === "project") {
            const oldStatus = getStringValue(mapped.old_values, "status_id");
            const newStatus = getStringValue(mapped.new_values, "status_id");
            if (oldStatus) statusIds.add(oldStatus);
            if (newStatus) statusIds.add(newStatus);
          }

          if (mapped.entity_type === "project_service" || mapped.entity_type === "project_package") {
            const oldService = getStringValue(mapped.old_values, "service_id") ?? getStringValue(mapped.old_values, "package_id");
            const newService = getStringValue(mapped.new_values, "service_id") ?? getStringValue(mapped.new_values, "package_id");
            if (oldService) serviceIds.add(oldService);
            if (newService) serviceIds.add(newService);
          }
        });
      };

      const runQuery = async (
        query: Promise<{ data: AuditLogRow[] | null; error: PostgrestError | null }>
      ) => {
        const { data, error } = await query;
        if (error) {
          console.error("Error fetching project history logs:", error);
          return;
        }
        collect(data);
      };

      await runQuery(
        supabase
          .from<AuditLogRow>("audit_log")
          .select("*")
          .eq("entity_type", "project")
          .eq("entity_id", projectId)
          .order("created_at", { ascending: false })
          .limit(200)
      );

      const entityTypes: Array<AuditLogRow["entity_type"]> = [
        "session",
        "payment",
        "todo",
        "activity",
        "project_service",
        "project_package",
      ];

      for (const type of entityTypes) {
        await runQuery(
          supabase
            .from<AuditLogRow>("audit_log")
            .select("*")
            .eq("entity_type", type)
            .order("created_at", { ascending: false })
            .limit(200)
        );
      }

      const logs = Array.from(logMap.values()).sort(
        (a, b) => new Date(b.created_at).getTime() - new Date(a.created_at).getTime()
      );

      const lookupPromises: Promise<void>[] = [];

      if (statusIds.size > 0) {
        lookupPromises.push(
          supabase
            .from<ProjectStatusRow>("project_statuses")
            .select("id, name")
            .in("id", Array.from(statusIds))
            .then(({ data, error }) => {
              if (error || !data) {
                console.error("Error fetching project statuses for history:", error);
                setStatusLookup({});
                return;
              }
              setStatusLookup(
                data.reduce<Record<string, string>>((acc, row) => {
                  acc[row.id] = row.name;
                  return acc;
                }, {})
              );
            })
        );
      } else {
        setStatusLookup({});
      }

      if (serviceIds.size > 0) {
        lookupPromises.push(
          supabase
            .from<ServiceRow>("services")
            .select("id, name")
            .in("id", Array.from(serviceIds))
            .then(({ data, error }) => {
              if (error || !data) {
                console.error("Error fetching services for history:", error);
                setServiceLookup({});
                return;
              }
              setServiceLookup(
                data.reduce<Record<string, string>>((acc, row) => {
                  acc[row.id] = row.name ?? row.id;
                  return acc;
                }, {})
              );
            })
        );
      } else {
        setServiceLookup({});
      }

      if (lookupPromises.length > 0) {
        await Promise.all(lookupPromises);
      }

      setAuditLogs(logs);
    } catch (error) {
      console.error("Error fetching project history:", error);
      setAuditLogs([]);
      setStatusLookup({});
      setServiceLookup({});
    }
  }, [projectId]);

  const logReferencesProject = useCallback(
    (values: Json | null): boolean => {
      const record = jsonToRecord(values);
      if (!record) return false;
      const candidateKeys = ["project_id", "projectId", "project"];

      return candidateKeys.some((key) => {
        const value = record[key];
        if (typeof value === "string") {
          return value === projectId;
        }
        if (
          value &&
          typeof value === "object" &&
          "id" in (value as Record<string, unknown>)
        ) {
          const maybeId = (value as Record<string, unknown>).id;
          return typeof maybeId === "string" && maybeId === projectId;
        }
        return false;
      });
    },
    [projectId]
  );

  const isRelevantLog = useCallback(
    (row: AuditLogRow) =>
      (row.entity_type === "project" && row.entity_id === projectId) ||
      logReferencesProject(row.new_values) ||
      logReferencesProject(row.old_values),
    [projectId, logReferencesProject]
  );

  useEffect(() => {
    let cancelled = false;

    const load = async () => {
      setLoading(true);
      try {
        await Promise.all([fetchProjectActivities(), fetchHistoryData()]);
      } finally {
        if (!cancelled) {
          setLoading(false);
        }
      }
    };

    void load();

    return () => {
      cancelled = true;
    };
  }, [fetchProjectActivities, fetchHistoryData]);

  useEffect(() => {
    const channel = supabase
      .channel(`project-history-${projectId}`)
      .on(
        "postgres_changes",
        {
          event: "INSERT",
          schema: "public",
          table: "audit_log",
        },
        (payload) => {
          const row = payload.new as AuditLogRow | null;
          if (row && isRelevantLog(row)) {
            void fetchHistoryData();
          }
        }
      )
      .subscribe();

    return () => {
      void supabase.removeChannel(channel);
    };
  }, [projectId, fetchHistoryData, isRelevantLog]);

  const handleSaveActivity = async (
    content: string,
    isReminderMode: boolean,
    reminderDateTime?: string
  ) => {
    if (!content.trim()) {
      toast({
        title: t("validation.required_field"),
        description: t("validation.content_required"),
        variant: "destructive",
      });
      return;
    }
    if (isReminderMode && !reminderDateTime) {
      toast({
        title: t("validation.required_field"),
        description: t("validation.datetime_required_for_reminders"),
        variant: "destructive",
      });
      return;
    }

    setSaving(true);
    try {
      const { data: userData } = await supabase.auth.getUser();
      if (!userData.user) throw new Error('Not authenticated');

      const organizationId = await getUserOrganizationId();
      if (!organizationId) {
        throw new Error("Organization required");
      }

      const activityData: Partial<ActivityRow> & {
        user_id: string;
        lead_id: string;
        project_id: string;
        type: ActivityRow["type"];
        content: string;
        organization_id: string;
        reminder_date: string | null;
        reminder_time: string | null;
      } = {
        user_id: userData.user.id,
        lead_id,
        project_id: projectId,
        type: isReminderMode ? "reminder" : "note",
        content: content.trim(),
        reminder_date:
          isReminderMode && reminderDateTime ? reminderDateTime.split("T")[0] : null,
        reminder_time:
          isReminderMode && reminderDateTime ? reminderDateTime.split("T")[1] : null,
        organization_id: organizationId,
      };

      const { error } = await supabase
        .from('activities')
        .insert(activityData)
        .select('id')
        .single();

      if (error) throw error;

      toast({
        title: t("success.saved"),
        description: `${isReminderMode ? t("activity.reminder") : t("activity.note")} ${t("activity.added_to_project")}.`
      });

      // Refresh data
      await Promise.all([fetchProjectActivities(), fetchHistoryData()]);
      onActivityUpdated?.();
    } catch (error) {
      toast({
        title: t("error.generic"),
        description: getErrorMessage(error),
        variant: "destructive",
      });
    } finally {
      setSaving(false);
    }
  };

  const toggleCompletion = async (activityId: string, completed: boolean) => {
    try {
      const { error } = await supabase
        .from('activities')
        .update({ completed })
        .eq('id', activityId);
      if (error) throw error;

      setActivities(prev => prev.map(activity => 
        activity.id === activityId ? { ...activity, completed } : activity
      ));

      toast({
        title: completed ? t("activity.task_completed") : t("activity.task_incomplete"),
        description: t("activity.task_status_updated")
      });

      await fetchHistoryData();
      onActivityUpdated?.();
    } catch (error) {
      toast({
        title: t("activity.error_updating_task"),
        description: getErrorMessage(error),
        variant: "destructive"
      });
    }
  };

  const formatAmount = (value?: number) => {
    if (value === undefined || Number.isNaN(value)) {
      return undefined;
    }
    return new Intl.NumberFormat(undefined, {
      minimumFractionDigits: 2,
      maximumFractionDigits: 2,
    }).format(value);
  };

  const toSentenceCase = (value?: string) => {
    if (!value) return undefined;
    return value
      .replace(/_/g, " ")
      .replace(/\b\w/g, (char) => char.toUpperCase());
  };

  const getStatusName = (statusId?: string) => {
    if (!statusId) return undefined;
    return statusLookup[statusId] ?? statusId;
  };

  const getServiceName = (serviceId?: string) => {
    if (!serviceId) return undefined;
    return serviceLookup[serviceId] ?? serviceId;
  };

  const getHistoryDescription = (log: AuditLogEntry): string => {
    const notSet = t("activitiesHistory.historyMessages.notSet");

    const formatChange = (
      label: string,
      oldValue?: string | null,
      newValue?: string | null
    ) => {
      const safeOld =
        oldValue && oldValue.length > 0 ? oldValue : notSet;
      const safeNew =
        newValue && newValue.length > 0 ? newValue : notSet;
      return t("activitiesHistory.historyMessages.changeTemplate", {
        label,
        oldValue: safeOld,
        newValue: safeNew,
      });
    };

    const joinChanges = (changes: string[]) => changes.join("; ");

    const formatSessionTimeValue = (value?: string | null) => {
      if (!value) return undefined;
      const timePart = value.slice(0, 5);
      try {
        return formatTime(timePart);
      } catch {
        return value;
      }
    };

    const formatSessionDateValue = (value?: string | null) =>
      value ? formatLongDate(value) : undefined;

    if (log.entity_type === "project") {
      const name =
        getStringValue(log.new_values, "name") ??
        getStringValue(log.old_values, "name") ??
        projectName ??
        t("activitiesHistory.historyMessages.projectUnnamed");

      if (log.action === "created") {
        return name
          ? t("activitiesHistory.historyMessages.projectCreated", { name })
          : t("activitiesHistory.historyMessages.projectCreatedUnnamed");
      }
      if (log.action === "archived") {
        return name
          ? t("activitiesHistory.historyMessages.projectArchived", { name })
          : t("activitiesHistory.historyMessages.projectArchivedUnnamed");
      }
      if (log.action === "restored") {
        return name
          ? t("activitiesHistory.historyMessages.projectRestored", { name })
          : t("activitiesHistory.historyMessages.projectRestoredUnnamed");
      }
      if (log.action === "updated") {
        const changes: string[] = [];
        const oldStatusId = getStringValue(log.old_values, "status_id");
        const newStatusId = getStringValue(log.new_values, "status_id");

        if (oldStatusId !== newStatusId) {
          const oldStatus = getStatusName(oldStatusId) ?? notSet;
          const newStatus = getStatusName(newStatusId) ?? notSet;
          changes.push(
            t("activitiesHistory.historyMessages.projectStageChanged", {
              oldStatus,
              newStatus,
            })
          );
        }

        const oldName = getStringValue(log.old_values, "name");
        const newName = getStringValue(log.new_values, "name");
        if (oldName && newName && oldName !== newName) {
          changes.push(
            t("activitiesHistory.historyMessages.projectRenamed", {
              oldName,
              newName,
            })
          );
        }

        const oldPrice = getNumberValue(log.old_values, "base_price");
        const newPrice = getNumberValue(log.new_values, "base_price");
        if (
          oldPrice !== undefined &&
          newPrice !== undefined &&
          oldPrice !== newPrice
        ) {
          const formattedOld = formatAmount(oldPrice) ?? oldPrice.toString();
          const formattedNew = formatAmount(newPrice) ?? newPrice.toString();
          changes.push(
            t("activitiesHistory.historyMessages.projectBasePriceChanged", {
              oldPrice: formattedOld,
              newPrice: formattedNew,
            })
          );
        }

        const oldDescription = getStringValue(log.old_values, "description");
        const newDescription = getStringValue(log.new_values, "description");
        if (oldDescription !== newDescription) {
          changes.push(
            t("activitiesHistory.historyMessages.projectDescriptionUpdated")
          );
        }

        const oldProjectType = getStringValue(log.old_values, "project_type_id");
        const newProjectType = getStringValue(log.new_values, "project_type_id");
        if (oldProjectType !== newProjectType) {
          changes.push(
            t("activitiesHistory.historyMessages.projectTypeChanged")
          );
        }

        if (changes.length > 0) {
          return t(
            "activitiesHistory.historyMessages.projectUpdatedWithChanges",
            {
              changes: joinChanges(changes),
            }
          );
        }
        return t("activitiesHistory.historyMessages.projectUpdated");
      }
    }

    if (log.entity_type === "session") {
      const sessionName =
        getStringValue(log.new_values, "session_name") ??
        getStringValue(log.old_values, "session_name") ??
        t("activitiesHistory.historyMessages.sessionUntitled");

      if (log.action === "created") {
        return t("activitiesHistory.historyMessages.sessionCreated", {
          name: sessionName,
        });
      }
      if (log.action === "deleted") {
        return t("activitiesHistory.historyMessages.sessionDeleted", {
          name: sessionName,
        });
      }
      if (log.action === "updated") {
        const sessionChanges: string[] = [];
        const sessionFieldLabels: Record<string, string> = {
          session_date: t(
            "activitiesHistory.historyMessages.sessionFieldLabels.session_date"
          ),
          session_time: t(
            "activitiesHistory.historyMessages.sessionFieldLabels.session_time"
          ),
          session_name: t(
            "activitiesHistory.historyMessages.sessionFieldLabels.session_name"
          ),
          location: t(
            "activitiesHistory.historyMessages.sessionFieldLabels.location"
          ),
          status: t(
            "activitiesHistory.historyMessages.sessionFieldLabels.status"
          ),
          notes: t(
            "activitiesHistory.historyMessages.sessionFieldLabels.notes"
          ),
        };

        const oldDate = getStringValue(log.old_values, "session_date");
        const newDate = getStringValue(log.new_values, "session_date");
        if (oldDate !== newDate) {
          sessionChanges.push(
            formatChange(
              sessionFieldLabels.session_date,
              formatSessionDateValue(oldDate),
              formatSessionDateValue(newDate)
            )
          );
        }

        const oldTime = getStringValue(log.old_values, "session_time");
        const newTime = getStringValue(log.new_values, "session_time");
        if (oldTime !== newTime) {
          sessionChanges.push(
            formatChange(
              sessionFieldLabels.session_time,
              formatSessionTimeValue(oldTime),
              formatSessionTimeValue(newTime)
            )
          );
        }

        const oldLocation = getStringValue(log.old_values, "location");
        const newLocation = getStringValue(log.new_values, "location");
        if (oldLocation !== newLocation) {
          sessionChanges.push(
            formatChange(
              sessionFieldLabels.location,
              oldLocation,
              newLocation
            )
          );
        }

        const oldStatus = getStringValue(log.old_values, "status");
        const newStatus = getStringValue(log.new_values, "status");
        if (oldStatus !== newStatus) {
          sessionChanges.push(
            formatChange(
              sessionFieldLabels.status,
              toSentenceCase(oldStatus),
              toSentenceCase(newStatus)
            )
          );
        }

        const oldSessionName = getStringValue(log.old_values, "session_name");
        const newSessionName = getStringValue(log.new_values, "session_name");
        if (oldSessionName && newSessionName && oldSessionName !== newSessionName) {
          sessionChanges.push(
            formatChange(
              sessionFieldLabels.session_name,
              oldSessionName,
              newSessionName
            )
          );
        }

        const oldNotes = getStringValue(log.old_values, "notes");
        const newNotes = getStringValue(log.new_values, "notes");
        if (oldNotes !== newNotes) {
          sessionChanges.push(
            t("activitiesHistory.historyMessages.sessionNotesUpdated")
          );
        }

        if (sessionChanges.length > 0) {
          return t("activitiesHistory.historyMessages.sessionUpdated", {
            name: sessionName,
            changes: joinChanges(sessionChanges),
          });
        }
        return t("activitiesHistory.historyMessages.sessionUpdatedSimple", {
          name: sessionName,
        });
      }
    }

    if (log.entity_type === "payment") {
      const amount =
        getNumberValue(log.new_values, "amount") ??
        getNumberValue(log.old_values, "amount");
      const status =
        getStringValue(log.new_values, "status") ??
        getStringValue(log.old_values, "status");
      const description =
        getStringValue(log.new_values, "description") ??
        getStringValue(log.old_values, "description");

      const amountText =
        amount !== undefined
          ? formatAmount(amount) ?? `${amount}`
          : t("activitiesHistory.historyMessages.paymentUnknownAmount");

      const statusSuffix = status
        ? t("activitiesHistory.historyMessages.paymentStatusSuffix", {
            status: toSentenceCase(status) ?? status,
          })
        : "";

      const detailsSuffix = description
        ? t("activitiesHistory.historyMessages.paymentDetailsSuffix", {
            details: description,
          })
        : "";

      if (log.action === "created") {
        return t("activitiesHistory.historyMessages.paymentRecorded", {
          amount: amountText,
          detailsSuffix,
        });
      }
      if (log.action === "updated") {
        return t("activitiesHistory.historyMessages.paymentUpdated", {
          amount: amountText,
          statusSuffix,
          detailsSuffix,
        });
      }
      if (log.action === "deleted") {
        return t("activitiesHistory.historyMessages.paymentRemoved", {
          amount: amountText,
          detailsSuffix,
        });
      }
    }

    if (log.entity_type === "todo") {
      const content =
        getStringValue(log.new_values, "content") ??
        getStringValue(log.old_values, "content") ??
        t("activitiesHistory.historyMessages.todoUnnamed");

      if (log.action === "created") {
        return t("activitiesHistory.historyMessages.todoAdded", { content });
      }
      if (log.action === "deleted") {
        return t("activitiesHistory.historyMessages.todoRemoved", { content });
      }
      if (log.action === "updated") {
        const oldCompleted = getBooleanValue(log.old_values, "is_completed");
        const newCompleted = getBooleanValue(log.new_values, "is_completed");
        if (
          oldCompleted !== undefined &&
          newCompleted !== undefined &&
          oldCompleted !== newCompleted
        ) {
          return t("activitiesHistory.historyMessages.todoToggled", {
            content,
            state: t(
              newCompleted
                ? "activitiesHistory.historyMessages.todoStateCompleted"
                : "activitiesHistory.historyMessages.todoStateIncomplete"
            ),
          });
        }

        const oldTodoContent = getStringValue(log.old_values, "content");
        const newTodoContent = getStringValue(log.new_values, "content");
        if (
          oldTodoContent &&
          newTodoContent &&
          oldTodoContent !== newTodoContent
        ) {
          return t("activitiesHistory.historyMessages.todoContentUpdated", {
            oldContent: oldTodoContent,
            newContent: newTodoContent,
          });
        }

        return t("activitiesHistory.historyMessages.todoUpdated", { content });
      }
    }

    if (log.entity_type === "activity") {
      const activityType =
        getStringValue(log.new_values, "type") ??
        getStringValue(log.old_values, "type") ??
        "activity";
      const defaultLabel =
        toSentenceCase(activityType) ??
        t("activitiesHistory.historyMessages.activityLabels.activity");
      const label = t(
        `activitiesHistory.historyMessages.activityLabels.${activityType}`,
        { defaultValue: defaultLabel }
      );
      const content =
        getStringValue(log.new_values, "content") ??
        getStringValue(log.old_values, "content") ??
        "";
      const contentSuffix = content
        ? t("activitiesHistory.historyMessages.activityContentSuffix", {
            content,
          })
        : "";

      if (log.action === "created") {
        return t("activitiesHistory.historyMessages.activityAdded", {
          label,
          content: contentSuffix,
        });
      }
      if (log.action === "updated") {
        return t("activitiesHistory.historyMessages.activityUpdated", {
          label,
          content: contentSuffix,
        });
      }
      if (log.action === "deleted") {
        return t("activitiesHistory.historyMessages.activityRemoved", {
          label,
          content: contentSuffix,
        });
      }
    }

    if (
      log.entity_type === "project_service" ||
      log.entity_type === "project_package"
    ) {
      const identifier =
        getStringValue(log.new_values, "service_id") ??
        getStringValue(log.new_values, "package_id") ??
        getStringValue(log.old_values, "service_id") ??
        getStringValue(log.old_values, "package_id");
      const label =
        getServiceName(identifier) ??
        t("activitiesHistory.historyMessages.serviceFallbackLabel");

      if (log.action === "created") {
        return t("activitiesHistory.historyMessages.serviceAdded", { label });
      }
      if (log.action === "updated") {
        return t("activitiesHistory.historyMessages.serviceUpdated", { label });
      }
      if (log.action === "deleted") {
        return t("activitiesHistory.historyMessages.serviceRemoved", { label });
      }
    }

    return t("activitiesHistory.historyMessages.genericChange", {
      entity: toSentenceCase(log.entity_type) ?? log.entity_type,
      action: log.action,
    });
  };

  if (loading) {
    return (
      <Card>
        <CardHeader>
          <Skeleton className="h-6 w-48" />
        </CardHeader>
        <CardContent className="space-y-4">
          <Skeleton className="h-24 w-full" />
          <Skeleton className="h-12 w-full" />
          <Skeleton className="h-12 w-full" />
        </CardContent>
      </Card>
    );
  }

  return (
    <Card>
      <CardHeader>
        <div className="flex items-center justify-between">
          <h3 className="text-lg font-semibold">
            {t("activitiesHistory.title")}
          </h3>
          <SegmentedControl
            size="sm"
            value={selectedSegment}
            onValueChange={(value) =>
              setSelectedSegment(value as typeof selectedSegment)
            }
            options={[
              { value: "activity", label: t("activitiesHistory.activity") },
              { value: "history", label: t("activitiesHistory.history") },
            ]}
          />
        </div>
      </CardHeader>
      <CardContent>
        {selectedSegment === "activity" ? (
          <div className="space-y-6">
            <ActivityForm
              onSubmit={handleSaveActivity}
              loading={saving}
              placeholder={t("projectDetails.activities.placeholder")}
            />

            <div className="space-y-4">
              {activities.length > 0 ? (
                <ActivityTimeline
                  activities={activities}
                  leadName={leadName}
                  onToggleCompletion={toggleCompletion}
                />
              ) : (
                <div className="text-sm text-muted-foreground text-center py-8">
                  {t("activitiesHistory.noActivitiesYet")}
                </div>
              )}
            </div>
          </div>
        ) : (
          <div className="space-y-4">
            {auditLogs.length > 0 ? (
              <div className="space-y-1">
                {auditLogs.map((log) => (
                  <div
                    key={log.id}
                    className="flex justify-between items-start p-2 text-sm hover:bg-muted/30 rounded"
                  >
                    <p className="text-foreground break-words flex-1">
                      {getHistoryDescription(log)}
                    </p>
                    <div className="text-xs text-muted-foreground whitespace-nowrap ml-4">
                      {formatLongDate(log.created_at)}
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <div className="text-sm text-muted-foreground text-center py-8">
                {t("activitiesHistory.noHistoryAvailable")}
              </div>
            )}
          </div>
        )}
      </CardContent>
    </Card>
  );
}

import { useState, useEffect } from "react";
// Import namespace form to avoid ESM/CJS interop edge cases that can surface as
// "Component is not a function" inside DragDropContext's ErrorBoundary.
import * as DnD from "@hello-pangea/dnd";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Loader2, Plus } from "lucide-react";
import { supabase } from "@/integrations/supabase/client";
import { useI18nToast } from "@/lib/toastHelpers";
import { ViewProjectDialog } from "@/components/ViewProjectDialog";
import { ProfessionalKanbanCard } from "@/components/ProfessionalKanbanCard";
import { KanbanLoadingSkeleton } from "@/components/ui/loading-presets";
import { useNotificationTriggers } from "@/hooks/useNotificationTriggers";
import { useOrganization } from "@/contexts/OrganizationContext";
import { useKanbanSettings } from "@/hooks/useKanbanSettings";
import { useTranslation } from 'react-i18next';
import { LIFECYCLE_STATES, PROJECT_STATUS } from "@/constants/entityConstants";
import type { ProjectListItem, ProjectStatusSummary } from "@/pages/projects/types";
import { ProjectCreationWizardSheet } from "@/features/project-creation";
import { cn } from "@/lib/utils";
import { promoteProjectToTop, PROJECT_SORT_GAP } from "@/lib/projects/sortOrder";

interface ProjectKanbanBoardProps {
  projects: ProjectListItem[];
  projectStatuses?: ProjectStatusSummary[];
  onProjectsChange: () => void;
  onProjectUpdate?: (project: ProjectListItem) => void;
  onQuickView?: (project: ProjectListItem) => void;
  isLoading?: boolean;
  onLoadMore?: () => void;
  hasMore?: boolean;
  isLoadingMore?: boolean;
}

const GAP = PROJECT_SORT_GAP;

const orderProjects = (list: ProjectListItem[]) =>
  [...list].sort((a, b) => {
    const ao = a.sort_order ?? Number.MAX_SAFE_INTEGER;
    const bo = b.sort_order ?? Number.MAX_SAFE_INTEGER;
    if (ao !== bo) return ao - bo;
    const ad = a.created_at ? new Date(a.created_at).getTime() : 0;
    const bd = b.created_at ? new Date(b.created_at).getTime() : 0;
    return ad - bd;
  });

function computeSortForInsert(
  withMoving: ProjectListItem[],
  index: number
): { value: number | null; needReindex: boolean } {
  const prev = index > 0 ? withMoving[index - 1] : undefined;
  const next = index < withMoving.length - 1 ? withMoving[index + 1] : undefined;

  if (!prev && !next) return { value: GAP, needReindex: false };
  if (!prev && next) {
    const nextOrder = next.sort_order ?? GAP;
    return { value: nextOrder - GAP, needReindex: false };
  }
  if (prev && !next) {
    const prevOrder = prev.sort_order ?? 0;
    return { value: prevOrder + GAP, needReindex: false };
  }

  const prevOrder = prev?.sort_order ?? 0;
  const nextOrder = next?.sort_order ?? prevOrder + 2;

  if (nextOrder - prevOrder >= 2) {
    return { value: Math.floor((prevOrder + nextOrder) / 2), needReindex: false };
  }
  return { value: null, needReindex: true };
}

async function reindexColumn(
  statusId: string | null,
  ordered: Array<Pick<ProjectListItem, "id">>
) {
  await Promise.all(
    ordered.map((p, i) =>
      supabase
        .from("projects")
        .update({ status_id: statusId, sort_order: (i + 1) * GAP })
        .eq("id", p.id)
    )
  );
}

const ProjectKanbanBoard = ({
  projects,
  projectStatuses,
  onProjectsChange,
  onProjectUpdate,
  onQuickView,
  isLoading,
  onLoadMore,
  hasMore = false,
  isLoadingMore = false,
}: ProjectKanbanBoardProps) => {
  const { t } = useTranslation('forms');
  const toast = useI18nToast();
  const [statuses, setStatuses] = useState<ProjectStatusSummary[]>([]);
  const [loading, setLoading] = useState(true);
  const [selectedStatusId, setSelectedStatusId] = useState<string | null>(null);
  const [viewingProject, setViewingProject] = useState<ProjectListItem | null>(null);
  const [showViewDialog, setShowViewDialog] = useState(false);
  const [isProjectWizardOpen, setProjectWizardOpen] = useState(false);

  const { triggerProjectMilestone } = useNotificationTriggers();
  const { activeOrganization } = useOrganization();
  const { settings: kanbanSettings } = useKanbanSettings();

  useEffect(() => {
    // Only self-fetch if no statuses were provided at all
    if (typeof projectStatuses === 'undefined') {
      fetchStatuses();
      return;
    }
    // When provided (even empty initially), use them and avoid duplicate fetches
    setStatuses((projectStatuses || []).filter(s => s.name?.toLowerCase?.() !== PROJECT_STATUS.ARCHIVED));
    setLoading(false);
  }, [projectStatuses]);

  const fetchStatuses = async () => {
    try {
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) return;

      // Use shared org util instead of RPC to avoid flaky errors
      const { getUserOrganizationId } = await import('@/lib/organizationUtils');
      const organizationId = await getUserOrganizationId();
      if (!organizationId) return;

      const { data, error } = await supabase
        .from("project_statuses")
        .select("*")
        .eq("organization_id", organizationId)
        .order("sort_order", { ascending: true });

      if (error) throw error;
      setStatuses(((data || []) as ProjectStatusSummary[]).filter(s => s.name?.toLowerCase?.() !== PROJECT_STATUS.ARCHIVED));
    } catch (error) {
      console.error("Error fetching project statuses:", error);
      // Avoid noisy toasts when board is not visible
      toast.error(t('forms:projects.toasts.statuses_load_failed'));
    } finally {
      setLoading(false);
    }
  };

  const getProjectsByStatus = (statusId: string) =>
    orderProjects(projects.filter(p => p.status_id === statusId));

  const getProjectsWithoutStatus = () =>
    orderProjects(projects.filter(p => !p.status_id));

  const handleDragEnd = async (result: any) => {
    const { destination, source, draggableId } = result;
    if (!destination) return;
    if (destination.droppableId === source.droppableId && destination.index === source.index) return;

    const projectId = draggableId;
    const srcStatusId = source.droppableId === "no-status" ? null : source.droppableId;
    const dstStatusId = destination.droppableId === "no-status" ? null : destination.droppableId;

    const moving = projects.find(p => p.id === projectId);
    if (!moving) return;

    const organizationId = activeOrganization?.id ?? null;
    const shouldForceRefresh = !onProjectUpdate;

    const dstBase = orderProjects(projects.filter(p => p.status_id === dstStatusId && p.id !== moving.id));
    const insertIndex = Math.min(Math.max(destination.index, 0), dstBase.length);

    const dstWithMoving: ProjectListItem[] = [...dstBase];
    const projectForInsertion: ProjectListItem = { ...moving, status_id: dstStatusId };
    dstWithMoving.splice(insertIndex, 0, projectForInsertion);

    const { value, needReindex } = computeSortForInsert(dstWithMoving, insertIndex);
    const requiresReindex = needReindex || value === null;

    const srcAfterMove =
      srcStatusId !== dstStatusId
        ? orderProjects(projects.filter(p => p.status_id === srcStatusId && p.id !== moving.id))
        : [];

    if (onProjectUpdate) {
      if (!requiresReindex && value !== null) {
        onProjectUpdate({
          ...moving,
          status_id: dstStatusId,
          sort_order: value,
        });
      } else {
        const updatedDest = dstWithMoving.map((project, idx) => ({
          ...project,
          status_id: dstStatusId,
          sort_order: (idx + 1) * GAP,
        }));
        updatedDest.forEach(project => onProjectUpdate(project));

        if (srcStatusId !== dstStatusId && srcAfterMove.length > 0) {
          const updatedSrc = srcAfterMove.map((project, idx) => ({
            ...project,
            status_id: srcStatusId,
            sort_order: (idx + 1) * GAP,
          }));
          updatedSrc.forEach(project => onProjectUpdate(project));
        }
      }
    }

    // Show immediate feedback
    if (srcStatusId !== dstStatusId) {
      const oldStatus = statuses.find(s => s.id === moving.status_id);
      const newStatus = statuses.find(s => s.id === (dstStatusId || ""));
      toast.success(t('forms:projects.toasts.moved_to', { status: newStatus?.name || t('pages:projects.noStatus') }));
    } else {
      toast.success(t('forms:projects.toasts.position_updated'));
    }

    // BACKGROUND OPERATIONS: Handle all database operations async
    (async () => {
      try {
        const { data: { user } } = await supabase.auth.getUser();
        if (!user) throw new Error(t('forms:user_not_authenticated'));

        // Update project position in database
        if (!requiresReindex && value !== null) {
          const { error } = await supabase
            .from("projects")
            .update({ status_id: dstStatusId, sort_order: value })
            .eq("id", projectId);
          if (error) throw error;
        } else {
          await reindexColumn(dstStatusId, dstWithMoving);
          if (srcStatusId !== dstStatusId) {
            await reindexColumn(srcStatusId, srcAfterMove);
          }
        }

        // Handle status change activities and notifications
        if (srcStatusId !== dstStatusId) {
          const oldStatus = statuses.find(s => s.id === moving.status_id);
          const newStatus = statuses.find(s => s.id === (dstStatusId || ""));

          // Insert activity log
          const activityPromise = organizationId
            ? supabase.from("activities").insert({
                type: "status_change",
                content: `Status changed from '${oldStatus?.name || "No Status"}' to '${newStatus?.name || "No Status"}'`,
                project_id: projectId,
                lead_id: moving.lead_id,
                user_id: user.id,
                organization_id: organizationId,
              })
            : Promise.resolve();

          // TEMPORARILY SKIP milestone notifications for completed/cancelled to test performance
          const shouldSkipNotification =
            newStatus?.lifecycle === LIFECYCLE_STATES.COMPLETED || newStatus?.lifecycle === LIFECYCLE_STATES.CANCELLED;

          const notificationPromise =
            !shouldSkipNotification && organizationId && triggerProjectMilestone 
            ? triggerProjectMilestone(
                projectId,
                srcStatusId || "",
                dstStatusId || "",
                organizationId
              ).catch(error => console.error("Failed to trigger milestone notification:", error))
            : Promise.resolve();

          if (shouldSkipNotification) {
            // Milestone notification triggered for status change
          }

          // Wait for both operations in parallel
          await Promise.all([activityPromise, notificationPromise]);
        }

        // Background database operations completed; fall back to full refresh only when local state wasn't updated
        if (shouldForceRefresh && onProjectsChange) {
          onProjectsChange();
        }
      } catch (error) {
        console.error("Error in background database operations:", error);
        // Sync with server to reflect actual state
        if (onProjectsChange) {
          onProjectsChange();
        }
        toast.error(t('forms:projects.toasts.move_sync_failed'));
      }
    })();
  };

  const handleAddProject = (statusId: string | null) => {
    setSelectedStatusId(statusId);
    setProjectWizardOpen(true);
  };

  const handleProjectClick = (project: ProjectListItem) => {
    if (onQuickView) onQuickView(project);
    else {
      setViewingProject(project);
      setShowViewDialog(true);
    }
  };

  const renderProjectCard = (project: ProjectListItem, index: number) => (
    <DnD.Draggable key={project.id} draggableId={project.id} index={index}>
      {(provided) => (
        <div
          ref={provided.innerRef}
          {...provided.draggableProps}
          {...provided.dragHandleProps}
          className="mb-2 last:mb-0"
        >
          <ProfessionalKanbanCard
            project={project}
            kanbanSettings={kanbanSettings}
            onClick={() => handleProjectClick(project)}
          />
        </div>
      )}
    </DnD.Draggable>
  );

  const renderColumn = (status: ProjectStatusSummary | null, columnProjects: ProjectListItem[]) => {
    const statusId = status?.id || "no-status";
    const statusName = status?.name || t('pages:projects.noStatus');
    const statusColor = status?.color || "#6B7280";

    const ordered = orderProjects(columnProjects);

    return (
      <div
        key={statusId}
        className="flex w-72 flex-shrink-0 flex-col rounded-2xl border border-border/40 bg-muted/30"
      >
        <div className="flex items-center justify-between gap-1.5 px-2.5 pt-3 pb-2">
          <div className="flex items-center gap-1.5">
            <button
              className="inline-flex items-center gap-1.5 px-2.5 py-1.5 rounded-full text-sm font-medium transition-all hover:opacity-80"
              style={{
                backgroundColor: statusColor + "20",
                color: statusColor,
                border: `1px solid ${statusColor}40`
              }}
            >
              <div className="w-2 h-2 rounded-full" style={{ backgroundColor: statusColor }} />
              <span className="uppercase tracking-wide font-semibold">{statusName}</span>
            </button>
            <Badge variant="secondary" className="text-xs">
              {ordered.length}
            </Badge>
          </div>
          <Button
            variant="ghost"
            size="icon"
            onClick={() => handleAddProject(status?.id || null)}
            className="h-6 w-6 text-muted-foreground hover:text-foreground"
          >
            <Plus className="h-4 w-4" />
          </Button>
        </div>

        <DnD.Droppable droppableId={statusId}>
          {(provided, snapshot) => (
            <div
              ref={provided.innerRef}
              {...provided.droppableProps}
              className={cn(
                "relative flex flex-col gap-2.5 px-2.5 pb-3 pt-1",
                snapshot.isDraggingOver && "bg-accent/10"
              )}
            >
              <div className="flex flex-col">
                {ordered.map((project, index) => renderProjectCard(project, index))}
              </div>

              <div>
                {ordered.length === 0 ? (
                  <div className="flex flex-col items-center justify-center gap-2.5 rounded-xl border-2 border-dashed border-muted-foreground/30 bg-background/60 px-3 py-7 text-center">
                    <Button
                      variant="outline"
                      onClick={() => handleAddProject(status?.id || null)}
                      className="flex items-center gap-1.5 border-dashed"
                    >
                      <Plus className="h-4 w-4" />
                      {t('common:buttons.add_project')}
                    </Button>
                    <p className="text-xs text-muted-foreground">
                      {t('forms:projects.drop_here')}
                    </p>
                  </div>
                ) : (
                  <Button
                    variant="outline"
                    onClick={() => handleAddProject(status?.id || null)}
                    className="w-full flex items-center gap-1.5 border-dashed"
                  >
                    <Plus className="h-4 w-4" />
                    {t('common:buttons.add_project')}
                  </Button>
                )}
              </div>

              <div className="flex-1 min-h-10" />
              {provided.placeholder}

              {snapshot.isDraggingOver && (
                <div className="pointer-events-none absolute inset-0 flex items-center justify-center rounded-2xl border-2 border-accent/40">
                  <span className="text-sm font-medium text-accent">
                    {t('forms:projects.drop_here')}
                  </span>
                </div>
              )}
            </div>
          )}
        </DnD.Droppable>
      </div>
    );
  };

  if (loading || isLoading) return <KanbanLoadingSkeleton />;

  const showLoadMore = Boolean(hasMore && onLoadMore);
  const loadMoreLabel = t('common:buttons.load_more', { defaultValue: 'Load more' });

  return (
    <>
      <div
        className="w-full max-w-full overflow-x-auto"
        style={{ WebkitOverflowScrolling: "touch", scrollbarWidth: "thin", touchAction: "pan-x pan-y" }}
      >
        <div className="px-3 py-3 sm:px-5 sm:py-4">
          <DnD.DragDropContext onDragEnd={handleDragEnd}>
            <div className="flex items-start gap-2.5 sm:gap-3 pb-3" style={{ width: "max-content", minWidth: "100%" }}>
              {statuses.map(status => renderColumn(status, getProjectsByStatus(status.id)))}
              {getProjectsWithoutStatus().length > 0 && renderColumn(null, getProjectsWithoutStatus())}
            </div>
          </DnD.DragDropContext>
        </div>
      </div>

      <ProjectCreationWizardSheet
        isOpen={isProjectWizardOpen}
        onOpenChange={(open) => {
          setProjectWizardOpen(open);
          if (!open) {
            setSelectedStatusId(null);
          }
        }}
        defaultStatusId={selectedStatusId}
        entrySource="kanban"
        onProjectCreated={async (createdProject) => {
          if (createdProject?.id) {
            await promoteProjectToTop(createdProject.id);
          }
          onProjectsChange();
          setSelectedStatusId(null);
        }}
      />

      <ViewProjectDialog
        project={viewingProject}
        open={showViewDialog}
        onOpenChange={setShowViewDialog}
        onProjectUpdated={onProjectsChange}
        onActivityUpdated={() => {}}
        leadName={viewingProject?.lead?.name || ""}
      />
      {showLoadMore ? (
        <div className="px-4 pb-6 flex justify-center">
          <Button
            variant="outline"
            onClick={onLoadMore}
            disabled={isLoadingMore}
            className="gap-2"
          >
            {isLoadingMore && <Loader2 className="h-4 w-4 animate-spin" />}
            {loadMoreLabel}
          </Button>
        </div>
      ) : null}
    </>
  );
};

export default ProjectKanbanBoard;

import { useState, useEffect, useRef } from "react";
import { supabase } from "@/integrations/supabase/client";
import { Card, CardHeader, CardTitle, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Edit2, Save, X, Plus } from "lucide-react";
import { useI18nToast } from "@/lib/toastHelpers";
import { ServicePicker, type PickerService } from "./ServicePicker";
import { useFormsTranslation } from '@/hooks/useTypedTranslation';
import type { Database } from "@/integrations/supabase/types";
interface Service {
  id: string;
  name: string;
  category: string | null;
  cost_price?: number;
  selling_price?: number;
  price?: number;
  extra?: boolean | null;
  billing_type?: "included" | "extra";
}
interface ProjectServicesSectionProps {
  projectId: string;
  onServicesUpdated?: () => void;
}
export function ProjectServicesSection({
  projectId,
  onServicesUpdated
}: ProjectServicesSectionProps) {
  const [services, setServices] = useState<Service[]>([]);
  const [isEditing, setIsEditing] = useState(false);
  const [loading, setLoading] = useState(true);
  const [saving, setSaving] = useState(false);
  const [availableServices, setAvailableServices] = useState<Service[]>([]);
  const [loadingAvailable, setLoadingAvailable] = useState(true);
  const [errorAvailable, setErrorAvailable] = useState<string | null>(null);
  const toast = useI18nToast();
  const { t } = useFormsTranslation();
  const sectionRef = useRef<HTMLDivElement>(null);
  const fetchProjectServices = async () => {
    try {
      const {
        data,
        error
      } = await supabase
        .from<{
          id: string;
          billing_type: "included" | "extra";
          services: Database["public"]["Tables"]["services"]["Row"] | null;
        }>('project_services')
        .select(`
          id,
          billing_type,
          services!inner (
            id,
            name,
            category,
            cost_price,
            selling_price,
            price,
            extra
          )
        `)
        .eq('project_id', projectId);
      if (error) throw error;
      const fetchedServices =
        data
          ?.map((ps) => {
            const svc = ps.services;
            if (!svc) return null;
            const record: Service = {
              id: svc.id,
              name: svc.name,
              category: svc.category,
              cost_price: svc.cost_price ?? undefined,
              selling_price: svc.selling_price ?? undefined,
              price: svc.price ?? undefined,
              extra: svc.extra,
              billing_type: ps.billing_type
            };
            return record;
          })
          .filter((record): record is Service => Boolean(record)) ?? [];
      setServices(fetchedServices);
    } catch (error) {
      console.error('Error fetching project services:', error);
      const message = error instanceof Error ? error.message : t('payments.error_loading', { defaultValue: 'Unable to load payments' });
      toast.error(message);
    } finally {
      setLoading(false);
    }
  };
  const fetchAvailableServices = async () => {
    setLoadingAvailable(true);
    setErrorAvailable(null);
    try {
      const {
        data: {
          user
        }
      } = await supabase.auth.getUser();
      if (!user) return;

      // Get user's active organization ID
      const { data: organizationId } = await supabase.rpc('get_user_active_organization_id');
      if (!organizationId) return;

      const {
        data,
        error
      } = await supabase
        .from<Database["public"]["Tables"]["services"]["Row"]>("services")
        .select("id, name, category, cost_price, selling_price, price, extra")
        .eq("organization_id", organizationId)
        .order("category", {
        ascending: true
      }).order("name", {
        ascending: true
      });
      if (error) throw error;
      setAvailableServices((data ?? []).map((svc) => ({
        id: svc.id,
        name: svc.name,
        category: svc.category,
        cost_price: svc.cost_price ?? undefined,
        selling_price: svc.selling_price ?? undefined,
        price: svc.price ?? undefined,
        extra: svc.extra
      })));
    } catch (err) {
      console.error("Error fetching available services:", err);
      const message = err instanceof Error ? err.message : t('payments.services.load_error', { defaultValue: 'Unable to load services.' });
      setErrorAvailable(message);
    } finally {
      setLoadingAvailable(false);
    }
  };
  useEffect(() => {
    fetchProjectServices();
    fetchAvailableServices();
  }, [projectId]);
  const handleServicePickerChange = (serviceIds: string[]) => {
    const selectedServices = availableServices
      .filter(service => serviceIds.includes(service.id))
      .map(service => {
        const existing = services.find(s => s.id === service.id);
        const billingType =
          existing?.billing_type ??
          (service.extra ? "extra" : "included");
        return { ...service, billing_type: billingType };
      });
    setServices(selectedServices);
  };
  const handleSaveServices = async (selectedServices: Service[]) => {
    setSaving(true);
    try {
      const {
        data: {
          user
        }
      } = await supabase.auth.getUser();
      if (!user) throw new Error('User not authenticated');

      // First, delete existing project services
      const {
        error: deleteError
      } = await supabase.from('project_services').delete().eq('project_id', projectId);
      if (deleteError) throw deleteError;

      // Then, insert new project services
      if (selectedServices.length > 0) {
        const serviceInserts = selectedServices.map(service => ({
          project_id: projectId,
          service_id: service.id,
          user_id: user.id,
          billing_type:
            service.billing_type ??
            (service.extra ? "extra" : "included")
        }));
        const {
          error: insertError
        } = await supabase.from('project_services').insert(serviceInserts);
        if (insertError) throw insertError;
      }
      setServices(selectedServices);
      setIsEditing(false);
      onServicesUpdated?.();

      // Smoothly scroll back to the top of Services section after saving
      setTimeout(() => {
        sectionRef.current?.scrollIntoView({
          behavior: 'smooth',
          block: 'start'
        });
      }, 50);
      toast.success(t('services.services_updated'));
    } catch (error) {
      const message = error instanceof Error ? error.message : String(error);
      toast.error(message);
    } finally {
      setSaving(false);
    }
  };
  if (loading) {
    return <Card>
        <CardHeader>
          <CardTitle className="flex items-center justify-between text-lg font-medium">
            <div className="flex items-center gap-2">
              <svg className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 9V7a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2m2 4h10a2 2 0 002-2v-6a2 2 0 00-2-2H9a2 2 0 00-2 2v6a2 2 0 002 2zm7-5a2 2 0 11-4 0 2 2 0 014 0z" />
              </svg>
              {t('services.title')}
            </div>
            <div className="w-6 h-6 bg-muted animate-pulse rounded" />
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-2">
            <div className="w-full h-4 bg-muted animate-pulse rounded" />
            <div className="w-3/4 h-4 bg-muted animate-pulse rounded" />
          </div>
        </CardContent>
      </Card>;
  }
  return <div ref={sectionRef}>
      <Card>
        <CardHeader>
        <CardTitle className="flex items-center justify-between text-xl font-semibold">
          <div className="flex items-center gap-2">
            <svg className="h-4 w-4" fill="none" viewBox="0 0 24 24" stroke="currentColor">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 9V7a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2m2 4h10a2 2 0 002-2v-6a2 2 0 00-2-2H9a2 2 0 00-2 2v6a2 2 0 002 2zm7-5a2 2 0 11-4 0 2 2 0 014 0z" />
            </svg>
            {t('services.title')}
          </div>
          {!isEditing && (services.length === 0 ? <Button size="sm" onClick={() => setIsEditing(true)} className="gap-2">
                <Plus className="h-4 w-4" />
                {t('services.add')}
              </Button> : <Button size="sm" onClick={() => setIsEditing(true)} className="gap-2">
                <Edit2 className="h-4 w-4" />
                {t('services.edit')}
              </Button>)}
        </CardTitle>
      </CardHeader>
      <CardContent>
        {isEditing ? <div className="space-y-4">
            <ServicePicker services={availableServices.map(s => ({
            ...s,
            cost_price: s.cost_price,
            selling_price: s.selling_price,
            price: s.price,
            active: true
          }))} value={services.map(s => s.id)} onChange={handleServicePickerChange} disabled={saving} isLoading={loadingAvailable} error={errorAvailable} onRetry={fetchAvailableServices} />

            {/* Selected services display in edit mode */}
            {services.length > 0 && (
              <div className="rounded-md border p-3">
                <div className="mb-2 flex items-center justify-between">
                  <span className="text-sm font-medium">{t('services.selected_services')} ({services.length})</span>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => setServices([])}
                    disabled={saving}
                    className="h-8"
                  >
                    {t('buttons.clearAll')}
                  </Button>
                </div>
                <div className="flex flex-wrap gap-2">
                  {services.map((service) => {
                    const costPrice = service.cost_price ?? 0;
                    const sellingPrice = service.selling_price ?? service.price ?? 0;
                    const hasPrices = costPrice > 0 || sellingPrice > 0;
                    return (
                      <Badge
                        key={service.id}
                        variant="secondary"
                        className="h-7 rounded-full px-3 text-xs"
                      >
                        <span>
                          {service.name}
                          {hasPrices && (
                            <>
                              <span className="mx-1">Â·</span>
                              <span className="text-foreground/70">â‚º{costPrice}/â‚º{sellingPrice}</span>
                            </>
                          )}
                        </span>
                        <button
                          className="ml-2 inline-flex rounded-full p-0.5 hover:text-foreground"
                          onClick={(e) => {
                            e.preventDefault();
                            e.stopPropagation();
                            const newServices = services.filter(s => s.id !== service.id);
                            setServices(newServices);
                          }}
                          aria-label={`Remove ${service.name}`}
                        >
                          <X className="h-3.5 w-3.5" />
                        </button>
                      </Badge>
                    );
                  })}
