              </Button>
              <Button size="sm" variant="outline" onClick={() => {
              setIsEditing(false);
              fetchProjectServices(); // Reset to original services
            }} disabled={saving}>
                <X className="h-4 w-4 mr-1" />
                {t('common:buttons.cancel')}
              </Button>
            </div>
          </div> : <div>
            {services.length > 0 ? <div className="flex flex-wrap gap-2">
                {services.map(service => {
              // Service data rendered
              const costPrice = service.cost_price ?? 0;
              const sellingPrice = service.selling_price ?? service.price ?? 0;
              const hasPrices = costPrice > 0 || sellingPrice > 0;
              // Price information processed
              return <Badge 
                      key={service.id} 
                      variant="secondary" 
                      className="h-auto min-h-7 rounded-lg px-3 py-1.5 text-xs whitespace-normal break-words max-w-full"
                      title={`${service.name}${hasPrices ? ` - Cost: â‚º${costPrice}, Selling: â‚º${sellingPrice}` : ''}`}
                    >
                       <div className="flex flex-col gap-0.5 w-full">
                         <span className="font-medium leading-tight">{service.name}</span>
                         {hasPrices && (
                            <span className="text-xs text-foreground/60 leading-tight">
                              {t('services.cost')}: â‚º{costPrice} Â· {t('services.selling')}: â‚º{sellingPrice}
                            </span>
                         )}
                       </div>
                     </Badge>;
            })}
              </div> : <div className="text-center py-6">
                <svg className="h-8 w-8 text-muted-foreground mx-auto mb-2" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M17 9V7a2 2 0 00-2-2H5a2 2 0 00-2 2v6a2 2 0 002 2h2m2 4h10a2 2 0 002-2v-6a2 2 0 00-2-2H9a2 2 0 00-2 2v6a2 2 0 002 2zm7-5a2 2 0 11-4 0 2 2 0 014 0z" />
                </svg>
                 <p className="text-muted-foreground text-sm">{t('projectDetails.services.emptyState')}</p>
                  <p className="text-xs text-muted-foreground mt-1">
                    {t('projectDetails.services.addHint')}
                  </p>
              </div>}
          </div>}
      </CardContent>
    </Card>
    </div>;
}

import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Checkbox } from '@/components/ui/checkbox';
import { Input } from '@/components/ui/input';
import { Button } from '@/components/ui/button';
import { ProgressBar } from '@/components/ui/progress-bar';
import { Trash2, Plus, Check } from 'lucide-react';
import { supabase } from '@/integrations/supabase/client';
import { useToast } from '@/hooks/use-toast';
import { useFormsTranslation } from '@/hooks/useTypedTranslation';

interface Todo {
  id: string;
  content: string;
  is_completed: boolean;
  created_at: string;
}

interface ProjectTodoListProps {
  projectId: string;
}

export const ProjectTodoList: React.FC<ProjectTodoListProps> = ({ projectId }) => {
  const [todos, setTodos] = useState<Todo[]>([]);
  const [loading, setLoading] = useState(true);
  const [isAddingTodo, setIsAddingTodo] = useState(false);
  const [newTodoContent, setNewTodoContent] = useState('');
  const [editingTodoId, setEditingTodoId] = useState<string | null>(null);
  const [editContent, setEditContent] = useState('');
  const { toast } = useToast();
  const { t } = useFormsTranslation();

  useEffect(() => {
    fetchTodos();
  }, [projectId]);

  const fetchTodos = async () => {
    try {
      const { data, error } = await supabase
        .from('todos')
        .select('*')
        .eq('project_id', projectId)
        .order('created_at', { ascending: false });

      if (error) throw error;
      setTodos(data || []);
    } catch (error) {
      console.error('Error fetching todos:', error);
      toast({
        title: "Error",
        description: t("todos.error_load"),
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const addTodo = async () => {
    if (!newTodoContent.trim()) return;

    try {
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) throw new Error('User not authenticated');

      const { data, error } = await supabase
        .from('todos')
        .insert({
          project_id: projectId,
          user_id: user.id,
          content: newTodoContent.trim(),
        })
        .select()
        .single();

      if (error) throw error;

      setTodos(prev => [data, ...prev]);
      setNewTodoContent('');
      setIsAddingTodo(false);

      toast({
        title: "Success",
        description: t("todos.todo_added"),
      });
    } catch (error) {
      console.error('Error adding todo:', error);
      toast({
        title: "Error",
        description: t("todos.error_add"),
        variant: "destructive",
      });
    }
  };

  const toggleTodo = async (id: string, isCompleted: boolean) => {
    try {
      const { error } = await supabase
        .from('todos')
        .update({ is_completed: !isCompleted })
        .eq('id', id);

      if (error) throw error;

      setTodos(prev => prev.map(todo => 
        todo.id === id ? { ...todo, is_completed: !isCompleted } : todo
      ));
    } catch (error) {
      console.error('Error updating todo:', error);
      toast({
        title: "Error",
        description: t("todos.error_update"),
        variant: "destructive",
      });
    }
  };

  const deleteTodo = async (id: string) => {
    try {
      const { error } = await supabase
        .from('todos')
        .delete()
        .eq('id', id);

      if (error) throw error;

      setTodos(prev => prev.filter(todo => todo.id !== id));

      toast({
        title: "Success",
        description: t("todos.todo_deleted"),
      });
    } catch (error) {
      console.error('Error deleting todo:', error);
      toast({
        title: "Error",
        description: t("todos.error_delete"),
        variant: "destructive",
      });
    }
  };

  const startEditing = (todo: Todo) => {
    setEditingTodoId(todo.id);
    setEditContent(todo.content);
  };

  const saveEdit = async () => {
    if (!editContent.trim() || !editingTodoId) return;

    try {
      const { error } = await supabase
        .from('todos')
        .update({ content: editContent.trim() })
        .eq('id', editingTodoId);

      if (error) throw error;

      setTodos(prev => prev.map(todo => 
        todo.id === editingTodoId ? { ...todo, content: editContent.trim() } : todo
      ));

      setEditingTodoId(null);
      setEditContent('');

      toast({
        title: "Success",
        description: t("todos.todo_updated"),
      });
    } catch (error) {
      console.error('Error updating todo:', error);
      toast({
        title: "Error",
        description: t("todos.error_update"),
        variant: "destructive",
      });
    }
  };

  const cancelEdit = () => {
    setEditingTodoId(null);
    setEditContent('');
  };

  const completedCount = todos.filter(todo => todo.is_completed).length;
  const totalCount = todos.length;
  const progressPercentage = totalCount > 0 ? Math.round((completedCount / totalCount) * 100) : 0;

  if (loading) {
    return (
      <Card>
        <CardHeader>
          <CardTitle className="text-lg">{t("todos.title")}</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="text-sm text-muted-foreground">{t("todos.loading")}</div>
        </CardContent>
      </Card>
    );
  }

  return (
    <Card>
      <CardHeader>
        <CardTitle className="text-lg">
          {t("todos.title")} {totalCount > 0 && `(${completedCount}/${totalCount})`}
        </CardTitle>
        {totalCount > 0 && (
          <div className="mt-3">
            <ProgressBar
              value={progressPercentage}
              total={totalCount}
              completed={completedCount}
              className="w-full"
              size="sm"
            />
          </div>
        )}
      </CardHeader>
      <CardContent className="space-y-2">
        {/* Add Todo Input */}
        {isAddingTodo ? (
          <div className="flex items-center gap-2 p-3 border rounded-lg bg-muted/50">
            <Input
              value={newTodoContent}
              onChange={(e) => setNewTodoContent(e.target.value)}
              placeholder={t("todos.enter_todo")}
              className="flex-1"
              autoFocus
              onKeyDown={(e) => {
                if (e.key === 'Enter') {
                  addTodo();
                } else if (e.key === 'Escape') {
                  setIsAddingTodo(false);
                  setNewTodoContent('');
                }
              }}
              onBlur={() => {
                if (!newTodoContent.trim()) {
                  setIsAddingTodo(false);
                  setNewTodoContent('');
                }
              }}
            />
            <Button
              size="sm"
              onClick={addTodo}
              disabled={!newTodoContent.trim()}
            >
              <Check className="h-4 w-4" />
            </Button>
          </div>
        ) : (
          <button
            onClick={() => setIsAddingTodo(true)}
            className="flex items-center gap-2 w-full p-3 text-left text-muted-foreground hover:bg-muted/50 rounded-lg border border-dashed transition-colors"
          >
            <Plus className="h-4 w-4" />
            <span>{t("todos.add_todo")}</span>
          </button>
        )}

        {/* Todo List */}
        {todos.length === 0 ? (
          <div className="text-sm text-muted-foreground text-center py-4">
            {t("todos.no_todos")}
          </div>
        ) : (
          <div className="space-y-1">
            {todos.map((todo) => (
              <div
                key={todo.id}
                className={`flex items-center gap-3 p-3 rounded-lg border transition-colors hover:bg-muted/30 ${
                  todo.is_completed ? 'bg-green-50' : ''
                }`}
              >
                <Checkbox
                  checked={todo.is_completed}
                  onCheckedChange={() => toggleTodo(todo.id, todo.is_completed)}
                />

                {editingTodoId === todo.id ? (
                  <div className="flex items-center gap-2 flex-1">
                    <Input
                      value={editContent}
                      onChange={(e) => setEditContent(e.target.value)}
                      className="flex-1"
                      autoFocus
                      onKeyDown={(e) => {
                        if (e.key === 'Enter') {
                          saveEdit();
                        } else if (e.key === 'Escape') {
                          cancelEdit();
                        }
                      }}
                      onBlur={saveEdit}
                    />
                    <Button
                      size="sm"
                      onClick={saveEdit}
                      disabled={!editContent.trim()}
                    >
                      <Check className="h-4 w-4" />
                    </Button>
                  </div>
                ) : (
                  <span
                    className={`flex-1 cursor-pointer ${
                      todo.is_completed
                        ? 'line-through text-muted-foreground'
                        : ''
                    }`}
                    onClick={() => startEditing(todo)}
                  >
                    {todo.content}
                  </span>
                )}

                <Button
                  variant="ghost"
                  size="sm"
                  onClick={() => deleteTodo(todo.id)}
                  className="h-8 w-8 p-0 text-destructive hover:text-destructive"
                >
                  <Trash2 className="h-4 w-4" />
                </Button>
              </div>
            ))}
          </div>
        )}
      </CardContent>
    </Card>
  );
};

import { useState, useEffect } from "react";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Badge } from "@/components/ui/badge";
import { Check, ChevronDown, Search } from "lucide-react";
import { cn } from "@/lib/utils";
import { supabase } from "@/integrations/supabase/client";
import { useI18nToast } from "@/lib/toastHelpers";
import { getUserOrganizationId } from "@/lib/organizationUtils";

interface ProjectType {
  id: string;
  name: string;
  is_default: boolean;
}

interface ProjectTypeSelectorProps {
  value?: string;
  onValueChange: (value: string, meta?: { isAutomatic?: boolean }) => void;
  placeholder?: string;
  disabled?: boolean;
  className?: string;
  required?: boolean;
}

export function ProjectTypeSelector({ 
  value, 
  onValueChange, 
  placeholder = "Select project type...", 
  disabled = false,
  className,
  required = false
}: ProjectTypeSelectorProps) {
  const [types, setTypes] = useState<ProjectType[]>([]);
  const [loading, setLoading] = useState(false);
  const [open, setOpen] = useState(false);
  const [searchTerm, setSearchTerm] = useState("");
  const toast = useI18nToast();

  const fetchProjectTypes = async () => {
    setLoading(true);
    try {
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) return;

      const organizationId = await getUserOrganizationId();
      if (!organizationId) return;

      const { data, error } = await supabase
        .from('project_types')
        .select('id, name, is_default')
        .eq('organization_id', organizationId)
        .order('is_default', { ascending: false }) // Default types first
        .order('name', { ascending: true });

      if (error) throw error;

      setTypes(data || []);

      // Auto-select default type if no value is set
      if (!value && data && data.length > 0) {
        const defaultType = data.find(type => type.is_default);
        if (defaultType) {
          onValueChange(defaultType.id, { isAutomatic: true });
        }
      }
    } catch (error: any) {
      toast.error(error.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProjectTypes();
  }, []);

  const filteredTypes = types.filter(type =>
    type.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  const selectedType = types.find(type => type.id === value);

  // Check if user has no project types
  if (!loading && types.length === 0) {
    return (
      <div className="text-sm text-muted-foreground p-2 border rounded">
        No project types configured. Please add project types in Settings first.
      </div>
    );
  }

  return (
    <Popover open={open} onOpenChange={setOpen}>
      <PopoverTrigger asChild>
        <Button
          variant="outline"
          role="combobox"
          aria-expanded={open}
          className={cn(
            "w-full justify-between text-left h-auto min-h-[40px]",
            !selectedType && "text-muted-foreground",
            className
          )}
          disabled={disabled || loading}
        >
          {loading ? (
            "Loading types..."
          ) : selectedType ? (
            <div className="flex items-center gap-2">
              <Badge 
                variant={selectedType.is_default ? "default" : "secondary"}
                className="text-xs"
              >
                {selectedType.name.toUpperCase()}
              </Badge>
              {selectedType.is_default && (
                <span className="text-xs text-muted-foreground">(Default)</span>
              )}
            </div>
          ) : (
            placeholder
          )}
          <ChevronDown className="ml-2 h-4 w-4 shrink-0 opacity-50" />
        </Button>
      </PopoverTrigger>
      <PopoverContent className="w-full p-0 z-50 bg-popover" align="start">
        <div className="p-3 border-b">
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground h-4 w-4" />
            <Input
              placeholder="Search project types..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="pl-10"
              autoFocus
            />
          </div>
        </div>
        <div className="max-h-64 overflow-y-auto">
          <div className="py-1">
            {loading ? (
              <div className="p-4 text-center text-sm text-muted-foreground">
                Loading project types...
              </div>
            ) : filteredTypes.length === 0 ? (
              <div className="p-4 text-center text-sm text-muted-foreground">
                {searchTerm ? 'No types match your search' : 'No project types found'}
              </div>
            ) : (
              filteredTypes.map((type) => (
                <div
                  key={type.id}
                  onClick={() => {
                    onValueChange(type.id);
                    setOpen(false);
                    setSearchTerm("");
                  }}
                  className={cn(
                    "flex items-center justify-between p-3 hover:bg-muted/50 cursor-pointer border-b last:border-b-0",
                    value === type.id && "bg-muted"
                  )}
                >
                  <div className="flex items-center space-x-3">
                    <Check
                      className={cn(
                        "h-4 w-4",
                        value === type.id ? "opacity-100" : "opacity-0"
                      )}
                    />
                    <div className="flex items-center gap-2">
                      <Badge 
                        variant={type.is_default ? "default" : "secondary"}
                        className="text-xs"
                      >
                        {type.name.toUpperCase()}
                      </Badge>
                      {type.is_default && (
                        <span className="text-xs text-muted-foreground">(Default)</span>
                      )}
                    </div>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>
      </PopoverContent>
    </Popover>
  );
}

import { useState, useEffect } from "react";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Checkbox } from "@/components/ui/checkbox";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { AlertDialog, AlertDialogAction, AlertDialogCancel, AlertDialogContent, AlertDialogDescription, AlertDialogFooter, AlertDialogHeader, AlertDialogTitle, AlertDialogTrigger } from "@/components/ui/alert-dialog";
import { Form, FormControl, FormField, FormItem, FormLabel, FormMessage } from "@/components/ui/form";
import { Plus, Trash2, Loader2, Check } from "lucide-react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import { supabase } from "@/integrations/supabase/client";
import { useI18nToast } from "@/lib/toastHelpers";
import { AddProjectTypeDialog, EditProjectTypeDialog } from "./settings/ProjectTypeDialogs";
import { cn } from "@/lib/utils";
import SettingsSection from "./SettingsSection";
import { useOrganization } from "@/contexts/OrganizationContext";
import { useProjectTypes } from "@/hooks/useOrganizationData";
import { FormLoadingSkeleton } from "@/components/ui/loading-presets";
import { useTranslation } from "react-i18next";
import { useMessagesTranslation } from "@/hooks/useTypedTranslation";

const projectTypeSchema = z.object({
  name: z.string().min(1, "Type name is required").max(50, "Type name must be less than 50 characters"),
  is_default: z.boolean().default(false),
});

type ProjectTypeForm = z.infer<typeof projectTypeSchema>;

interface ProjectType {
  id: string;
  name: string;
  is_default: boolean;
  created_at: string;
  updated_at: string;
}

const ProjectTypesSection = () => {
  const [editingType, setEditingType] = useState<ProjectType | null>(null);
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false);
  const [isEditDialogOpen, setIsEditDialogOpen] = useState(false);
  const [submitting, setSubmitting] = useState(false);
  const toast = useI18nToast();
  const { activeOrganizationId, loading: orgLoading } = useOrganization();
  const { data: types = [], isLoading, refetch } = useProjectTypes();
  const { t } = useTranslation('forms');
  const { t: tMessages } = useMessagesTranslation();

  const form = useForm<ProjectTypeForm>({
    resolver: zodResolver(projectTypeSchema),
    defaultValues: {
      name: "",
      is_default: false,
    },
  });

  const createDefaultTypes = async () => {
    try {
      if (!activeOrganizationId) return;

      const { data: { user } } = await supabase.auth.getUser();
      if (!user) return;

      await supabase.rpc('ensure_default_project_types_for_org', {
        user_uuid: user.id,
        org_id: activeOrganizationId
      });

      // Refetch data after creating defaults
      await refetch();
    } catch (error) {
      console.error('Error creating default types:', error);
      toast.error("Failed to create default types");
    }
  };

  // Create default types if none exist
  useEffect(() => {
    if (!isLoading && !orgLoading && activeOrganizationId && types.length === 0) {
      createDefaultTypes();
    }
  }, [isLoading, orgLoading, activeOrganizationId, types.length]);

  const onSubmit = async (data: ProjectTypeForm) => {
    if (!activeOrganizationId) {
      toast.error("No active organization found");
      return;
    }

    setSubmitting(true);
    try {
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) throw new Error('Not authenticated');

      if (editingType) {
        // Update existing type
        const { error } = await supabase
          .from('project_types')
          .update({ name: data.name, is_default: data.is_default })
          .eq('id', editingType.id)
          .eq('organization_id', activeOrganizationId);

        if (error) {
          console.error('Error updating type:', error);
          throw error;
        }

        // Show appropriate message based on what changed
        if (data.is_default && !editingType.is_default) {
          toast.success(`"${data.name}" is now the default project type`);
        } else {
          toast.success("Project type updated successfully");
        }
        setIsEditDialogOpen(false);
      } else {
        // Create new type
        const { error } = await supabase
          .from('project_types')
          .insert({
            name: data.name,
            is_default: data.is_default,
            user_id: user.id,
            organization_id: activeOrganizationId,
          });

        if (error) {
          if (error.code === '23505') { // Unique constraint violation
            throw new Error('A type with this name already exists');
          }
          throw error;
        }

        toast.success(data.is_default ? `"${data.name}" created and set as default` : "Project type created successfully");
        setIsAddDialogOpen(false);
      }

      form.reset({ name: "", is_default: false });
      setEditingType(null);
      await refetch(); // Refetch data after changes
    } catch (error) {
      console.error('Error saving project type:', error);
      toast.error(error instanceof Error ? error.message : "Failed to save project type");
    } finally {
      setSubmitting(false);
    }
  };

  const handleEdit = (type: ProjectType) => {
    setEditingType(type);
    form.reset({ name: type.name, is_default: type.is_default });
    setIsEditDialogOpen(true);
  };

  const handleAdd = () => {
    setEditingType(null);
    form.reset({ name: "", is_default: false });
    setIsAddDialogOpen(true);
  };

  const handleDelete = async (typeId: string) => {
    if (!activeOrganizationId) return;

    try {
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) throw new Error('Not authenticated');

      const { error } = await supabase
        .from('project_types')
        .delete()
        .eq('id', typeId)
        .eq('organization_id', activeOrganizationId);

      if (error) {
        if (error.code === '23503') { // Foreign key constraint violation
          throw new Error('Cannot delete this type because it is being used by existing projects. Please change those projects to a different type first.');
        }
        throw error;
      }

      toast.success("Project type deleted successfully");
      await refetch(); // Refetch data after deletion
    } catch (error) {
      console.error('Error deleting project type:', error);
      toast.error(error instanceof Error ? error.message : "Failed to delete project type");
    }
  };

  const renderTypeDialog = (isEdit: boolean) => (
    <DialogContent className="sm:max-w-md">
      <DialogHeader>
        <DialogTitle className="text-lg font-medium">
          {isEdit ? 'EDIT TYPE' : 'ADD TYPE'}
        </DialogTitle>
      </DialogHeader>
      <Form {...form}>
        <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
          <FormField
            control={form.control}
            name="name"
            render={({ field }) => (
              <FormItem>
                <FormLabel className="text-sm font-medium">Name</FormLabel>
                <FormControl>
                  <Input 
                    placeholder={isEdit ? "" : "e.g. Corporate, Wedding, Portrait"} 
                    {...field} 
                    className="mt-1"
                  />
                </FormControl>
                <FormMessage />
                {!isEdit && (
                  <p className="text-xs text-muted-foreground mt-1">
                    Customize your project types to reflect the type of work you offer.
                  </p>
                )}
              </FormItem>
            )}
          />

          <FormField
            control={form.control}
            name="is_default"
            render={({ field }) => (
              <FormItem className="flex flex-row items-start space-x-3 space-y-0">
                <FormControl>
                  <Checkbox
                    checked={field.value}
                    onCheckedChange={field.onChange}
                  />
                </FormControl>
                <div className="space-y-1 leading-none">
                  <FormLabel className="text-sm font-medium">
                    Set as default
                  </FormLabel>
                  <p className="text-xs text-muted-foreground">
                    This type will be pre-selected when creating new projects.
                  </p>
                </div>
              </FormItem>
            )}
          />

          <div className="flex justify-between items-center pt-4">
            {isEdit && editingType && !editingType.is_default && (
              <AlertDialog>
                <AlertDialogTrigger asChild>
                  <Button type="button" variant="destructive">
                    <Trash2 className="h-4 w-4 mr-2" />
                    Delete
                  </Button>
                </AlertDialogTrigger>
                <AlertDialogContent>
                  <AlertDialogHeader>
                    <AlertDialogTitle>Delete Project Type</AlertDialogTitle>
                    <AlertDialogDescription>
                      {tMessages('confirm.deleteWithName', { name: editingType?.name })} {tMessages('confirm.cannotUndo')}
                    </AlertDialogDescription>
                  </AlertDialogHeader>
                  <AlertDialogFooter>
                    <AlertDialogCancel>Cancel</AlertDialogCancel>
                    <AlertDialogAction
                      onClick={() => {
                        if (editingType) {
                          handleDelete(editingType.id);
                          setIsEditDialogOpen(false);
                        }
                      }}
                      className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
                    >
                      Delete
                    </AlertDialogAction>
                  </AlertDialogFooter>
                </AlertDialogContent>
              </AlertDialog>
            )}

            {isEdit && editingType && editingType.is_default && (
              <p className="text-sm text-muted-foreground">
                The default type cannot be deleted. Set another type as default first.
              </p>
            )}

            <div className="flex gap-2 ml-auto">
              <Button 
                type="button" 
                variant="outline" 
                onClick={() => isEdit ? setIsEditDialogOpen(false) : setIsAddDialogOpen(false)}
              >
                Cancel
              </Button>
              <Button type="submit" disabled={submitting}>
                {submitting ? (
                  <>
                    <Loader2 className="h-4 w-4 animate-spin mr-2" />
                    {isEdit ? 'Saving...' : 'Adding...'}
                  </>
                ) : (
                  isEdit ? 'Save' : 'Add'
                )}
              </Button>
            </div>
          </div>
        </form>
      </Form>
    </DialogContent>
  );

  const handleRefetch = async () => {
    await refetch();
  };

  if (orgLoading || isLoading) {
    return (
      <SettingsSection 
        title={t('project_types.title')}
        description={t('project_types.description')}
      >
        <FormLoadingSkeleton rows={3} />
      </SettingsSection>
    );
  }

  return (
    <>
      <SettingsSection 
        title={t('project_types.title')}
        description={t('project_types.description')}
        action={{
          label: t('project_types.add_type'),
          onClick: handleAdd,
          icon: <Plus className="h-4 w-4" />
        }}
      >
        <div className="flex flex-wrap gap-3 p-2">
          {types.map((type) => (
            <div
              key={type.id}
              className={cn(
                "inline-flex items-center gap-2 px-3 py-1.5 rounded-full text-sm font-medium transition-all select-none border cursor-pointer hover:opacity-80",
                type.is_default 
                  ? "bg-primary/10 border-primary/20 text-primary" 
                  : "bg-muted/50 border-muted text-muted-foreground hover:bg-muted/70"
              )}
              onClick={() => handleEdit(type)}
            >
              <span className="uppercase tracking-wide font-semibold">
                {type.name}
              </span>
              {type.is_default && (
                <Check className="w-3 h-3 text-primary" />
              )}
            </div>
          ))}
        </div>

        {/* Add Dialog */}
        <AddProjectTypeDialog
          open={isAddDialogOpen}
          onOpenChange={setIsAddDialogOpen}
          onTypeAdded={handleRefetch}
        />

        {/* Edit Dialog */}
        <EditProjectTypeDialog
          type={editingType}
          open={isEditDialogOpen}
          onOpenChange={setIsEditDialogOpen}
          onTypeUpdated={handleRefetch}
        />
      </SettingsSection>
    </>
  );
};

export default ProjectTypesSection;

import { useState, useEffect } from "react";
import { Check, ChevronDown, X } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import { Label } from "@/components/ui/label";
import { ScrollArea } from "@/components/ui/scroll-area";
import { Separator } from "@/components/ui/separator";
import { supabase } from "@/integrations/supabase/client";
import { useI18nToast } from "@/lib/toastHelpers";

interface Service {
  id: string;
  name: string;
  category: string | null;
  cost_price?: number;
  selling_price?: number;
}

interface ServiceSelectorProps {
  projectId?: string;
  selectedServices: Service[];
  onServicesChange: (services: Service[]) => void;
  disabled?: boolean;
}

export function ServiceSelector({ 
  projectId, 
  selectedServices, 
  onServicesChange, 
  disabled = false 
}: ServiceSelectorProps) {
  const [availableServices, setAvailableServices] = useState<Service[]>([]);
  const [isOpen, setIsOpen] = useState(false);
  const [loading, setLoading] = useState(true);
  const toast = useI18nToast();

  const fetchServices = async () => {
    try {
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) return;

      // Get user's active organization ID
      const { data: organizationId } = await supabase.rpc('get_user_active_organization_id');
      if (!organizationId) return;

      const { data, error } = await supabase
        .from("services")
        .select("id, name, category, cost_price, selling_price")
        .eq("organization_id", organizationId)
        .order("category", { ascending: true })
        .order("name", { ascending: true });

      if (error) throw error;
      setAvailableServices(data || []);
    } catch (error) {
      console.error("Error fetching services:", error);
      toast.error("Failed to load available services.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchServices();
  }, []);

  const groupedServices = availableServices.reduce((groups, service) => {
    const category = service.category || "Uncategorized";
    if (!groups[category]) {
      groups[category] = [];
    }
    groups[category].push(service);
    return groups;
  }, {} as Record<string, Service[]>);

  const handleServiceToggle = (service: Service) => {
    const isSelected = selectedServices.some(s => s.id === service.id);

    if (isSelected) {
      onServicesChange(selectedServices.filter(s => s.id !== service.id));
    } else {
      onServicesChange([...selectedServices, service]);
    }
  };

  const removeService = (serviceId: string) => {
    onServicesChange(selectedServices.filter(s => s.id !== serviceId));
  };

  if (loading) {
    return (
      <div className="space-y-2">
        <Label>Services</Label>
        <div className="w-full h-10 bg-muted animate-pulse rounded-md" />
      </div>
    );
  }

  if (availableServices.length === 0) {
    return (
      <div className="space-y-2">
        <p className="text-sm text-muted-foreground">
          No services defined yet. Go to Settings â†’ Services to add your offerings.
        </p>
      </div>
    );
  }

  return (
    <div className="space-y-2">
      <Label>Services</Label>
      <Popover open={isOpen} onOpenChange={setIsOpen}>
        <PopoverTrigger asChild>
          <Button
            variant="outline"
            role="combobox"
            aria-expanded={isOpen}
            className="w-full justify-between h-auto min-h-10 p-2"
            disabled={disabled}
          >
            <div className="flex flex-wrap gap-1 flex-1">
              {selectedServices.length === 0 ? (
                <span className="text-muted-foreground">Select services provided</span>
              ) : (
                selectedServices.map((service) => (
                  <Badge
                    key={service.id}
                    variant="secondary"
                    className="text-xs"
                  >
                    {service.name}
                    <button
                      className="ml-1 ring-offset-background rounded-full outline-none focus:ring-2 focus:ring-ring focus:ring-offset-2"
                      onKeyDown={(e) => {
                        if (e.key === "Enter") {
                          removeService(service.id);
                        }
                      }}
                      onMouseDown={(e) => {
                        e.preventDefault();
                        e.stopPropagation();
                      }}
                      onClick={(e) => {
                        e.preventDefault();
                        e.stopPropagation();
                        removeService(service.id);
                      }}
                    >
                      <X className="h-3 w-3 text-muted-foreground hover:text-foreground" />
                    </button>
                  </Badge>
                ))
              )}
            </div>
            <ChevronDown className="h-4 w-4 shrink-0 opacity-50" />
          </Button>
        </PopoverTrigger>
        <PopoverContent className="w-[400px] p-0" align="start">
          <ScrollArea className="max-h-60">
            <div className="p-4 space-y-3">
              {Object.entries(groupedServices).map(([category, services], categoryIndex) => (
                <div key={category}>
                  {categoryIndex > 0 && <Separator className="my-2" />}
                  <div className="space-y-2">
                    <h4 className="text-sm font-medium text-muted-foreground px-2">
                      {category}
                    </h4>
                    <div className="space-y-1">
                      {services.map((service) => {
                        const isSelected = selectedServices.some(s => s.id === service.id);
                        return (
                          <div
                            key={service.id}
                            className="flex items-center space-x-2 p-2 rounded-md hover:bg-accent cursor-pointer"
                            onClick={() => handleServiceToggle(service)}
                          >
                            <div className="flex items-center space-x-2 flex-1">
                              <div className="w-4 h-4 border border-primary rounded-sm flex items-center justify-center">
                                {isSelected && <Check className="h-3 w-3" />}
                              </div>
                              <div className="flex-1">
                                <span className="text-sm">{service.name}</span>
                                {((service.cost_price || 0) > 0 || (service.selling_price || 0) > 0) && (
                                  <div className="flex gap-3 mt-1">
                                    {(service.cost_price || 0) > 0 && (
                                      <span className="text-xs text-muted-foreground">
                                        Cost: TRY {service.cost_price}
                                      </span>
                                    )}
                                    {(service.selling_price || 0) > 0 && (
                                      <span className="text-xs text-muted-foreground">
                                        Selling: TRY {service.selling_price}
                                      </span>
                                    )}
                                  </div>
                                )}
                              </div>
                            </div>
                          </div>
                        );
                      })}
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </ScrollArea>
        </PopoverContent>
      </Popover>
    </div>
  );
}

import { useState } from "react";
import { Card, CardHeader, CardTitle, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Plus, Calendar } from "lucide-react";
import DeadSimpleSessionBanner from "./DeadSimpleSessionBanner";
import EditSessionDialog from "./EditSessionDialog";
import SessionSheetView from "./SessionSheetView";
import { NewSessionDialogForProject } from "./NewSessionDialogForProject";
import { useNavigate, useLocation } from "react-router-dom";
import { sortSessionsByLifecycle, SessionWithStatus } from "@/lib/sessionSorting";
import { useFormsTranslation } from "@/hooks/useTypedTranslation";
import { useIsMobile } from "@/hooks/use-mobile";
interface Session extends SessionWithStatus {
  session_name?: string | null;
  session_time: string;
  notes: string;
}
interface SessionsSectionProps {
  sessions: Session[];
  loading: boolean;
  leadId: string;
  projectId: string;
  leadName: string;
  projectName: string;
  onSessionUpdated: () => void;
  onDeleteSession: (sessionId: string) => void;
}
export function SessionsSection({
  sessions,
  loading,
  leadId,
  projectId,
  leadName,
  projectName,
  onSessionUpdated,
  onDeleteSession
}: SessionsSectionProps) {
  const [editingSessionId, setEditingSessionId] = useState<string | null>(null);
  const [selectedSessionId, setSelectedSessionId] = useState<string | null>(null);
  const [isSessionSheetOpen, setIsSessionSheetOpen] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();
  const { t } = useFormsTranslation();
  const isMobile = useIsMobile();
  const handleSessionUpdated = () => {
    onSessionUpdated();
    setEditingSessionId(null);
  };

  const handleSessionSheetUpdated = () => {
    onSessionUpdated(); // Propagate updates from session sheet to parent
  };

  const handleSessionSheetOpenChange = (open: boolean) => {
    setIsSessionSheetOpen(open);
    if (!open) {
      // When sheet closes, refresh the sessions to reflect any updates
      onSessionUpdated();
    }
  };

  const handleSessionClick = (sessionId: string) => {
    if (isMobile) {
      navigate(`/sessions/${sessionId}`);
      return;
    }
    setSelectedSessionId(sessionId);
    setIsSessionSheetOpen(true);
  };

  const handleViewFullSessionDetails = () => {
    if (selectedSessionId) {
      const currentPath = `${location.pathname}${location.search}${location.hash}`;
      navigate(`/sessions/${selectedSessionId}`, { state: { from: currentPath } });
    }
  };

  const handleNavigateToLead = (leadId: string) => {
    navigate(`/leads/${leadId}`);
  };

  const handleNavigateToProject = (projectId: string) => {
    navigate(`/projects/${projectId}`);
  };
  if (loading) {
    return <Card>
        <CardHeader>
          <CardTitle className="flex items-center justify-between">
            {t('sessions_form.title')}
            <div className="w-6 h-6 bg-muted animate-pulse rounded" />
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-2">
            <div className="w-full h-4 bg-muted animate-pulse rounded" />
            <div className="w-3/4 h-4 bg-muted animate-pulse rounded" />
          </div>
        </CardContent>
      </Card>;
  }
  return <>
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center justify-between text-xl font-semibold">
            <div className="flex items-center gap-2">
              <Calendar className="h-4 w-4" />
              {t('sessions_form.title')}
            </div>
            <NewSessionDialogForProject leadId={leadId} leadName={leadName} projectName={projectName} projectId={projectId} onSessionScheduled={onSessionUpdated} />
          </CardTitle>
        </CardHeader>
        <CardContent>
          {sessions.length > 0 ? <div className="space-y-3">
              {!loading && <p className="text-sm text-muted-foreground mb-3">
                  {sessions.length === 1 
                    ? t('sessions_form.project_sessions_count', { count: sessions.length })
                    : t('sessions_form.project_sessions_count_plural', { count: sessions.length })
                  }
                </p>}
              {sortSessionsByLifecycle(sessions).map(session => (
                <DeadSimpleSessionBanner 
                  key={session.id} 
                  session={session as any}
                  onClick={() => handleSessionClick(session.id)}
                />
              ))}
            </div> : <div className="text-center py-4">
              <Calendar className="h-8 w-8 text-muted-foreground mx-auto mb-2" />
              <p className="text-muted-foreground text-sm">{t('sessions_form.no_sessions')}</p>
              <p className="text-xs text-muted-foreground mt-1">
                {t('sessions_form.add_sessions_hint')}
              </p>
            </div>}
        </CardContent>
      </Card>

      {/* Edit Session Dialog */}
      {editingSessionId && (() => {
        const session = sessions.find(s => s.id === editingSessionId);
        return session ? (
           <EditSessionDialog 
             sessionId={session.id} 
             leadId={session.lead_id} 
             currentDate={session.session_date} 
             currentTime={session.session_time} 
             currentNotes={session.notes} 
             currentProjectId={session.project_id} 
             currentSessionName={session.session_name}
             leadName={leadName} 
             open={!!editingSessionId} 
             onOpenChange={open => {
               if (!open) {
                 setEditingSessionId(null);
               }
             }} 
             onSessionUpdated={handleSessionUpdated} 
           />
        ) : null;
      })()}

      {/* Session Sheet View */}
      <SessionSheetView
        sessionId={selectedSessionId || ''}
        isOpen={isSessionSheetOpen}
        onOpenChange={handleSessionSheetOpenChange}
        onViewFullDetails={handleViewFullSessionDetails}
        onNavigateToLead={handleNavigateToLead}
        onNavigateToProject={handleNavigateToProject}
        onSessionUpdated={handleSessionSheetUpdated}
      />
    </>;
}

import { useState, useEffect } from "react";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import { supabase } from "@/integrations/supabase/client";
import { useToast } from "@/hooks/use-toast";
import { getUserOrganizationId } from "@/lib/organizationUtils";

interface ProjectType {
  id: string;
  name: string;
  is_default: boolean;
}

interface SimpleProjectTypeSelectProps {
  value?: string;
  onValueChange: (value: string) => void;
  placeholder?: string;
  disabled?: boolean;
  className?: string;
  required?: boolean;
}

export function SimpleProjectTypeSelect({ 
  value, 
  onValueChange, 
  placeholder = "Select project type...", 
  disabled = false,
  className,
  required = false
}: SimpleProjectTypeSelectProps) {
  const [types, setTypes] = useState<ProjectType[]>([]);
  const [loading, setLoading] = useState(false);
  const { toast } = useToast();

  const fetchProjectTypes = async () => {
    setLoading(true);
    try {
      const { data: { user } } = await supabase.auth.getUser();
      if (!user) return;

      const organizationId = await getUserOrganizationId();
      if (!organizationId) return;

      const { data, error } = await supabase
        .from('project_types')
        .select('id, name, is_default')
        .eq('organization_id', organizationId)
        .order('is_default', { ascending: false }) // Default types first
        .order('name', { ascending: true });

      if (error) throw error;

      setTypes(data || []);

      // Auto-select default type if no value is set
      if (!value && data && data.length > 0) {
        const defaultType = data.find(type => type.is_default);
        if (defaultType) {
          onValueChange(defaultType.id);
        }
      }
    } catch (error: any) {
      toast({
        title: "Error loading project types",
        description: error.message,
        variant: "destructive"
      });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchProjectTypes();
  }, []);

  // Check if user has no project types
  if (!loading && types.length === 0) {
    return (
      <div className="text-sm text-muted-foreground p-2 border rounded">
        No project types configured. Please add project types in Settings first.
      </div>
    );
  }

  return (
    <Select
      value={value}
      onValueChange={onValueChange}
      disabled={disabled || loading}
    >
      <SelectTrigger className={className}>
        <SelectValue placeholder={loading ? "Loading types..." : placeholder} />
      </SelectTrigger>
      <SelectContent className="z-50 bg-popover">
        {types.map((type) => (
          <SelectItem key={type.id} value={type.id}>
            <div className="flex items-center gap-2">
              <span>{type.name}</span>
              {type.is_default && (
                <span className="text-xs text-muted-foreground">(Default)</span>
              )}
            </div>
          </SelectItem>
        ))}
      </SelectContent>
    </Select>
  );
}

import { useEffect, useMemo, useRef, useState } from "react";
import type { ComponentPropsWithoutRef, ElementType } from "react";
import {
  Tooltip,
  TooltipContent,
  TooltipProvider,
  TooltipTrigger,
} from "@/components/ui/tooltip";
import { cn } from "@/lib/utils";

type SupportedElement = "span" | "div" | "p";

const LINE_CLAMP_CLASSES: Record<number, string> = {
  1: "line-clamp-1",
  2: "line-clamp-2",
  3: "line-clamp-3",
  4: "line-clamp-4",
  5: "line-clamp-5",
  6: "line-clamp-6",
};

interface TruncatedTextWithTooltipProps<T extends SupportedElement = "span">
  extends ComponentPropsWithoutRef<T> {
  text: string;
  lines?: number;
  as?: T;
  tooltipSide?: ComponentPropsWithoutRef<typeof TooltipContent>["side"];
  tooltipAlign?: ComponentPropsWithoutRef<typeof TooltipContent>["align"];
  tooltipClassName?: string;
  delayDuration?: number;
}

export function TruncatedTextWithTooltip<T extends SupportedElement = "span">({
  text,
  lines = 2,
  as,
  className,
  tooltipSide = "top",
  tooltipAlign = "center",
  tooltipClassName,
  delayDuration = 150,
  ...rest
}: TruncatedTextWithTooltipProps<T>) {
  const Component = (as ?? "span") as ElementType;
  const textRef = useRef<HTMLElement | null>(null);
  const [isTruncated, setIsTruncated] = useState(false);

  const clampClass = useMemo(() => {
    return LINE_CLAMP_CLASSES[lines] || LINE_CLAMP_CLASSES[2];
  }, [lines]);

  useEffect(() => {
    if (!text) {
      setIsTruncated(false);
      return;
    }

    if (typeof window === "undefined") {
      setIsTruncated(false);
      return;
    }

    const el = textRef.current;
    if (!el) {
      setIsTruncated(false);
      return;
    }

    const checkTruncation = () => {
      const isOverflowingVertically = el.scrollHeight > el.clientHeight + 1;
      const isOverflowingHorizontally = el.scrollWidth > el.clientWidth + 1;
      setIsTruncated(isOverflowingVertically || isOverflowingHorizontally);
    };

    const frame = window.requestAnimationFrame(checkTruncation);

    let resizeObserver: ResizeObserver | null = null;
    if ("ResizeObserver" in window) {
      resizeObserver = new window.ResizeObserver(checkTruncation);
      resizeObserver.observe(el);
    } else {
      window.addEventListener("resize", checkTruncation);
    }

    return () => {
      window.cancelAnimationFrame(frame);
      if (resizeObserver) {
        resizeObserver.disconnect();
      } else {
        window.removeEventListener("resize", checkTruncation);
      }
    };
  }, [text, lines]);

  if (!text) {
    return null;
  }

  const content = (
    <Component
      ref={textRef as any}
      className={cn(
        "break-words",
        clampClass,
        isTruncated && "cursor-help",
        className
      )}
      {...(rest as ComponentPropsWithoutRef<typeof Component>)}
    >
      {text}
    </Component>
  );

  return (
    <TooltipProvider delayDuration={delayDuration}>
      <Tooltip>
        <TooltipTrigger asChild>{content}</TooltipTrigger>
        {isTruncated && (
          <TooltipContent
            side={tooltipSide}
            align={tooltipAlign}
            className={cn(
              "max-w-xs whitespace-pre-wrap break-words text-left",
              tooltipClassName
            )}
          >
            {text}
          </TooltipContent>
        )}
      </Tooltip>
    </TooltipProvider>
  );
}

import { useMemo } from "react";
import {
  addDays,
  differenceInCalendarDays,
  format,
  parseISO,
  startOfWeek,
} from "date-fns";
import { useFormsTranslation } from "@/hooks/useTypedTranslation";
import { cn } from "@/lib/utils";
import {
  Tooltip,
  TooltipContent,
  TooltipProvider,
  TooltipTrigger,
} from "@/components/ui/tooltip";

const MINUTES_IN_DAY = 24 * 60;
const PIXELS_PER_MINUTE = 0.9;
const MIN_BLOCK_HEIGHT = 36;
const MIN_VIEW_RANGE = 6 * 60;
const DEFAULT_DRAFT_DURATION = 60;

export interface WeeklyScheduleSession {
  id: string;
  session_date?: string | null;
  session_time?: string | null;
  duration_minutes?: number | null;
  session_type_name?: string | null;
  lead_name?: string | null;
  project_name?: string | null;
}

interface WeeklySchedulePreviewProps {
  sessions: WeeklyScheduleSession[];
  referenceDate: Date;
  selectedDate?: Date;
  selectedTime?: string;
  selectedDurationMinutes?: number | null;
  locale?: string;
  showDraftSelection?: boolean;
}

interface PositionedSession extends WeeklyScheduleSession {
  startMinutes: number;
  endMinutes: number;
  columnIndex: number;
  columnCount: number;
  dayIndex: number;
}

const formatTimeLabel = (minutes: number, locale: string) => {
  const hours = Math.floor(minutes / 60);
  const mins = minutes % 60;
  const reference = new Date();
  reference.setHours(hours, mins, 0, 0);
  return new Intl.DateTimeFormat(locale, {
    hour: "numeric",
    minute: "2-digit",
  }).format(reference);
};

const formatDurationLabel = (
  minutes: number,
  translate: (key: string, options?: Record<string, unknown>) => string
) => {
  const roundedMinutes = Math.max(Math.round(minutes), 0);
  const hours = Math.floor(roundedMinutes / 60);
  const remainingMinutes = roundedMinutes % 60;
  const parts: string[] = [];

  if (hours > 0) {
    const unitKey =
      hours === 1 ? "sessionTypes.units.hour" : "sessionTypes.units.hours";
    parts.push(`${hours} ${translate(unitKey)}`);
  }

  if (remainingMinutes > 0 || parts.length === 0) {
    const unitKey =
      remainingMinutes === 1
        ? "sessionTypes.units.minute"
        : "sessionTypes.units.minutes";
    parts.push(`${remainingMinutes} ${translate(unitKey)}`);
  }

  return parts.join(" ");
};

const parseTimeToMinutes = (time?: string | null): number | null => {
  if (!time) return null;
  const [hours, minutes] = time.split(":").map((part) => Number(part) || 0);
  if (Number.isNaN(hours) || Number.isNaN(minutes)) return null;
  return hours * 60 + minutes;
};

const buildDayLayout = (
  sessions: Array<WeeklyScheduleSession & { startMinutes: number; endMinutes: number }>
): PositionedSession[] => {
  const sorted = [...sessions].sort(
    (a, b) => a.startMinutes - b.startMinutes || a.endMinutes - b.endMinutes
  );

  const active: Array<{
    columnIndex: number;
    endMinutes: number;
    positioned: PositionedSession;
  }> = [];
  const positioned: PositionedSession[] = [];

  sorted.forEach((session) => {
    const { startMinutes, endMinutes } = session;

    for (let i = active.length - 1; i >= 0; i -= 1) {
      if (active[i].endMinutes <= startMinutes) {
        active.splice(i, 1);
      }
    }

    const usedColumns = new Set(active.map((item) => item.columnIndex));
    let columnIndex = 0;
    while (usedColumns.has(columnIndex)) {
      columnIndex += 1;
    }

    const positionedSession: PositionedSession = {
      ...session,
      columnIndex,
      columnCount: 1,
      id: session.id,
      dayIndex: 0,
    };

    active.push({ columnIndex, endMinutes, positioned: positionedSession });
    active.sort((a, b) => a.endMinutes - b.endMinutes);

    const overlapColumns = active.length;
    active.forEach((item) => {
      item.positioned.columnCount = Math.max(item.positioned.columnCount, overlapColumns);
    });

    positioned.push(positionedSession);
  });

  return positioned;
};

const clampRange = (start: number, end: number): { start: number; end: number } => {
  let rangeStart = Math.max(0, start);
  let rangeEnd = Math.min(MINUTES_IN_DAY, end);
  if (rangeEnd <= rangeStart) {
    rangeStart = 9 * 60;
    rangeEnd = rangeStart + MIN_VIEW_RANGE;
  }
  if (rangeEnd - rangeStart < MIN_VIEW_RANGE) {
    const deficit = MIN_VIEW_RANGE - (rangeEnd - rangeStart);
    const adjustStart = Math.max(0, rangeStart - deficit / 2);
    const adjustEnd = Math.min(MINUTES_IN_DAY, adjustStart + MIN_VIEW_RANGE);
    rangeStart = Math.max(0, adjustEnd - MIN_VIEW_RANGE);
    rangeEnd = adjustEnd;
  }
  return { start: rangeStart, end: rangeEnd };
};

export const WeeklySchedulePreview = ({
  sessions,
  referenceDate,
  selectedDate,
  selectedTime,
  selectedDurationMinutes,
  showDraftSelection = true,
  locale = typeof navigator !== "undefined" ? navigator.language : "en-US",
}: WeeklySchedulePreviewProps) => {
  const { t } = useFormsTranslation();

  const weekStart = useMemo(
    () => startOfWeek(referenceDate, { weekStartsOn: 1 }),
    [referenceDate]
  );

  const selectedDayIndex = useMemo(() => {
    if (!selectedDate) return null;
    return differenceInCalendarDays(selectedDate, weekStart);
  }, [selectedDate, weekStart]);

  const draftStartMinutes = useMemo(
    () => parseTimeToMinutes(selectedTime),
    [selectedTime]
  );

  const draftDurationMinutes = useMemo(() => {
    if (typeof selectedDurationMinutes === "number" && selectedDurationMinutes > 0) {
      return Math.min(selectedDurationMinutes, MINUTES_IN_DAY);
    }
    return DEFAULT_DRAFT_DURATION;
  }, [selectedDurationMinutes]);

  const isDraftWithinWeek =
    selectedDayIndex !== null &&
    selectedDayIndex >= 0 &&
    selectedDayIndex <= 6 &&
    draftStartMinutes !== null;

  const draftEndMinutes = isDraftWithinWeek
    ? Math.min(draftStartMinutes! + draftDurationMinutes, MINUTES_IN_DAY - 1)
    : null;

  const days = useMemo(
    () =>
      Array.from({ length: 7 }, (_, index) => ({
        date: addDays(weekStart, index),
        index,
      })),
    [weekStart]
  );

  const { dayLayouts, daySessionsMap, noTimeSessions, viewWindow } = useMemo(() => {
    const dayMap = new Map<number, Array<WeeklyScheduleSession & { startMinutes: number; endMinutes: number }>>();
    const noTimeMap = new Map<number, WeeklyScheduleSession[]>();
    const startCandidates: number[] = [];
    const endCandidates: number[] = [];

    sessions.forEach((session) => {
      if (!session.session_date) return;
      const parsedDate = parseISO(session.session_date);
      if (Number.isNaN(parsedDate.getTime())) return;
      const dayIndex = differenceInCalendarDays(parsedDate, weekStart);
      if (dayIndex < 0 || dayIndex > 6) return;

    const startMinutes = parseTimeToMinutes(session.session_time);
    const durationMinutes =
      typeof session.duration_minutes === "number" && session.duration_minutes > 0
        ? session.duration_minutes
        : 60;
    const safeEnd = startMinutes !== null ? startMinutes + durationMinutes : null;

      if (startMinutes === null || safeEnd === null) {
        const existing = noTimeMap.get(dayIndex) ?? [];
        noTimeMap.set(dayIndex, [...existing, session]);
        return;
      }

      const normalized = {
        ...session,
        startMinutes,
        endMinutes: safeEnd,
        duration_minutes: durationMinutes,
      };

      const daySessions = dayMap.get(dayIndex) ?? [];
      dayMap.set(dayIndex, [...daySessions, normalized]);
      startCandidates.push(startMinutes);
      endCandidates.push(safeEnd);
    });

    if (isDraftWithinWeek && draftStartMinutes !== null && draftEndMinutes !== null) {
      startCandidates.push(draftStartMinutes);
      endCandidates.push(draftEndMinutes);
    }

    const rawStart = startCandidates.length ? Math.min(...startCandidates) : 9 * 60;
    const rawEnd = endCandidates.length ? Math.max(...endCandidates) : 17 * 60;
    const viewWindow = clampRange(rawStart, rawEnd);

    const layoutsByDay = new Map<number, PositionedSession[]>();
    dayMap.forEach((daySessions, dayIndex) => {
      const layouts = buildDayLayout(daySessions).map((session) => ({
        ...session,
        dayIndex,
      }));
      layoutsByDay.set(dayIndex, layouts);
    });

    return {
      dayLayouts: layoutsByDay,
      daySessionsMap: dayMap,
      noTimeSessions: noTimeMap,
      viewWindow,
    };
  }, [
    draftEndMinutes,
    draftStartMinutes,
    isDraftWithinWeek,
    sessions,
    weekStart,
  ]);

  const containerHeight = (viewWindow.end - viewWindow.start) * PIXELS_PER_MINUTE;
  const hourMarkers = useMemo(() => {
    const markers: Array<{ label: string; minute: number }> = [];
    if (viewWindow.end <= viewWindow.start) {
      return markers;
    }
    const firstMarker = Math.ceil(viewWindow.start / 60) * 60;
    if (firstMarker > viewWindow.end) {
      return markers;
    }
    for (let minute = firstMarker; minute <= viewWindow.end; minute += 60) {
      markers.push({
        label: formatTimeLabel(minute, locale),
        minute,
      });
    }
    return markers;
  }, [viewWindow.end, viewWindow.start, locale]);

  const hasDraftSelection = Boolean(isDraftWithinWeek);

  if (!sessions.length && !hasDraftSelection) {
    return (
      <div className="rounded-xl border border-dashed border-primary/30 bg-primary/5 p-6 text-sm text-muted-foreground">
        {t("sessionScheduling.weekly_preview_empty")}
      </div>
    );
  }

  return (
    <TooltipProvider delayDuration={120}>
    <div className="space-y-4">
      <div className="flex flex-wrap items-center justify-between gap-2">
        <h3 className="text-sm font-semibold tracking-tight text-slate-900">
          {t("sessionScheduling.weekly_preview_heading")}
        </h3>
        <span className="text-xs font-medium uppercase tracking-wide text-muted-foreground">
          {format(weekStart, "MMM d")} â€“ {format(addDays(weekStart, 6), "MMM d")}
        </span>
      </div>
      <div className="overflow-hidden rounded-xl border bg-white shadow-sm">
        <div className="grid grid-cols-[72px_repeat(7,minmax(0,1fr))] border-b bg-slate-50/70 text-xs font-medium uppercase tracking-wide text-muted-foreground">
          <div className="px-3 py-2">{t("sessionScheduling.weekly_preview_time_column")}</div>
          {days.map(({ date, index }) => (
            <div
              key={index}
              className={cn(
                "border-l px-3 py-2 text-center first:border-l-0",
                selectedDayIndex === index && "bg-primary/10 text-primary"
              )}
            >
              <div>{new Intl.DateTimeFormat(locale, { weekday: "short" }).format(date)}</div>
              <div className="text-[11px] font-semibold text-slate-600">
                {new Intl.DateTimeFormat(locale, {
                  day: "2-digit",
                  month: "2-digit",
                }).format(date)}
              </div>
            </div>
          ))}
        </div>
        <div className="grid grid-cols-[72px_repeat(7,minmax(0,1fr))]">
          <div className="relative border-r bg-slate-50/40">
            <div
              className="relative"
              style={{ height: `${containerHeight}px` }}
            >
              {hourMarkers.map((marker) => {
                const offset = (marker.minute - viewWindow.start) * PIXELS_PER_MINUTE;
                return (
                  <div
                    key={marker.minute}
                    data-testid="weekly-hour-marker"
                    className="absolute left-0 right-0 flex items-center justify-end pr-2 text-[11px] text-muted-foreground"
                    style={{ top: `${offset}px` }}
                  >
                    {marker.label}
                  </div>
                );
              })}
          </div>
        </div>
          {days.map(({ index }) => {
            const baseDaySessions = dayLayouts.get(index) ?? [];
            const rawDaySessions = daySessionsMap.get(index) ?? [];
            const dayNoTime = noTimeSessions.get(index) ?? [];
            const draftStart = draftStartMinutes ?? 0;
            const draftEnd = draftEndMinutes ?? draftStart;
            const shouldRenderDraft =
              showDraftSelection &&
              hasDraftSelection &&
              selectedDayIndex === index &&
              draftStartMinutes !== null &&
              draftEndMinutes !== null;

            let renderedSessions = baseDaySessions;
            let draftLayout: PositionedSession | null = null;

            if (showDraftSelection) {
              const draftSession: WeeklyScheduleSession & {
                startMinutes: number;
                endMinutes: number;
              } = {
                id: "__draft__",
                session_time: selectedTime ?? null,
                duration_minutes: draftDurationMinutes,
                startMinutes: draftStart,
                endMinutes: draftEnd,
              };

              const layoutWithDraft = buildDayLayout([
                ...rawDaySessions,
                draftSession,
              ]).map((session) => ({
                ...session,
                dayIndex: index,
              }));

              draftLayout =
                layoutWithDraft.find((session) => session.id === draftSession.id) ??
                null;

              renderedSessions = layoutWithDraft.filter(
                (session) => session.id !== draftSession.id
              );
            }

            const draftBlockMetrics = showDraftSelection
              ? (() => {
                  const top =
                    (draftStart - viewWindow.start) * PIXELS_PER_MINUTE;
                  const height = Math.max(
                    (draftEnd - draftStart) * PIXELS_PER_MINUTE,
                    MIN_BLOCK_HEIGHT
                  );
                  const columnCount =
                    draftLayout?.columnCount && draftLayout.columnCount > 0
                      ? draftLayout.columnCount
                      : 1;
                  const columnIndex =
                    draftLayout?.columnIndex && draftLayout.columnIndex >= 0
                      ? draftLayout.columnIndex
                      : 0;
                  const widthPercent = 100 / columnCount;
                  const leftPercent = (columnIndex * widthPercent).toFixed(3);
                  const widthValue = widthPercent.toFixed(3);
                  const gutter = columnCount > 1 ? 1 : 0;

                  return {
                    top,
                    height,
                    left: `calc(${leftPercent}% + ${gutter}px)`,
                    width: `calc(${widthValue}% - ${gutter * 2}px)`,
                    startLabel: formatTimeLabel(draftStart, locale),
                    endLabel: formatTimeLabel(draftEnd, locale),
                  };
                })()
              : null;

            return (
              <div
                key={index}
                className={cn(
                  "relative border-l first:border-l-0",
                  selectedDayIndex === index && "bg-primary/5"
                )}
              >
                <div
                  className="relative"
                  style={{ height: `${containerHeight}px` }}
                >
                  {hourMarkers.map((marker) => {
                    const offset = (marker.minute - viewWindow.start) * PIXELS_PER_MINUTE;
                    return (
                      <div
                        key={marker.minute}
                        className="absolute left-0 right-0 border-t border-dashed border-slate-200/70"
                        style={{ top: `${offset}px` }}
                        aria-hidden="true"
                      />
                    );
                  })}
                  {draftBlockMetrics && shouldRenderDraft ? (
                    <div
                      data-testid="weekly-draft-selection"
                      aria-hidden="true"
                      className="pointer-events-none absolute flex flex-col items-stretch text-center"
                      style={{
                        top: `${draftBlockMetrics.top}px`,
                        left: draftBlockMetrics.left,
                        width: draftBlockMetrics.width,
                      }}
                    >
                      <div
                        className="rounded-xl border border-dashed border-amber-400/80 bg-amber-50/90 px-1.5 py-1 text-[10px] font-medium uppercase tracking-wide text-amber-900 shadow-none"
                        style={{ height: `${draftBlockMetrics.height}px` }}
                      >
                        <div className="text-[11px] capitalize tracking-normal">
                          {draftBlockMetrics.startLabel} â€“ {draftBlockMetrics.endLabel}
                        </div>
                      </div>
                      <div className="mt-1 text-[10px] font-semibold uppercase tracking-wide text-amber-700">
                        {t("sessionScheduling.weekly_preview_draft_label")}
                      </div>
                    </div>
                  ) : null}
                  {renderedSessions.map((session) => {
                    const topOffset =
                      (session.startMinutes - viewWindow.start) * PIXELS_PER_MINUTE;
                    const blockHeight = Math.max(
                      (session.endMinutes - session.startMinutes) * PIXELS_PER_MINUTE,
                      MIN_BLOCK_HEIGHT
                    );
                    const widthPercent = 100 / session.columnCount;
                    const leftOffset = (session.columnIndex * widthPercent).toFixed(3);
                    const widthValue = widthPercent.toFixed(3);
                    const startLabel = formatTimeLabel(session.startMinutes, locale);
                    const endLabel = formatTimeLabel(session.endMinutes, locale);
                    const durationMinutes =
                      typeof session.duration_minutes === "number"
                        ? Math.max(session.duration_minutes, 0)
                        : undefined;
                    const formattedDuration =
                      durationMinutes !== undefined
                        ? formatDurationLabel(durationMinutes, t)
                        : undefined;
                    const gutter = session.columnCount > 1 ? 1 : 0;
                    const tooltipDetails = [
                      session.lead_name || t("sessionScheduling.unknown_client"),
                      session.project_name,
                      session.session_type_name && formattedDuration
                        ? `${session.session_type_name} â€¢ ${formattedDuration}`
                        : formattedDuration,
                      `${startLabel} â€“ ${endLabel}`,
                    ].filter(Boolean);
                    const ariaLabel = tooltipDetails.join(" â€¢ ");

                    return (
                      <Tooltip key={session.id}>
                        <TooltipTrigger asChild>
                          <div
                            data-testid={`weekly-session-${session.id}`}
                            aria-label={ariaLabel}
                            className={cn(
                              "absolute overflow-hidden rounded-xl border border-emerald-300/80 bg-emerald-50 px-1.5 py-1.5 text-left shadow-[0_16px_28px_rgba(15,118,110,0.08)] transition-all",
                              "hover:border-emerald-400 hover:bg-emerald-100/90 hover:shadow-[0_20px_34px_rgba(15,118,110,0.15)]"
                            )}
                            style={{
                              top: `${topOffset}px`,
                              height: `${blockHeight}px`,
                              left: `calc(${leftOffset}% + ${gutter}px)`,
                              width: `calc(${widthValue}% - ${gutter * 2}px)`,
                            }}
                          >
                            <p className="flex-1 break-all text-[11px] font-light leading-snug text-slate-800 line-clamp-6">
                              {session.lead_name || t("sessionScheduling.unknown_client")}
                            </p>
                          </div>
                        </TooltipTrigger>
                        <TooltipContent className="max-w-xs space-y-1 text-left text-sm leading-snug">
                          <div className="font-medium text-emerald-900">
                            {session.lead_name || t("sessionScheduling.unknown_client")}
                          </div>
                          {session.project_name ? (
                            <div className="text-emerald-800">{session.project_name}</div>
                          ) : null}
                          {formattedDuration ? (
                            <div className="text-muted-foreground">
                              {session.session_type_name
                                ? `${session.session_type_name} â€¢ ${formattedDuration}`
                                : formattedDuration}
                            </div>
                          ) : null}
                          <div className="text-xs font-medium uppercase tracking-wide text-slate-500">
                            {startLabel} â€“ {endLabel}
                          </div>
                        </TooltipContent>
                      </Tooltip>
                    );
                  })}
                </div>
                {dayNoTime.length > 0 && (
                  <div className="border-t border-dashed border-slate-200 bg-slate-50/80 px-2 py-2 text-[11px] text-muted-foreground">
                    <div className="font-semibold uppercase tracking-wide">
                      {t("sessionScheduling.weekly_preview_time_tbd")}
                    </div>
                    <ul className="mt-1 space-y-1">
                      {dayNoTime.map((session) => (
                        <li key={session.id}>
                          {session.lead_name || t("sessionScheduling.unknown_client")}
                        </li>
                      ))}
                    </ul>
                  </div>
                )}
              </div>
            );
          })}
        </div>
      </div>
    </div>
    </TooltipProvider>
  );
};

export const __testUtils = {
  PIXELS_PER_MINUTE,
  MIN_BLOCK_HEIGHT,
  clampRange,
