interface NavTab {
  title: string;
  icon: React.ComponentType<{ className?: string }>;
  path?: string;
  action?: () => void;
  badge?: number;
}

export function MobileBottomNav({ hideForOnboarding = false }: { hideForOnboarding?: boolean }) {
  const [bookingsOpen, setBookingsOpen] = useState(false);
  const [automationOpen, setAutomationOpen] = useState(false);
  const [moreOpen, setMoreOpen] = useState(false);
  const [helpOpen, setHelpOpen] = useState(false);
  const [isVisible, setIsVisible] = useState(true);
  const [userEmail, setUserEmail] = useState<string>("");
  const location = useLocation();
  const navigate = useNavigate();
  const { profile } = useProfile();

  // Get user email from auth
  useEffect(() => {
    const getUser = async () => {
      const { data: { user } } = await supabase.auth.getUser();
      if (user?.email) {
        setUserEmail(user.email);
      }
    };
    getUser();
  }, []);

  // Hide on auth routes, keyboard open, and during onboarding
  useEffect(() => {
    const isAuthRoute = location.pathname === '/auth';

    const handleResize = () => {
      // Hide when keyboard is likely open (viewport height reduced significantly)
      const isKeyboardOpen = window.visualViewport 
        ? window.visualViewport.height < window.innerHeight * 0.75
        : false;

      setIsVisible(!isAuthRoute && !isKeyboardOpen && !hideForOnboarding);
    };

    handleResize();
    window.addEventListener('resize', handleResize);
    if (window.visualViewport) {
      window.visualViewport.addEventListener('resize', handleResize);
    }

    return () => {
      window.removeEventListener('resize', handleResize);
      if (window.visualViewport) {
        window.visualViewport.removeEventListener('resize', handleResize);
      }
    };
  }, [location.pathname, hideForOnboarding]);

  const handleSignOut = async () => {
    if (window.confirm('Are you sure you want to sign out?')) {
      try {
        await supabase.auth.signOut();
        navigate('/auth');
      } catch (error: any) {
        toast({
          title: "Error signing out",
          description: error.message,
          variant: "destructive"
        });
      }
    }
  };

  const scrollToTop = () => {
    window.scrollTo({ top: 0, behavior: 'smooth' });
  };

  const tabs: NavTab[] = [
    { title: 'Dashboard', icon: LayoutDashboard, path: '/' },
    { title: 'Leads', icon: Users, path: '/leads' },
    { title: 'Projects', icon: FolderOpen, path: '/projects' },
    { title: 'Bookings', icon: CalendarRange, action: () => setBookingsOpen(true) },
    { title: 'More', icon: MoreHorizontal, action: () => setMoreOpen(true) }
  ];

  const bookingItems = [
    {
      title: 'Calendar',
      icon: CalendarDays,
      onClick: () => navigate('/calendar')
    },
    {
      title: 'Sessions',
      icon: Calendar,
      onClick: () => navigate('/sessions')
    },
    {
      title: 'Reminders',
      icon: Bell,
      onClick: () => navigate('/reminders')
    }
  ];

  const automationItems = [
    {
      title: 'Workflows',
      icon: BarChart3,
      onClick: () => navigate('/workflows')
    },
    {
      title: 'Templates',
      icon: FileText,
      onClick: () => navigate('/templates')
    }
  ];

  const moreItems = [
    {
      title: 'Analytics',
      icon: BarChart3,
      onClick: () => navigate('/analytics')
    },
    {
      title: 'Automation',
      icon: Zap,
      onClick: () => setAutomationOpen(true)
    },
    {
      title: 'Payments',
      icon: CreditCard,
      onClick: () => navigate('/payments')
    },
    {
      title: 'Settings',
      icon: Settings,
      onClick: () => navigate('/settings'),
      testId: 'mobile-settings'
    },
    {
      title: 'Help & Support',
      icon: HelpCircle,
      onClick: () => setHelpOpen(true)
    },
    {
      title: 'Sign Out',
      icon: LogOut,
      onClick: handleSignOut,
      variant: 'destructive' as const
    }
  ];

  const isActive = (path: string) => {
    if (path === '/') {
      return location.pathname === '/';
    }
    return location.pathname.startsWith(path);
  };

  const isBookingsActive = ['/calendar', '/sessions', '/reminders'].some(path =>
    location.pathname.startsWith(path)
  );

  const isAutomationActive = ['/workflows', '/templates'].some(path =>
    location.pathname.startsWith(path)
  );

  const isMoreActive = ['/analytics', '/payments', '/settings'].some(path =>
    location.pathname.startsWith(path)
  ) || isAutomationActive;

  if (!isVisible) return null;

  return (
    <>
      <nav 
        className="md:hidden fixed bottom-0 left-0 right-0 z-50 bg-background/90 backdrop-blur-md border-t supports-[backdrop-filter]:bg-background/50 mobile-bottom-nav"
        style={{ 
          paddingBottom: 'max(env(safe-area-inset-bottom), 8px)',
          position: 'fixed',
          bottom: 0,
        }}
      >
        <div className="flex items-center justify-around px-2 py-2">
          {tabs.map((tab, index) => {
            const Icon = tab.icon;
            let active = false;

            if (tab.path) {
              active = isActive(tab.path);
            } else if (tab.title === 'Bookings') {
              active = isBookingsActive;
            } else if (tab.title === 'More') {
              active = isMoreActive;
            }

            const handleTabPress = () => {
              if (tab.path) {
                if (active) {
                  scrollToTop();
                } else {
                  navigate(tab.path);
                }
              } else if (tab.action) {
                tab.action();
              }
            };

            return (
              <button
                key={index}
                onClick={handleTabPress}
                data-walkthrough={tab.title === 'More' ? 'mobile-more-tab' : undefined}
                className={cn(
                  "flex flex-col items-center justify-center min-h-[44px] px-2 py-1 rounded-lg transition-colors relative",
                  "hover:bg-muted/50 active:bg-muted"
                )}
              >
                <Icon 
                  className={cn(
                    "h-5 w-5 mb-1",
                    active ? "text-primary" : "text-muted-foreground"
                  )} 
                />
                <span 
                  className={cn(
                    "text-[10px] font-medium leading-tight",
                    active ? "text-primary" : "text-muted-foreground"
                  )}
                >
                  {tab.title}
                </span>

                {tab.badge && tab.badge > 0 && (
                  <div className="absolute -top-1 -right-1 bg-destructive text-destructive-foreground text-[10px] font-bold rounded-full h-4 w-4 flex items-center justify-center">
                    {tab.badge > 9 ? '9+' : tab.badge}
                  </div>
                )}
              </button>
            );
          })}
        </div>
      </nav>

      <BottomSheetMenu
        title="Bookings"
        isOpen={bookingsOpen}
        onOpenChange={setBookingsOpen}
        items={bookingItems}
      />

      <BottomSheetMenu
        title="Automation"
        isOpen={automationOpen}
        onOpenChange={setAutomationOpen}
        items={automationItems}
      />

      <BottomSheetMenu
        title="More"
        isOpen={moreOpen}
        onOpenChange={setMoreOpen}
        items={moreItems}
        customContent={
          <div className="mt-4 border-t pt-4">
            <UserMenu 
              variant="mobile" 
              onNavigate={() => setMoreOpen(false)} 
            />
          </div>
        }
      />

      <HelpModal 
        isOpen={helpOpen} 
        onOpenChange={setHelpOpen} 
      />
    </>
  );
}

import { useEffect, useMemo, useRef, useState } from "react";
import { cn } from "@/lib/utils";

export interface StickySectionNavItem {
  id: string;
  title: string;
  onSelect?: () => void;
}

export interface StickySectionNavProps {
  items: StickySectionNavItem[];
  stickyTopOffset?: number;
  align?: "start" | "center" | "end";
  ariaLabel?: string;
  className?: string;
  navClassName?: string;
  observeIds?: string[];
  onActiveChange?: (id: string) => void;
  fallbackActiveId?: string;
  scrollBehavior?: ScrollBehavior;
  disableSticky?: boolean;
}

const ALIGN_CLASSNAMES: Record<NonNullable<StickySectionNavProps["align"]>, string> = {
  start: "justify-start",
  center: "justify-center",
  end: "justify-end"
};

const BASE_NAV_BUTTON_CLASSES = [
  "flex-shrink-0",
  "whitespace-nowrap",
  "rounded-full",
  "border",
  "px-3",
  "py-1.5",
  "text-sm",
  "font-medium",
  "transition-colors",
  "focus-visible:outline-none",
  "focus-visible:ring-2",
  "focus-visible:ring-offset-2",
  "focus-visible:ring-primary"
].join(" ");

const ACTIVE_NAV_CLASSES = "border-primary/50 bg-primary/10 text-primary shadow-sm";
const INACTIVE_NAV_CLASSES =
  "border-transparent bg-muted/20 text-muted-foreground hover:bg-muted/30 hover:text-foreground";

export const STICKY_NAV_CLASSES = {
  baseButton: BASE_NAV_BUTTON_CLASSES,
  active: ACTIVE_NAV_CLASSES,
  inactive: INACTIVE_NAV_CLASSES
};

export function StickySectionNav({
  items,
  stickyTopOffset = 0,
  align = "end",
  ariaLabel = "Section navigation",
  className,
  navClassName,
  observeIds,
  onActiveChange,
  fallbackActiveId,
  scrollBehavior = "smooth",
  disableSticky = false
}: StickySectionNavProps) {
  const [activeId, setActiveId] = useState<string>(() => fallbackActiveId ?? items[0]?.id ?? "");
  const observer = useRef<IntersectionObserver | null>(null);

  const idsToObserve = useMemo(() => {
    const ids = observeIds && observeIds.length > 0 ? observeIds : items.map((item) => item.id);
    return Array.from(new Set(ids)).filter(Boolean);
  }, [observeIds, items]);

  useEffect(() => {
    if (idsToObserve.length === 0 || typeof window === "undefined") {
      return undefined;
    }

    const headings = idsToObserve
      .map((id) => document.getElementById(id))
      .filter((el): el is HTMLElement => Boolean(el));

    observer.current?.disconnect();
    observer.current = new IntersectionObserver(
      (entries) => {
        const visible = entries
          .filter((entry) => entry.isIntersecting)
          .sort((a, b) => b.intersectionRatio - a.intersectionRatio);

        if (visible[0]?.target?.id) {
          setActiveId(visible[0].target.id);
        }
      },
      { rootMargin: "-100px 0px -60% 0px", threshold: [0, 0.5, 1] }
    );

    headings.forEach((el) => observer.current?.observe(el));

    return () => observer.current?.disconnect();
  }, [idsToObserve]);

  useEffect(() => {
    if (idsToObserve.length === 0) {
      setActiveId("");
      return;
    }

    setActiveId((prev) => {
      if (prev && idsToObserve.includes(prev)) {
        return prev;
      }
      const preferred =
        fallbackActiveId && idsToObserve.includes(fallbackActiveId)
          ? fallbackActiveId
          : idsToObserve[0];
      return preferred ?? "";
    });
  }, [idsToObserve, fallbackActiveId]);

  useEffect(() => {
    if (activeId && onActiveChange) {
      onActiveChange(activeId);
    }
  }, [activeId, onActiveChange]);

  const handleSelect = (item: StickySectionNavItem) => {
    setActiveId(item.id);

    if (item.onSelect) {
      item.onSelect();
      return;
    }

    if (typeof window === "undefined") {
      return;
    }

    const target = document.getElementById(item.id);
    if (target) {
      target.scrollIntoView({ behavior: scrollBehavior, block: "start" });
    }
  };

  if (items.length === 0) {
    return null;
  }

  const containerClasses = disableSticky
    ? "border-b border-border/20 bg-background/70 px-2 py-3 backdrop-blur supports-[backdrop-filter]:bg-background/50"
    : "sticky z-30 -mx-2 mb-6 border-b border-border/20 bg-background/70 px-2 py-3 backdrop-blur supports-[backdrop-filter]:bg-background/50";

  const containerStyle = disableSticky ? undefined : { top: stickyTopOffset };

  return (
    <div
      className={cn(containerClasses, className)}
      style={containerStyle}
    >
      <nav
        className={cn(
          "flex w-full max-w-full items-center gap-2 overflow-x-auto",
          ALIGN_CLASSNAMES[align],
          navClassName
        )}
        aria-label={ariaLabel}
      >
        {items.map((item) => {
          const buttonClasses = [
            BASE_NAV_BUTTON_CLASSES,
            item.id === activeId ? ACTIVE_NAV_CLASSES : INACTIVE_NAV_CLASSES
          ].join(" ");

          return (
            <button
              key={item.id}
              type="button"
              className={buttonClasses}
              onClick={() => handleSelect(item)}
              aria-current={item.id === activeId ? "page" : undefined}
            >
              {item.title}
            </button>
          );
        })}
      </nav>
    </div>
  );
}

export default StickySectionNav;

