interface SocialChannelsSectionProps {
  socialChannels: Record<string, SocialChannel>;
  onUpdate: (channels: Record<string, SocialChannel>) => void;
  isDirty: boolean;
}

const PREDEFINED_PLATFORMS = [
  { key: 'website', name: 'Website', icon: Globe },
  { key: 'facebook', name: 'Facebook', icon: Facebook },
  { key: 'instagram', name: 'Instagram', icon: Instagram },
  { key: 'twitter', name: 'Twitter/X', icon: Twitter },
  { key: 'linkedin', name: 'LinkedIn', icon: Linkedin },
  { key: 'youtube', name: 'YouTube', icon: Youtube },
  { key: 'tiktok', name: 'TikTok', icon: Music },
];

export function SocialChannelsSection({ socialChannels, onUpdate, isDirty }: SocialChannelsSectionProps) {
  const { t } = useTranslation('forms');
  const [customPlatformName, setCustomPlatformName] = useState('');

  const addPredefinedPlatform = (platformKey: string) => {
    const platform = PREDEFINED_PLATFORMS.find(p => p.key === platformKey);
    if (!platform || socialChannels[platformKey]) return;

    const maxOrder = Math.max(0, ...Object.values(socialChannels).map(c => c.order || 0));
    const newChannel: SocialChannel = {
      name: platform.name,
      url: '',
      platform: platformKey as any,
      enabled: true,
      order: maxOrder + 1,
    };

    onUpdate({
      ...socialChannels,
      [platformKey]: newChannel
    });
  };

  const addCustomPlatform = () => {
    if (!customPlatformName.trim()) return;

    const customKey = `custom_${Date.now()}`;
    const maxOrder = Math.max(0, ...Object.values(socialChannels).map(c => c.order || 0));
    const newChannel: SocialChannel = {
      name: customPlatformName.trim(),
      url: '',
      platform: 'custom',
      customPlatformName: customPlatformName.trim(),
      enabled: true,
      order: maxOrder + 1,
    };

    onUpdate({
      ...socialChannels,
      [customKey]: newChannel
    });
    setCustomPlatformName('');
  };

  const updateChannel = (key: string, updates: Partial<SocialChannel>) => {
    onUpdate({
      ...socialChannels,
      [key]: { ...socialChannels[key], ...updates }
    });
  };

  const removeChannel = (key: string) => {
    const { [key]: removed, ...remaining } = socialChannels;
    onUpdate(remaining);
  };

  const handleDragEnd = (result: DropResult) => {
    if (!result.destination) return;

    const sortedEntries = Object.entries(socialChannels)
      .sort(([, a], [, b]) => (a.order || 0) - (b.order || 0));

    const [reorderedItem] = sortedEntries.splice(result.source.index, 1);
    sortedEntries.splice(result.destination.index, 0, reorderedItem);

    const reorderedChannels = Object.fromEntries(
      sortedEntries.map(([key, channel], index) => [
        key,
        { ...channel, order: index }
      ])
    );
    onUpdate(reorderedChannels);
  };

  const validateUrl = (url: string): boolean => {
    try {
      new URL(url);
      return true;
    } catch {
      return false;
    }
  };

  const getChannelIcon = (channel: SocialChannel) => {
    const platform = PREDEFINED_PLATFORMS.find(p => p.key === channel.platform);
    return platform?.icon || Globe;
  };

  const availablePlatforms = PREDEFINED_PLATFORMS.filter(
    platform => !socialChannels[platform.key]
  );

  const channelEntries = Object.entries(socialChannels)
    .sort(([, a], [, b]) => (a.order || 0) - (b.order || 0));

  return (
    <Card>
      <CardHeader>
        <CardTitle className="flex items-center gap-2">
          <Globe className="h-5 w-5" />
          {t('social_channels.title')}
          {isDirty && <Badge variant="secondary">{t('social_channels.unsaved_changes')}</Badge>}
        </CardTitle>
        <p className="text-sm text-muted-foreground">
          {t('social_channels.description')}
        </p>
      </CardHeader>
      <CardContent>
        <div className="space-y-4">
          {/* Existing Channels with Drag & Drop */}
          {channelEntries.length > 0 && (
            <DragDropContext onDragEnd={handleDragEnd}>
              <Droppable droppableId="social-channels">
                {(provided) => (
                  <div 
                    {...provided.droppableProps} 
                    ref={provided.innerRef}
                    className="space-y-3"
                  >
                    {channelEntries.map(([key, channel], index) => {
                      const Icon = getChannelIcon(channel);
                      const hasValidUrl = channel.url && validateUrl(channel.url);

                      return (
                        <Draggable key={key} draggableId={key} index={index}>
                          {(provided, snapshot) => (
                            <div
                              ref={provided.innerRef}
                              {...provided.draggableProps}
                              className={cn(
                                "flex items-center gap-3 p-3 rounded-lg border bg-card transition-colors",
                                !hasValidUrl && channel.url && "border-destructive/50 bg-destructive/5",
                                snapshot.isDragging && "shadow-lg"
                              )}
                            >
                              <div 
                                {...provided.dragHandleProps}
                                className="cursor-grab active:cursor-grabbing"
                              >
                                <GripVertical className="h-4 w-4 text-muted-foreground" />
                              </div>

                              <Icon className="h-4 w-4 text-muted-foreground flex-shrink-0" />

                              <div className="min-w-0 w-20 flex-shrink-0">
                                <span className="text-sm font-medium truncate block">
                                  {channel.customPlatformName || channel.name}
                                </span>
                              </div>

                              <div className="flex-1">
                                <Input
                                  placeholder={`https://${channel.platform === 'website' ? 'yourwebsite.com' : `${channel.platform}.com/yourprofile`}`}
                                  value={channel.url}
                                  onChange={(e) => updateChannel(key, { url: e.target.value })}
                                  className={cn(
                                    "h-9 text-sm",
                                    !hasValidUrl && channel.url && "border-destructive focus:ring-destructive"
                                  )}
                                 />
                                 {!hasValidUrl && channel.url && (
                                   <p className="text-xs text-destructive mt-1">{t('social_channels.url_invalid')}</p>
                                 )}
                               </div>

                              <AlertDialog>
                                <AlertDialogTrigger asChild>
                                  <Button
                                    variant="ghost"
                                    size="sm"
                                    className="text-destructive hover:text-destructive hover:bg-destructive/10 h-9 w-9 p-0 flex-shrink-0"
                                  >
                                    <Trash2 className="h-4 w-4" />
                                  </Button>
                                </AlertDialogTrigger>
                                 <AlertDialogContent>
                                   <AlertDialogHeader>
                                     <AlertDialogTitle>{t('social_channels.remove_channel_title')}</AlertDialogTitle>
                                     <AlertDialogDescription>
                                       {t('social_channels.remove_channel_description', { name: channel.customPlatformName || channel.name })}
                                     </AlertDialogDescription>
                                   </AlertDialogHeader>
                                   <AlertDialogFooter>
                                     <AlertDialogCancel>{t('common:common.buttons.cancel')}</AlertDialogCancel>
                                     <AlertDialogAction 
                                       onClick={() => removeChannel(key)}
                                       className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
                                     >
                                       {t('social_channels.remove_button')}
                                     </AlertDialogAction>
                                   </AlertDialogFooter>
                                 </AlertDialogContent>
                              </AlertDialog>
                            </div>
                          )}
                        </Draggable>
                      );
                    })}
                    {provided.placeholder}
                  </div>
                )}
              </Droppable>
            </DragDropContext>
          )}

          {/* Empty State */}
          {channelEntries.length === 0 && (
            <div className="text-center py-6 text-muted-foreground">
              <Globe className="h-8 w-8 mx-auto mb-2 opacity-50" />
              <p className="text-sm">{t('social_channels.no_channels')}</p>
              <p className="text-xs">{t('social_channels.add_links_description')}</p>
            </div>
          )}

          {/* Add Platform Buttons */}
          {availablePlatforms.length > 0 && (
            <div>
              <Label className="text-sm font-medium mb-2 block">{t('social_channels.add_platform')}</Label>
              <div className="flex flex-wrap gap-2">
                {availablePlatforms.map((platform) => {
                  const Icon = platform.icon;
                  return (
                    <Button
                      key={platform.key}
                      variant="outline"
                      size="sm"
                      onClick={() => addPredefinedPlatform(platform.key)}
                      className="flex items-center gap-2 h-9"
                    >
                      <Icon className="h-4 w-4" />
                      {platform.name}
                    </Button>
                  );
                })}
              </div>
            </div>
          )}

          {/* Add Custom Platform */}
          <div className="flex gap-2">
            <Input
              placeholder={t('social_channels.custom_platform_placeholder')}
              value={customPlatformName}
              onChange={(e) => setCustomPlatformName(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && addCustomPlatform()}
              className="flex-1 h-9"
            />
            <Button
              variant="outline"
              size="sm"
              onClick={addCustomPlatform}
              disabled={!customPlatformName.trim()}
              className="flex items-center gap-2 h-9"
            >
              <Plus className="h-4 w-4" />
              {t('social_channels.add_custom')}
            </Button>
          </div>

          {/* Helper Text */}
          <div className="text-xs text-muted-foreground space-y-1">
            <p>â€¢ {t('social_channels.help_text.appear_in_emails')}</p>
            <p>â€¢ {t('social_channels.help_text.drag_to_reorder')}</p>
            <p>â€¢ {t('social_channels.help_text.valid_urls_only')}</p>
          </div>
        </div>
      </CardContent>
    </Card>
  );
}

import React, { useState, useEffect } from 'react';
import { AlertTriangle, HardDrive } from 'lucide-react';
import { Progress } from '@/components/ui/progress';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { supabase } from '@/integrations/supabase/client';
import { useOrganization } from '@/contexts/OrganizationContext';

interface StorageUsage {
  total_images: number;
  total_storage_bytes: number;
}

const STORAGE_LIMITS = {
  MAX_IMAGES: 50,
  MAX_STORAGE_BYTES: 50 * 1024 * 1024, // 50MB
};

interface CompactStorageIndicatorProps {
  onManageImages?: () => void;
}

export function CompactStorageIndicator({ onManageImages }: CompactStorageIndicatorProps) {
  const [storageUsage, setStorageUsage] = useState<StorageUsage>({ total_images: 0, total_storage_bytes: 0 });
  const [loading, setLoading] = useState(true);
  const { activeOrganization } = useOrganization();

  const fetchStorageUsage = async () => {
    if (!activeOrganization?.id) return;

    try {
      const { data, error } = await supabase
        .from('template_image_usage')
        .select('total_images, total_storage_bytes')
        .eq('organization_id', activeOrganization.id)
        .single();

      if (error && error.code !== 'PGRST116') {
        console.error('Error fetching storage usage:', error);
        return;
      }

      setStorageUsage(data || { total_images: 0, total_storage_bytes: 0 });
    } catch (error) {
      console.error('Error fetching storage usage:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchStorageUsage();
  }, [activeOrganization?.id]);

  if (loading) {
    return <div className="h-4 w-32 bg-muted animate-pulse rounded" />;
  }

  const imageProgress = (storageUsage.total_images / STORAGE_LIMITS.MAX_IMAGES) * 100;
  const storageProgress = (storageUsage.total_storage_bytes / STORAGE_LIMITS.MAX_STORAGE_BYTES) * 100;
  const isNearLimit = imageProgress > 80 || storageProgress > 80;
  const isAtLimit = imageProgress >= 100 || storageProgress >= 100;

  const formatFileSize = (bytes: number) => {
    if (bytes === 0) return '0 B';
    const k = 1024;
    const sizes = ['B', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return `${parseFloat((bytes / Math.pow(k, i)).toFixed(1))} ${sizes[i]}`;
  };

  return (
    <div className="flex items-center gap-3 p-3 bg-muted/50 rounded-lg">
      <div className="flex items-center gap-2">
        <HardDrive className="h-4 w-4 text-muted-foreground" />
        <div className="space-y-1">
          <div className="flex items-center gap-2">
            <span className="text-xs text-muted-foreground">
              {storageUsage.total_images}/{STORAGE_LIMITS.MAX_IMAGES} images
            </span>
            {isAtLimit && <AlertTriangle className="h-3 w-3 text-destructive" />}
            {isNearLimit && !isAtLimit && <AlertTriangle className="h-3 w-3 text-warning" />}
          </div>
          <div className="flex items-center gap-2">
            <Progress value={Math.min(imageProgress, 100)} className="w-20 h-1" />
            <span className="text-xs text-muted-foreground">
              {formatFileSize(storageUsage.total_storage_bytes)}/50MB
            </span>
          </div>
        </div>
      </div>

      {onManageImages && (
        <Button variant="ghost" size="sm" onClick={onManageImages} className="text-xs">
          Manage
        </Button>
      )}

      {isAtLimit && (
        <Badge variant="destructive" className="text-xs">
          Limit Reached
        </Badge>
      )}
      {isNearLimit && !isAtLimit && (
        <Badge variant="secondary" className="text-xs">
          Near Limit
        </Badge>
      )}
    </div>
  );
}

import React, { useState, useEffect } from 'react';
import { Sheet, SheetContent, SheetDescription, SheetHeader, SheetTitle } from '@/components/ui/sheet';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Input } from '@/components/ui/input';
import { AlertDialog, AlertDialogAction, AlertDialogCancel, AlertDialogContent, AlertDialogDescription, AlertDialogFooter, AlertDialogHeader, AlertDialogTitle, AlertDialogTrigger } from '@/components/ui/alert-dialog';
import { Copy, Trash2, Edit3, Check, X } from 'lucide-react';
import { useToast } from '@/hooks/use-toast';
import { supabase } from '@/integrations/supabase/client';
import { useOrganization } from '@/contexts/OrganizationContext';
import { CompactStorageIndicator } from './CompactStorageIndicator';
import { useTranslation } from 'react-i18next';
import { useMessagesTranslation } from '@/hooks/useTypedTranslation';

interface TemplateAsset {
  id: string;
  file_name: string;
  file_path: string;
  file_size: number;
  content_type: string;
  alt_text?: string;
  created_at: string;
}

interface ImageLibrarySheetProps {
  open: boolean;
  onOpenChange: (open: boolean) => void;
  onImageSelect?: (imageUrl: string, altText?: string) => void;
  templateId?: string;
}

export function ImageLibrarySheet({ open, onOpenChange, onImageSelect, templateId }: ImageLibrarySheetProps) {
  const [assets, setAssets] = useState<TemplateAsset[]>([]);
  const [loading, setLoading] = useState(true);
  const [editingAltText, setEditingAltText] = useState<string | null>(null);
  const [tempAltText, setTempAltText] = useState('');
  const { toast } = useToast();
  const { t } = useTranslation();
  const { t: tMessages } = useMessagesTranslation();
  const { activeOrganization } = useOrganization();

  const loadAssets = async () => {
    if (!activeOrganization?.id) return;

    try {
      setLoading(true);
      const { data, error } = await supabase
        .from('template_assets')
        .select('*')
        .eq('organization_id', activeOrganization.id)
        .order('created_at', { ascending: false });

      if (error) throw error;
      setAssets(data || []);
    } catch (error) {
      console.error('Error loading assets:', error);
      toast({
        title: 'Error',
        description: 'Failed to load images',
        variant: 'destructive',
      });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (open) {
      loadAssets();
    }
  }, [open, activeOrganization?.id]);

  const getImageUrl = (filePath: string) => {
    const { data: { publicUrl } } = supabase.storage
      .from('template-images')
      .getPublicUrl(filePath);
    return publicUrl;
  };

  const copyImageUrl = async (filePath: string) => {
    const imageUrl = getImageUrl(filePath);
    try {
      await navigator.clipboard.writeText(imageUrl);
      toast({
        title: 'Success',
        description: 'Image URL copied to clipboard',
      });
    } catch (error) {
      toast({
        title: 'Error',
        description: 'Failed to copy URL',
        variant: 'destructive',
      });
    }
  };

  const deleteAsset = async (asset: TemplateAsset) => {
    try {
      // Delete from storage
      const { error: storageError } = await supabase.storage
        .from('template-images')
        .remove([asset.file_path]);

      if (storageError) throw storageError;

      // Delete from database
      const { error: dbError } = await supabase
        .from('template_assets')
        .delete()
        .eq('id', asset.id);

      if (dbError) throw dbError;

      toast({
        title: 'Success',
        description: 'Image deleted successfully',
      });

      // Refresh the list
      loadAssets();
    } catch (error) {
      console.error('Error deleting asset:', error);
      toast({
        title: 'Error',
        description: 'Failed to delete image',
        variant: 'destructive',
      });
    }
  };

  const updateAltText = async (assetId: string, newAltText: string) => {
    try {
      const { error } = await supabase
        .from('template_assets')
        .update({ alt_text: newAltText })
        .eq('id', assetId);

      if (error) throw error;

      setAssets(prev => prev.map(asset => 
        asset.id === assetId ? { ...asset, alt_text: newAltText } : asset
      ));

      toast({
        title: 'Success',
        description: 'Alt text updated',
      });
    } catch (error) {
      console.error('Error updating alt text:', error);
      toast({
        title: 'Error',
        description: 'Failed to update alt text',
        variant: 'destructive',
      });
    }
  };

  const handleEditAltText = (assetId: string, currentAltText: string) => {
    setEditingAltText(assetId);
    setTempAltText(currentAltText || '');
  };

  const handleSaveAltText = (assetId: string) => {
    updateAltText(assetId, tempAltText);
    setEditingAltText(null);
    setTempAltText('');
  };

  const handleCancelEditAltText = () => {
    setEditingAltText(null);
    setTempAltText('');
  };

  const formatFileSize = (bytes: number) => {
    if (bytes === 0) return '0 B';
    const k = 1024;
    const sizes = ['B', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return `${parseFloat((bytes / Math.pow(k, i)).toFixed(1))} ${sizes[i]}`;
  };

  return (
    <Sheet open={open} onOpenChange={onOpenChange}>
      <SheetContent className="w-[600px] sm:w-[700px] overflow-hidden flex flex-col">
        <SheetHeader>
          <SheetTitle>Image Library</SheetTitle>
          <SheetDescription>
            Manage your uploaded images. Click on an image to select it for your template.
          </SheetDescription>
        </SheetHeader>

        <div className="mt-4">
          <CompactStorageIndicator />
        </div>

        <div className="flex-1 overflow-y-auto mt-6">
          {loading ? (
            <div className="grid grid-cols-2 gap-4">
              {Array.from({ length: 6 }).map((_, i) => (
                <div key={i} className="aspect-video bg-muted animate-pulse rounded-lg" />
              ))}
            </div>
          ) : assets.length === 0 ? (
            <div className="text-center py-12">
              <p className="text-muted-foreground">No images uploaded yet</p>
              <p className="text-sm text-muted-foreground mt-1">
                Upload an image using the image block to see it here
              </p>
            </div>
          ) : (
            <div className="grid grid-cols-2 gap-4">
              {assets.map((asset) => (
                <div key={asset.id} className="group relative bg-card border rounded-lg overflow-hidden">
                  <div className="aspect-video relative">
                    <img
                      src={getImageUrl(asset.file_path)}
                      alt={asset.alt_text || asset.file_name}
                      className="w-full h-full object-cover cursor-pointer hover:scale-105 transition-transform"
                      onClick={() => {
                        if (onImageSelect) {
                          onImageSelect(getImageUrl(asset.file_path), asset.alt_text);
                          onOpenChange(false);
                        }
                      }}
                    />
                    <div className="absolute inset-0 bg-black/0 group-hover:bg-black/20 transition-colors" />
                    <div className="absolute top-2 right-2 opacity-0 group-hover:opacity-100 transition-opacity flex gap-1">
                      <Button
                        size="sm"
                        variant="secondary"
                        onClick={() => copyImageUrl(asset.file_path)}
                        className="h-8 w-8 p-0"
                        aria-label="Copy image URL"
                      >
                        <Copy className="h-3 w-3" />
                      </Button>
                      <AlertDialog>
                        <AlertDialogTrigger asChild>
                          <Button
                            size="sm"
                            variant="destructive"
                            className="h-8 w-8 p-0"
                            aria-label="Delete image"
                          >
                            <Trash2 className="h-3 w-3" />
                          </Button>
                        </AlertDialogTrigger>
                        <AlertDialogContent>
                          <AlertDialogHeader>
                            <AlertDialogTitle>Delete Image</AlertDialogTitle>
                            <AlertDialogDescription>
                              {tMessages('confirm.deleteWithName', { name: asset.file_name })} {tMessages('confirm.cannotUndo')}
                            </AlertDialogDescription>
                          </AlertDialogHeader>
                          <AlertDialogFooter>
                            <AlertDialogCancel>Cancel</AlertDialogCancel>
                            <AlertDialogAction
                              onClick={() => deleteAsset(asset)}
                              className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
                            >
                              Delete
                            </AlertDialogAction>
                          </AlertDialogFooter>
                        </AlertDialogContent>
                      </AlertDialog>
                    </div>
                  </div>

                  <div className="p-3 space-y-2">
                    <div className="flex items-center justify-between">
                      <p className="text-sm font-medium truncate">{asset.file_name}</p>
                      <Badge variant="secondary" className="text-xs">
                        {formatFileSize(asset.file_size)}
                      </Badge>
                    </div>

                    <div className="flex items-center gap-2">
                      {editingAltText === asset.id ? (
                        <div className="flex items-center gap-1 flex-1">
                          <Input
                            value={tempAltText}
                            onChange={(e) => setTempAltText(e.target.value)}
                            placeholder="Alt text"
                            className="h-7 text-xs"
                          />
                          <Button
                            size="sm"
                            variant="ghost"
                            onClick={() => handleSaveAltText(asset.id)}
                            className="h-7 w-7 p-0"
                            aria-label="Confirm alt text"
                          >
                            <Check className="h-3 w-3" />
                          </Button>
                          <Button
                            size="sm"
                            variant="ghost"
                            onClick={handleCancelEditAltText}
                            className="h-7 w-7 p-0"
                            aria-label="Cancel alt text edit"
                          >
                            <X className="h-3 w-3" />
                          </Button>
                        </div>
                      ) : (
                        <div className="flex items-center gap-1 flex-1">
                          <p className="text-xs text-muted-foreground flex-1 truncate">
                            Alt: {asset.alt_text || 'No alt text'}
                          </p>
                          <Button
                            size="sm"
                            variant="ghost"
                            onClick={() => handleEditAltText(asset.id, asset.alt_text || '')}
                            className="h-7 w-7 p-0"
                            aria-label="Edit alt text"
                          >
                            <Edit3 className="h-3 w-3" />
                          </Button>
                        </div>
                      )}
                    </div>

                    {onImageSelect && (
                      <Button
                        size="sm"
                        variant="outline"
                        className="w-full h-7 text-xs"
                        onClick={() => {
                          onImageSelect(getImageUrl(asset.file_path), asset.alt_text);
                          onOpenChange(false);
                        }}
                      >
                        Insert
                      </Button>
                    )}
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </SheetContent>
    </Sheet>
  );
}

import React, { useState, useEffect } from 'react';
import { Progress } from '@/components/ui/progress';
import { Badge } from '@/components/ui/badge';
import { AlertTriangle, Image as ImageIcon, Trash2 } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { useToast } from '@/hooks/use-toast';
import { supabase } from '@/integrations/supabase/client';
import { useOrganization } from '@/contexts/OrganizationContext';
import { useAuth } from '@/contexts/AuthContext';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from '@/components/ui/dialog';

interface StorageUsage {
  total_images: number;
  total_storage_bytes: number;
}

interface TemplateAsset {
  id: string;
  file_name: string;
  file_path: string;
  file_size: number;
  created_at: string;
}

const STORAGE_LIMITS = {
  MAX_IMAGES: 20,
  MAX_STORAGE_BYTES: 50 * 1024 * 1024, // 50MB
};

export function StorageQuotaDisplay() {
  const [storageUsage, setStorageUsage] = useState<StorageUsage | null>(null);
  const [assets, setAssets] = useState<TemplateAsset[]>([]);
  const [loading, setLoading] = useState(true);
  const [isImageLibraryOpen, setIsImageLibraryOpen] = useState(false);
  const { activeOrganization } = useOrganization();
  const { user } = useAuth();
  const { toast } = useToast();

  const fetchStorageUsage = async () => {
    if (!activeOrganization?.id) return;

    try {
      const { data: usage } = await supabase
        .from('template_image_usage')
        .select('total_images, total_storage_bytes')
        .eq('organization_id', activeOrganization.id)
        .single();

      setStorageUsage(usage || { total_images: 0, total_storage_bytes: 0 });
    } catch (error) {
      console.error('Error fetching storage usage:', error);
      setStorageUsage({ total_images: 0, total_storage_bytes: 0 });
    }
  };

  const fetchAssets = async () => {
    if (!activeOrganization?.id) return;

    try {
      const { data: assetList } = await supabase
        .from('template_assets')
        .select('id, file_name, file_path, file_size, created_at')
        .eq('organization_id', activeOrganization.id)
        .order('created_at', { ascending: false });

      setAssets(assetList || []);
    } catch (error) {
      console.error('Error fetching assets:', error);
    } finally {
      setLoading(false);
    }
  };

  const deleteAsset = async (asset: TemplateAsset) => {
    try {
      // Delete from storage
      await supabase.storage
        .from('template-images')
        .remove([asset.file_path]);

      // Delete from database (this will trigger the usage update)
      await supabase
        .from('template_assets')
        .delete()
        .eq('id', asset.id);

      // Refresh data
      await fetchStorageUsage();
      await fetchAssets();

      toast({
        title: 'Success',
        description: 'Image deleted successfully',
      });
    } catch (error) {
      console.error('Error deleting asset:', error);
      toast({
        title: 'Error',
        description: 'Failed to delete image',
        variant: 'destructive',
      });
    }
  };

  useEffect(() => {
    if (activeOrganization?.id) {
      fetchStorageUsage();
      fetchAssets();
    }
  }, [activeOrganization?.id]);

  if (loading || !storageUsage) {
    return (
      <div className="p-4 bg-muted rounded-lg">
        <div className="text-sm text-muted-foreground">Loading storage info...</div>
      </div>
    );
  }

  const storagePercentage = (storageUsage.total_storage_bytes / STORAGE_LIMITS.MAX_STORAGE_BYTES) * 100;
  const imagesPercentage = (storageUsage.total_images / STORAGE_LIMITS.MAX_IMAGES) * 100;
  const storageInMB = storageUsage.total_storage_bytes / (1024 * 1024);

  const isStorageNearLimit = storagePercentage > 80;
  const isImagesNearLimit = imagesPercentage > 80;
  const isStorageAtLimit = storagePercentage >= 100;
  const isImagesAtLimit = imagesPercentage >= 100;

  return (
    <div className="space-y-4">
      <div className="p-4 bg-muted rounded-lg">
        <div className="flex items-center justify-between mb-3">
          <h3 className="text-sm font-medium">Storage Usage</h3>
          <Dialog open={isImageLibraryOpen} onOpenChange={setIsImageLibraryOpen}>
            <DialogTrigger asChild>
              <Button variant="outline" size="sm">
                <ImageIcon className="w-4 h-4 mr-2" />
                Manage Images
              </Button>
            </DialogTrigger>
            <DialogContent className="max-w-2xl max-h-[80vh] overflow-hidden">
              <DialogHeader>
                <DialogTitle>Image Library</DialogTitle>
                <DialogDescription>
                  Manage your uploaded template images
                </DialogDescription>
              </DialogHeader>
              <div className="overflow-y-auto max-h-[60vh] space-y-3">
                {assets.length === 0 ? (
                  <div className="text-center py-8 text-muted-foreground">
                    No images uploaded yet
                  </div>
                ) : (
                  assets.map((asset) => (
                    <div key={asset.id} className="flex items-center justify-between p-3 border rounded-lg">
                      <div className="flex-1">
                        <div className="font-medium text-sm">{asset.file_name}</div>
                        <div className="text-xs text-muted-foreground">
                          {(asset.file_size / 1024).toFixed(1)} KB â€¢ {new Date(asset.created_at).toLocaleDateString()}
                        </div>
                      </div>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => deleteAsset(asset)}
                        className="text-destructive hover:text-destructive"
                      >
                        <Trash2 className="w-4 h-4" />
                      </Button>
                    </div>
                  ))
                )}
              </div>
            </DialogContent>
          </Dialog>
        </div>

        <div className="space-y-3">
          <div>
            <div className="flex items-center justify-between text-xs mb-1">
              <span>Images ({storageUsage.total_images}/{STORAGE_LIMITS.MAX_IMAGES})</span>
              {(isImagesNearLimit || isImagesAtLimit) && (
                <Badge variant={isImagesAtLimit ? "destructive" : "secondary"} className="text-xs">
                  {isImagesAtLimit ? <AlertTriangle className="w-3 h-3 mr-1" /> : null}
                  {isImagesAtLimit ? 'Limit Reached' : 'Near Limit'}
                </Badge>
              )}
            </div>
            <Progress value={imagesPercentage} className="h-2" />
          </div>

          <div>
            <div className="flex items-center justify-between text-xs mb-1">
              <span>Storage ({storageInMB.toFixed(1)}/50 MB)</span>
              {(isStorageNearLimit || isStorageAtLimit) && (
                <Badge variant={isStorageAtLimit ? "destructive" : "secondary"} className="text-xs">
                  {isStorageAtLimit ? <AlertTriangle className="w-3 h-3 mr-1" /> : null}
                  {isStorageAtLimit ? 'Limit Reached' : 'Near Limit'}
                </Badge>
              )}
            </div>
            <Progress value={storagePercentage} className="h-2" />
          </div>
        </div>

        {(isStorageAtLimit || isImagesAtLimit) && (
          <div className="mt-3 p-2 bg-destructive/10 border border-destructive/20 rounded text-xs text-destructive">
            <div className="flex items-center gap-1">
              <AlertTriangle className="w-3 h-3" />
              Upload limit reached. Delete some images to free up space.
            </div>
          </div>
        )}
      </div>
    </div>
  );
}

export function checkStorageLimits(currentUsage: StorageUsage, newFileSize: number): { 
  canUpload: boolean; 
  reason?: string; 
} {
  if (currentUsage.total_images >= STORAGE_LIMITS.MAX_IMAGES) {
    return { 
      canUpload: false, 
      reason: `Maximum number of images (${STORAGE_LIMITS.MAX_IMAGES}) reached. Please delete some images first.` 
    };
  }

  if (currentUsage.total_storage_bytes + newFileSize > STORAGE_LIMITS.MAX_STORAGE_BYTES) {
    const remainingMB = (STORAGE_LIMITS.MAX_STORAGE_BYTES - currentUsage.total_storage_bytes) / (1024 * 1024);
    return { 
      canUpload: false, 
      reason: `Not enough storage space. Only ${remainingMB.toFixed(1)}MB remaining.` 
    };
  }

  return { canUpload: true };
}

import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Popover, PopoverContent, PopoverTrigger } from "@/components/ui/popover";
import { Command, CommandEmpty, CommandGroup, CommandInput, CommandItem, CommandList } from "@/components/ui/command";
import { Plus, Check } from "lucide-react";
import { useTemplateVariables } from "@/hooks/useTemplateVariables";

interface VariablePickerProps {
  onVariableSelect: (variable: string) => void;
  trigger?: React.ReactNode;
}

export function VariablePicker({ onVariableSelect, trigger }: VariablePickerProps) {
  const [open, setOpen] = useState(false);
  const { variables, loading } = useTemplateVariables();

  const handleSelect = (variableKey: string) => {
    onVariableSelect(`{${variableKey}}`);
    setOpen(false);
  };

  const groupedVariables = variables.reduce((acc, variable) => {
    if (!acc[variable.category]) {
      acc[variable.category] = [];
    }
    acc[variable.category].push(variable);
    return acc;
  }, {} as Record<string, any[]>);

  return (
    <Popover open={open} onOpenChange={setOpen}>
      <PopoverTrigger asChild>
        {trigger || (
          <Button variant="outline" size="sm">
            <Plus className="h-3 w-3" />
            Variable
          </Button>
        )}
      </PopoverTrigger>
      <PopoverContent className="w-80 p-0" align="start">
        <Command>
          <CommandInput placeholder="Search variables..." />
          <CommandList>
            <CommandEmpty>No variables found.</CommandEmpty>
            {loading ? (
              <div className="p-4 text-center text-muted-foreground">
                Loading variables...
              </div>
            ) : (
              Object.entries(groupedVariables).map(([category, vars]) => (
                <CommandGroup key={category} heading={category}>
                  {vars.map((variable) => (
                    <CommandItem
                      key={variable.key}
                      onSelect={() => handleSelect(variable.key)}
                      className="cursor-pointer"
                    >
                      <Check className="mr-2 h-4 w-4 opacity-0" />
                      <div>
                        <div className="font-medium">{variable.label}</div>
                        <div className="text-xs text-muted-foreground">{`{${variable.key}}`}</div>
                      </div>
                    </CommandItem>
                  ))}
                </CommandGroup>
              ))
            )}
          </CommandList>
        </Command>
      </PopoverContent>
    </Popover>
  );
}

