import React, { createContext, useContext, useState, useEffect, ReactNode } from 'react';
import { User, Session } from '@supabase/supabase-js';
import { supabase } from '@/integrations/supabase/client';

type UserRole = 'admin' | 'support' | 'user';

interface AuthContextType {
  user: User | null;
  session: Session | null;
  userRoles: UserRole[];
  loading: boolean;
  signOut: () => Promise<void>;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export function AuthProvider({ children }: { children: ReactNode }) {
  const [user, setUser] = useState<User | null>(null);
  const [session, setSession] = useState<Session | null>(null);
  const [userRoles, setUserRoles] = useState<UserRole[]>([]);
  const [loading, setLoading] = useState(true);
  const [initialized, setInitialized] = useState(false);

  const fetchUserRoles = async (userId: string) => {
    try {
      const { data: roles, error } = await supabase
        .rpc('get_user_roles', { user_uuid: userId });

      if (error) {
        console.error('Error fetching user roles:', error);
        return [];
      }

      return roles || [];
    } catch (error) {
      console.error('Error fetching user roles:', error);
      return [];
    }
  };

  const signOut = async () => {
    try {
      // Clear everything first
      localStorage.clear();
      setUser(null);
      setSession(null);
      setUserRoles([]);

      // Then sign out
      await supabase.auth.signOut({ scope: 'global' });

      // Use window navigation for auth redirect
      window.location.href = '/auth';
    } catch (error) {
      console.error('Sign out error:', error);
      // Force reload anyway
      window.location.href = '/auth';
    }
  };

  useEffect(() => {
    console.log('AuthProvider initializing...');

    let isMounted = true;

    const initializeAuth = async () => {
      try {
        // Get current session
        const { data: { session }, error } = await supabase.auth.getSession();

        if (error) {
          console.error('Session error:', error);
        }

        if (isMounted && !initialized) {
          console.log('Setting initial auth state:', session?.user?.id || 'no user');
          setSession(session);
          setUser(session?.user ?? null);

          // Fetch user roles if user exists
          if (session?.user) {
            setTimeout(async () => {
              const roles = await fetchUserRoles(session.user.id);
              if (isMounted) {
                setUserRoles(roles);
              }
            }, 0);
          }

          setLoading(false);
          setInitialized(true);
        }
      } catch (error) {
        console.error('Auth initialization error:', error);
        if (isMounted) {
          setLoading(false);
          setInitialized(true);
        }
      }
    };

    // Set up auth state listener
    const { data: { subscription } } = supabase.auth.onAuthStateChange(
      async (event, session) => {
        console.log('Auth state change:', event, session?.user?.id || 'no user');

        if (isMounted) {
          setSession(session);
          setUser(session?.user ?? null);

          // Fetch user roles if user exists
          if (session?.user) {
            setTimeout(async () => {
              const roles = await fetchUserRoles(session.user.id);
              if (isMounted) {
                setUserRoles(roles);
              }
            }, 0);
          } else {
            setUserRoles([]);
          }

          // Only set loading to false if we haven't initialized yet
          if (!initialized) {
            setLoading(false);
            setInitialized(true);
          }
        }
      }
    );

    // Initialize auth state
    initializeAuth();

    return () => {
      console.log('AuthProvider cleanup...');
      isMounted = false;
      subscription.unsubscribe();
    };
  }, [initialized]);

  return (
    <AuthContext.Provider value={{ user, session, userRoles, loading, signOut }}>
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
}

import React, { createContext, useCallback, useContext, useEffect, useMemo, useRef, useState } from 'react';

type RetryFn = () => Promise<void> | void;

interface ConnectivityContextValue {
  isOffline: boolean;
  isRetrying: boolean;
  reportNetworkError: (error?: unknown) => void;
  reportRecovery: () => void;
  registerRetry: (key: string, fn: RetryFn) => () => void;
  runRetryAll: () => Promise<void>;
}

const ConnectivityContext = createContext<ConnectivityContextValue | undefined>(undefined);

export const ConnectivityProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  // Default to online; rely on real network errors or offline events to flip state.
  // navigator.onLine can be unreliable on some platforms at startup.
  const [isOffline, setIsOffline] = useState<boolean>(false);
  const [isRetrying, setIsRetrying] = useState(false);
  const retryCallbacksRef = useRef<Map<string, RetryFn>>(new Map());

  // Window online/offline listeners keep our state accurate even if no requests are made
  useEffect(() => {
    const handleOnline = () => setIsOffline(false);
    const handleOffline = () => setIsOffline(true);

    window.addEventListener('online', handleOnline);
    window.addEventListener('offline', handleOffline);
    return () => {
      window.removeEventListener('online', handleOnline);
      window.removeEventListener('offline', handleOffline);
    };
  }, []);

  const reportNetworkError = useCallback((_error?: unknown) => {
    setIsOffline(true);
  }, []);

  const reportRecovery = useCallback(() => {
    if (typeof navigator === 'undefined' || navigator.onLine) {
      setIsOffline(false);
    }
  }, []);

  const registerRetry = useCallback((key: string, fn: RetryFn) => {
    retryCallbacksRef.current.set(key, fn);
    return () => {
      retryCallbacksRef.current.delete(key);
    };
  }, []);

  const runRetryAll = useCallback(async () => {
    if (isRetrying) return;
    setIsRetrying(true);
    try {
      const fns = Array.from(retryCallbacksRef.current.values());
      for (const fn of fns) {
        try {
          await fn();
        } catch (err) {
          // Intentionally swallow individual errors here
          // Offline banner will remain if we are still offline
          // and pages can surface context-specific errors/toasts.
          // eslint-disable-next-line no-console
          console.error('Retry action failed', err);
        }
      }
      // If device is online and retries did not throw hard, clear offline
      if (typeof navigator === 'undefined' || navigator.onLine) {
        setIsOffline(false);
      }
    } finally {
      setIsRetrying(false);
    }
  }, [isRetrying]);

  const value = useMemo<ConnectivityContextValue>(() => ({
    isOffline,
    isRetrying,
    reportNetworkError,
    reportRecovery,
    registerRetry,
    runRetryAll,
  }), [isOffline, isRetrying, registerRetry, reportNetworkError, reportRecovery, runRetryAll]);

  return (
    <ConnectivityContext.Provider value={value}>
      {children}
    </ConnectivityContext.Provider>
  );
};

export const useConnectivity = (): ConnectivityContextValue => {
  const ctx = useContext(ConnectivityContext);
  if (!ctx) throw new Error('useConnectivity must be used within a ConnectivityProvider');
  return ctx;
};

import React, { createContext, useContext, useState, useEffect, ReactNode } from 'react';
import { useTranslation } from 'react-i18next';
import { supabase } from '@/integrations/supabase/client';
import { useAuth } from '@/contexts/AuthContext';
import { toast } from '@/hooks/use-toast';

interface LanguageContextType {
  currentLanguage: string;
  availableLanguages: Array<{
    code: string;
    name: string;
    native_name: string;
  }>;
  isLoading: boolean;
  changeLanguage: (languageCode: string) => Promise<void>;
}

const LanguageContext = createContext<LanguageContextType | undefined>(undefined);

interface LanguageProviderProps {
  children: ReactNode;
}

export function LanguageProvider({ children }: LanguageProviderProps) {
  const { i18n } = useTranslation();
  const { user } = useAuth();
  const [currentLanguage, setCurrentLanguage] = useState(i18n.language || 'en');
  const [availableLanguages, setAvailableLanguages] = useState<Array<{
    code: string;
    name: string;
    native_name: string;
  }>>([]);
  const [isLoading, setIsLoading] = useState(true);

  // Load available languages
  useEffect(() => {
    loadAvailableLanguages();
  }, []);

  // Load user's language preference
  useEffect(() => {
    if (user) {
      loadUserLanguagePreference();
    } else {
      // For non-authenticated users, use browser detection or default
      setIsLoading(false);
    }
  }, [user]);

  const loadAvailableLanguages = async () => {
    try {
      const { data: languages } = await supabase
        .from('languages')
        .select('code, name, native_name')
        .eq('is_active', true)
        .order('sort_order');

      if (languages) {
        setAvailableLanguages(languages);
      }
    } catch (error) {
      console.error('Error loading languages:', error);
    }
  };

  const loadUserLanguagePreference = async () => {
    if (!user) return;

    try {
      const { data: preference } = await supabase
        .from('user_language_preferences')
        .select('language_code')
        .eq('user_id', user.id)
        .single();

      if (preference?.language_code) {
        await changeLanguageInternal(preference.language_code);
      }
    } catch (error) {
      // No preference found, use browser detection or default
      console.log('No user language preference found');
    } finally {
      setIsLoading(false);
    }
  };

  const changeLanguageInternal = async (languageCode: string) => {
    try {
      await i18n.changeLanguage(languageCode);
      setCurrentLanguage(languageCode);
    } catch (error) {
      console.error('Error changing language:', error);
    }
  };

  const changeLanguage = async (languageCode: string) => {
    try {
      setIsLoading(true);

      // Change the i18n language
      await changeLanguageInternal(languageCode);

      // Save user preference if authenticated
      if (user) {
        const { error } = await supabase
          .from('user_language_preferences')
          .upsert({
            user_id: user.id,
            language_code: languageCode,
          }, {
            onConflict: 'user_id'
          });

        if (error) {
          console.error('Error saving language preference:', error);
          toast({
            title: "Warning",
            description: "Language changed but preference not saved",
            variant: "destructive",
          });
        }
      }

      toast({
        title: "Language Changed",
        description: `Interface language changed to ${languageCode.toUpperCase()}`,
      });
    } catch (error) {
      console.error('Error changing language:', error);
      toast({
        title: "Error",
        description: "Failed to change language",
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  const value: LanguageContextType = {
    currentLanguage,
    availableLanguages,
    isLoading,
    changeLanguage,
  };

  return (
    <LanguageContext.Provider value={value}>
      {children}
    </LanguageContext.Provider>
  );
}

export function useLanguage() {
  const context = useContext(LanguageContext);
  if (context === undefined) {
    throw new Error('useLanguage must be used within a LanguageProvider');
  }
  return context;
}

import React, { createContext, useContext, useMemo } from "react";
import { useUserPreferences } from "@/hooks/useUserPreferences";
import { ONBOARDING_STEPS, TOTAL_STEPS, OnboardingStage } from "@/constants/onboarding";

interface OnboardingContextValue {
  // State
  stage: OnboardingStage;
  currentStep: number;
  loading: boolean;

  // Computed values (memoized for performance)
  shouldShowWelcomeModal: boolean;
  isInGuidedSetup: boolean;
  isOnboardingComplete: boolean;
  shouldLockNavigation: boolean;

  // Step information
  currentStepInfo: (typeof ONBOARDING_STEPS)[number] | null;
  nextStepInfo: (typeof ONBOARDING_STEPS)[number] | null;
  completedSteps: (typeof ONBOARDING_STEPS)[number][];
  isAllStepsComplete: boolean;
  totalSteps: number;

  // Actions
  startGuidedSetup: () => Promise<void>;
  completeCurrentStep: () => Promise<void>;
  completeMultipleSteps: (numberOfSteps: number) => Promise<void>;
  completeOnboarding: () => Promise<void>;
  skipOnboarding: () => Promise<void>;
  resetOnboarding: () => Promise<void>;
}

const OnboardingContext = createContext<OnboardingContextValue | null>(null);

export function OnboardingProvider({ children }: { children: React.ReactNode }) {
  const { data: preferences, isLoading, updatePreferences } = useUserPreferences();

  // Memoized computed values to prevent excessive recalculations
  const computedValues = useMemo(() => {
    if (!preferences || isLoading) {
      return {
        shouldShowWelcomeModal: false,
        isInGuidedSetup: false,
        isOnboardingComplete: false,
        shouldLockNavigation: false,
        currentStepInfo: null,
        nextStepInfo: null,
        completedSteps: [],
        isAllStepsComplete: false
      };
    }

    const { onboardingStage, currentOnboardingStep, welcomeModalShown } = preferences;

    // BULLETPROOF: Modal shows ONLY if user has NEVER seen it
    const shouldShowWelcomeModal = onboardingStage === 'not_started' && !welcomeModalShown;

    const isInGuidedSetup = onboardingStage === 'in_progress';

    const isOnboardingComplete = onboardingStage === 'completed' || onboardingStage === 'skipped';

    const shouldLockNavigation = onboardingStage === 'in_progress';

    // Step information
    const currentStepInfo = isInGuidedSetup && currentOnboardingStep >= 1 && currentOnboardingStep <= TOTAL_STEPS
      ? ONBOARDING_STEPS[currentOnboardingStep - 1]
      : null;

    const nextStepInfo = isInGuidedSetup && currentOnboardingStep < TOTAL_STEPS
      ? ONBOARDING_STEPS[currentOnboardingStep]
      : null;

    const completedSteps = isInGuidedSetup 
      ? ONBOARDING_STEPS.slice(0, currentOnboardingStep - 1)
      : [];

    const isAllStepsComplete = isInGuidedSetup && currentOnboardingStep > TOTAL_STEPS;

    return {
      shouldShowWelcomeModal,
      isInGuidedSetup,
      isOnboardingComplete,
      shouldLockNavigation,
      currentStepInfo,
      nextStepInfo,
      completedSteps,
      isAllStepsComplete
    };
  }, [preferences, isLoading]);

  // Action functions
  const startGuidedSetup = async () => {
    if (!preferences) return;

    // Starting guided setup - modal disabled for improved performance

    await updatePreferences({
      onboardingStage: 'in_progress',
      currentOnboardingStep: 1,
      welcomeModalShown: true // PERMANENT - NEVER reset
    });
  };

  const completeCurrentStep = async () => {
    if (!preferences || preferences.onboardingStage !== 'in_progress') {
      console.warn("ðŸš« completeCurrentStep: Cannot complete - not in progress");
      return;
    }

    const nextStep = preferences.currentOnboardingStep + 1;
    // Completing current step and advancing to next

    // Bulletproof: Prevent completing beyond total steps
    if (preferences.currentOnboardingStep >= TOTAL_STEPS) {
      console.warn("ðŸš« completeCurrentStep: Attempted to complete step beyond total steps");
      return;
    }

    await updatePreferences({
      currentOnboardingStep: nextStep
    });
  };

  const completeMultipleSteps = async (numberOfSteps: number) => {
    if (!preferences || preferences.onboardingStage !== 'in_progress') {
      console.warn("ðŸš« completeMultipleSteps: Cannot complete - not in progress");
      return;
    }

    const targetStep = preferences.currentOnboardingStep + numberOfSteps;
    // Completing multiple steps in batch

    // Prevent completing beyond total steps
    if (preferences.currentOnboardingStep >= TOTAL_STEPS) {
      console.warn("ðŸš« completeMultipleSteps: Already at or beyond total steps");
      return;
    }

    const finalStep = Math.min(targetStep, TOTAL_STEPS + 1);

    await updatePreferences({
      currentOnboardingStep: finalStep
    });
  };

  const completeOnboarding = async () => {
    if (!preferences) return;

    // Completing onboarding process

    await updatePreferences({
      onboardingStage: 'completed',
      currentOnboardingStep: TOTAL_STEPS + 1
    });
  };

  const skipOnboarding = async () => {
    if (!preferences) return;

    await updatePreferences({
      onboardingStage: 'skipped'
    });
  };

  const resetOnboarding = async () => {
    if (!preferences) return;

    // Resetting onboarding to guided mode

    await updatePreferences({
      onboardingStage: 'in_progress',
      currentOnboardingStep: 1,
      welcomeModalShown: true // Keep modal permanently disabled
    });
  };

  const contextValue: OnboardingContextValue = {
    // State
    stage: preferences?.onboardingStage || 'not_started',
    currentStep: preferences?.currentOnboardingStep || 1,
    loading: isLoading,

    // Computed values (memoized)
    ...computedValues,
    totalSteps: TOTAL_STEPS,

    // Actions
    startGuidedSetup,
    completeCurrentStep,
    completeMultipleSteps,
    completeOnboarding,
    skipOnboarding,
    resetOnboarding
  };

  return (
    <OnboardingContext.Provider value={contextValue}>
      {children}
    </OnboardingContext.Provider>
  );
}

export function useOnboarding(): OnboardingContextValue {
  const context = useContext(OnboardingContext);
  if (!context) {
    throw new Error("useOnboarding must be used within an OnboardingProvider");
  }
  return context;
}

import React, { createContext, useContext, useState, useEffect, ReactNode, useCallback } from 'react';
import { supabase } from '@/integrations/supabase/client';
import { useToast } from '@/hooks/use-toast';
import { useQueryClient } from '@tanstack/react-query';
import { detectBrowserTimezone } from '@/lib/dateFormatUtils';
import {
  fetchOrganizationSettingsWithCache,
  ORGANIZATION_SETTINGS_CACHE_TTL,
} from '@/lib/organizationSettingsCache';

interface Organization {
  id: string;
  name: string;
  owner_id: string;
}

interface OrganizationContextType {
  activeOrganizationId: string | null;
  activeOrganization: Organization | null;
  loading: boolean;
  refreshOrganization: () => Promise<void>;
  setActiveOrganization: (orgId: string) => Promise<void>;
}

const OrganizationContext = createContext<OrganizationContextType | undefined>(undefined);

export function useOrganization() {
  const context = useContext(OrganizationContext);
  if (context === undefined) {
    throw new Error('useOrganization must be used within an OrganizationProvider');
  }
  return context;
}

interface OrganizationProviderProps {
  children: ReactNode;
}

export function OrganizationProvider({ children }: OrganizationProviderProps) {
  const [activeOrganizationId, setActiveOrganizationId] = useState<string | null>(null);
  const [activeOrganization, setActiveOrganization] = useState<Organization | null>(null);
  const [loading, setLoading] = useState(true);
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const fetchActiveOrganization = async () => {
    try {
      setLoading(true);

      // Use the organization utils function
      const { getUserOrganizationId } = await import('@/lib/organizationUtils');
      const orgId = await getUserOrganizationId();

      if (!orgId) {
        setActiveOrganizationId(null);
        setActiveOrganization(null);
        return;
      }

      // Fetch organization details
      const { data: org, error: orgDetailsError } = await supabase
        .from('organizations')
        .select('id, name, owner_id')
        .eq('id', orgId)
        .single();

      if (orgDetailsError) {
        console.error('Error getting organization details:', orgDetailsError);
        return;
      }

      setActiveOrganizationId(orgId);
      setActiveOrganization(org);
    } catch (error) {
      console.error('Error in fetchActiveOrganization:', error);
    } finally {
      setLoading(false);
    }
  };

  const refreshOrganization = async () => {
    await fetchActiveOrganization();
  };

  const setActiveOrganizationHandler = async (orgId: string) => {
    // In single-photographer model, organization is determined by ownership
    // No need to update user settings since getUserOrganizationId handles this
    console.log('Organization switch not needed in single-photographer mode:', orgId);

    // Just refresh the organization data
    await refreshOrganization();

    toast({
      title: "Success", 
      description: "Organization data refreshed",
    });
  };

  // Set up simplified presence tracking for single-photographer
  useEffect(() => {
    let presenceInterval: ReturnType<typeof setInterval> | null = null;

    const setupPresence = async () => {
      const { data: { user } } = await supabase.auth.getUser();
      if (!user || !activeOrganizationId) return;

      // Simple periodic activity update - no complex presence channels needed
      presenceInterval = setInterval(async () => {
        try {
          // Just update user_settings to show activity
          await supabase
            .from('user_settings')
            .update({ updated_at: new Date().toISOString() })
            .eq('user_id', user.id);
        } catch (error) {
          console.warn('Failed to update activity timestamp:', error);
        }
      }, 300000); // Update every 5 minutes
    };

    if (activeOrganizationId) {
      setupPresence();
    }

    return () => {
      if (presenceInterval) {
        clearInterval(presenceInterval);
      }
    };
  }, [activeOrganizationId]);

  // Initialize on mount and when auth state changes
  useEffect(() => {
    const initializeOrganization = async () => {
      const { data: { user } } = await supabase.auth.getUser();
      if (user) {
        await fetchActiveOrganization();
      } else {
        setActiveOrganizationId(null);
        setActiveOrganization(null);
        setLoading(false);
      }
    };

    initializeOrganization();

    // Listen for auth state changes
    const { data: { subscription } } = supabase.auth.onAuthStateChange(
      (event, session) => {
        if (event === 'SIGNED_IN' && session) {
          fetchActiveOrganization();
        } else if (event === 'SIGNED_OUT') {
          setActiveOrganizationId(null);
          setActiveOrganization(null);
          setLoading(false);
        }
      }
    );

    return () => {
      subscription.unsubscribe();
    };
  }, []);

  // Prefetch common organization-scoped data to speed up first paint across pages
  const prefetchOrgData = useCallback(async (orgId: string) => {
    const tasks: Array<Promise<unknown>> = [];

    // Project types
    tasks.push(
      queryClient.prefetchQuery({
        queryKey: ['project_types', orgId],
        queryFn: async () => {
          const { data, error } = await supabase
            .from('project_types')
            .select('*')
            .eq('organization_id', orgId)
            .order('sort_order');
          if (error) throw error;
          return data || [];
        },
        staleTime: 5 * 60 * 1000,
      })
    );

    // Project statuses
    tasks.push(
      queryClient.prefetchQuery({
        queryKey: ['project_statuses', orgId],
        queryFn: async () => {
          const { data, error } = await supabase
            .from('project_statuses')
            .select('*')
            .eq('organization_id', orgId)
            .order('sort_order');
          if (error) throw error;
          return data || [];
        },
        staleTime: 5 * 60 * 1000,
      })
    );

    // Services
    tasks.push(
      queryClient.prefetchQuery({
        queryKey: ['services', orgId],
        queryFn: async () => {
          const { data, error } = await supabase
            .from('services')
            .select('*')
            .eq('organization_id', orgId)
            .order('name');
          if (error) throw error;
          return data || [];
        },
        staleTime: 5 * 60 * 1000,
      })
    );

    // Session statuses
    tasks.push(
      queryClient.prefetchQuery({
        queryKey: ['session_statuses', orgId],
        queryFn: async () => {
          const { data, error } = await supabase
            .from('session_statuses')
            .select('*')
            .eq('organization_id', orgId)
            .order('sort_order');
          if (error) throw error;
          return data || [];
        },
        staleTime: 5 * 60 * 1000,
      })
    );

    // Lead statuses (used widely in leads and dialogs)
    tasks.push(
      queryClient.prefetchQuery({
        queryKey: ['lead_statuses', orgId],
        queryFn: async () => {
          const { data, error } = await supabase
            .from('lead_statuses')
            .select('*')
            .eq('organization_id', orgId)
            .order('sort_order');
          if (error) throw error;
          return data || [];
        },
        staleTime: 5 * 60 * 1000,
      })
    );

    // Organization settings (cache aware)
    tasks.push(
      queryClient.prefetchQuery({
        queryKey: ['organization_settings', orgId],
        queryFn: async () => {
          const detectedTimezone =
            typeof window !== 'undefined' ? detectBrowserTimezone() : undefined;
          return fetchOrganizationSettingsWithCache(orgId, {
            detectedTimezone,
          });
        },
        staleTime: ORGANIZATION_SETTINGS_CACHE_TTL,
      })
    );

    // Packages (used by settings, dialogs)
    tasks.push(
      queryClient.prefetchQuery({
        queryKey: ['packages', orgId],
        queryFn: async () => {
          const { data: user } = await supabase.auth.getUser();
          if (!user.user) return [];
          await supabase.rpc('ensure_default_packages_for_org', {
            user_uuid: user.user.id,
            org_id: orgId,
          });
          const { data, error } = await supabase
            .from('packages')
            .select('*')
            .eq('organization_id', orgId)
            .order('created_at', { ascending: false });
          if (error) throw error;
          return data || [];
        },
        staleTime: 5 * 60 * 1000,
      })
    );

    // Session types
    tasks.push(
      queryClient.prefetchQuery({
        queryKey: ['session_types', orgId],
        queryFn: async () => {
          const { data: user } = await supabase.auth.getUser();
          if (!user.user) return [];
          await supabase.rpc('ensure_default_session_types_for_org', {
            user_uuid: user.user.id,
            org_id: orgId,
          });
          const { data, error } = await supabase
            .from('session_types')
            .select('*')
            .eq('organization_id', orgId)
            .order('sort_order', { ascending: true })
            .order('name', { ascending: true });
          if (error) throw error;
          return data || [];
        },
        staleTime: 5 * 60 * 1000,
      })
    );

    // Lead field definitions â€” not on React Query, seed localStorage for fast bootstrap
    tasks.push((async () => {
      try {
        const { data: authUser } = await supabase.auth.getUser();
        await supabase.rpc('ensure_default_lead_field_definitions', { org_id: orgId, user_uuid: authUser.user?.id });
      } catch (error) {
        console.warn('Failed to ensure default lead field definitions', error);
      }
      try {
        const { data, error } = await supabase
          .from('lead_field_definitions')
          .select('*')
          .eq('organization_id', orgId)
          .order('sort_order', { ascending: true });
        if (!error && data && typeof window !== 'undefined') {
          try {
            localStorage.setItem(`lead_field_definitions:${orgId}`, JSON.stringify(data));
          } catch (storageError) {
            console.warn('Failed to cache lead field definitions', storageError);
          }
        }
      } catch (error) {
        console.warn('Failed to preload lead field definitions', error);
      }
    })());

    await Promise.allSettled(tasks);
  }, [queryClient]);

  // Trigger prefetch whenever org becomes available
  useEffect(() => {
    if (activeOrganizationId) {
      prefetchOrgData(activeOrganizationId);
    }
  }, [activeOrganizationId, prefetchOrgData]);

  return (
    <OrganizationContext.Provider
      value={{
        activeOrganizationId,
        activeOrganization,
        loading,
        refreshOrganization,
        setActiveOrganization: setActiveOrganizationHandler,
      }}
    >
      {children}
    </OrganizationContext.Provider>
  );
}

