const VALID_SESSION_ID = "123e4567-e89b-12d3-a456-426614174000";
const VALID_ORG_ID = "123e4567-e89b-12d3-a456-426614174001";
const invokeMock = supabase.functions.invoke as jest.Mock;
const toastMock = (useToast() as { toast: jest.Mock }).toast;

beforeEach(() => {
  invokeMock.mockReset();
  toastMock.mockReset();
});

describe("useWorkflowTriggers", () => {
  describe("triggerWorkflow", () => {
    it("rejects when required parameters are missing", async () => {
      const { triggerWorkflow } = useWorkflowTriggers();

      await expect(
        triggerWorkflow("session_scheduled", "session", "", VALID_ORG_ID)
      ).rejects.toThrow("Missing required parameters");
      expect(invokeMock).not.toHaveBeenCalled();
    });

    it("rejects when ids fail UUID validation", async () => {
      const { triggerWorkflow } = useWorkflowTriggers();

      await expect(
        triggerWorkflow("session_scheduled", "session", "not-a-uuid", VALID_ORG_ID)
      ).rejects.toThrow("Invalid entity ID format");

      await expect(
        triggerWorkflow("session_scheduled", "session", VALID_SESSION_ID, "invalid-org")
      ).rejects.toThrow("Invalid organization ID format");

      expect(invokeMock).not.toHaveBeenCalled();
    });

    it("surfaces Supabase errors and shows a destructive toast", async () => {
      const { triggerWorkflow } = useWorkflowTriggers();

      const supabaseError = new Error("RPC exploded");
      invokeMock.mockResolvedValueOnce({ data: null, error: supabaseError });

      await expect(
        triggerWorkflow("session_completed", "session", VALID_SESSION_ID, VALID_ORG_ID)
      ).rejects.toThrow("RPC exploded");

      expect(toastMock).toHaveBeenCalledWith({
        title: "Workflow Error",
        description: "RPC exploded",
        variant: "destructive",
      });
    });

    it("does not toast when Supabase reports no workflows", async () => {
      const { triggerWorkflow } = useWorkflowTriggers();

      const noWorkflowError = new Error("No workflows found for this trigger");
      invokeMock.mockResolvedValueOnce({ data: null, error: noWorkflowError });

      await expect(
        triggerWorkflow("session_completed", "session", VALID_SESSION_ID, VALID_ORG_ID)
      ).rejects.toThrow("No workflows found for this trigger");

      expect(toastMock).not.toHaveBeenCalled();
    });

    it("resolves with result payload when invocation succeeds", async () => {
      const { triggerWorkflow } = useWorkflowTriggers();

      invokeMock.mockResolvedValueOnce({
        data: { result: { triggered_workflows: 0, detail: "ok" } },
        error: null,
      });

      await expect(
        triggerWorkflow("session_completed", "session", VALID_SESSION_ID, VALID_ORG_ID)
      ).resolves.toEqual({ triggered_workflows: 0, detail: "ok" });

      expect(toastMock).not.toHaveBeenCalled();
    });
  });

  describe("trigger wrappers", () => {
    beforeEach(() => {
      invokeMock.mockResolvedValue({
        data: { result: { triggered_workflows: 1 } },
        error: null,
      });
    });

    it("enriches session scheduled trigger payload with session metadata", async () => {
      const { triggerSessionScheduled } = useWorkflowTriggers();

      await triggerSessionScheduled(VALID_SESSION_ID, VALID_ORG_ID, {
        session_date: "2024-01-01",
        session_time: "09:00",
        location: "Studio",
        custom: "value",
      });

      expect(invokeMock).toHaveBeenCalledWith("workflow-executor", {
        body: expect.objectContaining({
          trigger_type: "session_scheduled",
          trigger_entity_type: "session",
          trigger_entity_id: VALID_SESSION_ID,
          organization_id: VALID_ORG_ID,
          trigger_data: expect.objectContaining({
            session_date: "2024-01-01",
            session_time: "09:00",
            location: "Studio",
            custom: "value",
          }),
        }),
      });
    });

    it("passes through project status change payload", async () => {
      const { triggerProjectStatusChange } = useWorkflowTriggers();

      await triggerProjectStatusChange(
        VALID_SESSION_ID,
        VALID_ORG_ID,
        "proposal",
        "booked",
        { project_name: "Wedding" }
      );

      expect(invokeMock).toHaveBeenCalledWith("workflow-executor", {
        body: expect.objectContaining({
          trigger_type: "project_status_change",
          trigger_entity_type: "project",
          trigger_data: expect.objectContaining({
            old_status: "proposal",
            new_status: "booked",
            project_name: "Wedding",
          }),
        }),
      });
    });
  });
});

import { useState, useEffect, useCallback } from 'react';
import { toast } from '@/hooks/use-toast';

export interface UseEntityDataOptions<T> {
  fetchFn: () => Promise<T[]>;
  onError?: (error: Error) => void;
  dependencies?: any[];
}

export interface EntityDataState<T> {
  data: T[];
  loading: boolean;
  error: string | null;
  refetch: () => Promise<void>;
  refresh: () => void;
}

export function useEntityData<T>({
  fetchFn,
  onError,
  dependencies = []
}: UseEntityDataOptions<T>): EntityDataState<T> {
  const [data, setData] = useState<T[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const fetchData = useCallback(async () => {
    try {
      setLoading(true);
      setError(null);
      const result = await fetchFn();
      setData(result);
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'An unknown error occurred';
      setError(errorMessage);

      if (onError) {
        onError(err instanceof Error ? err : new Error(errorMessage));
      } else {
        toast({
          title: "Error",
          description: errorMessage,
          variant: "destructive",
        });
      }
    } finally {
      setLoading(false);
    }
  }, [fetchFn, onError]);

  const refresh = useCallback(() => {
    fetchData();
  }, [fetchData]);

  useEffect(() => {
    fetchData();
  }, [fetchData, ...dependencies]);

  return {
    data,
    loading,
    error,
    refetch: fetchData,
    refresh
  };
}

import { useCallback, useMemo } from "react";
import { useQuery, UseQueryResult } from "@tanstack/react-query";
import { useOrganization } from "@/contexts/OrganizationContext";
import {
  fetchLeadById,
  fetchLeadSessions,
  fetchLeadProjectSummary,
  fetchLatestLeadActivity,
  LeadDetailRecord,
  LeadSessionRecord,
  LeadProjectSummaryPayload,
  AggregatedPaymentSummary,
  ProjectSummary
} from "@/services/LeadDetailService";
import { useLeadStatuses } from "./useOrganizationData";

const ACTIVE_SESSION_STATUSES = new Set(["planned", "upcoming", "confirmed", "scheduled"]);

interface SessionMetrics {
  todayCount: number;
  todayNext: LeadSessionRecord | null;
  nextUpcoming: LeadSessionRecord | null;
  overdueCount: number;
}

export interface LeadDetailQueryResult {
  lead: LeadDetailRecord | null;
  leadQuery: UseQueryResult<LeadDetailRecord | null, unknown>;
  sessions: LeadSessionRecord[];
  sessionsQuery: UseQueryResult<LeadSessionRecord[], unknown>;
  projectSummary: ProjectSummary;
  aggregatedPayments: AggregatedPaymentSummary;
  summaryQuery: UseQueryResult<LeadProjectSummaryPayload, unknown>;
  latestLeadActivity: string | null;
  latestActivityQuery: UseQueryResult<string | null, unknown>;
  leadStatuses: any[];
  sessionMetrics: SessionMetrics;
  latestSessionUpdate: string | null;
  hasProjects: boolean;
  isLoading: boolean;
  refetchAll: () => Promise<void>;
}

const DEFAULT_SUMMARY: ProjectSummary = { count: 0, latestUpdate: null };
const DEFAULT_PAYMENTS: AggregatedPaymentSummary = {
  totalPaid: 0,
  total: 0,
  remaining: 0,
  currency: "TRY"
};
const DEFAULT_METRICS: SessionMetrics = {
  todayCount: 0,
  todayNext: null,
  nextUpcoming: null,
  overdueCount: 0
};

function sortSessions(sessions: LeadSessionRecord[]): LeadSessionRecord[] {
  const sorted = [...sessions];

  sorted.sort((a, b) => {
    const aStatus = (a.status || "").toLowerCase();
    const bStatus = (b.status || "").toLowerCase();

    const aIsPlanned = aStatus === "planned";
    const bIsPlanned = bStatus === "planned";

    if (aIsPlanned !== bIsPlanned) {
      return aIsPlanned ? -1 : 1;
    }

    const aDate = a.session_date ? new Date(a.session_date).getTime() : 0;
    const bDate = b.session_date ? new Date(b.session_date).getTime() : 0;

    return bDate - aDate;
  });

  return sorted;
}

function computeSessionMetrics(sessions: LeadSessionRecord[]): SessionMetrics {
  if (!sessions.length) {
    return DEFAULT_METRICS;
  }

  const today = new Date();
  today.setHours(0, 0, 0, 0);
  const todayKey = `${today.getFullYear()}-${String(today.getMonth() + 1).padStart(2, "0")}-${String(
    today.getDate()
  ).padStart(2, "0")}`;

  const activeSessions = sessions
    .map((session) => {
      if (!session.session_date) return null;
      const normalizedStatus = (session.status || "").toLowerCase();
      if (!ACTIVE_SESSION_STATUSES.has(normalizedStatus)) return null;

      const date = (() => {
        const timePart = (session.session_time ?? "").trim();
        if (timePart) {
          const candidate = new Date(`${session.session_date}T${timePart}`);
          if (!Number.isNaN(candidate.getTime())) return candidate;
        }

        const fallback = new Date(session.session_date);
        return Number.isNaN(fallback.getTime()) ? null : fallback;
      })();

      if (!date) return null;
      return { session, date };
    })
    .filter((value): value is { session: LeadSessionRecord; date: Date } => Boolean(value))
    .sort((a, b) => a.date.getTime() - b.date.getTime());

  const todays: LeadSessionRecord[] = [];
  let nextUpcoming: LeadSessionRecord | null = null;
  let overdueCount = 0;

  for (const entry of activeSessions) {
    const sessionKey = entry.session.session_date
      ? entry.session.session_date.slice(0, 10)
      : null;

    if (!sessionKey) continue;

    if (sessionKey < todayKey) {
      overdueCount += 1;
      continue;
    }

    if (sessionKey === todayKey) {
      todays.push(entry.session);
      continue;
    }

    if (!nextUpcoming) {
      nextUpcoming = entry.session;
    }
  }

  return {
    todayCount: todays.length,
    todayNext: todays[0] ?? null,
    nextUpcoming,
    overdueCount
  };
}

function computeLatestSessionUpdate(sessions: LeadSessionRecord[]): string | null {
  if (!sessions.length) return null;

  return sessions.reduce<string | null>((latest, session) => {
    const candidate = session.updated_at || session.created_at || null;
    if (!candidate) return latest;
    if (!latest) return candidate;
    return new Date(candidate).getTime() > new Date(latest).getTime() ? candidate : latest;
  }, null);
}

export function useLeadDetailData(leadId?: string): LeadDetailQueryResult {
  const { activeOrganizationId, loading: orgLoading } = useOrganization();
  const {
    data: leadStatuses = [],
    isLoading: statusesLoading
  } = useLeadStatuses();

  const leadQuery = useQuery({
    queryKey: ["lead", leadId],
    queryFn: () => fetchLeadById(leadId!),
    enabled: !!leadId,
    staleTime: 5 * 60 * 1000
  });

  const sessionsQuery = useQuery({
    queryKey: ["lead", leadId, "sessions", activeOrganizationId],
    queryFn: () => fetchLeadSessions(leadId!, activeOrganizationId),
    enabled: !!leadId && !orgLoading,
    staleTime: 2 * 60 * 1000
  });

  const summaryQuery = useQuery({
    queryKey: ["lead", leadId, "project-summary", activeOrganizationId],
    queryFn: () => fetchLeadProjectSummary(leadId!, activeOrganizationId!),
    enabled: !!leadId && !!activeOrganizationId,
    staleTime: 2 * 60 * 1000
  });

  const latestActivityQuery = useQuery({
    queryKey: ["lead", leadId, "latest-activity"],
    queryFn: () => fetchLatestLeadActivity(leadId!),
    enabled: !!leadId,
    staleTime: 60 * 1000
  });

  const sessions = useMemo(() => {
    if (!sessionsQuery.data) return [];
    return sortSessions(sessionsQuery.data).map((session) => ({
      ...session,
      project_name: session.projects?.name ?? undefined
    }));
  }, [sessionsQuery.data]);

  const sessionMetrics = useMemo(() => computeSessionMetrics(sessions), [sessions]);
  const latestSessionUpdate = useMemo(() => computeLatestSessionUpdate(sessions), [sessions]);

  const projectSummary = summaryQuery.data?.summary ?? DEFAULT_SUMMARY;
  const aggregatedPayments = summaryQuery.data?.payments ?? DEFAULT_PAYMENTS;

  const isLoading =
    leadQuery.isLoading ||
    sessionsQuery.isLoading ||
    summaryQuery.isLoading ||
    latestActivityQuery.isLoading ||
    statusesLoading ||
    orgLoading;

  const refetchAll = useCallback(async () => {
    await Promise.all([
      leadQuery.refetch(),
      sessionsQuery.refetch(),
      summaryQuery.refetch(),
      latestActivityQuery.refetch()
    ]);
  }, [leadQuery, sessionsQuery, summaryQuery, latestActivityQuery]);

  return {
    lead: leadQuery.data ?? null,
    leadQuery,
    sessions,
    sessionsQuery,
    projectSummary,
    aggregatedPayments,
    summaryQuery,
    latestLeadActivity: latestActivityQuery.data ?? null,
    latestActivityQuery,
    leadStatuses,
    sessionMetrics,
    latestSessionUpdate,
    hasProjects: summaryQuery.data?.hasProjects ?? false,
    isLoading,
    refetchAll
  };
}

import React, { useState } from "react";
import { supabase } from "@/integrations/supabase/client";
import { useToast } from "@/hooks/use-toast";
import { ToastAction } from "@/components/ui/toast";

interface UseLeadStatusActionsProps {
  leadId: string;
  onStatusChange: () => void;
}

export function useLeadStatusActions({ leadId, onStatusChange }: UseLeadStatusActionsProps) {
  const [isUpdating, setIsUpdating] = useState(false);
  const { toast } = useToast();

  const updateLeadStatus = async (newStatusName: string, previousStatus?: string) => {
    setIsUpdating(true);
    try {
      const { data: userData } = await supabase.auth.getUser();
      if (!userData.user) throw new Error('User not authenticated');

      // Ensure system statuses exist for this user
      await supabase.rpc('ensure_system_lead_statuses', {
        user_uuid: userData.user.id
      });

      // Find the status by name to get the ID
      const { data: statusData, error: statusError } = await supabase
        .from('lead_statuses')
        .select('id, name')
        .eq('user_id', userData.user.id)
        .eq('name', newStatusName)
        .maybeSingle();

      if (statusError) throw statusError;
      if (!statusData) throw new Error('Status not found');

      const { error } = await supabase
        .from('leads')
        .update({ 
          status_id: statusData.id,
          status: newStatusName // Keep text field updated for backward compatibility
        })
        .eq('id', leadId);

      if (error) throw error;

      onStatusChange();

      // Show toast with undo functionality
      const undoRef = { current: false };

      toast({
        title: "Status Updated",
        description: `Lead marked as ${newStatusName}`,
        action: previousStatus ? (
          <ToastAction
            altText="Undo status change"
            onClick={async () => {
              if (undoRef.current) return; // Prevent multiple clicks
              undoRef.current = true;

              try {
                // Find the previous status by name to get the ID
                const { data: prevStatusData, error: prevStatusError } = await supabase
                  .from('lead_statuses')
                  .select('id, name')
                  .eq('user_id', userData.user.id)
                  .eq('name', previousStatus)
                  .maybeSingle();

                if (prevStatusError) throw prevStatusError;
                if (!prevStatusData) throw new Error('Previous status not found');

                const { error: undoError } = await supabase
                  .from('leads')
                  .update({ 
                    status_id: prevStatusData.id,
                    status: previousStatus 
                  })
                  .eq('id', leadId);

                if (undoError) throw undoError;

                onStatusChange();
                toast({
                  title: "Undone",
                  description: `Status reverted to ${previousStatus}`
                });
              } catch (error: any) {
                toast({
                  title: "Undo failed",
                  description: error.message,
                  variant: "destructive"
                });
              }
            }}
          >
            Undo
          </ToastAction>
        ) : undefined
      });

    } catch (error: any) {
      toast({
        title: "Error updating status",
        description: error.message,
        variant: "destructive"
      });
    } finally {
      setIsUpdating(false);
    }
  };

  const markAsCompleted = (previousStatus?: string, customCompletedLabel?: string) => 
    updateLeadStatus(customCompletedLabel || 'Completed', previousStatus);

  const markAsLost = (previousStatus?: string, customLostLabel?: string) => 
    updateLeadStatus(customLostLabel || 'Lost', previousStatus);

  return {
    markAsCompleted,
    markAsLost,
    isUpdating
  };
}

import { useEffect } from 'react';
import { supabase } from '@/integrations/supabase/client';
import { useWorkflowTriggers } from '@/hooks/useWorkflowTriggers';

export function useLeadStatusWorkflows() {
  const { triggerLeadStatusChange } = useWorkflowTriggers();

  const triggerLeadStatusWorkflow = async (
    leadId: string,
    organizationId: string,
    oldStatusName: string,
    newStatusName: string,
    leadData?: any
  ) => {
    try {
      await triggerLeadStatusChange(leadId, organizationId, oldStatusName, newStatusName, leadData);
    } catch (error) {
      console.error('Error triggering lead status workflow:', error);
    }
  };

  return {
    triggerLeadStatusWorkflow
  };
}

import { useState } from 'react';
import { supabase } from '@/integrations/supabase/client';
import { useToast } from '@/hooks/use-toast';

interface UseLeadUpdateProps {
  leadId: string;
  onSuccess?: () => void;
}

export function useLeadUpdate({ leadId, onSuccess }: UseLeadUpdateProps) {
  const [isUpdating, setIsUpdating] = useState(false);
  const { toast } = useToast();

  const updateCoreField = async (fieldKey: string, value: string | null) => {
    if (isUpdating) return;

    setIsUpdating(true);
    try {
      const updates: Record<string, any> = {
        [fieldKey]: value || null,
        updated_at: new Date().toISOString()
      };

      const { error } = await supabase
        .from('leads')
        .update(updates)
        .eq('id', leadId);

      if (error) throw error;

      toast({
        title: "Field updated",
        description: "The field has been updated successfully.",
      });

      onSuccess?.();
    } catch (error) {
      console.error('Error updating core field:', error);
      toast({
        title: "Update failed",
        description: "Failed to update the field. Please try again.",
        variant: "destructive",
      });
      throw error;
    } finally {
      setIsUpdating(false);
    }
  };

  const updateCustomField = async (fieldKey: string, value: string | null) => {
    if (isUpdating) return;

    setIsUpdating(true);
    try {
      const { error } = await supabase
        .from('lead_field_values')
        .upsert({
          lead_id: leadId,
          field_key: fieldKey,
          value: value || null,
          updated_at: new Date().toISOString()
        }, {
          onConflict: 'lead_id,field_key'
        });

      if (error) throw error;

      toast({
        title: "Field updated",
        description: "The custom field has been updated successfully.",
      });

      onSuccess?.();
    } catch (error) {
      console.error('Error updating custom field:', error);
      toast({
        title: "Update failed",
        description: "Failed to update the custom field. Please try again.",
        variant: "destructive",
      });
      throw error;
    } finally {
      setIsUpdating(false);
    }
  };

  return {
    isUpdating,
    updateCoreField,
    updateCustomField
  };
}

import { useState, useEffect } from 'react';
import { supabase } from '@/integrations/supabase/client';
import { getUserOrganizationId } from '@/lib/organizationUtils';
import { useToast } from '@/hooks/use-toast';

export interface LeadWithCustomFields {
  id: string;
  name: string;
  email: string;
  phone: string;
  status: string;
  status_id: string;
  updated_at: string;
  created_at: string;
  due_date?: string;
  notes?: string;
  custom_fields: Record<string, string | null>;
  lead_statuses?: {
    id: string;
    name: string;
    color: string;
    is_system_final: boolean;
  };
}

export function useLeadsWithCustomFields() {
  const [leads, setLeads] = useState<LeadWithCustomFields[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const { toast } = useToast();

  const fetchLeadsWithCustomFields = async () => {
    try {
      setLoading(true);
      setError(null);

      const organizationId = await getUserOrganizationId();
      if (!organizationId) {
        throw new Error('No active organization found');
      }

      // Fetch leads with status information
      const { data: leadsData, error: leadsError } = await supabase
        .from('leads')
        .select(`
          *,
          lead_statuses(id, name, color, is_system_final)
        `)
        .eq('organization_id', organizationId)
        .order('updated_at', { ascending: false });

      if (leadsError) throw leadsError;

      // Fetch all custom field values for these leads
      const leadIds = leadsData?.map(lead => lead.id) || [];
      const { data: fieldValues, error: fieldValuesError } = await supabase
        .from('lead_field_values')
        .select('lead_id, field_key, value')
        .in('lead_id', leadIds);

      if (fieldValuesError) throw fieldValuesError;

      // Group field values by lead_id
      const fieldValuesByLead = (fieldValues || []).reduce((acc, fv) => {
        if (!acc[fv.lead_id]) {
          acc[fv.lead_id] = {};
        }
        acc[fv.lead_id][fv.field_key] = fv.value;
        return acc;
      }, {} as Record<string, Record<string, string | null>>);

      // Combine leads with their custom field values
      const leadsWithFields: LeadWithCustomFields[] = (leadsData || []).map(lead => ({
        ...lead,
        custom_fields: fieldValuesByLead[lead.id] || {},
      }));

      setLeads(leadsWithFields);
    } catch (err) {
      console.error('Error fetching leads with custom fields:', err);
      const errorMessage = err instanceof Error ? err.message : 'Failed to fetch leads';
      setError(errorMessage);
      toast({
        title: "Error fetching leads",
        description: errorMessage,
        variant: "destructive"
      });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchLeadsWithCustomFields();
  }, []);

  return {
    leads,
    loading,
    error,
    refetch: fetchLeadsWithCustomFields
  };
}

