import { useCallback, useMemo } from 'react';
import { addDays, addWeeks, addMonths, format } from 'date-fns';
import { getUserLocale, getStartOfWeek, getEndOfWeek, getDateFnsLocale } from '@/lib/utils';

type ViewMode = 'day' | 'week' | 'month';

/**
 * Optimized calendar navigation with memoized calculations
 * Handles date navigation and view title generation efficiently
 */
export function useOptimizedCalendarNavigation(
  currentDate: Date,
  viewMode: ViewMode,
  setCurrentDate: (date: Date | ((prev: Date) => Date)) => void
) {
  const userLocale = getUserLocale();
  const dateFnsLocale = getDateFnsLocale();

  // Memoized navigation functions to prevent re-creation on every render
  const navigatePrevious = useCallback(() => {
    setCurrentDate(prev => {
      switch (viewMode) {
        case "day":
          return addDays(prev, -1);
        case "week":
          return addWeeks(prev, -1);
        case "month":
          return addMonths(prev, -1);
        default:
          return prev;
      }
    });
  }, [viewMode, setCurrentDate]);

  const navigateNext = useCallback(() => {
    setCurrentDate(prev => {
      switch (viewMode) {
        case "day":
          return addDays(prev, 1);
        case "week":
          return addWeeks(prev, 1);
        case "month":
          return addMonths(prev, 1);
        default:
          return prev;
      }
    });
  }, [viewMode, setCurrentDate]);

  const goToToday = useCallback(() => {
    setCurrentDate(new Date());
  }, [setCurrentDate]);

  // Memoized view title to avoid expensive date formatting on every render
  const viewTitle = useMemo(() => {
    switch (viewMode) {
      case "day":
        return format(currentDate, "EEEE, MMMM d, yyyy", { locale: dateFnsLocale });
      case "week":
        const weekStart = getStartOfWeek(currentDate, userLocale);
        const weekEnd = getEndOfWeek(currentDate, userLocale);
        return `${format(weekStart, "MMM d", { locale: dateFnsLocale })} - ${format(weekEnd, "MMM d, yyyy", { locale: dateFnsLocale })}`;
      case "month":
        return format(currentDate, "MMMM yyyy", { locale: dateFnsLocale });
      default:
        return '';
    }
  }, [currentDate, viewMode, userLocale, dateFnsLocale]);

  // Memoized keyboard navigation handler
  const handleKeyboardNavigation = useCallback((e: KeyboardEvent) => {
    switch (e.key) {
      case 'ArrowLeft':
        e.preventDefault();
        navigatePrevious();
        break;
      case 'ArrowRight':
        e.preventDefault();
        navigateNext();
        break;
      case 'Home':
        e.preventDefault();
        goToToday();
        break;
      case 'Escape':
        e.preventDefault();
        // Could be used to close modals or reset view
        break;
    }
  }, [navigatePrevious, navigateNext, goToToday]);

  return {
    navigatePrevious,
    navigateNext,
    goToToday,
    viewTitle,
    handleKeyboardNavigation
  };
}

import { useCallback, useEffect, useState } from "react";
import { supabase } from "@/integrations/supabase/client";

export interface ProjectHeaderPaymentSummary {
  totalPaid: number;
  total: number;
  remaining: number;
  currency: string;
}

export interface ProjectHeaderTodoSummary {
  total: number;
  completed: number;
}

export interface ProjectHeaderServicesSummary {
  total: number;
  names: string[];
  totalValue: number;
}

interface ProjectHeaderSummaryState {
  payments: ProjectHeaderPaymentSummary;
  todos: ProjectHeaderTodoSummary;
  services: ProjectHeaderServicesSummary;
}

const DEFAULT_SUMMARY: ProjectHeaderSummaryState = {
  payments: {
    totalPaid: 0,
    total: 0,
    remaining: 0,
    currency: "TRY"
  },
  todos: {
    total: 0,
    completed: 0
  },
  services: {
    total: 0,
    names: [],
    totalValue: 0
  }
};

export function useProjectHeaderSummary(projectId?: string | null, refreshToken?: number) {
  const [summary, setSummary] = useState<ProjectHeaderSummaryState>(DEFAULT_SUMMARY);
  const [loading, setLoading] = useState(false);

  const fetchSummary = useCallback(async () => {
    if (!projectId) {
      setSummary(DEFAULT_SUMMARY);
      return;
    }

    setLoading(true);
    try {
      const [projectResponse, servicesResponse, paymentsResponse, todosResponse] = await Promise.all([
        supabase.from("projects").select("base_price").eq("id", projectId).single(),
        supabase
          .from("project_services")
          .select(`
            billing_type,
            services!inner (
              id,
              name,
              selling_price,
              price
            )
          `)
          .eq("project_id", projectId),
        supabase
          .from("payments")
          .select("amount, status")
          .eq("project_id", projectId),
        supabase.from("todos").select("id, is_completed").eq("project_id", projectId)
      ]);

      if (projectResponse.error) throw projectResponse.error;
      if (servicesResponse.error) throw servicesResponse.error;
      if (paymentsResponse.error) throw paymentsResponse.error;
      if (todosResponse.error) throw todosResponse.error;

      const basePrice = Number(projectResponse.data?.base_price) || 0;

      const serviceEntries = (servicesResponse.data || [])
        .map((entry: any) => {
          const service = entry.services;
          if (!service) return null;
          return {
            billing_type: entry.billing_type as string,
            service: service as {
              id: string;
              name: string;
              selling_price?: number | null;
              price?: number | null;
            }
          };
        })
        .filter(Boolean) as Array<{
          billing_type: string;
          service: {
            id: string;
            name: string;
            selling_price?: number | null;
            price?: number | null;
          };
        }>;
      const serviceNames = serviceEntries.map(entry => entry.service.name).filter(Boolean);
      const servicesTotal = serviceEntries.reduce((total, entry) => {
        if (entry.billing_type !== "extra") {
          return total;
        }
        const value = entry.service.selling_price ?? entry.service.price ?? 0;
        return total + Number(value) || total;
      }, 0);

      const paymentRows = paymentsResponse.data || [];
      const totalPaid = paymentRows.reduce((total, payment) => {
        if (payment.status === "paid") {
          return total + Number(payment.amount) || total;
        }
        return total;
      }, 0);

      const totalProjectValue = basePrice + servicesTotal;
      const remaining = Math.max(0, totalProjectValue - totalPaid);

      const todoRows = todosResponse.data || [];
      const completedTodos = todoRows.filter(todo => todo.is_completed).length;

      setSummary({
        payments: {
          totalPaid,
          total: totalProjectValue,
          remaining,
          currency: "TRY"
        },
        todos: {
          total: todoRows.length,
          completed: completedTodos
        },
        services: {
          total: serviceNames.length,
          names: serviceNames,
          totalValue: servicesTotal
        }
      });
    } catch (error) {
      console.error("Failed to load project header summary:", error);
      setSummary(DEFAULT_SUMMARY);
    } finally {
      setLoading(false);
    }
  }, [projectId]);

  useEffect(() => {
    fetchSummary();
  }, [fetchSummary, refreshToken]);

  return {
    summary,
    loading,
    refresh: fetchSummary
  };
}

import { useState, useEffect } from 'react';
import { supabase } from '@/integrations/supabase/client';

interface PaymentSummary {
  totalPaid: number;
  totalProject: number;
  remaining: number;
  currency: string;
}

export const useProjectPayments = (projectId: string, refreshTrigger?: number) => {
  const [paymentSummary, setPaymentSummary] = useState<PaymentSummary>({
    totalPaid: 0,
    totalProject: 0,
    remaining: 0,
    currency: 'TRY',
  });
  const [loading, setLoading] = useState(true);

  const fetchPaymentSummary = async () => {
    try {
      // Get project base price
      const { data: projectData, error: projectError } = await supabase
        .from('projects')
        .select('base_price')
        .eq('id', projectId)
        .single();

      if (projectError) throw projectError;

      // Get total cost of added services
      const { data: servicesData, error: servicesError } = await supabase
        .from('project_services')
        .select(`
          services!inner (
            selling_price,
            price
          )
        `)
        .eq('project_id', projectId);

      if (servicesError) throw servicesError;

      // Get total paid amount
      const { data: paymentsData, error: paymentsError } = await supabase
        .from('payments')
        .select('amount')
        .eq('project_id', projectId)
        .eq('status', 'paid');

      if (paymentsError) throw paymentsError;

      const basePrice = projectData?.base_price || 0;
      const servicesCost = servicesData?.reduce((sum, ps) => {
        const service = ps.services;
        const price = service.selling_price || service.price || 0;
        return sum + Number(price);
      }, 0) || 0;

      const totalProject = basePrice + servicesCost;
      const totalPaid = paymentsData?.reduce((sum, payment) => sum + Number(payment.amount), 0) || 0;
      const remaining = Math.max(0, totalProject - totalPaid);

      setPaymentSummary({
        totalPaid,
        totalProject,
        remaining,
        currency: 'TRY',
      });
    } catch (error) {
      console.error('Error fetching payment summary:', error);
      setPaymentSummary({
        totalPaid: 0,
        totalProject: 0,
        remaining: 0,
        currency: 'TRY',
      });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPaymentSummary();
  }, [projectId, refreshTrigger]);

  return { paymentSummary, loading, refetch: fetchPaymentSummary };
};

import { useState, useEffect } from 'react';
import { supabase } from '@/integrations/supabase/client';

interface TodoProgress {
  total: number;
  completed: number;
  percentage: number;
}

export const useProjectProgress = (projectId: string, refreshTrigger?: number) => {
  const [progress, setProgress] = useState<TodoProgress>({
    total: 0,
    completed: 0,
    percentage: 0,
  });
  const [loading, setLoading] = useState(true);

  const fetchTodoProgress = async () => {
    try {
      const { data, error } = await supabase
        .from('todos')
        .select('is_completed')
        .eq('project_id', projectId);

      if (error) throw error;

      const todos = data || [];
      const total = todos.length;
      const completed = todos.filter(todo => todo.is_completed).length;
      const percentage = total > 0 ? Math.round((completed / total) * 100) : 0;

      setProgress({ total, completed, percentage });
    } catch (error) {
      console.error('Error fetching todo progress:', error);
      setProgress({ total: 0, completed: 0, percentage: 0 });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchTodoProgress();
  }, [projectId, refreshTrigger]);

  return { progress, loading, refetch: fetchTodoProgress };
};

import { useCallback, useEffect, useMemo, useState } from "react";
import { supabase } from "@/integrations/supabase/client";
import { useToast } from "@/hooks/use-toast";
import { useNotificationTriggers } from "@/hooks/useNotificationTriggers";
import { useOrganization } from "@/contexts/OrganizationContext";
import { useWorkflowTriggers } from "@/hooks/useWorkflowTriggers";
import { useFormsTranslation } from "@/hooks/useTypedTranslation";

export interface ProjectStatus {
  id: string;
  name: string;
  color: string;
}

interface UseProjectStatusControllerOptions {
  projectId: string;
  currentStatusId?: string | null;
  onStatusChange?: () => void;
  statuses?: ProjectStatus[];
  statusesLoading?: boolean;
}

interface UseProjectStatusControllerResult {
  statuses: ProjectStatus[];
  currentStatus: ProjectStatus | null;
  loading: boolean;
  isUpdating: boolean;
  handleStatusChange: (newStatusId: string) => Promise<void>;
}

export function useProjectStatusController({
  projectId,
  currentStatusId,
  onStatusChange,
  statuses: passedStatuses,
  statusesLoading
}: UseProjectStatusControllerOptions): UseProjectStatusControllerResult {
  const [statuses, setStatuses] = useState<ProjectStatus[]>([]);
  const [currentStatus, setCurrentStatus] = useState<ProjectStatus | null>(null);
  const [loading, setLoading] = useState(true);
  const [isUpdating, setIsUpdating] = useState(false);
  const { toast } = useToast();
  const { triggerProjectMilestone } = useNotificationTriggers();
  const { activeOrganization } = useOrganization();
  const { triggerProjectStatusChange } = useWorkflowTriggers();
  const { t: tForms } = useFormsTranslation();

  const fetchProjectStatuses = useCallback(async () => {
    try {
      const { data, error } = await supabase
        .from("project_statuses")
        .select("*")
        .order("sort_order", { ascending: true });

      if (error) throw error;
      setStatuses(data || []);
    } catch (error) {
      console.error("Error fetching project statuses:", error);
      toast({
        title: tForms("status.errorUpdatingStatus"),
        description: tForms("status.statusNotFound"),
        variant: "destructive"
      });
    } finally {
      setLoading(false);
    }
  }, [toast, tForms]);

  useEffect(() => {
    if (passedStatuses === undefined) {
      fetchProjectStatuses();
      return;
    }

    setStatuses(passedStatuses);
    if (typeof statusesLoading === "boolean") {
      setLoading(statusesLoading);
    } else {
      setLoading(false);
    }
  }, [fetchProjectStatuses, passedStatuses, statusesLoading]);

  useEffect(() => {
    if (currentStatusId && statuses.length > 0) {
      const status = statuses.find(statusItem => statusItem.id === currentStatusId);
      setCurrentStatus(status || null);
    } else if (!currentStatusId) {
      setCurrentStatus(null);
    }
  }, [currentStatusId, statuses]);

  const handleStatusChange = useCallback(
    async (newStatusId: string) => {
      if (currentStatus && newStatusId === currentStatus.id) return;

      setIsUpdating(true);
      try {
        const { data: userData } = await supabase.auth.getUser();
        if (!userData.user) throw new Error("User not authenticated");

        const nextStatus = statuses.find(statusItem => statusItem.id === newStatusId);
        if (!nextStatus) throw new Error(tForms("status.statusNotFound"));
        const organizationId = activeOrganization?.id ?? null;

        const { data: projectData, error: projectError } = await supabase
          .from("projects")
          .select("lead_id")
          .eq("id", projectId)
          .single();

        if (projectError) throw projectError;

        const { error: updateError } = await supabase
          .from("projects")
          .update({ status_id: newStatusId })
          .eq("id", projectId);

        if (updateError) throw updateError;

        if (organizationId && currentStatus) {
          const { error: activityError } = await supabase
            .from("activities")
            .insert({
              content: `Status changed from "${currentStatus.name}" to "${nextStatus.name}"`,
              type: "status_change",
              project_id: projectId,
              lead_id: projectData.lead_id,
              user_id: userData.user.id,
              organization_id: organizationId
            });

          if (activityError) {
            console.error("Error logging activity:", activityError);
          }
        } else if (organizationId) {
          const { error: activityError } = await supabase
            .from("activities")
            .insert({
              content: `Status set to "${nextStatus.name}"`,
              type: "status_change",
              project_id: projectId,
              lead_id: projectData.lead_id,
              user_id: userData.user.id,
              organization_id: organizationId
            });

          if (activityError) {
            console.error("Error logging activity:", activityError);
          }
        }

        setCurrentStatus(nextStatus);
        onStatusChange?.();

        toast({
          title: tForms("status.statusUpdated"),
          description: `Project status ${currentStatus ? "changed" : "set"} to "${nextStatus.name}"`
        });

        if (organizationId && currentStatus?.id) {
          await triggerProjectMilestone(projectId, currentStatus.id, newStatusId, organizationId);
        }

        if (organizationId && currentStatus?.id) {
          try {
            await triggerProjectStatusChange(projectId, organizationId, currentStatus.name, nextStatus.name, {
              old_status_id: currentStatus.id,
              new_status_id: newStatusId,
              project_id: projectId,
              lead_id: projectData.lead_id
            });
          } catch (workflowError) {
            console.error("Error triggering project status workflow:", workflowError);
          }
        }
      } catch (error: any) {
        toast({
          title: tForms("status.errorUpdatingStatus"),
          description: error.message,
          variant: "destructive"
        });
      } finally {
        setIsUpdating(false);
      }
    },
    [
      activeOrganization?.id,
      currentStatus,
      onStatusChange,
      projectId,
      statuses,
      tForms,
      toast,
      triggerProjectMilestone,
      triggerProjectStatusChange
    ]
  );

  return useMemo(
    () => ({
      statuses,
      currentStatus,
      loading,
      isUpdating,
      handleStatusChange
    }),
    [currentStatus, handleStatusChange, isUpdating, loading, statuses]
  );
}

import { supabase } from '@/integrations/supabase/client';
import { useToast } from '@/hooks/use-toast';

export const useReminderActions = () => {
