  };
};

import { useState, useEffect } from "react";
import { supabase } from "@/integrations/supabase/client";
import { toast } from "@/hooks/use-toast";
import { useWorkflowTriggers } from "@/hooks/useWorkflowTriggers";
import { useSessionReminderScheduling } from "@/hooks/useSessionReminderScheduling";
import { sessionSchema, sanitizeInput, sanitizeHtml } from "@/lib/validation";
import { ZodError } from "zod";
import { getUserOrganizationId } from "@/lib/organizationUtils";
import { generateSessionName } from "@/lib/sessionUtils";


interface SessionEditFormData {
  session_name: string;
  session_date: string;
  session_time: string;
  notes: string;
  location: string;
  project_id?: string;
}

interface UseSessionEditFormProps {
  sessionId: string;
  leadId: string;
  leadName: string;
  initialData: {
    session_name?: string;
    session_date: string;
    session_time: string;
    notes?: string;
    location?: string;
    project_id?: string;
  };
  onSuccess?: () => void;
}

export function useSessionEditForm({ 
  sessionId, 
  leadId, 
  leadName, 
  initialData, 
  onSuccess 
}: UseSessionEditFormProps) {
  const [loading, setLoading] = useState(false);
  const [errors, setErrors] = useState<Record<string, string>>({});
  const [formData, setFormData] = useState<SessionEditFormData>({
    session_name: initialData.session_name || "",
    session_date: initialData.session_date,
    session_time: initialData.session_time,
    notes: initialData.notes || "",
    location: initialData.location || "",
    project_id: initialData.project_id || ""
  });

  const { triggerSessionRescheduled } = useWorkflowTriggers();
  const { rescheduleSessionReminders } = useSessionReminderScheduling();

  // Update form data when initial data changes (using primitive dependencies)
  useEffect(() => {
    setFormData(prev => {
      const newData = {
        session_name: initialData.session_name || "",
        session_date: initialData.session_date,
        session_time: initialData.session_time,
        notes: initialData.notes || "",
        location: initialData.location || "",
        project_id: initialData.project_id || ""
      };

      // Only update if data actually changed to prevent unnecessary rerenders
      if (JSON.stringify(prev) !== JSON.stringify(newData)) {
        return newData;
      }
      return prev;
    });
  }, [
    initialData.session_name,
    initialData.session_date,
    initialData.session_time,
    initialData.notes,
    initialData.location,
    initialData.project_id
  ]);


  const handleInputChange = (field: keyof SessionEditFormData, value: string) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));

    // Clear field-specific errors when user starts typing
    if (errors[field]) {
      setErrors(prev => {
        const newErrors = { ...prev };
        delete newErrors[field];
        return newErrors;
      });
    }
  };

  const resetForm = () => {
    setFormData({
      session_name: initialData.session_name || "",
      session_date: initialData.session_date,
      session_time: initialData.session_time,
      notes: initialData.notes || "",
      location: initialData.location || "",
      project_id: initialData.project_id || ""
    });
    setErrors({});
  };

  const isDirty = Boolean(
    formData.session_name !== (initialData.session_name || "") ||
    formData.session_date !== initialData.session_date ||
    formData.session_time !== initialData.session_time ||
    formData.notes !== (initialData.notes || "") ||
    formData.location !== (initialData.location || "") ||
    formData.project_id !== (initialData.project_id || "")
  );

  const isValid = Boolean(
    formData.session_date &&
    formData.session_time &&
    Object.keys(errors).length === 0
  );

  const validateForm = async () => {
    setErrors({});

    try {
      await sessionSchema.parseAsync({
        session_date: sanitizeInput(formData.session_date),
        session_time: sanitizeInput(formData.session_time),
        notes: formData.notes ? await sanitizeHtml(formData.notes) : undefined
      });
      return true;
    } catch (error) {
      if (error instanceof ZodError) {
        const newErrors: Record<string, string> = {};
        error.issues.forEach((err) => {
          const field = err.path[0] as string;
          newErrors[field] = err.message;
        });
        setErrors(newErrors);
      }
      return false;
    }
  };

  const submitForm = async () => {
    if (!(await validateForm())) {
      toast({
        title: "Validation error",
        description: "Please fix the validation errors before saving.",
        variant: "destructive"
      });
      return false;
    }

    if (!isValid) {
      toast({
        title: "Validation error",
        description: "Date and time are required.",
        variant: "destructive"
      });
      return false;
    }

    setLoading(true);
    try {
      // Check if date/time changed for workflow trigger
      const dateTimeChanged = 
        formData.session_date !== initialData.session_date || 
        formData.session_time !== initialData.session_time;
      const oldDateTime = `${initialData.session_date} ${initialData.session_time}`;
      const newDateTime = `${formData.session_date} ${formData.session_time}`;

      // Generate session name if empty
      const finalSessionName = formData.session_name.trim() || 
        generateSessionName(leadName || "Client");

      const { error } = await supabase
        .from('sessions')
        .update({
          session_name: sanitizeInput(finalSessionName),
          session_date: sanitizeInput(formData.session_date),
          session_time: sanitizeInput(formData.session_time),
          notes: formData.notes ? await sanitizeHtml(formData.notes) : null,
          location: formData.location.trim() || null,
          project_id: formData.project_id || null
        })
        .eq('id', sessionId);

      if (error) throw error;

      // Trigger workflow for session rescheduled if date/time changed
      if (dateTimeChanged) {
        try {
          const organizationId = await getUserOrganizationId();
          if (organizationId) {
            await triggerSessionRescheduled(sessionId, organizationId, oldDateTime, newDateTime, {
              session_date: formData.session_date,
              session_time: formData.session_time,
              location: formData.location,
              client_name: leadName,
              lead_id: leadId,
              project_id: formData.project_id || null
            });
          }
        } catch (workflowError) {
          console.error('Error triggering rescheduled workflow:', workflowError);
          // Don't block session update if workflow fails
        }
      }

      // Reschedule session reminders if date/time changed
      if (dateTimeChanged) {
        try {
          await rescheduleSessionReminders(sessionId);
        } catch (reminderError) {
          console.error('Error rescheduling session reminders:', reminderError);
          // Don't block session update if reminder scheduling fails
        }
      }

      toast({
        title: "Success",
        description: "Session updated successfully."
      });

      onSuccess?.();
      return true;
    } catch (error: any) {
      toast({
        title: "Error updating session",
        description: error.message,
        variant: "destructive"
      });
      return false;
    } finally {
      setLoading(false);
    }
  };

  return {
    formData,
    loading,
    errors,
    isDirty,
    isValid,
    handleInputChange,
    resetForm,
    submitForm
  };
}

import { useState } from "react";
import { toast } from "@/hooks/use-toast";
import { useWorkflowTriggers } from "@/hooks/useWorkflowTriggers";
import { useSessionReminderScheduling } from "@/hooks/useSessionReminderScheduling";
import { createSession } from "@/features/session-planning/api/sessionCreation";

interface SessionFormData {
  session_name: string;
  session_date: string;
  session_time: string;
  notes: string;
  location: string;
  project_id?: string;
}

interface UseSessionFormProps {
  leadId: string;
  leadName: string;
  projectId?: string;
  onSuccess?: () => void;
}

export function useSessionForm({ leadId, leadName, projectId, onSuccess }: UseSessionFormProps) {
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState<SessionFormData>({
    session_name: "",
    session_date: "",
    session_time: "",
    notes: "",
    location: "",
    project_id: projectId || ""
  });

  const { triggerSessionScheduled } = useWorkflowTriggers();
  const { scheduleSessionReminders } = useSessionReminderScheduling();

  const handleInputChange = (field: keyof SessionFormData, value: string) => {
    setFormData(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const resetForm = () => {
    setFormData({
      session_name: "",
      session_date: "",
      session_time: "",
      notes: "",
      location: "",
      project_id: projectId || ""
    });
  };

  const isDirty = Boolean(
    formData.session_name.trim() ||
    formData.session_date.trim() ||
    formData.session_time.trim() ||
    formData.notes.trim() ||
    formData.location.trim()
  );

  const isValid = Boolean(
    formData.session_name.trim() &&
    formData.session_date &&
    formData.session_time
  );

  const submitForm = async () => {
    if (!isValid) {
      toast({
        title: "Validation error",
        description: "Session name, date and time are required.",
        variant: "destructive"
      });
      return false;
    }

    setLoading(true);
    try {
      const resolvedProjectId = formData.project_id || projectId || undefined;

      const { sessionId, organizationId } = await createSession(
        {
          leadId,
          leadName,
          sessionName: formData.session_name.trim(),
          sessionDate: formData.session_date,
          sessionTime: formData.session_time,
          notes: formData.notes,
          location: formData.location,
          projectId: resolvedProjectId
        },
        {
          updateLeadStatus: !resolvedProjectId,
          createActivity: !resolvedProjectId
        }
      );

      // Trigger workflow for session scheduled
      try {
        console.log(`ðŸš€ Triggering session_scheduled workflow for session: ${sessionId}`);
        const workflowResult = await triggerSessionScheduled(sessionId, organizationId, {
          session_date: formData.session_date,
          session_time: formData.session_time,
          location: formData.location,
          client_name: leadName,
          lead_id: leadId,
          project_id: resolvedProjectId,
          status: 'planned'
        });
        console.log(`âœ… Session workflow result:`, workflowResult);
      } catch (workflowError) {
        console.error('âŒ Error triggering session_scheduled workflow:', workflowError);
        toast({
          title: "Warning", 
          description: "Session created successfully, but notifications may not be sent.",
          variant: "default"
        });
      }

      // Schedule session reminders
      try {
        console.log(`â° Scheduling reminders for session: ${sessionId}`);
        await scheduleSessionReminders(sessionId);
      } catch (reminderError) {
        console.error('âŒ Error scheduling session reminders:', reminderError);
      }

      toast({
        title: "Success",
        description: "Session scheduled successfully."
      });

      resetForm();
      onSuccess?.();
      return true;
    } catch (error: any) {
      toast({
        title: "Error scheduling session",
        description: error.message,
        variant: "destructive"
      });
      return false;
    } finally {
      setLoading(false);
    }
  };

  return {
    formData,
    loading,
    isDirty,
    isValid,
    handleInputChange,
    resetForm,
    submitForm
  };
}

import { useEffect } from "react";
import { useSettingsSection } from "./useSettingsSection";
import { useSettingsContext } from "@/contexts/SettingsContext";

interface SettingsSectionWithContextOptions<T> {
  sectionId: string;
  sectionName: string;
  initialValues: T;
  onSave: (values: T) => Promise<void>;
  autoSave?: boolean;
  throttleMs?: number;
}

export function useSettingsSectionWithContext<T extends Record<string, any>>(
  options: SettingsSectionWithContextOptions<T>
) {
  const { addDirtySection, removeDirtySection } = useSettingsContext();
  const section = useSettingsSection(options);

  // Track dirty state in context
  useEffect(() => {
    if (section.isDirty) {
      addDirtySection(options.sectionId);
    } else {
      removeDirtySection(options.sectionId);
    }
  }, [section.isDirty, options.sectionId, addDirtySection, removeDirtySection]);

  // Override handleSave to update context
  const handleSave = async () => {
    await section.handleSave();
    removeDirtySection(options.sectionId);
  };

  // Override handleCancel to update context
  const handleCancel = () => {
    section.handleCancel();
    removeDirtySection(options.sectionId);
  };

  return {
    ...section,
    handleSave,
    handleCancel
  };
}

import { useState, useEffect } from "react";
import { supabase } from "@/integrations/supabase/client";
import { useOrganization } from "@/contexts/OrganizationContext";
import { TemplateVariable } from "@/types/templateBuilder";

interface BusinessInfo {
  name: string;
  logo_url: string | null;
  primary_brand_color: string;
  phone?: string;
  email?: string;
  address?: string;
  website?: string;
}

export function useTemplateVariables() {
  const [variables, setVariables] = useState<TemplateVariable[]>([]);
  const [businessInfo, setBusinessInfo] = useState<BusinessInfo | null>(null);
  const [loading, setLoading] = useState(true);
  const { activeOrganization } = useOrganization();

  useEffect(() => {
    if (activeOrganization?.id) {
      fetchVariables();
    }
  }, [activeOrganization?.id]);

  const fetchVariables = async () => {
    if (!activeOrganization?.id) return;

    try {
      setLoading(true);

      // Fetch organization settings for business info
      const { data: orgSettings } = await supabase
        .from("organization_settings")
        .select("photography_business_name, logo_url, primary_brand_color")
        .eq("organization_id", activeOrganization.id)
        .single();

      // Fetch lead field definitions for dynamic variables
      const { data: leadFields } = await supabase
        .from("lead_field_definitions")
        .select("field_key, label, field_type")
        .eq("organization_id", activeOrganization.id)
        .order("sort_order");

      // Set business info
      if (orgSettings) {
        setBusinessInfo({
          name: orgSettings.photography_business_name || "Your Business",
          logo_url: orgSettings.logo_url,
          primary_brand_color: orgSettings.primary_brand_color || "#1EB29F",
        });
      }

      // Build template variables
      const templateVariables: TemplateVariable[] = [
        // Business variables (only real ones from organization_settings)
        {
          key: "business_name",
          label: "Business Name",
          category: "business"
        },

        // Lead variables from field definitions
        ...(leadFields?.map(field => ({
          key: `lead_${field.field_key}`,
          label: `Lead ${field.label}`,
          category: "lead" as const
        })) || []),

        // Static lead variables that exist in database
        {
          key: "lead_status",
          label: "Lead Status",
          category: "lead"
        },
        {
          key: "lead_due_date",
          label: "Lead Due Date", 
          category: "lead"
        },
        {
          key: "lead_created_date",
          label: "Lead Created Date",
          category: "lead"
        },
        {
          key: "lead_updated_date",
          label: "Lead Updated Date",
          category: "lead"
        },

        // Session variables (real database fields)
        {
          key: "session_date",
          label: "Session Date",
          category: "session"
        },
        {
          key: "session_time", 
          label: "Session Time",
          category: "session"
        },
        {
          key: "session_location",
          label: "Session Location",
          category: "session"
        },
        {
          key: "session_notes",
          label: "Session Notes",
          category: "session"
        },
        {
          key: "session_status",
          label: "Session Status",
          category: "session"
        },

        // Project variables (real database fields)
        {
          key: "project_name",
          label: "Project Name",
          category: "project"
        },
        {
          key: "project_type",
          label: "Project Type", 
          category: "project"
        },
        {
          key: "project_status",
          label: "Project Status",
          category: "project"
        },
        {
          key: "project_due_date",
          label: "Project Due Date",
          category: "project"
        },

        // System variables
        {
          key: "current_date",
          label: "Current Date",
          category: "custom"
        },
        {
          key: "current_time",
          label: "Current Time", 
          category: "custom"
        }
      ];

      setVariables(templateVariables);
    } catch (error) {
      console.error("Error fetching template variables:", error);
    } finally {
      setLoading(false);
    }
  };

  const getVariableValue = (key: string, mockData?: Record<string, string>): string => {
    // If mock data is provided, use it first
    if (mockData && mockData[key]) {
      return mockData[key];
    }

    // Business variables (only real ones)
    if (key === "business_name") return businessInfo?.name || "Your Business";

    // Lead variables with fallbacks
    if (key.startsWith("lead_")) {
      const fieldKey = key.replace("lead_", "");
      if (fieldKey === "name") return "John Smith";
      if (fieldKey === "email") return "john@example.com";
      if (fieldKey === "phone") return "-"; // Show dash for empty phone
      if (fieldKey === "notes") return "Sample notes";
      if (fieldKey === "status") return "New";
      if (fieldKey === "due_date") return new Date().toLocaleDateString();
      if (fieldKey === "created_date") return new Date().toLocaleDateString();
      if (fieldKey === "updated_date") return new Date().toLocaleDateString();
      // For custom fields, return placeholder
      return `Sample ${fieldKey}`;
    }

    // Session variables (real database fields)
    if (key === "session_date") return new Date().toLocaleDateString();
    if (key === "session_time") return "2:00 PM";  
    if (key === "session_location") return "Studio Location";
    if (key === "session_notes") return "Bring comfortable clothes";
    if (key === "session_status") return "Planned";

    // Project variables (real database fields)
    if (key === "project_name") return "Wedding Photography";
    if (key === "project_type") return "Wedding";
    if (key === "project_status") return "In Progress";
    if (key === "project_due_date") return new Date().toLocaleDateString();

    // System variables
    if (key === "current_date") return new Date().toLocaleDateString();
    if (key === "current_time") return new Date().toLocaleTimeString();

    return `{${key}}`;
  };

  return {
    variables,
    businessInfo,
    loading,
    getVariableValue,
    refetch: fetchVariables
  };
}

import { useState, useEffect, useCallback } from "react";
import { useLocation, useNavigate } from "react-router-dom";
import { usePortalReset } from "@/contexts/PortalResetContext";

interface UseTutorialExitOptions {
  currentStepTitle?: string;
  onExitComplete?: () => void;
}

export function useTutorialExit({ currentStepTitle, onExitComplete }: UseTutorialExitOptions = {}) {
  const [isExiting, setIsExiting] = useState(false);
  const navigate = useNavigate();
  const location = useLocation();
  const { reset } = usePortalReset();

  const handleExitNow = useCallback(() => {
    if (isExiting) return;
    setIsExiting(true);
    console.debug("[useTutorialExit] Long-press confirmed, exiting tutorial");
    try {
      onExitComplete?.();
    } finally {
      try { reset(); } catch {}
      requestAnimationFrame(() => {
        requestAnimationFrame(() => {
          if (location.pathname !== "/getting-started") {
            navigate("/getting-started", { replace: true });
          }
          setTimeout(() => setIsExiting(false), 0);
        });
      });
    }
  }, [isExiting, onExitComplete, reset, location.pathname, navigate]);

  useEffect(() => {
    return () => setIsExiting(false);
  }, []);

  return {
    currentStepTitle,
    isExiting,
    handleExitNow
  };
}

import { useQuery, useQueryClient } from "@tanstack/react-query";
import { supabase } from "@/integrations/supabase/client";
import { useAuth } from "@/contexts/AuthContext";
import { OnboardingStage } from "@/constants/onboarding";

export interface UserPreferences {
  // User Settings
  userId: string;
  activeOrganizationId: string | null;

  // Onboarding Data
  onboardingStage: OnboardingStage;
  currentOnboardingStep: number;
  welcomeModalShown: boolean;

  // Organization Settings
  organizationId: string | null;
  businessName: string;
  logoUrl: string | null;
  primaryBrandColor: string;
  timezone: string;
  dateFormat: string;
  timeFormat: string;

  // User Profile
  displayName: string | null;
  avatarUrl: string | null;

  // Timestamps
  lastUpdated: string;
}

const PREFERENCES_CACHE_KEY = "user-preferences";
const CACHE_TTL = 12 * 60 * 60 * 1000; // 12 hours in ms

// Fetch all user preferences in a single optimized query
async function fetchUserPreferences(userId: string): Promise<UserPreferences> {
  console.log("ðŸ”„ fetchUserPreferences: Starting unified fetch for user", userId);

  // Single query to get essential onboarding data (only columns we know exist)
  const { data, error } = await supabase
    .from('user_settings')
    .select(`
      user_id,
      onboarding_stage,
      current_onboarding_step,
      welcome_modal_shown,
      updated_at
    `)
    .eq('user_id', userId)
    .maybeSingle();

  if (error) {
    console.error("ðŸ”„ fetchUserPreferences: Database error:", error);
    throw error;
  }

  // Create default preferences for new users
  if (!data) {
    console.log("ðŸ”„ fetchUserPreferences: No data found, creating defaults");

    // Create minimal default user settings (only essential columns)
    const { error: insertError } = await supabase
      .from('user_settings')
      .insert({
        user_id: userId,
        onboarding_stage: 'not_started',
        current_onboarding_step: 1,
        welcome_modal_shown: false
      });

    if (insertError) {
      console.error("ðŸ”„ fetchUserPreferences: Error creating defaults:", insertError);
      throw insertError;
    }

    // Return default preferences
    return {
      userId,
      activeOrganizationId: null,
      onboardingStage: 'not_started' as OnboardingStage,
      currentOnboardingStep: 1,
      welcomeModalShown: false,
      organizationId: null,
      businessName: '',
      logoUrl: null,
      primaryBrandColor: '#1EB29F',
      timezone: 'UTC',
      dateFormat: 'DD/MM/YYYY',
      timeFormat: '12-hour',
      displayName: null,
      avatarUrl: null,
      lastUpdated: new Date().toISOString()
    };
  }

  const preferences: UserPreferences = {
    userId: data.user_id,
    activeOrganizationId: null, // Will get from organization context
    onboardingStage: (data.onboarding_stage as OnboardingStage) || 'not_started',
    currentOnboardingStep: data.current_onboarding_step || 1,
    welcomeModalShown: data.welcome_modal_shown || false,
    organizationId: null, // Will get from organization context
    businessName: '', // Will get from organization settings
    logoUrl: null, // Will get from organization settings
    primaryBrandColor: '#1EB29F', // Will get from organization settings
    timezone: 'UTC', // Will get from organization settings
    dateFormat: 'DD/MM/YYYY', // Will get from organization settings
    timeFormat: '12-hour', // Default value
    displayName: null, // Will get from profiles table
    avatarUrl: null, // Will get from profiles table
    lastUpdated: data.updated_at || new Date().toISOString()
  };

  console.log("ðŸ”„ fetchUserPreferences: Successfully fetched preferences", {
    onboardingStage: preferences.onboardingStage,
    welcomeModalShown: preferences.welcomeModalShown,
    currentStep: preferences.currentOnboardingStep
  });

  return preferences;
}

export function useUserPreferences() {
  const { user } = useAuth();
  const queryClient = useQueryClient();

  const query = useQuery({
    queryKey: [PREFERENCES_CACHE_KEY, user?.id],
    queryFn: () => fetchUserPreferences(user!.id),
    enabled: !!user?.id,
    staleTime: CACHE_TTL, // Consider data fresh for 12 hours
    gcTime: CACHE_TTL * 2, // Keep in cache for 24 hours
    refetchOnWindowFocus: false, // Don't refetch on window focus
    refetchOnReconnect: true, // Do refetch on reconnect
    retry: 3,
    retryDelay: (attemptIndex) => Math.min(1000 * 2 ** attemptIndex, 30000)
  });

  // Update preferences function with optimistic updates
  const updatePreferences = async (updates: Partial<UserPreferences>) => {
    if (!user?.id) return;

    console.log("ðŸ”„ updatePreferences: Updating with optimistic update", updates);

    // Optimistic update
    queryClient.setQueryData(
      [PREFERENCES_CACHE_KEY, user.id],
      (old: UserPreferences | undefined) => {
        if (!old) return old;
        return { ...old, ...updates, lastUpdated: new Date().toISOString() };
      }
    );

    try {
      // Update database (only onboarding-related columns for now)
      const updateData: any = {};

      if (updates.onboardingStage !== undefined) updateData.onboarding_stage = updates.onboardingStage;
      if (updates.currentOnboardingStep !== undefined) updateData.current_onboarding_step = updates.currentOnboardingStep;
      if (updates.welcomeModalShown !== undefined) updateData.welcome_modal_shown = updates.welcomeModalShown;

      // Only update if we have data to update
      if (Object.keys(updateData).length === 0) {
        console.log("ðŸ”„ updatePreferences: No onboarding data to update, skipping");
        return;
      }

      const { error } = await supabase
        .from('user_settings')
        .update(updateData)
        .eq('user_id', user.id);

      if (error) throw error;

      console.log("ðŸ”„ updatePreferences: Database updated successfully");

      // Invalidate and refetch to ensure consistency
      queryClient.invalidateQueries({ queryKey: [PREFERENCES_CACHE_KEY, user.id] });

    } catch (error) {
      console.error("ðŸ”„ updatePreferences: Error updating preferences:", error);

      // Revert optimistic update on error
      queryClient.invalidateQueries({ queryKey: [PREFERENCES_CACHE_KEY, user.id] });
      throw error;
    }
  };

  // Forced refresh function for login/logout/org switch
  const forceRefresh = () => {
    console.log("ðŸ”„ forceRefresh: Forcing preferences refresh");
    queryClient.invalidateQueries({ queryKey: [PREFERENCES_CACHE_KEY] });
  };

  // Clear cache function
  const clearCache = () => {
    console.log("ðŸ”„ clearCache: Clearing preferences cache");
    queryClient.removeQueries({ queryKey: [PREFERENCES_CACHE_KEY] });
  };

  return {
    data: query.data,
    isLoading: query.isLoading,
    error: query.error,
    updatePreferences,
    forceRefresh,
    clearCache,
    // Convenience computed values
    isReady: !query.isLoading && !!query.data,
    cacheStatus: query.isStale ? 'stale' : 'fresh'
  };
}

import { useState, useEffect } from "react";
import { supabase } from "@/integrations/supabase/client";
import { useToast } from "@/hooks/use-toast";
import type { Database } from "@/integrations/supabase/types";

type UserSettings = Database['public']['Tables']['user_settings']['Row'];

const defaultSettings: Partial<UserSettings> = {
  show_quick_status_buttons: true,
  photography_business_name: "",
  logo_url: null,
  primary_brand_color: "#1EB29F",
  date_format: "DD/MM/YYYY",
  notification_global_enabled: true,
  notification_daily_summary_enabled: false,
  notification_project_milestone_enabled: false,
  notification_scheduled_time: "09:00"
};

export function useUserSettings() {
  const [settings, setSettings] = useState<Partial<UserSettings>>(defaultSettings);
  const [loading, setLoading] = useState(true);
  const [uploading, setUploading] = useState(false);
  const { toast } = useToast();

  // Fetch user settings
  const fetchSettings = async () => {
    try {
      setLoading(true);
      const { data: { user } } = await supabase.auth.getUser();

      if (!user) {
        setLoading(false);
        return;
      }

      // First ensure user settings exist
      const { error: ensureError } = await supabase.rpc('ensure_user_settings', {
        user_uuid: user.id
      });

      if (ensureError) throw ensureError;

      // Then fetch the settings
      const { data, error } = await supabase
        .from('user_settings')
        .select('*')
        .eq('user_id', user.id)
        .maybeSingle();

      if (error && error.code !== 'PGRST116') {
        throw error;
      }

      if (data) {
        setSettings(data);
      } else {
        setSettings(defaultSettings);
      }
    } catch (error) {
      console.error('Error fetching user settings:', error);
      toast({
        title: "Error",
        description: "Failed to load settings",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  // Update user settings
  const updateSettings = async (updates: Partial<UserSettings>) => {
    try {
      const { data: { user } } = await supabase.auth.getUser();

      if (!user) throw new Error("No user found");

      const { data, error } = await supabase
        .from('user_settings')
        .update(updates)
        .eq('user_id', user.id)
        .select()
        .single();

      if (error) throw error;

      setSettings(data);
      return { success: true };
    } catch (error) {
      console.error('Error updating user settings:', error);
      return { success: false, error };
    }
  };

  // Upload logo file
  const uploadLogo = async (file: File) => {
    try {
      setUploading(true);
      const { data: { user } } = await supabase.auth.getUser();

      if (!user) throw new Error("No user found");

      // Validate file type
      if (!file.type.startsWith('image/')) {
        throw new Error('File must be an image');
      }

      // Validate file size (2MB limit)
      if (file.size > 2 * 1024 * 1024) {
        throw new Error('File size must be less than 2MB');
      }

      // Create unique filename
      const fileExt = file.name.split('.').pop();
      const fileName = `${user.id}/logo-${Date.now()}.${fileExt}`;

      // Delete existing logo if it exists
      if (settings?.logo_url) {
        const oldPath = settings.logo_url.split('/').pop();
        if (oldPath) {
          await supabase.storage
            .from('logos')
            .remove([`${user.id}/${oldPath}`]);
        }
      }

      // Upload new file
      const { data: uploadData, error: uploadError } = await supabase.storage
        .from('logos')
        .upload(fileName, file);

      if (uploadError) throw uploadError;

      // Get public URL
      const { data: { publicUrl } } = supabase.storage
        .from('logos')
        .getPublicUrl(fileName);

      // Update settings with new logo URL
      const updateResult = await updateSettings({ logo_url: publicUrl });

      if (!updateResult.success) throw updateResult.error;

      toast({
        title: "Success",
        description: "Logo uploaded successfully",
      });

      return { success: true, url: publicUrl };
    } catch (error: any) {
      console.error('Error uploading logo:', error);
      toast({
        title: "Error",
        description: error.message || "Failed to upload logo",
        variant: "destructive",
      });
      return { success: false, error };
    } finally {
      setUploading(false);
    }
  };

  // Initialize settings on mount
  useEffect(() => {
    fetchSettings();
  }, []);

  return {
    settings,
    loading,
    uploading,
    updateSettings,
    uploadLogo,
    refetch: fetchSettings
  };
}

import { useState, useEffect } from 'react';
import { supabase } from '@/integrations/supabase/client';
import { useToast } from '@/hooks/use-toast';
import { getUserOrganizationId } from '@/lib/organizationUtils';

interface WorkflowMetrics {
  id: string;
  organization_id: string;
  date: string;
  total_executions: number;
  successful_executions: number;
  failed_executions: number;
  average_execution_time_ms: number;
}

interface WorkflowHealth {
  totalWorkflows: number;
  activeWorkflows: number;
  pausedWorkflows: number;
  recentExecutions: number;
  successRate: number;
  averageExecutionTime: number;
  failedExecutions: number;
  stuckExecutions: number;
  metrics: WorkflowMetrics[];
}

export function useWorkflowHealth() {
  const [health, setHealth] = useState<WorkflowHealth | null>(null);
  const [loading, setLoading] = useState(true);
  const { toast } = useToast();

  const fetchWorkflowHealth = async () => {
    try {
      setLoading(true);
      const organizationId = await getUserOrganizationId();
      if (!organizationId) return;

      // Get workflow counts
      const { data: workflows, error: workflowsError } = await supabase
        .from('workflows')
        .select('id, is_active')
        .eq('organization_id', organizationId);

      if (workflowsError) throw workflowsError;

      // Get execution metrics for the last 7 days
      const sevenDaysAgo = new Date();
      sevenDaysAgo.setDate(sevenDaysAgo.getDate() - 7);

      const { data: metrics, error: metricsError } = await supabase
        .from('workflow_execution_metrics')
        .select('*')
        .eq('organization_id', organizationId)
        .gte('date', sevenDaysAgo.toISOString().split('T')[0])
        .order('date', { ascending: false });

      if (metricsError) throw metricsError;

      // Get recent executions (last 24 hours)
      const yesterday = new Date();
      yesterday.setDate(yesterday.getDate() - 1);

      const { data: recentExecutions, error: executionsError } = await supabase
        .from('workflow_executions')
        .select('id, status, started_at, completed_at')
        .eq('workflows.organization_id', organizationId)
        .gte('created_at', yesterday.toISOString());

      if (executionsError) {
        console.warn('Could not fetch recent executions:', executionsError);
      }

      // Check for stuck executions (running for more than 5 minutes)
      const fiveMinutesAgo = new Date();
      fiveMinutesAgo.setMinutes(fiveMinutesAgo.getMinutes() - 5);

      const { data: stuckExecutions, error: stuckError } = await supabase
        .from('workflow_executions')
        .select('id')
        .eq('status', 'running')
        .lt('started_at', fiveMinutesAgo.toISOString());

      if (stuckError) {
        console.warn('Could not check for stuck executions:', stuckError);
      }

      // Calculate health metrics
      const totalWorkflows = workflows?.length || 0;
      const activeWorkflows = workflows?.filter(w => w.is_active).length || 0;
      const pausedWorkflows = totalWorkflows - activeWorkflows;

      const totalMetrics = metrics?.reduce((acc, m) => ({
        total_executions: acc.total_executions + m.total_executions,
        successful_executions: acc.successful_executions + m.successful_executions,
        failed_executions: acc.failed_executions + m.failed_executions,
        total_execution_time: acc.total_execution_time + (m.average_execution_time_ms * m.total_executions),
      }), {
        total_executions: 0,
        successful_executions: 0,
        failed_executions: 0,
        total_execution_time: 0,
      }) || {
        total_executions: 0,
        successful_executions: 0,
        failed_executions: 0,
        total_execution_time: 0,
      };

      const successRate = totalMetrics.total_executions > 0 
        ? (totalMetrics.successful_executions / totalMetrics.total_executions) * 100 
        : 100;

      const averageExecutionTime = totalMetrics.total_executions > 0
        ? totalMetrics.total_execution_time / totalMetrics.total_executions
        : 0;

      setHealth({
        totalWorkflows,
        activeWorkflows,
        pausedWorkflows,
        recentExecutions: recentExecutions?.length || 0,
        successRate,
        averageExecutionTime,
        failedExecutions: totalMetrics.failed_executions,
        stuckExecutions: stuckExecutions?.length || 0,
        metrics: metrics || [],
      });

    } catch (error) {
      console.error('Error fetching workflow health:', error);
      toast({
        title: 'Error',
        description: 'Failed to load workflow health metrics',
        variant: 'destructive',
      });
    } finally {
      setLoading(false);
    }
  };

  const cleanupStuckExecutions = async () => {
    try {
      const { data, error } = await supabase.rpc('cleanup_workflow_executions');

      if (error) throw error;

      toast({
        title: 'Success',
        description: `Cleaned up ${data || 0} stuck executions`,
      });

      // Refresh health metrics
      await fetchWorkflowHealth();
    } catch (error) {
      console.error('Error cleaning up executions:', error);
      toast({
        title: 'Error',
        description: 'Failed to cleanup stuck executions',
        variant: 'destructive',
      });
    }
  };

  const getHealthStatus = (): 'healthy' | 'warning' | 'critical' => {
    if (!health) return 'warning';

    if (health.stuckExecutions > 0) return 'critical';
    if (health.successRate < 80) return 'critical';
    if (health.successRate < 95) return 'warning';
    if (health.activeWorkflows === 0 && health.totalWorkflows > 0) return 'warning';

    return 'healthy';
  };

  const getHealthColor = () => {
    const status = getHealthStatus();
    switch (status) {
      case 'healthy': return 'text-green-600';
      case 'warning': return 'text-yellow-600';
      case 'critical': return 'text-red-600';
      default: return 'text-gray-600';
    }
  };

  const getHealthIcon = () => {
    const status = getHealthStatus();
    switch (status) {
      case 'healthy': return 'âœ…';
      case 'warning': return 'âš ï¸';
      case 'critical': return 'ðŸš¨';
      default: return 'â“';
    }
  };

  useEffect(() => {
    fetchWorkflowHealth();
  }, []);

  return {
    health,
    loading,
    fetchWorkflowHealth,
    cleanupStuckExecutions,
    getHealthStatus,
    getHealthColor,
    getHealthIcon,
    refetch: fetchWorkflowHealth,
  };
}

import { useState, useEffect } from "react";
import { supabase } from "@/integrations/supabase/client";
import { useToast } from "@/hooks/use-toast";

interface WorkingHour {
  id?: string;
  day_of_week: number;
  enabled: boolean;
  start_time: string | null;
  end_time: string | null;
}

export function useWorkingHours() {
  const [workingHours, setWorkingHours] = useState<WorkingHour[]>([]);
  const [loading, setLoading] = useState(true);
  const { toast } = useToast();

  const fetchWorkingHours = async () => {
    try {
      setLoading(true);
      const { data: { user } } = await supabase.auth.getUser();

      if (!user) {
        setLoading(false);
        return;
      }

      const { data, error } = await supabase
        .from('working_hours')
        .select('*')
        .eq('user_id', user.id)
        .order('day_of_week');

      if (error) throw error;

      setWorkingHours(data || []);
    } catch (error) {
      console.error('Error fetching working hours:', error);
      toast({
        title: "Error",
        description: "Failed to load working hours",
        variant: "destructive",
      });
    } finally {
      setLoading(false);
    }
  };

  const updateWorkingHour = async (dayOfWeek: number, updates: Partial<WorkingHour>) => {
    try {
      const { data: { user } } = await supabase.auth.getUser();

      if (!user) throw new Error("No user found");

      const { data, error } = await supabase
        .from('working_hours')
        .update(updates)
        .eq('user_id', user.id)
        .eq('day_of_week', dayOfWeek)
        .select()
        .single();

      if (error) throw error;

      setWorkingHours(prev => 
        prev.map(wh => 
          wh.day_of_week === dayOfWeek ? { ...wh, ...updates } : wh
        )
      );

      return { success: true };
    } catch (error) {
      console.error('Error updating working hour:', error);
      toast({
        title: "Error",
        description: "Failed to update working hours",
        variant: "destructive",
      });
      return { success: false, error };
    }
  };

  useEffect(() => {
    fetchWorkingHours();
  }, []);

  return {
    workingHours,
    loading,
    updateWorkingHour,
    refetch: fetchWorkingHours
  };
}

import React, { useState, useCallback } from 'react';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { AlertTriangle, Save, X } from 'lucide-react';
import { FormField } from './FormField';
import { validateEntityForm, ValidationRule } from '@/utils/validationUtils';

export interface EntityFormField {
  key: string;
  type: 'text' | 'textarea' | 'email' | 'phone' | 'date' | 'time' | 'number' | 'select' | 'checkbox';
  label: string;
  placeholder?: string;
  required?: boolean;
  validation?: ValidationRule[];
  options?: Array<{ value: string; label: string }>;
  rows?: number; // for textarea
  min?: number; // for number/date
  max?: number; // for number/date
  step?: number; // for number
  disabled?: boolean;
  helpText?: string;
}

export interface EntityFormProps<T> {
  title?: string;
  fields: EntityFormField[];
  initialData?: Partial<T>;
  onSubmit: (data: T) => Promise<void>;
  onCancel?: () => void;
  submitLabel?: string;
  cancelLabel?: string;
  loading?: boolean;
  error?: string | null;
  className?: string;
  showCard?: boolean;
  validateOnChange?: boolean;
}

export function EntityForm<T extends Record<string, any>>({
  title,
  fields,
  initialData = {},
  onSubmit,
  onCancel,
  submitLabel = 'Save',
  cancelLabel = 'Cancel',
  loading = false,
  error = null,
  className,
  showCard = true,
  validateOnChange = false
}: EntityFormProps<T>) {
  const [formData, setFormData] = useState<Partial<T>>(initialData);
  const [fieldErrors, setFieldErrors] = useState<Record<string, string>>({});
  const [touched, setTouched] = useState<Record<string, boolean>>({});

  const handleFieldChange = useCallback((fieldKey: string, value: any) => {
    setFormData(prev => ({ ...prev, [fieldKey]: value }));
    setTouched(prev => ({ ...prev, [fieldKey]: true }));

    // Clear field error when user starts typing
    if (fieldErrors[fieldKey]) {
      setFieldErrors(prev => {
        const newErrors = { ...prev };
        delete newErrors[fieldKey];
        return newErrors;
      });
    }

    // Validate on change if enabled
    if (validateOnChange) {
      const field = fields.find(f => f.key === fieldKey);
      if (field && field.validation) {
        const fieldError = validateEntityForm({ [fieldKey]: value }, [field]);
        if (fieldError[fieldKey]) {
          setFieldErrors(prev => ({ ...prev, [fieldKey]: fieldError[fieldKey] }));
        }
      }
    }
  }, [fieldErrors, fields, validateOnChange]);

  const handleSubmit = useCallback(async (e: React.FormEvent) => {
    e.preventDefault();

    // Validate all fields
    const errors = validateEntityForm(formData, fields);
    setFieldErrors(errors);

    // Mark all fields as touched to show errors
    const touchedFields = fields.reduce((acc, field) => {
      acc[field.key] = true;
      return acc;
    }, {} as Record<string, boolean>);
    setTouched(touchedFields);

    // Don't submit if there are validation errors
    if (Object.keys(errors).length > 0) {
      return;
    }

    try {
      await onSubmit(formData as T);
    } catch (error) {
      // Error is handled by the parent component
      console.error('Form submission error:', error);
    }
  }, [formData, fields, onSubmit]);

  const handleCancel = useCallback(() => {
    setFormData(initialData);
    setFieldErrors({});
    setTouched({});
    onCancel?.();
  }, [initialData, onCancel]);

  const isValid = Object.keys(fieldErrors).length === 0;
  const hasRequiredFields = fields.some(field => field.required);
  const canSubmit = hasRequiredFields ? isValid && Object.keys(touched).length > 0 : isValid;

  const content = (
    <form onSubmit={handleSubmit} className={className}>
      {title && !showCard && <h2 className="text-xl font-semibold mb-6">{title}</h2>}

      {error && (
        <Alert variant="destructive" className="mb-6">
          <AlertTriangle className="h-4 w-4" />
          <AlertDescription>{error}</AlertDescription>
        </Alert>
      )}

      <div className="space-y-4">
        {fields.map((field) => (
          <FormField
            key={field.key}
            field={field}
            value={formData[field.key]}
            onChange={(value) => handleFieldChange(field.key, value)}
            error={touched[field.key] ? fieldErrors[field.key] : undefined}
            disabled={loading || field.disabled}
          />
        ))}
      </div>

      <div className="flex flex-col sm:flex-row gap-3 pt-6">
        <Button
          type="submit"
          disabled={loading || !canSubmit}
          className="flex-1 sm:flex-none"
        >
          <Save className="h-4 w-4 mr-2" />
          {loading ? 'Saving...' : submitLabel}
        </Button>

        {onCancel && (
          <Button
            type="button"
            variant="outline"
            onClick={handleCancel}
            disabled={loading}
            className="flex-1 sm:flex-none"
          >
            <X className="h-4 w-4 mr-2" />
            {cancelLabel}
          </Button>
        )}
      </div>
    </form>
  );

  if (!showCard) {
    return content;
  }

  return (
    <Card>
      {title && (
        <CardHeader>
          <CardTitle>{title}</CardTitle>
        </CardHeader>
      )}
      <CardContent>
        {content}
      </CardContent>
    </Card>
  );
}

import React from 'react';
import { Input } from '@/components/ui/input';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Checkbox } from '@/components/ui/checkbox';
import { Label } from '@/components/ui/label';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { HelpCircle } from 'lucide-react';
import { Tooltip, TooltipContent, TooltipProvider, TooltipTrigger } from '@/components/ui/tooltip';
import { EntityFormField } from './EntityForm';

interface FormFieldProps {
  field: EntityFormField;
  value: any;
  onChange: (value: any) => void;
  error?: string;
  disabled?: boolean;
}

export function FormField({
  field,
  value,
  onChange,
  error,
  disabled = false
}: FormFieldProps) {
  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const newValue = field.type === 'number' ? parseFloat(e.target.value) : e.target.value;
    onChange(newValue);
  };

  const handleSelectChange = (newValue: string) => {
    onChange(newValue);
  };

  const handleCheckboxChange = (checked: boolean) => {
    onChange(checked);
  };

  const renderInput = () => {
    const baseProps = {
      id: field.key,
      value: value || '',
      disabled,
      className: error ? 'border-destructive' : '',
    };

    switch (field.type) {
      case 'textarea':
        return (
          <Textarea
            {...baseProps}
            placeholder={field.placeholder}
            rows={field.rows || 3}
            onChange={handleInputChange}
          />
        );

      case 'select':
        return (
          <Select value={value || ''} onValueChange={handleSelectChange} disabled={disabled}>
            <SelectTrigger className={error ? 'border-destructive' : ''}>
              <SelectValue placeholder={field.placeholder} />
            </SelectTrigger>
            <SelectContent>
              {field.options?.map((option) => (
                <SelectItem key={option.value} value={option.value}>
                  {option.label}
                </SelectItem>
              ))}
            </SelectContent>
          </Select>
        );

      case 'checkbox':
        return (
          <div className="flex items-center space-x-2">
            <Checkbox
              id={field.key}
              checked={value || false}
              onCheckedChange={handleCheckboxChange}
              disabled={disabled}
            />
            <Label
              htmlFor={field.key}
              className={`text-sm font-medium leading-none peer-disabled:cursor-not-allowed peer-disabled:opacity-70 ${
                error ? 'text-destructive' : ''
              }`}
            >
              {field.label}
              {field.required && <span className="text-destructive ml-1">*</span>}
            </Label>
          </div>
        );

      case 'number':
        return (
          <Input
            {...baseProps}
            type="number"
            placeholder={field.placeholder}
            min={field.min}
            max={field.max}
            step={field.step}
            onChange={handleInputChange}
          />
        );

      case 'date':
        return (
          <Input
            {...baseProps}
            type="date"
            min={field.min}
            max={field.max}
            onChange={handleInputChange}
          />
        );

      case 'time':
        return (
          <Input
            {...baseProps}
            type="time"
            step={field.step}
            onChange={handleInputChange}
          />
        );

      case 'email':
        return (
          <Input
            {...baseProps}
            type="email"
            placeholder={field.placeholder}
            onChange={handleInputChange}
          />
        );

      case 'phone':
        return (
          <Input
            {...baseProps}
            type="tel"
            placeholder={field.placeholder}
            onChange={handleInputChange}
          />
        );

      default:
        return (
          <Input
            {...baseProps}
            type="text"
            placeholder={field.placeholder}
            onChange={handleInputChange}
          />
        );
    }
  };

  if (field.type === 'checkbox') {
    return (
      <div className="space-y-2">
        {renderInput()}
        {field.helpText && (
          <p className="text-xs text-muted-foreground">{field.helpText}</p>
        )}
        {error && (
          <Alert variant="destructive">
            <AlertDescription className="text-xs">{error}</AlertDescription>
          </Alert>
        )}
      </div>
    );
  }

  return (
    <div className="space-y-2">
      <div className="flex items-center gap-2">
        <Label
          htmlFor={field.key}
          className={`text-sm font-medium ${error ? 'text-destructive' : ''}`}
        >
          {field.label}
          {field.required && <span className="text-destructive ml-1">*</span>}
        </Label>

        {field.helpText && (
          <TooltipProvider>
            <Tooltip>
              <TooltipTrigger asChild>
                <HelpCircle className="h-4 w-4 text-muted-foreground cursor-help" />
              </TooltipTrigger>
              <TooltipContent>
                <p className="max-w-xs text-xs">{field.helpText}</p>
              </TooltipContent>
            </Tooltip>
          </TooltipProvider>
        )}
      </div>

      {renderInput()}

      {error && (
        <Alert variant="destructive">
          <AlertDescription className="text-xs">{error}</AlertDescription>
        </Alert>
      )}
    </div>
  );
}

import React, { useState, useEffect, useRef, useMemo, useCallback } from "react";
import { supabase } from "@/integrations/supabase/client";
import { Input } from "@/components/ui/input";
import { Search, X } from "lucide-react";
import { SearchLoadingSkeleton } from "@/components/ui/loading-presets";
import { useNavigate } from "react-router-dom";
import { toast } from "@/hooks/use-toast";
import { useKeyboardNavigation, useScreenReader } from '@/hooks/useAccessibility';
import { performanceMonitor } from '@/utils/performance';

interface SearchResult {
  id: string;
  leadId?: string;
  leadName?: string;
  projectId?: string;
  projectName?: string;
  type: 'lead' | 'note' | 'reminder' | 'session' | 'project';
  matchedContent: string;
  status: string;
  icon: React.ReactNode;
}

/**
 * Accessible Global Search with keyboard navigation and screen reader support
 */
export const AccessibleGlobalSearch = React.memo(() => {
  const [query, setQuery] = useState("");
  const [results, setResults] = useState<SearchResult[]>([]);
  const [isOpen, setIsOpen] = useState(false);
  const [loading, setLoading] = useState(false);

  const searchRef = useRef<HTMLDivElement>(null);
  const inputRef = useRef<HTMLInputElement>(null);
  const navigate = useNavigate();

  // Accessibility hooks
  const { announce } = useScreenReader();
  const { activeIndex, handleKeyDown, containerRef } = useKeyboardNavigation(
    results,
    useCallback((result: SearchResult) => {
      handleResultClick(result);
    }, [])
  );

  // Memoized search function for performance
  const performSearch = useCallback(async (searchQuery: string) => {
    performanceMonitor.startTiming('GlobalSearch.performSearch');
    setLoading(true);

    try {
      // Simplified search for demonstration - would include full search logic
      const { data: leads } = await supabase
        .from('leads')
        .select('id, name, email, phone, status, status_id')
        .or(`name.ilike.%${searchQuery}%,email.ilike.%${searchQuery}%`);

      const searchResults: SearchResult[] = (leads || []).map(lead => ({
        id: lead.id,
        leadId: lead.id,
        leadName: lead.name,
        type: 'lead' as const,
        matchedContent: lead.email ? `Email: ${lead.email}` : '',
        status: lead.status,
        icon: <Search className="h-4 w-4" />
      }));

      setResults(searchResults);
      setIsOpen(true);

      // Announce results to screen readers
      const resultCount = searchResults.length;
      announce(
        resultCount === 0 
          ? "No search results found"
          : `${resultCount} search ${resultCount === 1 ? 'result' : 'results'} found`,
        'polite'
      );

    } catch (error: any) {
      toast({
        title: "Search error",
        description: error.message,
        variant: "destructive"
      });
      announce("Search failed", 'assertive');
    } finally {
      setLoading(false);
      performanceMonitor.endTiming('GlobalSearch.performSearch');
    }
  }, [announce]);

  // Debounced search effect
  useEffect(() => {
    const delayedSearch = setTimeout(() => {
      if (query.trim().length > 2) {
        performSearch(query.trim());
      } else {
        setResults([]);
        setIsOpen(false);
      }
    }, 300);

    return () => clearTimeout(delayedSearch);
  }, [query, performSearch]);

  const handleResultClick = useCallback((result: SearchResult) => {
    if (result.type === 'project' && result.leadId) {
      navigate(`/leads/${result.leadId}`);
    } else if (result.leadId) {
      navigate(`/leads/${result.leadId}`);
    }

    // Clear search and announce navigation
    setQuery("");
    setResults([]);
    setIsOpen(false);
    announce(`Navigating to ${result.leadName || result.projectName}`, 'polite');
  }, [navigate, announce]);

  const handleClearSearch = useCallback(() => {
    setQuery("");
    setResults([]);
    setIsOpen(false);
    announce("Search cleared", 'polite');
    // Keep focus in the search bar
    setTimeout(() => inputRef.current?.focus(), 0);
  }, [announce]);

  // Combined keyboard handler
  const handleInputKeyDown = useCallback((e: React.KeyboardEvent) => {
    if (e.key === 'Escape') {
      setIsOpen(false);
      announce("Search closed", 'polite');
      return;
    }

    if (isOpen && results.length > 0) {
      handleKeyDown(e);
    }
  }, [isOpen, results.length, handleKeyDown, announce]);

  return (
    <div className="relative w-full min-w-0" ref={searchRef}>
      <div className="relative">
        <Search 
          className="absolute left-3 top-1/2 h-4 w-4 -translate-y-1/2 text-muted-foreground z-10 pointer-events-none" 
          aria-hidden="true"
        />
        <Input
          ref={inputRef}
          placeholder="Search everything..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
          onKeyDown={handleInputKeyDown}
          onFocus={() => {
            if (results.length > 0) setIsOpen(true);
          }}
          className="pl-10 pr-10 w-full truncate"
          role="combobox"
          aria-expanded={isOpen}
          aria-haspopup="listbox"
          aria-autocomplete="list"
          aria-describedby="search-instructions"
        />

        {/* Screen reader instructions */}
        <div id="search-instructions" className="sr-only">
          Type to search. Use arrow keys to navigate results, Enter to select, Escape to close.
        </div>

        {query && (
          <button
            onClick={handleClearSearch}
            className="absolute right-3 top-1/2 h-4 w-4 -translate-y-1/2 text-muted-foreground hover:text-foreground transition-colors"
            aria-label="Clear search"
            tabIndex={0}
          >
            <X className="h-4 w-4" />
          </button>
        )}
      </div>

      {isOpen && (
        <div
          className="absolute top-full mt-2 left-0 right-0 bg-background border border-slate-200 dark:border-slate-700 rounded-lg shadow-xl backdrop-blur-sm z-[9999] max-h-96 overflow-y-auto"
          role="listbox"
          aria-label="Search results"
        >
          {loading ? (
            <SearchLoadingSkeleton rows={3} />
          ) : results.length === 0 ? (
            <div className="p-6 text-center text-muted-foreground">
              <Search className="h-10 w-10 mx-auto mb-3 opacity-50" aria-hidden="true" />
              <p className="text-sm font-medium" role="status">No results found</p>
              <p className="text-xs mt-2 opacity-75">Try different keywords or check your spelling</p>
            </div>
          ) : (
            <div className="py-3">
              {results.map((result, index) => (
                <button
                  key={`${result.type}-${result.id}`}
                  id={`search-result-${index}`}
                  data-index={index}
                  onClick={() => handleResultClick(result)}
                  className={`w-full text-left px-4 py-3 transition-colors group ${
                    index === activeIndex ? 'bg-muted/50' : 'hover:bg-muted/30'
                  }`}
                  role="option"
                  aria-selected={index === activeIndex}
                  aria-describedby={`result-description-${index}`}
                >
                  <div className="flex items-start gap-3">
                    <div className="flex-shrink-0 mt-0.5 text-muted-foreground" aria-hidden="true">
                      {result.icon}
                    </div>
                    <div className="flex-1 min-w-0">
                      <p className="font-medium text-sm truncate">
                        {result.type === 'project' ? result.projectName : result.leadName}
                      </p>
                      {result.matchedContent && (
                        <p 
                          id={`result-description-${index}`}
                          className="text-xs text-muted-foreground truncate mt-1"
                        >
                          {result.matchedContent}
                        </p>
                      )}
                    </div>
                  </div>
                </button>
              ))}
            </div>
          )}
        </div>
      )}
    </div>
  );
});

AccessibleGlobalSearch.displayName = 'AccessibleGlobalSearch';

import React, { useState, useEffect, useRef, useMemo, useCallback } from 'react';
import { performanceMonitor } from '@/utils/performance';

interface VirtualizedListProps<T> {
  items: T[];
  renderItem: (item: T, index: number) => React.ReactNode;
  itemHeight: number;
  containerHeight: number;
  overscan?: number;
  className?: string;
  'aria-label'?: string;
}

/**
 * High-performance virtualized list component
 * Only renders items that are currently visible
 */
export function VirtualizedList<T>({
  items,
  renderItem,
  itemHeight,
  containerHeight,
  overscan = 5,
  className = '',
  'aria-label': ariaLabel = 'Virtual list'
}: VirtualizedListProps<T>) {
  const [scrollTop, setScrollTop] = useState(0);
  const scrollElementRef = useRef<HTMLDivElement>(null);

  // Calculate visible range with memoization
  const { startIndex, endIndex, visibleItems } = useMemo(() => {
    performanceMonitor.startTiming('VirtualizedList.calculateRange');

    const start = Math.max(0, Math.floor(scrollTop / itemHeight) - overscan);
    const end = Math.min(
      items.length - 1,
      Math.ceil((scrollTop + containerHeight) / itemHeight) + overscan
    );

    const visible = items.slice(start, end + 1);

    performanceMonitor.endTiming('VirtualizedList.calculateRange');

    return {
      startIndex: start,
      endIndex: end,
      visibleItems: visible
    };
  }, [scrollTop, itemHeight, containerHeight, overscan, items]);

  // Optimized scroll handler with requestAnimationFrame
  const handleScroll = useCallback((e: React.UIEvent<HTMLDivElement>) => {
    const target = e.currentTarget;
    requestAnimationFrame(() => {
      setScrollTop(target.scrollTop);
    });
  }, []);

  // Keyboard navigation support
  const handleKeyDown = useCallback((e: React.KeyboardEvent) => {
    if (!scrollElementRef.current) return;

    const container = scrollElementRef.current;
    let newScrollTop = scrollTop;

    switch (e.key) {
      case 'ArrowDown':
        e.preventDefault();
        newScrollTop = Math.min(
          (items.length - 1) * itemHeight,
          scrollTop + itemHeight
        );
        break;
      case 'ArrowUp':
        e.preventDefault();
        newScrollTop = Math.max(0, scrollTop - itemHeight);
        break;
      case 'PageDown':
        e.preventDefault();
        newScrollTop = Math.min(
          (items.length - 1) * itemHeight,
          scrollTop + containerHeight
        );
        break;
      case 'PageUp':
        e.preventDefault();
        newScrollTop = Math.max(0, scrollTop - containerHeight);
        break;
      case 'Home':
        if (e.ctrlKey) {
          e.preventDefault();
          newScrollTop = 0;
        }
        break;
      case 'End':
        if (e.ctrlKey) {
          e.preventDefault();
          newScrollTop = (items.length - 1) * itemHeight;
        }
        break;
    }

    if (newScrollTop !== scrollTop) {
      container.scrollTop = newScrollTop;
    }
  }, [scrollTop, itemHeight, containerHeight, items.length]);

  // Total height for scrollbar
  const totalHeight = items.length * itemHeight;

  return (
    <div
      ref={scrollElementRef}
      className={`overflow-auto ${className}`}
      style={{ height: containerHeight }}
      onScroll={handleScroll}
      onKeyDown={handleKeyDown}
      tabIndex={0}
      role="list"
      aria-label={ariaLabel}
      aria-rowcount={items.length}
    >
      <div style={{ height: totalHeight, position: 'relative' }}>
        <div
          style={{
            transform: `translateY(${startIndex * itemHeight}px)`,
            position: 'absolute',
            width: '100%'
          }}
        >
          {visibleItems.map((item, index) => (
            <div
              key={startIndex + index}
              style={{ height: itemHeight }}
              role="listitem"
              aria-rowindex={startIndex + index + 1}
            >
              {renderItem(item, startIndex + index)}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

/**
 * Hook for managing virtualized list state
 */
export function useVirtualizedList<T>(
  items: T[],
  itemHeight: number,
  containerHeight: number
) {
  const [scrollTop, setScrollTop] = useState(0);

  const scrollToIndex = useCallback((index: number) => {
    const targetScrollTop = Math.max(0, Math.min(
      index * itemHeight,
      (items.length - 1) * itemHeight
    ));
    setScrollTop(targetScrollTop);
  }, [itemHeight, items.length]);

  const scrollToTop = useCallback(() => {
    setScrollTop(0);
  }, []);

  const scrollToBottom = useCallback(() => {
    setScrollTop((items.length - 1) * itemHeight);
  }, [items.length, itemHeight]);

  return {
    scrollTop,
    scrollToIndex,
    scrollToTop,
    scrollToBottom
  };
}

import React, { useState, useEffect } from 'react';
import { Image as ImageIcon, Trash2, Edit3, Copy } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { useToast } from '@/hooks/use-toast';
import { supabase } from '@/integrations/supabase/client';
import { useOrganization } from '@/contexts/OrganizationContext';
import { useMessagesTranslation } from '@/hooks/useTypedTranslation';
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from '@/components/ui/alert-dialog';
import { useTranslation } from 'react-i18next';

interface TemplateAsset {
  id: string;
  file_name: string;
  file_path: string;
  content_type: string;
  file_size: number;
  alt_text: string | null;
  created_at: string;
}

interface ImageManagerProps {
  onImageSelect: (imageUrl: string, altText?: string) => void;
  templateId?: string;
  className?: string;
}

export function ImageManager({ onImageSelect, templateId, className }: ImageManagerProps) {
  const [assets, setAssets] = useState<TemplateAsset[]>([]);
  const [loading, setLoading] = useState(true);
  const [editingAlt, setEditingAlt] = useState<string | null>(null);
  const [altText, setAltText] = useState('');
  const { toast } = useToast();
  const { t: tMessages } = useMessagesTranslation();
  const { activeOrganization } = useOrganization();
  const { t } = useTranslation("pages");
  const { t: tCommon } = useTranslation("common");

  const loadAssets = async () => {
    if (!activeOrganization?.id) return;

    try {
      setLoading(true);
      const { data, error } = await supabase
        .from('template_assets')
        .select('*')
        .eq('organization_id', activeOrganization.id)
        .order('created_at', { ascending: false });

      if (error) throw error;
      setAssets(data || []);
    } catch (error) {
      console.error('Error loading assets:', error);
      toast({
        title: tCommon('toast.error'),
        description: t('templateBuilder.imageManager.messages.loadError'),
        variant: 'destructive',
      });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadAssets();
  }, [activeOrganization?.id]);

  const deleteAsset = async (asset: TemplateAsset) => {
    try {
      // Delete from storage
      const { error: storageError } = await supabase.storage
        .from('template-images')
        .remove([asset.file_path]);

      if (storageError) throw storageError;

      // Delete from database
      const { error: dbError } = await supabase
        .from('template_assets')
        .delete()
        .eq('id', asset.id);

      if (dbError) throw dbError;

      setAssets(prev => prev.filter(a => a.id !== asset.id));

      toast({
        title: tCommon('toast.success'),
        description: t('templateBuilder.imageManager.messages.deleteSuccess'),
      });
    } catch (error) {
      console.error('Error deleting asset:', error);
      toast({
        title: tCommon('toast.error'),
        description: t('templateBuilder.imageManager.messages.deleteError'),
        variant: 'destructive',
      });
    }
  };

  const updateAltText = async (assetId: string, newAltText: string) => {
    try {
      const { error } = await supabase
        .from('template_assets')
        .update({ alt_text: newAltText })
        .eq('id', assetId);

      if (error) throw error;

      setAssets(prev => prev.map(asset => 
        asset.id === assetId ? { ...asset, alt_text: newAltText } : asset
      ));

      toast({
        title: tCommon('toast.success'),
        description: t('templateBuilder.imageManager.messages.altSuccess'),
      });
    } catch (error) {
      console.error('Error updating alt text:', error);
      toast({
        title: tCommon('toast.error'),
        description: t('templateBuilder.imageManager.messages.altError'),
        variant: 'destructive',
      });
    }
  };

  const getImageUrl = (filePath: string) => {
    const { data: { publicUrl } } = supabase.storage
      .from('template-images')
      .getPublicUrl(filePath);
    return publicUrl;
  };

  const copyImageUrl = (filePath: string) => {
    const url = getImageUrl(filePath);
    navigator.clipboard.writeText(url);
    toast({
      title: tCommon('toast.success'),
      description: t('templateBuilder.imageManager.messages.copySuccess'),
    });
  };

  const formatFileSize = (bytes: number) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  if (loading) {
    return (
      <div className={`space-y-4 ${className}`}>
        <div className="flex items-center gap-2">
          <ImageIcon className="w-5 h-5" />
          <h3 className="font-medium">{t('templateBuilder.imageManager.loading')}</h3>
        </div>
        <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
          {[...Array(6)].map((_, i) => (
            <div key={i} className="aspect-square bg-muted animate-pulse rounded-lg" />
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className={`space-y-4 ${className}`}>
      <div className="flex items-center gap-2">
        <ImageIcon className="w-5 h-5" />
        <h3 className="font-medium">{t('templateBuilder.imageManager.titleWithCount', { count: assets.length })}</h3>
      </div>

      {assets.length === 0 ? (
        <div className="text-center py-8 text-muted-foreground">
          <ImageIcon className="w-12 h-12 mx-auto mb-3 opacity-50" />
          <p>{t('templateBuilder.imageManager.empty.title')}</p>
          <p className="text-sm">{t('templateBuilder.imageManager.empty.description')}</p>
        </div>
      ) : (
        <div className="grid grid-cols-2 md:grid-cols-3 gap-4">
          {assets.map((asset) => (
            <div key={asset.id} className="group relative bg-card border rounded-lg overflow-hidden">
              <div className="aspect-square relative">
                <img
                  src={getImageUrl(asset.file_path)}
                  alt={asset.alt_text || asset.file_name}
                  className="w-full h-full object-cover cursor-pointer hover:scale-105 transition-transform"
                  onClick={() => onImageSelect(getImageUrl(asset.file_path), asset.alt_text || undefined)}
                />

                {/* Overlay with actions */}
                <div className="absolute inset-0 bg-black/50 opacity-0 group-hover:opacity-100 transition-opacity flex items-center justify-center gap-2">
                  <Button
                    size="sm"
                    variant="secondary"
                    onClick={() => onImageSelect(getImageUrl(asset.file_path), asset.alt_text || undefined)}
                  >
                    {t('templateBuilder.imageManager.actions.insert')}
                  </Button>
                  <Button
                    size="sm"
                    variant="outline"
                    onClick={() => copyImageUrl(asset.file_path)}
                  >
                    <Copy className="w-4 h-4" />
                  </Button>
                  <AlertDialog>
                    <AlertDialogTrigger asChild>
                      <Button size="sm" variant="destructive">
                        <Trash2 className="w-4 h-4" />
                      </Button>
                    </AlertDialogTrigger>
                    <AlertDialogContent>
                      <AlertDialogHeader>
                        <AlertDialogTitle>{t('templateBuilder.imageManager.actions.deleteTitle')}</AlertDialogTitle>
                        <AlertDialogDescription>
                          {tMessages('confirm.deleteImage')} {tMessages('confirm.cannotUndo')}
                        </AlertDialogDescription>
                      </AlertDialogHeader>
                      <AlertDialogFooter>
                        <AlertDialogCancel>{tCommon('buttons.cancel')}</AlertDialogCancel>
                        <AlertDialogAction onClick={() => deleteAsset(asset)}>
                          {tCommon('buttons.delete')}
                        </AlertDialogAction>
                      </AlertDialogFooter>
                    </AlertDialogContent>
                  </AlertDialog>
                </div>
              </div>

              {/* Image info */}
              <div className="p-3 space-y-2">
                <div className="flex items-center justify-between">
                  <p className="text-sm font-medium truncate">{asset.file_name}</p>
                  <Button
                    size="sm"
                    variant="ghost"
                    onClick={() => {
                      setEditingAlt(asset.id);
                      setAltText(asset.alt_text || '');
                    }}
                  >
                    <Edit3 className="w-3 h-3" />
                  </Button>
                </div>

                {editingAlt === asset.id ? (
                  <div className="flex gap-1">
                    <Input
                      value={altText}
                      onChange={(e) => setAltText(e.target.value)}
                      placeholder={t('templateBuilder.imageManager.actions.altPlaceholder')}
                      className="text-xs h-7"
                      onKeyDown={(e) => {
                        if (e.key === 'Enter') {
                          updateAltText(asset.id, altText);
                          setEditingAlt(null);
                        }
                        if (e.key === 'Escape') {
                          setEditingAlt(null);
                        }
                      }}
                      autoFocus
                    />
                    <Button
                      size="sm"
                      onClick={() => {
                        updateAltText(asset.id, altText);
                        setEditingAlt(null);
                      }}
                      className="h-7 px-2"
                    >
                      {tCommon('buttons.save')}
                    </Button>
                  </div>
                ) : (
                  <p className="text-xs text-muted-foreground truncate">
                    {asset.alt_text || t('templateBuilder.imageManager.messages.noAltText')}
                  </p>
                )}

                <p className="text-xs text-muted-foreground">
                  {formatFileSize(asset.file_size)}
                </p>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

import { useState } from "react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Plus, GripVertical, Eye, EyeOff, Trash2 } from "lucide-react";
import { TemplateBlock, BlockData, TextBlockData, SessionDetailsBlockData, CTABlockData, ImageBlockData, FooterBlockData } from "@/types/templateBuilder";
import { BlockEditor } from "./BlockEditor";
import { AddBlockSheet } from "./AddBlockSheet";
import { cn } from "@/lib/utils";
import { DragDropContext, Droppable, Draggable, DropResult } from "@hello-pangea/dnd";
import { useTranslation } from 'react-i18next';

interface TemplateEditorProps {
  blocks: TemplateBlock[];
  onBlocksChange: (blocks: TemplateBlock[]) => void;
}

export function TemplateEditor({ blocks, onBlocksChange }: TemplateEditorProps) {
  const { t } = useTranslation('pages');
  const [activeBlock, setActiveBlock] = useState<string | null>(null);
  const [showAddBlock, setShowAddBlock] = useState(false);

  const getBlockTitle = (type: TemplateBlock["type"]) => {
    switch (type) {
      case "text": return t('templateBuilder.blockTitles.text');
      case "session-details": return t('templateBuilder.blockTitles.sessionDetails');
      case "cta": return t('templateBuilder.blockTitles.cta');
      case "image": return t('templateBuilder.blockTitles.image');
      case "footer": return t('templateBuilder.blockTitles.footer');
      case "divider": return t('templateBuilder.blockTitles.divider');
      case "columns": return t('templateBuilder.blockTitles.columns');
      case "social-links": return t('templateBuilder.blockTitles.socialLinks');
      case "header": return t('templateBuilder.blockTitles.header');
      case "raw-html": return t('templateBuilder.blockTitles.rawHtml');
      default: return t('templateBuilder.blockTitles.unknown');
    }
  };

  const addBlock = (type: TemplateBlock["type"]) => {
    const newBlock: TemplateBlock = {
      id: `block-${Date.now()}`,
      type,
      data: getDefaultBlockData(type),
      visible: true,
      order: blocks.length,
    };

    onBlocksChange([...blocks, newBlock]);
    setActiveBlock(newBlock.id);
    setShowAddBlock(false);
  };

  const updateBlock = (blockId: string, data: any) => {
    onBlocksChange(
      blocks.map(block => 
        block.id === blockId ? { ...block, data } : block
      )
    );
  };

  const toggleBlockVisibility = (blockId: string) => {
    onBlocksChange(
      blocks.map(block => 
        block.id === blockId ? { ...block, visible: !block.visible } : block
      )
    );
  };

  const removeBlock = (blockId: string) => {
    onBlocksChange(blocks.filter(block => block.id !== blockId));
    if (activeBlock === blockId) {
      setActiveBlock(null);
    }
  };

  const moveBlock = (blockId: string, direction: "up" | "down") => {
    const blockIndex = blocks.findIndex(b => b.id === blockId);
    if (blockIndex === -1) return;

    const newIndex = direction === "up" ? blockIndex - 1 : blockIndex + 1;
    if (newIndex < 0 || newIndex >= blocks.length) return;

    const newBlocks = [...blocks];
    [newBlocks[blockIndex], newBlocks[newIndex]] = [newBlocks[newIndex], newBlocks[blockIndex]];

    // Update order values
    newBlocks.forEach((block, index) => {
      block.order = index;
    });

    onBlocksChange(newBlocks);
  };

  const onDragEnd = (result: DropResult) => {
    if (!result.destination) return;

    const items = Array.from(blocks);
    const [reorderedItem] = items.splice(result.source.index, 1);
    items.splice(result.destination.index, 0, reorderedItem);

    // Update order values
    const updatedItems = items.map((item, index) => ({
      ...item,
      order: index
    }));

    onBlocksChange(updatedItems);
  };

  return (
    <div className="h-full flex flex-col">
      {/* Header */}
      <div className="border-b px-6 py-4">
        <h2 className="font-semibold">{t('templateBuilder.editor.title')}</h2>
        <p className="text-sm text-muted-foreground">{t('templateBuilder.editor.description')}</p>
      </div>

      {/* Blocks List */}
      <div className="flex-1 overflow-y-auto p-6 space-y-4">
        <DragDropContext onDragEnd={onDragEnd}>
          <Droppable droppableId="template-blocks">
            {(provided) => (
              <div
                {...provided.droppableProps}
                ref={provided.innerRef}
                className="space-y-4"
              >
                {blocks.map((block, index) => (
                  <Draggable key={block.id} draggableId={block.id} index={index}>
                    {(provided, snapshot) => (
                      <Card
                        ref={provided.innerRef}
                        {...provided.draggableProps}
                        className={cn(
                          "cursor-pointer transition-all duration-200",
                          activeBlock === block.id && "ring-2 ring-primary",
                          !block.visible && "opacity-50",
                          snapshot.isDragging && "shadow-lg"
                        )}
                        onClick={() => setActiveBlock(activeBlock === block.id ? null : block.id)}
                      >
                        <CardHeader className="pb-2">
                          <div className="flex items-center justify-between">
                            <CardTitle className="text-sm flex items-center gap-2">
                              <div 
                                {...provided.dragHandleProps}
                                className="cursor-grab active:cursor-grabbing"
                              >
                                <GripVertical className="h-4 w-4 text-muted-foreground" />
                              </div>
                              {getBlockTitle(block.type)}
                            </CardTitle>
                            <div className="flex items-center gap-1">
                              <Button
                                size="sm"
                                variant="ghost"
                                onClick={(e) => {
                                  e.stopPropagation();
                                  toggleBlockVisibility(block.id);
                                }}
                              >
                                {block.visible ? (
                                  <Eye className="h-3 w-3" />
                                ) : (
                                  <EyeOff className="h-3 w-3" />
                                )}
                              </Button>
                              <Button
                                size="sm"
                                variant="ghost"
                                className="text-destructive hover:text-destructive hover:bg-destructive/10"
                                onClick={(e) => {
                                  e.stopPropagation();
                                  removeBlock(block.id);
                                }}
                              >
                                <Trash2 className="h-3 w-3" />
                              </Button>
                            </div>
                          </div>
                        </CardHeader>
                        {activeBlock === block.id && (
                          <CardContent className="pt-0 overflow-hidden animate-in slide-in-from-top-2 duration-200">
                            <BlockEditor
                              block={block}
                              onUpdate={(data) => updateBlock(block.id, data)}
                              onRemove={() => removeBlock(block.id)}
                              onMoveUp={() => moveBlock(block.id, "up")}
                              onMoveDown={() => moveBlock(block.id, "down")}
                              canMoveUp={index > 0}
                              canMoveDown={index < blocks.length - 1}
                            />
                          </CardContent>
                        )}
                      </Card>
                    )}
                  </Draggable>
                ))}
                {provided.placeholder}
              </div>
            )}
          </Droppable>
        </DragDropContext>

        {/* Add Block Button */}
        <Button
          variant="outline"
          className="w-full h-12 border-dashed"
          onClick={() => setShowAddBlock(true)}
        >
          <Plus className="h-4 w-4 mr-2" />
          {t('templateBuilder.editor.addBlock')}
        </Button>
      </div>

      {/* Add Block Sheet */}
      <AddBlockSheet
        open={showAddBlock}
        onOpenChange={setShowAddBlock}
        onAddBlock={addBlock}
      />
    </div>
  );
}

function getDefaultBlockData(type: TemplateBlock["type"]): BlockData {
  switch (type) {
    case "text":
      return {
        content: "Enter your text here...",
        formatting: {
          fontSize: "p" as const,
          fontFamily: "Arial",
          alignment: "left" as const,
        },
      } as TextBlockData;
    case "session-details":
      return {
        showDate: true,
        showTime: true,
        showLocation: true,
        showNotes: false,
      } as SessionDetailsBlockData;
    case "cta":
      return {
        text: "Book Now",
        variant: "primary" as const,
      } as CTABlockData;
    case "image":
      return {
        placeholder: true,
        alt: "Image",
      } as ImageBlockData;
    case "footer":
      return {
        showLogo: true,
        showStudioName: true,
        showContactInfo: true,
      } as FooterBlockData;
    case "divider":
      return {
        style: "line" as const,
        color: "#e5e5e5",
      };
    case "columns":
      return {
        columns: 2,
        content: ["Column 1 content...", "Column 2 content..."],
      };
    case "social-links":
      return {
        channelVisibility: {},
      };
    case "header":
      return {
        showLogo: true,
        tagline: "Professional Photography",
        backgroundColor: "#ffffff",
      };
    case "raw-html":
      return {
        html: "<div>Your custom HTML here...</div>",
        sanitized: false,
      };
    default:
      return {} as any;
  }
}

