interface ProjectDetailState {
  project: ProjectWithDetails | null;
  lead: any | null;
  projectType: any | null;
  isArchived: boolean;
}

export default function ProjectDetailRefactored() {
  const { t } = useTranslation("pages");
  const { id } = useParams<{ id: string }>();
  const navigate = useNavigate();

  const [isEditing, setIsEditing] = useState(false);
  const [editName, setEditName] = useState("");
  const [editDescription, setEditDescription] = useState("");
  const [editProjectTypeId, setEditProjectTypeId] = useState("");
  const [showDeleteDialog, setShowDeleteDialog] = useState(false);

  // Services
  const projectService = useMemo(() => new ProjectService(), []);
  const { executeAction, getActionState } = useEntityActions();

  // Data state management
  const [projectDetailData, setProjectDetailData] = useState<ProjectDetailState>({
    project: null,
    lead: null, 
    projectType: null,
    isArchived: false
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const fetchProjectData = useCallback(async () => {
    if (!id) return;

    try {
      setLoading(true);
      setError(null);
      const project = await projectService.fetchProjectById(id);
      const lead = project.lead_id ? await projectService.fetchLeadById(project.lead_id) : null;
      const projectType = project.project_type_id ? await projectService.fetchProjectTypeById(project.project_type_id) : null;
      const isArchived = project.project_status?.name.toLowerCase() === PROJECT_STATUS.ARCHIVED;

      setProjectDetailData({ project, lead, projectType, isArchived });
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : t("projectDetail.errors.loadFailed");
      setError(errorMessage);
    } finally {
      setLoading(false);
    }
  }, [id, projectService, t]);

  useEffect(() => {
    fetchProjectData();
  }, [fetchProjectData]);

  const project = projectDetailData.project;
  const lead = projectDetailData.lead;
  const projectType = projectDetailData.projectType;
  const isArchived = projectDetailData.isArchived;

  const saveState = getActionState('save');
  const deleteState = getActionState('delete');
  const archiveState = getActionState('archive');

  useEffect(() => {
    if (project) {
      setEditName(project.name);
      setEditDescription(project.description || "");
      setEditProjectTypeId(project.project_type_id || "");
    }
  }, [project]);

  const handleSave = async () => {
    if (!project || !editName.trim() || !editProjectTypeId) return;

    const result = await executeAction(
      'save',
      () => projectService.updateProject(project.id, {
        name: editName.trim(),
        description: editDescription.trim() || null,
        project_type_id: editProjectTypeId
      }),
      {
        successMessage: t("projectDetail.toast.updateSuccess"),
        onSuccess: () => {
          setIsEditing(false);
          fetchProjectData();
        }
      }
    );
  };

  const handleDelete = async () => {
    if (!project) return;

    const result = await executeAction(
      'delete',
      () => projectService.deleteProject(project.id),
      {
        successMessage: t("projectDetail.toast.deleteSuccess"),
        onSuccess: () => navigate('/projects')
      }
    );

    if (result) {
      setShowDeleteDialog(false);
    }
  };

  const handleArchive = async () => {
    if (!project) return;

    const successMessage = isArchived
      ? t("projectDetail.toast.restoreSuccess")
      : t("projectDetail.toast.archiveSuccess");
    const result = await executeAction(
      'archive',
      () => projectService.toggleArchiveStatus(project.id, isArchived),
      {
        successMessage,
        onSuccess: () => fetchProjectData()
      }
    );
  };

  const handleCancel = () => {
    setIsEditing(false);
    if (project) {
      setEditName(project.name);
      setEditDescription(project.description || "");
      setEditProjectTypeId(project.project_type_id || "");
    }
  };

  const handleStatusChange = () => {
    fetchProjectData();
  };

  if (loading) {
    return <DetailLoadingState />;
  }

  if (error || !project) {
    return (
      <EntityErrorState 
        error={error || t("projectDetail.errors.notFound")}
        onRetry={fetchProjectData}
        title={t("projectDetail.errors.loadFailed")}
      />
    );
  }

  const badges = [
    {
      label: project.project_status?.name || t("projectDetail.badges.noStatus"),
      variant: 'outline' as const,
      className: 'text-sm'
    }
  ];

  if (projectType) {
    badges.push({
      label: projectType.name.toUpperCase(),
      variant: 'outline' as const,
      className: 'text-sm'
    });
  }

  const editForm = (
    <div className="space-y-4">
      <div className="flex items-center gap-3">
        <Input 
          value={editName} 
          onChange={e => setEditName(e.target.value)} 
          placeholder={t("projectDetail.placeholders.name")}
          className="text-3xl font-bold border rounded-md px-4 py-3 h-auto flex-1" 
        />
      </div>
      <Textarea 
        value={editDescription} 
        onChange={e => setEditDescription(e.target.value)} 
        placeholder={t("projectDetail.placeholders.description")}
        className="text-lg border rounded-md px-4 py-3 resize-none" 
        rows={3} 
      />
      <SimpleProjectTypeSelect 
        value={editProjectTypeId} 
        onValueChange={setEditProjectTypeId} 
        disabled={saveState.loading} 
        required 
      />
    </div>
  );

  const actions = [
    {
      label: isArchived ? t("projectDetail.actions.restore") : t("projectDetail.actions.archive"),
      onClick: handleArchive,
      icon: isArchived ? <ArchiveRestore className="h-4 w-4" /> : <Archive className="h-4 w-4" />,
      variant: 'outline' as const
    },
    {
      label: t("projectDetail.actions.delete"),
      onClick: () => setShowDeleteDialog(true),
      icon: <Trash2 className="h-4 w-4" />,
      variant: 'destructive' as const
    }
  ];

  return (
    <ErrorBoundary>
      <EntityDetailLayout
        title={project.name}
        description={project.description}
        badges={badges}
        onBack={() => navigate('/projects')}
        onEdit={() => setIsEditing(true)}
        onSave={handleSave}
        onCancel={handleCancel}
        isEditing={isEditing}
        isSaving={saveState.loading}
        canEdit={!isArchived}
        editForm={editForm}
        actions={actions}
      >
        <ProjectDetailsLayout 
          header={
            <div className="space-y-2">
              <h2 className="text-lg font-semibold">{t("projectDetail.sections.overview")}</h2>
              <UnifiedClientDetails 
                lead={lead}
              />
            </div>
          }
          left={
            <div className="space-y-4">
              <ProjectStatusBadge 
                projectId={project.id}
                currentStatusId={project.status_id}
                onStatusChange={handleStatusChange}
                editable={!isArchived}
                className="text-sm w-full justify-center"
              />
            </div>
          }
          sections={[
            {
              id: "activities",
              title: t("projectDetail.sections.activities"),
              content: (
                <ProjectActivitySection
                  projectId={project.id}
                  leadId={project.lead_id}
                  leadName={lead?.name || ""}
                  projectName={project.name}
                  onActivityUpdated={fetchProjectData}
                />
              )
            },
            {
              id: "todos", 
              title: t("projectDetail.sections.todos"),
              content: (
                <ProjectTodoListEnhanced
                  projectId={project.id}
                />
              )
            },
            {
              id: "services",
              title: t("projectDetail.sections.services"), 
              content: (
                <ProjectServicesSection
                  projectId={project.id}
                  onServicesUpdated={fetchProjectData}
                />
              )
            },
            {
              id: "sessions",
              title: t("projectDetail.sections.sessions"),
              content: (
                <SessionsSection
                  sessions={[]}
                  loading={false}
                  leadId={project.lead_id}
                  projectId={project.id}
                  leadName={lead?.name || ""}
                  projectName={project.name}
                  onSessionUpdated={fetchProjectData}
                  onDeleteSession={async (sessionId: string) => {
                    // Handle session deletion
                    fetchProjectData();
                  }}
                />
              )
            },
            {
              id: "payments",
              title: t("projectDetail.sections.payments"),
              content: (
                <ProjectPaymentsSection
                  projectId={project.id}
                  onPaymentsUpdated={fetchProjectData}
                />
              )
            }
          ]}
        />
      </EntityDetailLayout>

      {/* Delete Confirmation Dialog */}
      <AlertDialog open={showDeleteDialog} onOpenChange={setShowDeleteDialog}>
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle>{t("projectDetail.dialogs.deleteTitle")}</AlertDialogTitle>
            <AlertDialogDescription>
              {t("projectDetail.dialogs.deleteDescription")}
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel disabled={deleteState.loading}>
              {t("projectDetail.actions.cancel")}
            </AlertDialogCancel>
            <AlertDialogAction 
              onClick={handleDelete}
              disabled={deleteState.loading}
              className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
            >
              {deleteState.loading ? t("projectDetail.actions.deleting") : t("projectDetail.actions.delete")}
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>
    </ErrorBoundary>
  );
}

import { getRelativeDate, getDateDisplayClasses, isOverdueSession } from "./dateUtils";
import { formatDate } from "@/lib/utils";

jest.mock("@/lib/utils", () => ({
  formatDate: jest.fn(),
  formatTime: jest.fn(),
}));

const mockFormatDate = formatDate as jest.MockedFunction<typeof formatDate>;
const RealDate = Date;

function setCurrentDate(isoDate: string) {
  const fixedDate = new RealDate(isoDate);

  class MockDate extends RealDate {
    constructor(...args: any[]) {
      if (args.length) {
        super(...(args as [any]));
        return;
      }
      super(fixedDate.valueOf());
    }
  }

  const MockDateCtor = MockDate as unknown as DateConstructor;
  MockDateCtor.UTC = RealDate.UTC;
  MockDateCtor.parse = RealDate.parse;
  MockDateCtor.now = () => fixedDate.valueOf();

  globalThis.Date = MockDateCtor;
}

describe("dateUtils", () => {
  beforeAll(() => {
    setCurrentDate("2025-01-15T10:00:00");
  });

  afterAll(() => {
    globalThis.Date = RealDate;
  });

  afterEach(() => {
    jest.clearAllMocks();
  });

  describe("getRelativeDate", () => {
    it("returns fallback English labels for today, tomorrow, and yesterday", () => {
      expect(getRelativeDate("2025-01-15")).toBe("Today");
      expect(getRelativeDate("2025-01-16")).toBe("Tomorrow");
      expect(getRelativeDate("2025-01-14")).toBe("Yesterday");
      expect(mockFormatDate).not.toHaveBeenCalled();
    });

    it("returns localized labels when translator is provided", () => {
      const translations: Record<string, string> = {
        "relativeDates.today": "Bugun",
        "relativeDates.tomorrow": "Yarin",
        "relativeDates.yesterday": "Dun",
      };
      const t = (key: string) => translations[key] ?? key;

      expect(getRelativeDate("2025-01-15", t)).toBe("Bugun");
      expect(getRelativeDate("2025-01-16", t)).toBe("Yarin");
      expect(getRelativeDate("2025-01-14", t)).toBe("Dun");
    });

    it("falls back to formatDate for other dates", () => {
      mockFormatDate.mockReturnValueOnce("Jan 20");

      const result = getRelativeDate("2025-01-20");

      expect(result).toBe("Jan 20");
      expect(mockFormatDate).toHaveBeenCalledWith("2025-01-20");
    });
  });

  describe("isOverdueSession", () => {
    it("returns true when session is before today and still planned", () => {
      expect(isOverdueSession("2025-01-10", "planned")).toBe(true);
    });

    it("returns false when session is today or later", () => {
      expect(isOverdueSession("2025-01-15", "planned")).toBe(false);
      expect(isOverdueSession("2025-01-17", "planned")).toBe(false);
    });

    it("returns false when status is not planned", () => {
      expect(isOverdueSession("2025-01-10", "completed")).toBe(false);
    });
  });

  describe("getDateDisplayClasses", () => {
    const translations: Record<string, string> = {
      "relativeDates.today": "Bugun",
      "relativeDates.tomorrow": "Yarin",
    };
    const t = (key: string) => translations[key] ?? key;

    it("returns highlight classes for today and tomorrow labels", () => {
      expect(getDateDisplayClasses("2025-01-15", t)).toBe(
        "text-primary font-medium"
      );
      expect(getDateDisplayClasses("2025-01-16", t)).toBe(
        "text-primary font-medium"
      );
    });

    it("returns default classes for other dates", () => {
      mockFormatDate.mockReturnValueOnce("Jan 18");
      expect(getDateDisplayClasses("2025-01-18", t)).toBe(
        "text-foreground"
      );
    });
  });
});

import { formatDate, formatTime } from "@/lib/utils";
import { useTranslation } from "react-i18next";

export const getRelativeDate = (dateString: string, t?: any): string => {
  const sessionDate = new Date(dateString);
  const today = new Date();
  const tomorrow = new Date(today);
  tomorrow.setDate(today.getDate() + 1);
  const yesterday = new Date(today);
  yesterday.setDate(today.getDate() - 1);

  // Reset time to compare just dates
  const sessionDateOnly = new Date(sessionDate.getFullYear(), sessionDate.getMonth(), sessionDate.getDate());
  const todayOnly = new Date(today.getFullYear(), today.getMonth(), today.getDate());
  const tomorrowOnly = new Date(tomorrow.getFullYear(), tomorrow.getMonth(), tomorrow.getDate());
  const yesterdayOnly = new Date(yesterday.getFullYear(), yesterday.getMonth(), yesterday.getDate());

  if (sessionDateOnly.getTime() === todayOnly.getTime()) {
    return t ? t('relativeDates.today') : "Today";
  } else if (sessionDateOnly.getTime() === tomorrowOnly.getTime()) {
    return t ? t('relativeDates.tomorrow') : "Tomorrow";
  } else if (sessionDateOnly.getTime() === yesterdayOnly.getTime()) {
    return t ? t('relativeDates.yesterday') : "Yesterday";
  }

  return formatDate(dateString);
};

export const isOverdueSession = (dateString: string, status: string): boolean => {
  const sessionDate = new Date(dateString);
  const today = new Date();

  // Reset time to compare just dates
  const sessionDateOnly = new Date(sessionDate.getFullYear(), sessionDate.getMonth(), sessionDate.getDate());
  const todayOnly = new Date(today.getFullYear(), today.getMonth(), today.getDate());

  return sessionDateOnly < todayOnly && status === 'planned';
};

export const getDateDisplayClasses = (dateString: string, t?: any): string => {
  const relativeDate = getRelativeDate(dateString, t);

  const todayText = t ? t('relativeDates.today') : "Today";
  const tomorrowText = t ? t('relativeDates.tomorrow') : "Tomorrow";

  if (relativeDate === todayText || relativeDate === tomorrowText) {
    return "text-primary font-medium";
  }

  return "text-foreground";
};

const FEATURE_FLAG_PREFIX = "VITE_FEATURE_";

const toEnvKey = (flag: string) => {
  const normalized = flag.replace(/[^a-z0-9]/gi, "_").toUpperCase();
  return `${FEATURE_FLAG_PREFIX}${normalized}`;
};

const resolveEnv = (): Record<string, string | undefined> => {
  // Accessing import.meta breaks under Jest (CommonJS). Eval keeps bundlers happy.
  try {
    // eslint-disable-next-line no-eval
    const meta = (0, eval)("import.meta");
    if (meta && typeof meta === "object" && "env" in meta) {
      return (meta as Record<string, unknown>).env as Record<string, string | undefined>;
    }
  } catch {
    // ignore, fall back to process.env below
  }

  if (typeof process !== "undefined" && process.env) {
    return process.env as Record<string, string | undefined>;
  }

  return {};
};

export const isFeatureEnabled = (flag: string, defaultValue = false): boolean => {
  const key = toEnvKey(flag);
  const env = resolveEnv();
  const raw = env[key];

  if (raw === "true") return true;
  if (raw === "false") return false;

  if (typeof window !== "undefined") {
    try {
      const override = window.localStorage.getItem(`flag:${flag}`);
      if (override === "true") return true;
      if (override === "false") return false;
    } catch (error) {
      console.warn("Feature flag localStorage lookup failed", error);
    }
  }

  return defaultValue;
};

