        content: (
          <div className="space-y-4">
            {schedulingSections.map((section, index) => {
              const Icon = schedulingIcons[index] ?? Calendar;
              return (
                <div key={section.title} className="flex items-start gap-3">
                  <Icon className="w-5 h-5 text-primary mt-0.5" />
                  <div>
                    <h4 className="font-medium">{section.title}</h4>
                    <p className="text-sm text-muted-foreground">{section.description}</p>
                  </div>
                </div>
              );
            })}
          </div>
        ),
        mode: "floating",
        canProceed: hasScheduledSession,
        requiresAction: !hasScheduledSession,
        disabledTooltip: tPages("leadDetail.scheduling.scheduleSession.disabledTooltip"),
      },
      {
        id: 4,
        title: tPages("leadDetail.scheduling.sessionScheduled.title"),
        description: tPages("leadDetail.scheduling.sessionScheduled.description"),
        content: (
          <div className="space-y-3">
            <div className="p-3 bg-green-50 border border-green-200 rounded-lg">
              <p className="text-sm font-medium text-green-800">
                {tPages("leadDetail.scheduling.sessionScheduled.banner")}
              </p>
            </div>
            <div className="text-sm space-y-2">
              {scheduledTips.map(tip => (
                <div key={tip} className="flex items-center gap-2">
                  <span className="w-2 h-2 bg-primary rounded-full"></span>
                  <span>{tip}</span>
                </div>
              ))}
            </div>
          </div>
        ),
        mode: "modal",
        canProceed: true,
      },
    ];
  }, [hasScheduledSession, tPages]);

  // Check if we should show tutorial when component mounts
  useEffect(() => {
    const continueTutorial = location.state?.continueTutorial;
    const tutorialStep = location.state?.tutorialStep;
    const tutorialType = location.state?.tutorialType;

    // Check if we should show tutorial either from navigation state OR onboarding progress
    const shouldShowFromState = continueTutorial && tutorialStep;
    const shouldShowFromProgress = currentStep === 2; // User completed first step, now on leads

    if (shouldShowFromState) {
      if (tutorialType === 'scheduling') {
        setIsSchedulingTutorial(true);
        setShowTutorial(true);
        setCurrentTutorialStep(tutorialStep - 3); // Convert to 0-based index for scheduling steps
      } else {
        setShowTutorial(true);
        setCurrentTutorialStep(tutorialStep - 4); // Convert to 0-based index for our steps array
      }
    } else if (shouldShowFromProgress) {
      setShowTutorial(true);
      setCurrentTutorialStep(0); // Start with first lead details step
    }
  }, [location.state?.continueTutorial, location.state?.tutorialStep, location.state?.tutorialType, currentStep]);

  // Auto-start scheduling tutorial when currentStep is 5 (after step 4, now on step 5)
  useEffect(() => {
    if (currentStep === 5 && !showTutorial && !location.state?.continueTutorial) {
      console.log('ðŸš€ Auto-starting scheduling tutorial for step 5');
      setIsSchedulingTutorial(true);
      setShowTutorial(true);
      setCurrentTutorialStep(0); // Start with first scheduling step
    }
  }, [currentStep, showTutorial, location.state?.continueTutorial]);

  // Handle tutorial completion with BULLETPROOF step completion
  const handleTutorialComplete = async () => {
    try {
      if (isSchedulingTutorial) {
        // For scheduling tutorial, complete step 5 (scheduling step)
        console.log('ðŸŽ¯ BULLETPROOF LeadDetail: Completing scheduling tutorial (Step 5)');
        await completeCurrentStep();
        setShowTutorial(false);
        console.log('ðŸŽ‰ BULLETPROOF LeadDetail: Scheduling tutorial completed! Navigating back to getting-started');
        navigate('/getting-started');
      } else {
        // For regular lead details tutorial that includes project creation
        // Complete BOTH Step 2 (leads) and Step 3 (projects) in one atomic operation
        console.log('ðŸŽ¯ BULLETPROOF LeadDetail: Completing combined tutorial (Steps 2 & 3) atomically');
        await completeMultipleSteps(2); // Complete 2 steps at once: current + next

        setShowTutorial(false);
        console.log('ðŸŽ‰ BULLETPROOF LeadDetail: Combined tutorial completed! Both Step 2 & 3 completed atomically, navigating back to getting-started');
        navigate('/getting-started');
      }
    } catch (error) {
      console.error('âŒ BULLETPROOF LeadDetail: Error completing tutorial:', error);
      toast({
        title: tCommon("toast.error"),
        description: tPages("leadDetail.toast.saveProgressFailed"),
        variant: "destructive"
      });
    }
  };
  const handleTutorialExit = () => {
    setShowTutorial(false);
  };

  // Handle project clicked during tutorial
  const handleProjectClicked = () => {
    setHasViewedProject(true);
  };

  const handleSessionScheduled = () => {
    setHasScheduledSession(true);
    void refetchAll();
  };

  useEffect(() => {
    if (!hasScheduledSession && sessions.length > 0) {
      setHasScheduledSession(true);
    }
  }, [hasScheduledSession, sessions.length]);

  // Debug tutorial step changes
  useEffect(() => {
    // Development only debug logging would go here if needed
  }, [currentTutorialStep, showTutorial]);

  // UI state for Lead Information card
  const [showDeleteDialog, setShowDeleteDialog] = useState(false);
  const [confirmDeleteText, setConfirmDeleteText] = useState("");

  // Get system status labels for buttons (refresh when leadStatuses changes)
  const completedStatus = leadStatuses.find(s => s.is_system_final && (s.name.toLowerCase().includes('completed') || s.name.toLowerCase().includes('delivered'))) || leadStatuses.find(s => s.name === 'Completed');
  const lostStatus = leadStatuses.find(s => s.is_system_final && (s.name.toLowerCase().includes('lost') || s.name.toLowerCase().includes('not interested'))) || leadStatuses.find(s => s.name === 'Lost');


  const formatRelativeTime = (dateString?: string | null) => {
    if (!dateString) return null;
    const date = new Date(dateString);
    if (Number.isNaN(date.getTime())) return null;
    try {
      return formatDistanceToNow(date, {
        addSuffix: true,
        locale: getDateFnsLocale()
      });
    } catch (error) {
      console.error("Error formatting relative time:", error);
      return null;
    }
  };

  const formatCurrency = useCallback((amount: number) => {
    try {
      return new Intl.NumberFormat("tr-TR", {
        style: "currency",
        currency: aggregatedPayments.currency || "TRY",
        minimumFractionDigits: 0
      }).format(amount);
    } catch (error) {
      console.error("Error formatting currency:", error);
      return `${Number.isFinite(amount) ? Math.round(amount) : 0} ${aggregatedPayments.currency || "TRY"}`;
    }
  }, [aggregatedPayments.currency]);

  const handleDelete = async () => {
    if (!lead) return;
    setDeleting(true);
    try {
      // 1) Fetch all project IDs for this lead
      const {
        data: leadProjects,
        error: projectsFetchError
      } = await supabase.from('projects').select('id').eq('lead_id', lead.id);
      if (projectsFetchError) throw projectsFetchError;
      const projectIds = (leadProjects || []).map(p => p.id);
      if (projectIds.length > 0) {
        // Delete related project data in safe order
        const {
          error: servicesError
        } = await supabase.from('project_services').delete().in('project_id', projectIds);
        if (servicesError) throw servicesError;
        const {
          error: todosError
        } = await supabase.from('todos').delete().in('project_id', projectIds);
        if (todosError) throw todosError;
        const {
          error: projSessionsError
        } = await supabase.from('sessions').delete().in('project_id', projectIds);
        if (projSessionsError) throw projSessionsError;
        const {
          error: projActivitiesError
        } = await supabase.from('activities').delete().in('project_id', projectIds);
        if (projActivitiesError) throw projActivitiesError;
        const {
          error: paymentsError
        } = await supabase.from('payments').delete().in('project_id', projectIds);
        if (paymentsError) throw paymentsError;

        // Finally delete projects
        const {
          error: deleteProjectsError
        } = await supabase.from('projects').delete().in('id', projectIds);
        if (deleteProjectsError) throw deleteProjectsError;
      }

      // 2) Delete sessions associated directly with this lead (not tied to a project)
      const {
        error: leadSessionsError
      } = await supabase.from('sessions').delete().eq('lead_id', lead.id);
      if (leadSessionsError) throw leadSessionsError;

      // 3) Delete activities/reminders/notes for this lead
      const {
        error: leadActivitiesError
      } = await supabase.from('activities').delete().eq('lead_id', lead.id);
      if (leadActivitiesError) throw leadActivitiesError;

      // 4) Finally delete the lead
      const {
        error: deleteLeadError
      } = await supabase.from('leads').delete().eq('id', lead.id);
      if (deleteLeadError) throw deleteLeadError;
      toast({
        title: tMessages('success.deleted'),
        description: tForms('deleteLeadDialog.successDescription')
      });
      navigate('/leads');
    } catch (error: any) {
      toast({
        title: tMessages('error.generic'),
        description: error.message,
        variant: 'destructive'
      });
    } finally {
      setDeleting(false);
      setShowDeleteDialog(false);
      setConfirmDeleteText('');
    }
  };
  const handleDeleteSession = async (sessionId: string) => {
    const success = await deleteSession(sessionId);
    if (success) {
      void sessionsQuery.refetch();
      void latestActivityQuery.refetch();
    }
  };
  const handleSessionUpdated = () => {
    void sessionsQuery.refetch();
    void latestActivityQuery.refetch();
  };

  const handleProjectUpdated = () => {
    // Refresh sessions and activities when project changes (archive/restore should affect visibility)
    void sessionsQuery.refetch();
    void summaryQuery.refetch();
    void latestActivityQuery.refetch();

    // If tutorial is active and we're on the project creation step (Step 5 = index 1), advance to project exploration step
    if (showTutorial && currentTutorialStep === 1) {
      // Use setTimeout to ensure state update happens after component re-render
      setTimeout(() => {
        setCurrentTutorialStep(2); // Move to "Now Explore Your Project" step (Step 6)
      }, 100);
    }
  };
  const handleActivityUpdated = () => {
    // Force activity section to refresh when activities are updated in project modal
    void latestActivityQuery.refetch();
  };
  const handleBack = () => {
    const from = location.state?.from;
    if (from === 'dashboard') {
      navigate('/');
    } else if (from === 'all-leads') {
      navigate('/leads');
    } else if (from === 'all-sessions') {
      navigate('/sessions');
    } else {
      // Default fallback - go back in history if possible, otherwise to all leads
      if (window.history.length > 1) {
        navigate(-1);
      } else {
        navigate('/leads');
      }
    }
  };
  const handleMarkAsCompleted = () => {
    if (!lead || !completedStatus) return;
    markAsCompleted(lead.status, completedStatus.name);
  };
  const handleMarkAsLost = () => {
    if (!lead || !lostStatus) return;
    markAsLost(lead.status, lostStatus.name);
  };
  const activitySummary = useMemo(() => {
    const timeline: Array<{ type: "lead" | "project" | "session" | "note"; timestamp: string }> = [];

    if (lead?.updated_at) {
      timeline.push({ type: "lead", timestamp: lead.updated_at });
    }

    if (projectSummary.latestUpdate) {
      timeline.push({ type: "project", timestamp: projectSummary.latestUpdate });
    }

    if (latestSessionUpdate) {
      timeline.push({ type: "session", timestamp: latestSessionUpdate });
    }

    if (latestLeadActivity) {
      timeline.push({ type: "note", timestamp: latestLeadActivity });
    }

    if (timeline.length === 0) {
      return {
        primary: tPages("leadDetail.header.activity.none"),
        secondary: tPages("leadDetail.header.activity.hint")
      };
    }

    const latest = timeline.reduce((currentLatest, entry) => {
      if (!currentLatest) return entry;
      return new Date(entry.timestamp).getTime() > new Date(currentLatest.timestamp).getTime() ? entry : currentLatest;
    });

    const relative = formatRelativeTime(latest.timestamp);
    const primary = relative
      ? tPages("leadDetail.header.activity.last", { time: relative })
      : tPages("leadDetail.header.activity.lastFallback");
    const secondary = tPages(`leadDetail.header.activity.sources.${latest.type}`);

    return {
      primary,
      secondary
    };
  }, [lead?.updated_at, projectSummary.latestUpdate, latestSessionUpdate, latestLeadActivity, tPages]);

  const projectUpdateRelative = formatRelativeTime(projectSummary.latestUpdate);
  const projectPrimary = projectSummary.count
    ? tPages("leadDetail.header.projects.count", { count: projectSummary.count })
    : tPages("leadDetail.header.projects.none");
  const projectSecondary = projectSummary.count
    ? projectUpdateRelative
      ? tPages("leadDetail.header.projects.updated", { time: projectUpdateRelative })
      : tPages("leadDetail.header.projects.updatedFallback")
    : tPages("leadDetail.header.projects.hint");

  const sessionsCount = sessions.length;
  const sessionsPrimary = sessionsCount
    ? tPages("leadDetail.header.sessions.count", { count: sessionsCount })
    : tPages("leadDetail.header.sessions.none");
  const sessionsSecondary = useMemo<ReactNode>(() => {
    if (sessionsCount === 0) {
      return tPages("leadDetail.header.sessions.hint");
    }

    const chips: ReactNode[] = [];

    if (overdueCount > 0) {
      chips.push(
        <span
          key="overdue"
          className="inline-flex items-center rounded-md border border-orange-200 bg-orange-50 px-2 py-1 text-[11px] font-semibold text-orange-700"
        >
          {tPages("leadDetail.header.sessions.chips.overdue", { count: overdueCount })}
        </span>
      );
    }

    if (todayCount > 0) {
      const todayTime = safeFormatTime(todayNext?.session_time);
      const label = todayTime
        ? todayCount > 1
          ? tPages("leadDetail.header.sessions.chips.todayMultiple", { count: todayCount, time: todayTime })
          : tPages("leadDetail.header.sessions.chips.todaySingle", { time: todayTime })
        : tPages("leadDetail.header.sessions.chips.todayMultipleNoTime", { count: todayCount });

      chips.push(
        <span
          key="today"
          className="inline-flex items-center rounded-md border border-blue-200 bg-blue-50 px-2 py-1 text-[11px] font-semibold text-blue-700"
        >
          {label}
        </span>
      );
    }

    if (nextUpcoming?.session_date) {
      const upcomingDateLabel = safeFormatDate(nextUpcoming.session_date);
      const upcomingTime = safeFormatTime(nextUpcoming.session_time);

      if (upcomingDateLabel) {
        const today = new Date();
        today.setHours(0, 0, 0, 0);
        const tomorrow = new Date(today);
        tomorrow.setDate(tomorrow.getDate() + 1);
        const tomorrowKey = `${tomorrow.getFullYear()}-${String(tomorrow.getMonth() + 1).padStart(2, "0")}-${String(
          tomorrow.getDate()
        ).padStart(2, "0")}`;
        const upcomingDateKey = getDateKey(nextUpcoming.session_date);

        const label = upcomingDateKey && upcomingDateKey === tomorrowKey && upcomingTime
          ? tPages("leadDetail.header.sessions.chips.tomorrow", { time: upcomingTime })
          : upcomingTime
            ? tPages("leadDetail.header.sessions.chips.upcomingWithTime", {
                date: upcomingDateLabel,
                time: upcomingTime
              })
            : tPages("leadDetail.header.sessions.chips.upcoming", { date: upcomingDateLabel });

        chips.push(
          <span
            key="upcoming"
            className="inline-flex items-center rounded-md border border-emerald-200 bg-emerald-50 px-2 py-1 text-[11px] font-semibold text-emerald-700"
          >
            {label}
          </span>
        );
      }
    }

    if (chips.length === 0) {
      return tPages("leadDetail.header.sessions.hint");
    }

    return (
      <div className="flex flex-wrap items-center gap-1.5">
        {chips}
      </div>
    );
  }, [
    sessionsCount,
    overdueCount,
    todayCount,
    todayNext?.session_time,
    nextUpcoming?.session_date,
    nextUpcoming?.session_time,
    tPages
  ]);

  const paymentsPrimary = aggregatedPayments.total > 0 || aggregatedPayments.totalPaid > 0
    ? tPages("leadDetail.header.payments.primary", { paid: formatCurrency(aggregatedPayments.totalPaid) })
    : tPages("leadDetail.header.payments.primaryZero");
  const paymentsSecondary = aggregatedPayments.total > 0
    ? aggregatedPayments.remaining > 0
      ? tPages("leadDetail.header.payments.secondary", {
          remaining: formatCurrency(aggregatedPayments.remaining),
          total: formatCurrency(aggregatedPayments.total)
        })
      : tPages("leadDetail.header.payments.paidInFull")
    : tPages("leadDetail.header.payments.secondaryZero");

  const summaryItems: EntitySummaryItem[] = useMemo(() => {
    const paymentsSecondaryClass = aggregatedPayments.total > 0 && aggregatedPayments.remaining <= 0
      ? "text-emerald-600"
      : undefined;
    const sessionsSecondaryClass =
      typeof sessionsSecondary === "string" || typeof sessionsSecondary === "number"
        ? "text-muted-foreground"
        : undefined;

    return [
      {
        key: "projects",
        icon: FolderPlus,
        label: tPages("leadDetail.header.projects.label"),
        primary: projectPrimary,
        secondary: projectSecondary
      },
      {
        key: "payments",
        icon: CreditCard,
        label: tPages("leadDetail.header.payments.label"),
        primary: paymentsPrimary,
        secondary: paymentsSecondary,
        secondaryClassName: paymentsSecondaryClass
      },
      {
        key: "sessions",
        icon: Calendar,
        label: tPages("leadDetail.header.sessions.label"),
        primary: sessionsPrimary,
        secondary: sessionsSecondary,
        secondaryClassName: sessionsSecondaryClass
      },
      {
        key: "activity",
        icon: Activity,
        label: tPages("leadDetail.header.activity.label"),
        primary: activitySummary.primary,
        secondary: activitySummary.secondary,
        info: {
          content: tPages("leadDetail.header.activity.info"),
          ariaLabel: tPages("leadDetail.header.activity.infoLabel")
        }
      }
    ];
  }, [
    projectPrimary,
    projectSecondary,
    paymentsPrimary,
    paymentsSecondary,
    sessionsPrimary,
    sessionsSecondary,
    activitySummary,
    tPages
  ]);
  if (detailLoading) {
    return <DetailPageLoadingSkeleton />;
  }
  if (!lead) {
    return null;
  }
  return (
    <div className="min-h-screen bg-background">
      <div className="mx-auto max-w-full px-4 py-4 md:px-8 md:py-8">
        <div className="space-y-6 md:space-y-8">
          <EntityHeader
            className="mb-0"
            name={lead.name || ""}
            title={lead.name || tPages("leadDetail.defaultTitle")}
            onBack={handleBack}
            backLabel={tPages("leadDetail.header.back")}
            statusBadge={
              <LeadStatusBadge
                leadId={lead.id}
                currentStatusId={lead.status_id}
                currentStatus={lead.status}
                onStatusChange={() => {
                  void refetchAll();
                }}
                editable={true}
                statuses={leadStatuses}
              />
            }
            summaryItems={summaryItems}
            fallbackInitials="LD"
            actions={
              <>
                <ScheduleSessionDialog
                  leadId={lead.id}
                  leadName={lead.name}
                  onSessionScheduled={handleSessionScheduled}
                  disabled={sessions.some(s => (s.status || "").toLowerCase() === "planned")}
                  disabledTooltip={tPages("leadDetail.tooltips.sessionAlreadyPlanned")}
                />

                {!settingsLoading &&
                  userSettings.show_quick_status_buttons &&
                  completedStatus &&
                  lead.status !== completedStatus.name && (
                    <Button
                      onClick={handleMarkAsCompleted}
                      disabled={isUpdating}
                      className="h-10 bg-green-600 text-white hover:bg-green-700"
                      size="sm"
                    >
                      <CheckCircle className="mr-2 h-4 w-4" />
                      {isUpdating ? "Updating..." : completedStatus.name}
                    </Button>
                  )}

                {!settingsLoading &&
                  userSettings.show_quick_status_buttons &&
                  lostStatus &&
                  lead.status !== lostStatus.name && (
                    <Button onClick={handleMarkAsLost} disabled={isUpdating} variant="destructive" size="sm" className="h-10">
                      {isUpdating ? "Updating..." : lostStatus.name}
                    </Button>
                  )}
              </>
            }
          />

          <ProjectDetailsLayout
            header={null}
            left={
              <div className="space-y-6">
                <UnifiedClientDetails
                  lead={lead}
                  createdAt={lead.created_at}
                  showQuickActions={true}
                  showLeadNameInHeader={false}
                  onLeadUpdated={() => {
                    void refetchAll();
                  }}
                />
              </div>
            }
            sections={[
              {
                id: "lead-projects",
                title: tPages("leadDetail.header.projects.label"),
                content: (
                  <ProjectsSection
                    leadId={lead.id}
                    leadName={lead.name}
                    onProjectUpdated={handleProjectUpdated}
                    onActivityUpdated={handleActivityUpdated}
                    onProjectClicked={handleProjectClicked}
                  />
                )
              },
              {
                id: "lead-activity",
                title: tPages("leadDetail.header.activity.label"),
                content: (
                  <LeadActivitySection
                    leadId={lead.id}
                    leadName={lead.name}
                    onActivityUpdated={() => {
                      void latestActivityQuery.refetch();
                    }}
                  />
                )
              }
            ]}
            overviewNavId="lead-detail-overview"
            overviewLabel={tForms("project_sheet.overview_tab")}
            rightFooter={
              <div className="border border-destructive/20 bg-destructive/5 rounded-md p-4 max-w-full text-center">
                <div className="space-y-3">
                  <Button
                    variant="outline"
                    onClick={() => setShowDeleteDialog(true)}
                    className="w-full max-w-xs border-destructive text-destructive hover:bg-destructive hover:text-destructive-foreground"
                  >
                    {tForms("leadDangerZone.deleteLead")}
                  </Button>
                  <p className="text-xs text-muted-foreground break-words">
                    {tForms("leadDangerZone.deleteWarning")}
                  </p>
                </div>
              </div>
            }
          />
        </div>
      </div>

      {/* Edit Session Dialog */}
      {editingSessionId && (() => {
        const session = sessions.find(s => s.id === editingSessionId);
        return session ? (
          <EditSessionDialog
            sessionId={session.id}
            leadId={lead.id}
            currentDate={session.session_date}
            currentTime={session.session_time}
            currentNotes={session.notes}
            currentProjectId={session.project_id}
            currentSessionName={session.session_name ?? undefined}
            leadName={lead.name}
            open={!!editingSessionId}
            onOpenChange={open => {
              if (!open) {
                setEditingSessionId(null);
              }
            }}
            onSessionUpdated={() => {
              handleSessionUpdated();
              setEditingSessionId(null);
            }}
          />
        ) : null;
      })()}

      {/* Delete Session Dialog */}
      <AlertDialog open={!!deletingSessionId} onOpenChange={(open) => !open && setDeletingSessionId(null)}>
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle>Delete Session?</AlertDialogTitle>
            <AlertDialogDescription>
              {tMessages("confirm.deleteSession")} {tMessages("confirm.cannotUndo")}
            </AlertDialogDescription>
          </AlertDialogHeader>
          <AlertDialogFooter>
            <AlertDialogCancel>Cancel</AlertDialogCancel>
            <AlertDialogAction
              onClick={() => {
                if (deletingSessionId) {
                  handleDeleteSession(deletingSessionId);
                  setDeletingSessionId(null);
                }
              }}
              className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
            >
              Delete Session
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>

      {/* Delete Lead Confirmation Dialog */}
      <AlertDialog
        open={showDeleteDialog}
        onOpenChange={(open) => {
          setShowDeleteDialog(open);
          if (!open) setConfirmDeleteText("");
        }}
      >
        <AlertDialogContent>
          <AlertDialogHeader>
            <AlertDialogTitle>{tForms("deleteLeadDialog.title")}</AlertDialogTitle>
            <AlertDialogDescription>
              {tForms("deleteLeadDialog.description", { name: lead.name })}
            </AlertDialogDescription>
          </AlertDialogHeader>
          <div className="py-2">
            <Label htmlFor="confirm-delete" className="sr-only">
              Confirmation
            </Label>
            <Input
              id="confirm-delete"
              placeholder={tForms("deleteLeadDialog.placeholder", { name: lead.name })}
              value={confirmDeleteText}
              onChange={(e) => setConfirmDeleteText(e.target.value)}
            />
            <p className="text-xs text-muted-foreground mt-2">
              {tForms("deleteLeadDialog.cannotUndo")}
            </p>
          </div>
          <AlertDialogFooter>
            <AlertDialogCancel disabled={deleting}>{tCommon('buttons.cancel')}</AlertDialogCancel>
            <AlertDialogAction onClick={handleDelete} disabled={deleting || ![lead.name, 'DELETE'].includes(confirmDeleteText.trim())} className="bg-destructive text-destructive-foreground hover:bg-destructive/90">
              {deleting ? tForms('deleteLeadDialog.deleting') : tForms('deleteLeadDialog.deleteLead')}
            </AlertDialogAction>
          </AlertDialogFooter>
        </AlertDialogContent>
      </AlertDialog>

      <OnboardingTutorial
        key={`tutorial-${currentTutorialStep}`}
        steps={isSchedulingTutorial ? schedulingTutorialSteps : leadDetailsTutorialSteps}
        isVisible={showTutorial}
        onComplete={handleTutorialComplete}
        onExit={handleTutorialExit}
        initialStepIndex={currentTutorialStep}
      />
    </div>
  );
};
export default LeadDetail;

import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { useNavigate, useSearchParams } from 'react-router-dom';
import { ArrowLeft, Save, Eye, Edit } from 'lucide-react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Badge } from '@/components/ui/badge';
import { Label } from '@/components/ui/label';
import { useToast } from '@/hooks/use-toast';
import { OptimizedTemplateEditor } from '@/components/template-builder/OptimizedTemplateEditor';
import { OptimizedTemplatePreview } from '@/components/template-builder/OptimizedTemplatePreview';
import { TemplateBlock } from '@/types/templateBuilder';
import { InlineSubjectEditor } from '@/components/template-builder/InlineSubjectEditor';
import { InlinePreheaderEditor } from '@/components/template-builder/InlinePreheaderEditor';
import { useTemplateBuilder } from '@/hooks/useTemplateBuilder';
import { useTemplateVariables } from '@/hooks/useTemplateVariables';
import { getCharacterCount, checkSpamWords, getPreviewDataSets } from '@/lib/templateUtils';
import { NavigationGuardDialog } from '@/components/settings/NavigationGuardDialog';
import { useSettingsNavigation } from '@/hooks/useSettingsNavigation';
import { TemplateNameDialog } from '@/components/template-builder/TemplateNameDialog';
import { supabase } from '@/integrations/supabase/client';
import { TemplateErrorBoundary } from "@/components/template-builder/TemplateErrorBoundary";
import { useTranslation } from "react-i18next";

// Optimized TemplateBuilder component
const OptimizedTemplateBuilderContent = React.memo(() => {
  const { t, i18n } = useTranslation("pages");
  const navigate = useNavigate();
  const { toast } = useToast();
  const [searchParams] = useSearchParams();
  const templateId = searchParams.get('id');

  // Backend hooks
  const { template, loading, saving, lastSaved, isDirty, saveTemplate, publishTemplate, updateTemplate, resetDirtyState } = useTemplateBuilder(templateId || undefined);
  const { getVariableValue } = useTemplateVariables();

  // Local state for UI
  const [activeChannel, setActiveChannel] = useState<'email' | 'whatsapp' | 'sms' | 'plain'>('email');
  const [selectedPreviewData, setSelectedPreviewData] = useState(0);
  const [isEditingName, setIsEditingName] = useState(false);
  const [editingName, setEditingName] = useState('');
  const [isEditingSubject, setIsEditingSubject] = useState(false);
  const [isEditingPreheader, setIsEditingPreheader] = useState(false);
  const [showNameDialog, setShowNameDialog] = useState(false);
  const [pendingAction, setPendingAction] = useState<'save' | 'publish' | null>(null);
  const [existingTemplateNames, setExistingTemplateNames] = useState<string[]>([]);

  // Template data from backend or defaults
  const templateName = template?.name || t("templateBuilder.untitledTemplate");
  const subject = template?.subject || '';
  const preheader = template?.preheader || '';
  const blocks = template?.blocks || [];
  const isDraft = template?.status === 'draft' || !template;

  // Fetch existing template names for validation
  useEffect(() => {
    const fetchTemplateNames = async () => {
      try {
        const { data, error } = await supabase
          .from('message_templates')
          .select('name')
          .neq('id', templateId || '');

        if (error) {
          console.error('Error fetching template names:', error);
          return;
        }

        setExistingTemplateNames(data?.map(t => t.name) || []);
      } catch (error) {
        console.error('Error fetching template names:', error);
      }
    };

    fetchTemplateNames();
  }, [templateId]);

  // Helper function to check if template name is untitled
  const isUntitledTemplate = (name: string) => {
    const normalizedName = name.toLowerCase().trim();
    return normalizedName === 'untitled template' || 
           normalizedName.includes('untitled') ||
           normalizedName === '' ||
           normalizedName === 'new template' ||
           normalizedName === 'template';
  };

  // Navigation guard
  const {
    showGuard,
    message: guardMessage,
    handleNavigationAttempt,
    handleDiscardChanges,
    handleStayOnPage,
    handleSaveAndExit
  } = useSettingsNavigation({
    isDirty,
    onDiscard: () => {
      resetDirtyState();
    },
    onSaveAndExit: async () => {
      await handleSaveTemplate();
    },
    message: t("templateBuilder.navigationGuard.unsavedChanges")
  });

  const handleSaveTemplate = useCallback(async (customName?: string) => {
    const nameToUse = customName || templateName;

    // Check if template needs a name
    if (isUntitledTemplate(nameToUse)) {
      setPendingAction('save');
      setShowNameDialog(true);
      return;
    }

    if (!template) {
      // Create new template
      const newTemplate = await saveTemplate({
        name: nameToUse,
        subject,
        preheader,
        blocks,
        status: 'draft',
        category: 'general',
      });

      if (newTemplate) {
        // Update URL to include template ID
        navigate(`/template-builder?id=${newTemplate.id}`, { replace: true });
      }
    } else {
      // Update existing template
      await saveTemplate({
        ...template,
        name: nameToUse,
        subject,
        preheader,
        blocks,
      });
    }
  }, [template, saveTemplate, templateName, subject, preheader, blocks, navigate]);

  const handleNavigateBack = useCallback(() => {
    if (isDirty) {
      if (!handleNavigationAttempt('/templates')) {
        return; // Navigation blocked by guard
      }
    }
    navigate('/templates');
  }, [isDirty, handleNavigationAttempt, navigate]);


  const handlePublishTemplate = useCallback(async (customName?: string) => {
    const nameToUse = customName || templateName;

    // Check if template needs a name
    if (isUntitledTemplate(nameToUse)) {
      setPendingAction('publish');
      setShowNameDialog(true);
      return;
    }

    // Directly publish with all current data (atomic operation)
    const publishedTemplate = await publishTemplate({
      name: nameToUse,
      subject,
      preheader,
      blocks,
      category: template?.category || 'general',
    });

    // Update URL if it's a new template
    if (publishedTemplate && !templateId) {
      navigate(`/template-builder?id=${publishedTemplate.id}`, { replace: true });
    }
  }, [templateName, subject, preheader, blocks, template?.category, publishTemplate, templateId, navigate]);

  const handleSubjectSave = async (newSubject: string) => {
    updateTemplate({ subject: newSubject });
    setIsEditingSubject(false);
  };

  const handlePreheaderSave = async (newPreheader: string) => {
    updateTemplate({ preheader: newPreheader });
    setIsEditingPreheader(false);
  };

  const handleNameEdit = () => {
    setEditingName(templateName);
    setIsEditingName(true);
  };

  const handleNameBlur = () => {
    setIsEditingName(false);
    if (editingName !== templateName) {
      updateTemplate({ name: editingName });
    }
  };

  const handleNameKeyDown = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter') {
      handleNameBlur();
    }
  };


  const handleBlocksChange = useCallback((newBlocks: TemplateBlock[]) => {
    updateTemplate({ blocks: newBlocks });
  }, [updateTemplate]);

  // Handle name dialog confirmation
  const handleNameDialogConfirm = async (name: string) => {
    setShowNameDialog(false);

    // Update the template name in local state first
    updateTemplate({ name });

    // Perform the pending action with the new name
    if (pendingAction === 'save') {
      await handleSaveTemplate(name);
    } else if (pendingAction === 'publish') {
      await handlePublishTemplate(name);
    }

    setPendingAction(null);
  };

  // Handle name dialog cancel
  const handleNameDialogCancel = () => {
    setShowNameDialog(false);
    setPendingAction(null);
  };

  // Memoize expensive calculations
  const subjectCharCount = useMemo(() => getCharacterCount(subject), [subject]);
  const spamWords = useMemo(() => checkSpamWords(subject), [subject]);
  const previewDataSets = useMemo(() => getPreviewDataSets(i18n.language), [i18n.language]);
  const selectedData = useMemo(() => previewDataSets[selectedPreviewData], [selectedPreviewData, previewDataSets]);

  if (loading) {
    return (
      <div className="h-screen flex items-center justify-center">
        <div className="text-center">
          <div className="text-lg font-semibold">{t("templateBuilder.loading")}</div>
        </div>
      </div>
    );
  }

  return (
    <div className="h-screen flex flex-col">
      {/* Header */}
      <div className="border-b bg-background px-6 py-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center gap-4">
            <ArrowLeft 
              className="h-6 w-6 cursor-pointer text-foreground hover:text-[hsl(var(--accent-foreground))] transition-colors" 
              strokeWidth={2.5}
              onClick={handleNavigateBack}
            />
            <div className="flex items-center gap-2">
              {isEditingName ? (
                <Input
                  value={editingName}
                  onChange={(e) => setEditingName(e.target.value)}
                  onBlur={handleNameBlur}
                  onKeyDown={handleNameKeyDown}
                  className="font-semibold text-lg border bg-background px-2 py-1 h-auto focus-visible:ring-1"
                  autoFocus
                />
              ) : (
                <div className="flex items-center gap-2">
                  <span className="font-semibold text-lg">{templateName}</span>
                  <Button
                    variant="ghost"
                    size="sm"
                    onClick={handleNameEdit}
                    className="h-6 w-6 p-0"
                  >
                    <Edit className="h-3 w-3" />
                  </Button>
                </div>
              )}
            </div>
          </div>

          <div className="flex items-center gap-2">
            <div className="flex items-center gap-2">
              <Badge variant={isDraft ? "secondary" : "default"}>
                {isDraft ? t("templateBuilder.badges.draft") : t("templateBuilder.badges.published")}
              </Badge>
              {isDirty && (
                <Badge variant="outline" className="text-amber-600 border-amber-600">
                  {t("templateBuilder.badges.unsavedChanges")}
                </Badge>
              )}
              {lastSaved && !isDirty && (
                <span className="text-xs text-muted-foreground">
                  {t("templateBuilder.saved", { time: lastSaved.toLocaleTimeString() })}
                </span>
              )}
            </div>
            <Button variant="outline" onClick={() => handleSaveTemplate()} disabled={saving}>
              <Save className="h-4 w-4" />
              {saving ? t("templateBuilder.buttons.saving") : t("templateBuilder.buttons.saveDraft")}
            </Button>
            <Button onClick={() => handlePublishTemplate()} disabled={saving}>
              <Eye className="h-4 w-4" />
              {isDraft ? t("templateBuilder.buttons.publish") : t("templateBuilder.buttons.published")}
            </Button>
          </div>
        </div>

        {/* Compact Email Settings */}
        {activeChannel === "email" && (
          <div className="mt-3 pt-3 border-t space-y-3">
            {/* Subject Line - Side by Side */}
            <div className="space-y-1">
              {isEditingSubject ? (
                <InlineSubjectEditor
                  value={subject}
                  onSave={handleSubjectSave}
                  onCancel={() => setIsEditingSubject(false)}
                />
              ) : (
                <div className="flex items-center gap-3 pl-1">
                  <Label className="text-sm text-muted-foreground w-20 flex-shrink-0">
                    {t("templateBuilder.email.subject")}
                  </Label>
                  <button
                    onClick={() => setIsEditingSubject(true)}
                    className="flex-1 flex items-center gap-2 text-left hover:bg-muted/50 rounded px-2 py-1 -mx-2 -my-1 cursor-pointer group"
                  >
                    <span className={`text-sm flex-1 ${!subject ? 'text-muted-foreground' : 'text-foreground'}`}>
                      {subject || t("templateBuilder.email.addSubject")}
                    </span>
                    <Button
                      variant="ghost"
                      size="sm"
                      className="h-6 w-6 p-0 opacity-100"
                    >
                      <Edit className="h-3 w-3" />
                    </Button>
                  </button>
                </div>
              )}

              {/* Character count and spam warnings for subject */}
              {!isEditingSubject && subject && (subjectCharCount > 60 || spamWords.length > 0) && (
                <div className="flex items-center gap-2 text-xs text-muted-foreground ml-24">
                  {subjectCharCount > 60 && (
                    <div className="flex items-center gap-1">
                      <span className="text-amber-600">âš ï¸</span>
                      <span className="text-amber-600">
                        {t("templateBuilder.warnings.tooLong", { count: subjectCharCount })}
                      </span>
                    </div>
                  )}
                  {spamWords.length > 0 && (
                    <div className="flex items-center gap-1">
                      <span className="text-amber-600">âš ï¸</span>
                      <span>{t("templateBuilder.warnings.spamWords")}</span>
                      <div className="flex gap-1">
                        {spamWords.slice(0, 2).map(word => (
                          <Badge key={word} variant="secondary" className="text-xs px-1 py-0">
                            {word}
                          </Badge>
                        ))}
                        {spamWords.length > 2 && (
                          <span className="text-amber-600">
                            {t("templateBuilder.warnings.moreSpamWords", { count: spamWords.length - 2 })}
                          </span>
                        )}
                      </div>
                    </div>
                  )}
                </div>
              )}
            </div>

            {/* Preheader - Side by Side */}
            <div>
              {isEditingPreheader ? (
                <InlinePreheaderEditor
                  value={preheader}
                  onSave={handlePreheaderSave}
                  onCancel={() => setIsEditingPreheader(false)}
                />
              ) : (
                <div className="flex items-center gap-3 pl-1">
                  <Label className="text-sm text-muted-foreground w-20 flex-shrink-0">
                    {t("templateBuilder.email.preheader")}
                  </Label>
                  <button
                    onClick={() => setIsEditingPreheader(true)}
                    className="flex-1 flex items-center gap-2 text-left hover:bg-muted/50 rounded px-2 py-1 -mx-2 -my-1 cursor-pointer group"
                  >
                    <span className={`text-sm flex-1 ${!preheader ? 'text-muted-foreground' : 'text-foreground'}`}>
                      {preheader || t("templateBuilder.email.addPreheader")}
                    </span>
                    <Button
                      variant="ghost"
                      size="sm"
                      className="h-6 w-6 p-0 opacity-100"
                    >
                      <Edit className="h-3 w-3" />
                    </Button>
                  </button>
                </div>
              )}
            </div>
          </div>
        )}
      </div>

      {/* Main Content */}
      <div className="flex-1 flex min-h-0">
        {/* Left Side - Template Editor */}
        <div className="w-1/2 border-r">
          <OptimizedTemplateEditor
            blocks={blocks}
            onBlocksChange={handleBlocksChange}
          />
        </div>

        {/* Right Side - Live Preview */}
        <div className="w-1/2">
          <OptimizedTemplatePreview
            blocks={blocks}
            activeChannel={activeChannel as 'email' | 'whatsapp' | 'sms' | 'plaintext'}
            onChannelChange={(channel) => setActiveChannel(channel as 'email' | 'whatsapp' | 'sms' | 'plain')}
            emailSubject={subject}
            preheader={preheader}
            previewData={selectedData.data}
          />
        </div>
      </div>

      {/* Navigation Guard Dialog */}
      <NavigationGuardDialog
        open={showGuard}
        onDiscard={handleDiscardChanges}
        onStay={handleStayOnPage}
        onSaveAndExit={handleSaveAndExit}
        message={guardMessage}
      />

      {/* Template Name Dialog */}
      <TemplateNameDialog
        open={showNameDialog}
        onClose={handleNameDialogCancel}
        onConfirm={handleNameDialogConfirm}
        currentName={templateName}
        existingNames={existingTemplateNames}
        action={pendingAction || 'save'}
        loading={saving}
      />
    </div>
  );
});

OptimizedTemplateBuilderContent.displayName = 'OptimizedTemplateBuilderContent';

export default function TemplateBuilder() {
  return (
    <TemplateErrorBoundary>
      <OptimizedTemplateBuilderContent />
    </TemplateErrorBoundary>
  );
}

import { useState, useMemo, useEffect, useCallback } from "react";
import { PageHeader } from "@/components/ui/page-header";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import {
  AdvancedDataTable,
  type AdvancedTableColumn,
  type AdvancedDataTableSortState,
} from "@/components/data-table";
import { PageLoadingSkeleton } from "@/components/ui/loading-presets";
import { useWorkflows } from "@/hooks/useWorkflows";
import { CreateWorkflowSheet } from "@/components/CreateWorkflowSheet";
import { WorkflowDeleteDialog } from "@/components/WorkflowDeleteDialog";
import { Workflow } from "@/types/workflow";
import { Plus, Zap, CheckCircle, Clock, AlertTriangle, Edit, Trash2, Mail, MessageCircle, Phone } from "lucide-react";
import { Switch } from "@/components/ui/switch";
import { formatDistanceToNow } from "date-fns";
import { enUS, tr } from 'date-fns/locale';
import { useTranslation } from "react-i18next";
import { KpiCard } from "@/components/ui/kpi-card";
import {
  KPI_ACTION_BUTTON_CLASS,
  getKpiIconPreset,
} from "@/components/ui/kpi-presets";
import { SegmentedControl } from "@/components/ui/segmented-control";

export default function Workflows() {
  const { t, i18n } = useTranslation("pages");
  const { workflows, loading, createWorkflow, updateWorkflow, deleteWorkflow, toggleWorkflowStatus } = useWorkflows();
  const dateLocale = i18n.language === 'tr' ? tr : enUS;
  const [searchQuery, setSearchQuery] = useState("");
  const [statusFilter, setStatusFilter] = useState<"all" | "active" | "paused">("all");
  const [page, setPage] = useState(1);
  const pageSize = 25;
  const [sortState, setSortState] = useState<AdvancedDataTableSortState>({
    columnId: "name",
    direction: "asc",
  });

  const filteredWorkflows = useMemo(() => {
    const query = searchQuery.trim().toLowerCase();
    return workflows.filter((workflow) => {
      const matchesSearch =
        query.length === 0 ||
        workflow.name.toLowerCase().includes(query) ||
        workflow.description?.toLowerCase().includes(query);
      const matchesStatus =
        statusFilter === "all" ||
        (statusFilter === "active" && workflow.is_active) ||
        (statusFilter === "paused" && !workflow.is_active);
      return matchesSearch && matchesStatus;
    });
  }, [workflows, searchQuery, statusFilter]);

  const getTriggerLabel = useCallback((triggerType: string) => {
    const triggerKey = `workflows.triggers.${triggerType}` as const;
    return t(triggerKey, { defaultValue: triggerType });
  }, [t]);

  const getChannelIcons = useCallback((channels: string[]) => {
    const iconMap = {
      email: Mail,
      sms: Phone,
      whatsapp: MessageCircle,
    };

    return (
      channels?.map((channel) => {
        const IconComponent = iconMap[channel as keyof typeof iconMap];
        if (!IconComponent) return null;

        return (
          <div key={channel} className="p-1 bg-muted rounded">
            <IconComponent className="h-3 w-3" />
          </div>
        );
      }).filter(Boolean) || []
    );
  }, []);

  const sortedWorkflows = useMemo(() => {
    const data = [...filteredWorkflows];
    const { columnId, direction } = sortState;
    if (!columnId) {
      return data;
    }

    const multiplier = direction === "asc" ? 1 : -1;

    data.sort((a, b) => {
      switch (columnId) {
        case "name":
          return multiplier * a.name.localeCompare(b.name);
        case "trigger_type":
          return multiplier * getTriggerLabel(a.trigger_type).localeCompare(
            getTriggerLabel(b.trigger_type)
          );
        case "status":
          return multiplier * ((a.is_active === b.is_active) ? 0 : a.is_active ? -1 : 1);
        case "created_at":
          return multiplier * (
            new Date(a.created_at).getTime() - new Date(b.created_at).getTime()
          );
        default:
          return 0;
      }
    });

    return data;
  }, [filteredWorkflows, getTriggerLabel, sortState]);

  const totalCount = sortedWorkflows.length;

  const paginatedWorkflows = useMemo(
    () => sortedWorkflows.slice(0, page * pageSize),
    [page, pageSize, sortedWorkflows]
  );

  const hasMoreWorkflows = paginatedWorkflows.length < totalCount;
  const handleLoadMoreWorkflows = useCallback(() => {
    if (!hasMoreWorkflows) return;
    setPage((prev) => prev + 1);
  }, [hasMoreWorkflows]);

  useEffect(() => {
    setPage(1);
  }, [searchQuery, statusFilter]);

  useEffect(() => {
    const maxPage = Math.max(1, Math.ceil(totalCount / pageSize));
    if (page > maxPage) {
      setPage(maxPage);
    }
  }, [page, pageSize, totalCount]);

  const [editingWorkflow, setEditingWorkflow] = useState<Workflow | null>(null);
  const [deletingWorkflow, setDeletingWorkflow] = useState<Workflow | null>(null);
  const [isDeleting, setIsDeleting] = useState(false);

  const handleSearchChange = useCallback((value: string) => {
    setSearchQuery(value);
    setPage(1);
  }, []);

  const handleStatusFilterChange = useCallback((value: string) => {
    const normalized = (value as "all" | "active" | "paused") || "all";
    setStatusFilter(normalized);
    setPage(1);
  }, []);

  const handleEditWorkflow = useCallback((workflow: Workflow) => {
    setEditingWorkflow(workflow);
  }, []);

  const handleDeleteWorkflow = useCallback((workflow: Workflow) => {
    setDeletingWorkflow(workflow);
  }, []);

  const confirmDeleteWorkflow = async () => {
    if (!deletingWorkflow) return;

    setIsDeleting(true);
    try {
      await deleteWorkflow(deletingWorkflow.id);
      setDeletingWorkflow(null);
    } catch (error) {
      console.error('Failed to delete workflow:', error);
    } finally {
      setIsDeleting(false);
    }
  };

  const cancelDeleteWorkflow = () => {
    if (!isDeleting) {
      setDeletingWorkflow(null);
    }
  };

  const stats = useMemo(
    () => ({
      total: workflows.length,
      active: workflows.filter((w) => w.is_active).length,
      paused: workflows.filter((w) => !w.is_active).length,
    }),
    [workflows]
  );

  const totalSummary = useMemo(
    () =>
      t("workflows.stats.summary", {
        active: stats.active,
        paused: stats.paused,
      }),
    [stats.active, stats.paused, t]
  );

  const activeCoverage = stats.total > 0 ? (stats.active / stats.total) * 100 : 0;
  const pausedShare = stats.total > 0 ? (stats.paused / stats.total) * 100 : 0;
  // Prefab icon style sets for KPI cards (shared across the app)
  const iconIndigo = getKpiIconPreset("indigo");
  const iconSky = getKpiIconPreset("sky");
  const iconEmerald = getKpiIconPreset("emerald");

  const workflowColumns: AdvancedTableColumn<Workflow>[] = useMemo(
    () => [
      {
        id: "name",
        label: t("workflows.table.workflowName"),
        sortable: true,
        sortId: "name",
        hideable: false,
        minWidth: "200px",
        render: (workflow) => (
          <div>
            <div className="font-medium">{workflow.name}</div>
            {workflow.description && (
              <div className="text-sm text-muted-foreground truncate max-w-[200px]">
                {workflow.description}
              </div>
            )}
          </div>
        ),
      },
      {
        id: "trigger_type",
        label: t("workflows.table.trigger"),
        sortable: true,
        sortId: "trigger_type",
        hideable: true,
        minWidth: "160px",
        render: (workflow) => (
          <div className="text-sm">
            {getTriggerLabel(workflow.trigger_type)}
          </div>
        ),
      },
      {
        id: "channels",
        label: t("workflows.table.channels"),
        sortable: false,
        hideable: true,
        minWidth: "120px",
        render: (workflow) => {
          const channels = (workflow as any).channels || [];
          const icons = getChannelIcons(channels);

          return (
            <div className="flex gap-1">
              {icons.length > 0 ? (
                icons
              ) : (
                <span className="text-muted-foreground text-xs">
                  {t("workflows.channels.noChannels")}
                </span>
              )}
            </div>
          );
        },
      },
      {
        id: "status",
        label: t("workflows.table.status"),
        sortable: true,
        sortId: "status",
        hideable: true,
        minWidth: "120px",
        render: (workflow) => (
          <Badge variant={workflow.is_active ? "default" : "secondary"}>
            {workflow.is_active
              ? t("workflows.status.active")
              : t("workflows.status.paused")}
          </Badge>
        ),
      },
      {
        id: "created_at",
        label: t("workflows.table.created"),
        sortable: true,
        sortId: "created_at",
        hideable: true,
        minWidth: "160px",
        render: (workflow) => (
          <div className="text-sm text-muted-foreground">
            {formatDistanceToNow(new Date(workflow.created_at), {
              addSuffix: true,
              locale: dateLocale,
            })}
          </div>
        ),
      },
    ],
    [dateLocale, getTriggerLabel, t]
  );

  const headerActions = useMemo(
    () => (
      <div className="flex flex-wrap items-center justify-end gap-3">
        <SegmentedControl
          size="md"
          value={statusFilter}
          onValueChange={(value) => handleStatusFilterChange(value as typeof statusFilter)}
          options={[
            { value: "all", label: t("workflows.tabs.all") },
            { value: "active", label: t("workflows.tabs.active") },
            { value: "paused", label: t("workflows.tabs.paused") },
          ]}
        />
        <CreateWorkflowSheet
          onCreateWorkflow={createWorkflow}
          editWorkflow={editingWorkflow}
          onUpdateWorkflow={updateWorkflow}
          setEditingWorkflow={setEditingWorkflow}
        >
          <Button className="flex items-center gap-2 whitespace-nowrap">
            <Plus className="h-4 w-4" />
            {t("workflows.buttons.createWorkflow")}
          </Button>
        </CreateWorkflowSheet>
      </div>
    ),
    [
      createWorkflow,
      editingWorkflow,
      handleStatusFilterChange,
      statusFilter,
      setEditingWorkflow,
      t,
      updateWorkflow,
    ]
  );

  const renderRowActions = useCallback(
    (workflow: Workflow) => (
      <div className="flex items-center gap-2">
        <Switch
          checked={workflow.is_active}
          onCheckedChange={(checked) => toggleWorkflowStatus(workflow.id, checked)}
        />
        <Button
          variant="ghost"
          size="sm"
          onClick={() => handleEditWorkflow(workflow)}
          className="h-8 w-8 p-0"
        >
          <Edit className="h-4 w-4" />
        </Button>
        <Button
          variant="ghost"
          size="sm"
          onClick={() => handleDeleteWorkflow(workflow)}
          className="h-8 w-8 p-0 text-destructive hover:text-destructive"
        >
          <Trash2 className="h-4 w-4" />
        </Button>
      </div>
    ),
    [handleDeleteWorkflow, handleEditWorkflow, toggleWorkflowStatus]
  );

  const handleTableSortChange = useCallback((next: AdvancedDataTableSortState) => {
    if (!next.columnId) {
      setSortState({ columnId: null, direction: next.direction });
    } else {
      setSortState(next);
    }
    setPage(1);
  }, []);

  if (loading) {
    return (
      <div className="min-h-screen overflow-x-hidden">
        <PageHeader 
          title={t("workflows.title")}
          subtitle={t("workflows.subtitle")}
        />
        <PageLoadingSkeleton />
      </div>
    );
  }

  return (
    <div className="min-h-screen overflow-x-hidden">
      <PageHeader 
        title={t("workflows.title")}
        subtitle={t("workflows.subtitle")}
      />

      <div className="p-4 space-y-6">
        {/* Stats Cards */}
        <div className="grid grid-cols-1 gap-4 lg:grid-cols-3">
          <KpiCard
            icon={Zap}
            {...iconIndigo}
            title={t("workflows.stats.totalWorkflows")}
            value={stats.total}
            description={totalSummary}
            footer={
              <Button
                size="xs"
                variant="outline"
                className={KPI_ACTION_BUTTON_CLASS}
                onClick={() => handleStatusFilterChange("all")}
              >
                {t("workflows.stats.viewAll")}
              </Button>
            }
          />
          <KpiCard
            icon={CheckCircle}
            {...iconSky}
            title={t("workflows.stats.active")}
            value={stats.active}
            description={t("workflows.stats.activeDescription")}
            progress={{
              value: activeCoverage,
              label: t("workflows.stats.coverageLabel"),
              ariaLabel: t("workflows.stats.coverageAriaLabel"),
              action: (
                <Button
                  size="xs"
                  variant="outline"
                  className={KPI_ACTION_BUTTON_CLASS}
                  onClick={() => handleStatusFilterChange("active")}
                >
                  {t("workflows.stats.quickFilterActive")}
                </Button>
              ),
            }}
          />
          <KpiCard
            icon={Clock}
            {...iconEmerald}
            title={t("workflows.stats.paused")}
            value={stats.paused}
            description={t("workflows.stats.pausedDescription")}
            progress={{
              value: pausedShare,
              label: t("workflows.stats.pausedShareLabel"),
              ariaLabel: t("workflows.stats.pausedShareAriaLabel"),
              action: (
                <Button
                  size="xs"
                  variant="outline"
                  className={KPI_ACTION_BUTTON_CLASS}
                  onClick={() => handleStatusFilterChange("paused")}
                >
                  {t("workflows.stats.quickFilterPaused")}
                </Button>
              ),
            }}
          />
        </div>

        <AdvancedDataTable
          title={t("workflows.table.header", { defaultValue: t("workflows.title") })}
          data={paginatedWorkflows}
          columns={workflowColumns}
          rowKey={(workflow) => workflow.id}
          isLoading={loading}
          zebra
          actions={headerActions}
          searchValue={searchQuery}
          onSearchChange={handleSearchChange}
          searchPlaceholder={t("workflows.search")}
          searchLoading={loading}
          searchDelay={0}
          sortState={sortState}
          onSortChange={handleTableSortChange}
          onLoadMore={hasMoreWorkflows ? handleLoadMoreWorkflows : undefined}
          hasMore={hasMoreWorkflows}
          emptyState={
            workflows.length === 0 ? (
              <div className="flex flex-col items-center justify-center p-12 text-center">
                <div className="p-4 bg-primary/10 rounded-full mb-4">
                  <Zap className="h-8 w-8 text-primary" />
                </div>
                <h3 className="text-lg font-semibold mb-2">
                  {t("workflows.emptyState.noWorkflowsYet")}
                </h3>
                <p className="text-muted-foreground mb-6 max-w-sm">
                  {t("workflows.emptyState.createFirstMessage")}
                </p>
                <CreateWorkflowSheet
                  onCreateWorkflow={createWorkflow}
                  editWorkflow={editingWorkflow}
                  onUpdateWorkflow={updateWorkflow}
                  setEditingWorkflow={setEditingWorkflow}
                >
                  <Button>
                    <Plus className="h-4 w-4 mr-2" />
                    {t("workflows.buttons.createFirstWorkflow")}
                  </Button>
                </CreateWorkflowSheet>
              </div>
            ) : (
              <div className="flex flex-col items-center justify-center p-8 text-center">
                <AlertTriangle className="h-8 w-8 text-muted-foreground mb-4" />
                <h3 className="text-lg font-semibold mb-2">
                  {t("workflows.emptyState.noMatchingFilters")}
                </h3>
                <p className="text-muted-foreground">
                  {t("workflows.emptyState.adjustFilters")}
                </p>
              </div>
            )
          }
          rowActions={renderRowActions}
        />
      </div>

      {/* Delete Confirmation Dialog */}
      <WorkflowDeleteDialog
        open={!!deletingWorkflow}
        workflow={deletingWorkflow}
        onConfirm={confirmDeleteWorkflow}
        onCancel={cancelDeleteWorkflow}
        isDeleting={isDeleting}
      />
    </div>
  );
}

import userEvent from "@testing-library/user-event";
import { render, screen, waitFor } from "@/utils/testUtils";
import GettingStarted from "../GettingStarted";
import { useAuth } from "@/contexts/AuthContext";
import { useOnboarding } from "@/contexts/OnboardingContext";
import { useNavigate } from "react-router-dom";

jest.mock("react-router-dom", () => ({
  ...jest.requireActual("react-router-dom"),
  useNavigate: jest.fn(),
}));

jest.mock("@/contexts/AuthContext", () => ({
  useAuth: jest.fn(),
}));

jest.mock("@/contexts/OnboardingContext", () => ({
  useOnboarding: jest.fn(),
}));

jest.mock("@/components/SampleDataModal", () => ({
  SampleDataModal: ({ open, onClose }: any) =>
    open ? (
      <div data-testid="sample-data-modal">
        <button onClick={onClose}>close-sample</button>
      </div>
    ) : null,
}));

jest.mock("@/components/RestartGuidedModeButton", () => ({
  RestartGuidedModeButton: () => <div data-testid="restart-guided" />,
}));

jest.mock("@/components/ExitGuidanceModeButton", () => ({
  ExitGuidanceModeButton: () => <div data-testid="exit-guided" />,
}));

jest.mock("@/components/GuidedStepProgress", () => ({
  GuidedStepProgress: (props: any) => (
    <div data-testid="guided-progress" data-props={JSON.stringify(props)} />
  ),
}));

jest.mock("react-i18next", () => ({
  useTranslation: () => ({
    t: (key: string) => key,
  }),
}));

const mockUseAuth = useAuth as jest.Mock;
const mockUseOnboarding = useOnboarding as jest.Mock;
const mockUseNavigate = useNavigate as jest.Mock;

const buildOnboardingState = (overrides: Partial<ReturnType<typeof mockUseOnboarding>> = {}) => ({
  loading: false,
  isInGuidedSetup: true,
  isOnboardingComplete: false,
  currentStepInfo: { id: 1, route: "/leads", title: "Lead" },
  nextStepInfo: { id: 2, route: "/projects", title: "Project" },
  completedSteps: [],
  isAllStepsComplete: false,
  totalSteps: 5,
  currentStep: 1,
  completeOnboarding: jest.fn().mockResolvedValue(undefined),
  ...overrides,
});

describe("GettingStarted page", () => {
  const navigate = jest.fn();

  beforeEach(() => {
    jest.clearAllMocks();
    mockUseAuth.mockReturnValue({ user: { id: "user-1" } });
    mockUseNavigate.mockReturnValue(navigate);
  });

  it("shows loading state while onboarding data is fetching", () => {
    mockUseOnboarding.mockReturnValue(
      buildOnboardingState({
        loading: true,
        completedSteps: [],
        currentStepInfo: null,
        nextStepInfo: null,
      })
    );

    render(<GettingStarted />);

    expect(
      screen.getByText("onboarding.getting_started.loading")
    ).toBeInTheDocument();
  });

  it("redirects to dashboard when onboarding already complete", () => {
    const completeState = buildOnboardingState({
      loading: false,
      isInGuidedSetup: false,
      isOnboardingComplete: true,
    });
    mockUseOnboarding.mockReturnValue(completeState);

    render(<GettingStarted />);

    expect(navigate).toHaveBeenCalledWith("/", { replace: true });
  });

  it("renders current step info and allows navigation to tutorial routes", async () => {
    const state = buildOnboardingState({
      completedSteps: [
        { id: 99, route: "/completed" },
      ],
      currentStepInfo: { id: 1, route: "/projects", title: "Projects" },
      nextStepInfo: { id: 3, route: "/calendar", title: "Calendar" },
      currentStep: 2,
    });
    mockUseOnboarding.mockReturnValue(state);

    render(<GettingStarted />);

    const progress = screen.getByTestId("guided-progress");
    expect(progress).toHaveAttribute(
      "data-props",
      expect.stringContaining('"currentValue":1')
    );

    const user = userEvent.setup();
    const primaryButton = screen.getByRole("button", {
      name: "onboarding.steps.step_1.button",
    });
    await user.click(primaryButton);

    expect(navigate).toHaveBeenCalledWith("/projects?tutorial=true");

    expect(screen.queryByTestId("sample-data-modal")).not.toBeInTheDocument();
    const skipButton = screen.getByRole("button", {
      name: "onboarding.getting_started.skip_setup",
    });
    await user.click(skipButton);
    expect(screen.getByTestId("sample-data-modal")).toBeInTheDocument();

    await user.click(screen.getByText("close-sample"));
    await waitFor(() =>
      expect(screen.queryByTestId("sample-data-modal")).not.toBeInTheDocument()
    );
  });

  it("completes onboarding when finishing guided steps", async () => {
    const completeOnboarding = jest.fn().mockResolvedValue(undefined);
    const state = buildOnboardingState({
      isAllStepsComplete: true,
      completeOnboarding,
    });
    mockUseOnboarding.mockReturnValue(state);

    render(<GettingStarted />);

    const user = userEvent.setup();
    const finishButton = screen.getByRole("button", {
      name: "onboarding.getting_started.go_to_dashboard",
    });

    await user.click(finishButton);

    await waitFor(() => expect(completeOnboarding).toHaveBeenCalledTimes(1));
    expect(navigate).toHaveBeenCalledWith("/", { replace: true });
  });
});

import { useState, useEffect, useRef, useMemo } from "react";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Button } from "@/components/ui/button";
import { Switch } from "@/components/ui/switch";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogTrigger } from "@/components/ui/dialog";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { supabase } from "@/integrations/supabase/client";
import { toast } from "@/hooks/use-toast";
import { Language } from "@/i18n/types";
import { useTranslationFiles } from "@/hooks/useTranslationFiles";
import { Globe, FileText, Upload, Download, Edit, Info, FileDown, Package } from "lucide-react";
import { useTranslation } from "react-i18next";
import { useCommonTranslation } from "@/hooks/useTypedTranslation";
import { SegmentedControl } from "@/components/ui/segmented-control";

type SegmentValue = "json-manager" | "languages";

export default function AdminLocalization() {
  const [languages, setLanguages] = useState<Language[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [activeSegment, setActiveSegment] = useState<SegmentValue>("json-manager");

  const fileInputRef = useRef<HTMLInputElement>(null);
  const {
    downloadLanguageFile,
    downloadLanguagePack,
    downloadAllTranslations,
    uploadTranslationFile,
    getAvailableLanguages,
    getNamespacesForLanguage,
    getTranslationStats,
    isProcessing,
  } = useTranslationFiles();
  const { t: tCommon } = useCommonTranslation();
  const { t, i18n } = useTranslation("pages");
  const workflowSteps = useMemo(
    () =>
      t("admin.localization.cards.workflow.steps", {
        returnObjects: true,
      }) as Array<{ title: string; description: string }>,
    [t, i18n.language]
  );

  useEffect(() => {
    loadData();
  }, []);

  const loadData = async () => {
    try {
      setIsLoading(true);

      // Load languages
      const { data: languagesData } = await supabase
        .from('languages')
        .select('*')
        .order('sort_order');
      setLanguages(languagesData || []);
    } catch (error) {
      console.error('Error loading data:', error);
      toast({
        title: tCommon("toast.error"),
        description: t("admin.localization.toast.loadError"),
        variant: "destructive",
      });
    } finally {
      setIsLoading(false);
    }
  };

  const toggleLanguage = async (languageId: string, isActive: boolean) => {
    try {
      const { error } = await supabase
        .from('languages')
        .update({ is_active: isActive })
        .eq('id', languageId);

      if (error) throw error;

      setLanguages(prev => prev.map(lang => 
        lang.id === languageId ? { ...lang, is_active: isActive } : lang
      ));

      toast({
        title: tCommon("toast.success"),
        description: isActive
          ? t("admin.localization.toast.languageActivated")
          : t("admin.localization.toast.languageDeactivated"),
      });
    } catch (error) {
      console.error('Error updating language:', error);
      toast({
        title: tCommon("toast.error"),
        description: t("admin.localization.toast.languageUpdateError"),
        variant: "destructive",
      });
    }
  };

  const handleFileUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const files = event.target.files;
    if (!files || files.length === 0) return;

    // Process files with file writing capability
    const results = await Promise.all(
      Array.from(files).map(async (file) => {
        // Create a file writer function that will write files immediately
        const fileWriter = async (filePath: string, content: string) => {
          // Create a promise that will be resolved when the file is written
          await writeTranslationFile(filePath, content);
        };

        return uploadTranslationFile(file, fileWriter);
      })
    );

    const successCount = results.filter(Boolean).length;
    const totalCount = results.length;

    if (successCount === totalCount) {
      toast({
        title: tCommon("toast.success"),
        description: t("admin.localization.toast.uploadSuccess", { count: successCount }),
      });
    } else {
      toast({
        title: tCommon("toast.warning"),
        description: t("admin.localization.toast.uploadPartial", { success: successCount, total: totalCount }),
        variant: "destructive",
      });
    }

    // Reset the input and reload data
    event.target.value = '';
    await loadData();
  };

  // Function to handle actual file writing
  const writeTranslationFile = async (filePath: string, content: string) => {
    // Store the file write request for batch processing
    const pendingWrites = JSON.parse(localStorage.getItem('pendingTranslationWrites') || '[]');
    pendingWrites.push({ filePath, content, timestamp: Date.now() });
    localStorage.setItem('pendingTranslationWrites', JSON.stringify(pendingWrites));

    // Trigger immediate processing
    processPendingWrites();
  };

  // Process pending file writes
  const processPendingWrites = () => {
    const pendingWrites = JSON.parse(localStorage.getItem('pendingTranslationWrites') || '[]');
    if (pendingWrites.length > 0) {
      // Emit custom event that parent component can listen to
      window.dispatchEvent(new CustomEvent('processPendingTranslationWrites', {
        detail: { writes: pendingWrites }
      }));
      // Clear pending writes
      localStorage.removeItem('pendingTranslationWrites');
    }
  };

  if (isLoading) {
    return (
      <div className="container mx-auto py-6 space-y-6">
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold tracking-tight">{t("admin.localization.title")}</h1>
            <p className="text-muted-foreground">{t("admin.localization.loading")}</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto py-6 space-y-6">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold tracking-tight">{t("admin.localization.title")}</h1>
          <p className="text-muted-foreground">
            {t("admin.localization.subtitle")}
          </p>
        </div>
        <div className="flex gap-2">
          <input
            ref={fileInputRef}
            type="file"
            accept=".json"
            multiple
            onChange={handleFileUpload}
            className="hidden"
          />
          <Button 
            variant="outline" 
            size="sm"
            onClick={() => fileInputRef.current?.click()}
            disabled={isProcessing}
          >
            <Upload className="w-4 h-4 mr-2" />
            {isProcessing ? t("admin.localization.buttons.processing") : t("admin.localization.buttons.upload")}
          </Button>
          <Dialog>
            <DialogTrigger asChild>
              <Button variant="outline" size="sm">
                <Download className="w-4 h-4 mr-2" />
                {t("admin.localization.buttons.download")}
              </Button>
            </DialogTrigger>
            <DialogContent className="max-w-md">
              <DialogHeader>
                <DialogTitle>{t("admin.localization.dialog.title")}</DialogTitle>
              </DialogHeader>
              <div className="space-y-4">
                <div className="space-y-2">
                  <h4 className="font-medium">{t("admin.localization.dialog.individual")}</h4>
                  <div className="grid grid-cols-2 gap-2">
                    {getAvailableLanguages().flatMap(lang =>
                      getNamespacesForLanguage(lang).map(ns => (
                        <Button
                          key={`${lang}-${ns}`}
                          variant="outline"
                          size="sm"
                          onClick={() => downloadLanguageFile(lang, ns)}
                          className="text-xs"
                        >
                          <FileDown className="w-3 h-3 mr-1" />
                          {lang}/{ns}
                        </Button>
                      ))
                    )}
                  </div>
                </div>
                <div className="space-y-2">
                  <h4 className="font-medium">{t("admin.localization.dialog.packs")}</h4>
                  <div className="space-y-1">
                    {getAvailableLanguages().map(lang => (
                      <Button
                        key={lang}
                        variant="outline"
                        size="sm"
                        onClick={() => downloadLanguagePack(lang)}
                        className="w-full justify-start"
                      >
                        <Package className="w-4 h-4 mr-2" />
                        {t("admin.localization.dialog.completePack", { code: lang.toUpperCase() })}
                      </Button>
                    ))}
                  </div>
                </div>
                <Button
                  variant="default"
                  onClick={downloadAllTranslations}
                  className="w-full"
                >
                  <Download className="w-4 h-4 mr-2" />
                  {t("admin.localization.buttons.downloadAll")}
                </Button>
              </div>
            </DialogContent>
          </Dialog>
        </div>
      </div>

      <Alert className="mb-6">
        <Info className="h-4 w-4" />
        <AlertDescription>
          <strong>{t("admin.localization.alert.title")}</strong>{" "}
          {t("admin.localization.alert.description")}
          <div className="mt-2">
            <span>{t("admin.localization.alert.formatLabel")} </span>
            <code className="bg-muted px-1 rounded">
              {t("admin.localization.alert.formatCode")}
            </code>
          </div>
        </AlertDescription>
      </Alert>

      <div className="space-y-4">
        <SegmentedControl
          value={activeSegment}
          onValueChange={(value) => setActiveSegment(value as SegmentValue)}
          options={[
            {
              value: "json-manager",
              label: (
                <span className="flex items-center gap-2">
                  <FileText className="h-3.5 w-3.5" />
                  {t("admin.localization.tabs.jsonManager")}
                </span>
              ),
            },
            {
              value: "languages",
              label: (
                <span className="flex items-center gap-2">
                  <Globe className="h-3.5 w-3.5" />
                  {t("admin.localization.tabs.languages")}
                </span>
              ),
            },
          ]}
        />

        {activeSegment === "json-manager" && (
          <div className="space-y-4">
            <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
              <Card>
                <CardHeader>
                  <CardTitle>{t("admin.localization.cards.status.title")}</CardTitle>
                  <CardDescription>{t("admin.localization.cards.status.description")}</CardDescription>
                </CardHeader>
                <CardContent>
                  <div className="space-y-4">
                    {Object.entries(getTranslationStats()).map(([lang, namespaces]) => (
                      <div key={lang} className="border rounded-lg p-3">
                        <div className="flex items-center justify-between mb-2">
                          <h4 className="font-medium">{lang.toUpperCase()}</h4>
                          <Badge variant="outline">
                            {t("admin.localization.cards.status.keysBadge", {
                              count: Object.values(namespaces).reduce((a, b) => a + b, 0),
                            })}
                          </Badge>
                        </div>
                        <div className="grid grid-cols-2 gap-2 text-sm">
                          {Object.entries(namespaces).map(([ns, count]) => (
                            <div key={ns} className="flex justify-between">
                              <span className="text-muted-foreground">{ns}:</span>
                              <span>{count}</span>
                            </div>
                          ))}
                        </div>
                      </div>
                    ))}
                  </div>
                </CardContent>
              </Card>

              <Card>
                <CardHeader>
                  <CardTitle>{t("admin.localization.cards.quickActions.title")}</CardTitle>
                  <CardDescription>{t("admin.localization.cards.quickActions.description")}</CardDescription>
                </CardHeader>
                <CardContent className="space-y-3">
                  <div className="space-y-4">
                    <h4 className="font-medium text-sm">{t("admin.localization.cards.quickActions.downloadIndividual")}</h4>
                    <div className="space-y-3">
                      {getAvailableLanguages().map(lang => (
                        <div key={lang} className="space-y-2">
                          <div className="text-xs font-medium text-muted-foreground">
                            {lang.toUpperCase()}
                          </div>
                          <div className="grid grid-cols-2 gap-2">
                            {getNamespacesForLanguage(lang).map(ns => (
                              <Button
                                key={`${lang}-${ns}`}
                                variant="outline"
                                size="sm"
                                onClick={() => downloadLanguageFile(lang, ns)}
                                className="text-xs justify-start"
                              >
                                <FileDown className="w-3 h-3 mr-1" />
                                {lang}/{ns}
                              </Button>
                            ))}
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>

                  <div className="space-y-2">
                    <h4 className="font-medium text-sm">{t("admin.localization.cards.quickActions.downloadPacks")}</h4>
                    {getAvailableLanguages().map(lang => (
                      <Button
                        key={lang}
                        variant="outline"
                        size="sm"
                        onClick={() => downloadLanguagePack(lang)}
                        className="w-full justify-start"
                      >
                        <Package className="w-4 h-4 mr-2" />
