import { supabase } from '@/integrations/supabase/client';
import { BaseEntityService } from './BaseEntityService';

export interface LeadWithCustomFields {
  id: string;
  name: string;
  email: string;
  phone: string;
  status: string;
  status_id: string;
  updated_at: string;
  created_at: string;
  due_date?: string;
  notes?: string;
  custom_fields: Record<string, string | null>;
  lead_statuses?: {
    id: string;
    name: string;
    color: string;
    is_system_final: boolean;
  };
}

export interface CreateLeadData {
  name: string;
  email?: string;
  phone?: string;
  notes?: string;
  status_id?: string;
  due_date?: string;
  custom_fields?: Record<string, string | null>;
}

export interface UpdateLeadData {
  name?: string;
  email?: string;
  phone?: string;
  notes?: string;
  status_id?: string;
  due_date?: string;
}

export interface LeadFilters {
  status?: string;
  statusId?: string;
  search?: string;
  dueDateFrom?: string;
  dueDateTo?: string;
}

export interface LeadSort {
  field: 'name' | 'email' | 'phone' | 'status' | 'due_date' | 'created_at' | 'updated_at';
  direction: 'asc' | 'desc';
}

/**
 * Service for lead-related operations
 */
export class LeadService extends BaseEntityService {
  constructor() {
    super();
  }

  /**
   * Fetch leads with custom fields and status information
   */
  async fetchLeadsWithCustomFields(): Promise<LeadWithCustomFields[]> {
    const organizationId = await this.getOrganizationId();
    if (!organizationId) return [];

    // Fetch leads with status information
    const { data: leadsData, error: leadsError } = await supabase
      .from('leads')
      .select(`
        *,
        lead_statuses(id, name, color, is_system_final)
      `)
      .eq('organization_id', organizationId)
      .order('updated_at', { ascending: false });

    if (leadsError) throw leadsError;

    // Fetch all custom field values for these leads
    const leadIds = leadsData?.map(lead => lead.id) || [];
    if (leadIds.length === 0) return [];

    const { data: fieldValues, error: fieldValuesError } = await supabase
      .from('lead_field_values')
      .select('lead_id, field_key, value')
      .in('lead_id', leadIds);

    if (fieldValuesError) throw fieldValuesError;

    // Group field values by lead_id
    const fieldValuesByLead = (fieldValues || []).reduce((acc, fv) => {
      if (!acc[fv.lead_id]) {
        acc[fv.lead_id] = {};
      }
      acc[fv.lead_id][fv.field_key] = fv.value;
      return acc;
    }, {} as Record<string, Record<string, string | null>>);

  // Combine leads with their custom field values
  return (leadsData || []).map(lead => ({
    ...lead,
    custom_fields: fieldValuesByLead[lead.id] || {},
  }));
}

  /**
   * Fetch filtered and sorted leads
   */
  async fetchLeads(filters?: LeadFilters, sort?: LeadSort): Promise<LeadWithCustomFields[]> {
    let leads = await this.fetchLeadsWithCustomFields();

    // Apply filters
    if (filters) {
      if (filters.status && filters.status !== 'all') {
        leads = leads.filter(lead => lead.lead_statuses?.name === filters.status);
      }
      if (filters.statusId) {
        leads = leads.filter(lead => lead.status_id === filters.statusId);
      }
      if (filters.search) {
        const searchLower = filters.search.toLowerCase();
        leads = leads.filter(lead => 
          lead.name.toLowerCase().includes(searchLower) ||
          lead.email?.toLowerCase().includes(searchLower) ||
          lead.phone?.toLowerCase().includes(searchLower) ||
          lead.notes?.toLowerCase().includes(searchLower)
        );
      }
      if (filters.dueDateFrom) {
        leads = leads.filter(lead => 
          lead.due_date && lead.due_date >= filters.dueDateFrom!
        );
      }
      if (filters.dueDateTo) {
        leads = leads.filter(lead => 
          lead.due_date && lead.due_date <= filters.dueDateTo!
        );
      }
    }

    // Apply sorting
    if (sort) {
      leads.sort((a, b) => {
        let aValue: any = a[sort.field];
        let bValue: any = b[sort.field];

        // Handle special cases
        if (sort.field === 'status') {
          aValue = a.lead_statuses?.name || '';
          bValue = b.lead_statuses?.name || '';
        }

        if (typeof aValue === 'string' && typeof bValue === 'string') {
          aValue = aValue.toLowerCase();
          bValue = bValue.toLowerCase();
        }

        if (sort.field === 'created_at' || sort.field === 'updated_at' || sort.field === 'due_date') {
          aValue = aValue ? new Date(aValue).getTime() : 0;
          bValue = bValue ? new Date(bValue).getTime() : 0;
        }

        if (aValue < bValue) return sort.direction === 'asc' ? -1 : 1;
        if (aValue > bValue) return sort.direction === 'asc' ? 1 : -1;
        return 0;
      });
    }

    return leads;
  }

  /**
   * Create a new lead
   */
  async createLead(data: CreateLeadData): Promise<LeadWithCustomFields> {
    const organizationId = await this.getOrganizationId();
    if (!organizationId) throw new Error('No organization ID found');

    const user = await this.getAuthenticatedUser();

    // Create the lead first
    const { data: leadData, error } = await supabase
      .from('leads')
      .insert({
        name: data.name,
        email: data.email,
        phone: data.phone,
        notes: data.notes,
        status_id: data.status_id,
        due_date: data.due_date,
        organization_id: organizationId,
        user_id: user.id,
      })
      .select()
      .single();

    if (error || !leadData) throw new Error('Failed to create lead');

    // Handle custom fields if provided
    if (data.custom_fields) {
      const fieldValues = Object.entries(data.custom_fields)
        .filter(([_, value]) => value !== null && value !== undefined && value !== '')
        .map(([field_key, value]) => ({
          lead_id: leadData.id,
          field_key,
          value,
        }));

      if (fieldValues.length > 0) {
        const { error } = await supabase
          .from('lead_field_values')
          .insert(fieldValues);

        if (error) throw error;
      }
    }

    // Return the created lead with custom fields
    const leads = await this.fetchLeadsWithCustomFields();
    const createdLead = leads.find(lead => lead.id === leadData.id);
    if (!createdLead) throw new Error('Failed to fetch created lead');
    return createdLead;
  }

  /**
   * Update an existing lead
   */
  async updateLead(id: string, data: UpdateLeadData): Promise<LeadWithCustomFields | null> {
    const { data: result, error } = await supabase
      .from('leads')
      .update(data)
      .eq('id', id)
      .select()
      .single();

    if (error || !result) return null;

    // Fetch and return updated lead with custom fields
    const leads = await this.fetchLeadsWithCustomFields();
    return leads.find(lead => lead.id === id) || null;
  }

  /**
   * Delete a lead
   */
  async deleteLead(id: string): Promise<boolean> {
    const { error } = await supabase
      .from('leads')
      .delete()
      .eq('id', id);

    if (error) throw error;
    return true;
  }

  /**
   * Update custom field value for a lead
   */
  async updateCustomField(leadId: string, fieldKey: string, value: string | null): Promise<void> {
    const { error } = await supabase
      .from('lead_field_values')
      .upsert(
        { lead_id: leadId, field_key: fieldKey, value },
        { onConflict: 'lead_id,field_key' }
      );

    if (error) throw error;
  }
}

import { supabase } from '@/integrations/supabase/client';
import { BaseEntityService } from './BaseEntityService';

export interface Session {
  id: string;
  lead_id: string;
  session_date: string;
  session_time: string;
  notes: string;
  status: string;
  created_at: string;
  project_id?: string | null;
  session_name?: string | null;
  location?: string | null;
  google_event_id?: string | null;
  organization_id: string;
  user_id: string;
  project_name?: string;
  lead_name?: string;
  lead_status?: string;
  lead?: {
    id: string;
    name: string;
    status: string;
    email?: string;
    phone?: string;
  };
  project?: {
    id: string;
    name: string;
    status_id?: string;
  };
}

export interface CreateSessionData {
  lead_id: string;
  session_date: string;
  session_time: string;
  notes?: string;
  status?: string;
  project_id?: string;
  session_name?: string;
  location?: string;
}

export interface UpdateSessionData {
  session_date?: string;
  session_time?: string;
  notes?: string;
  status?: string;
  project_id?: string;
  session_name?: string;
  location?: string;
}

export interface SessionFilters {
  status?: string;
  dateRange?: {
    start: Date;
    end: Date;
  };
  leadId?: string;
  projectId?: string;
  search?: string;
}

export interface SessionSort {
  field: 'session_date' | 'session_time' | 'status' | 'lead_name' | 'created_at';
  direction: 'asc' | 'desc';
}

/**
 * Service for session-related operations
 */
export class SessionService extends BaseEntityService {
  constructor() {
    super();
  }

  /**
   * Fetch sessions with all related data
   */
  async fetchSessions(): Promise<Session[]> {
    const organizationId = await this.getOrganizationId();
    if (!organizationId) return [];

    // Get sessions with proper validation using inner joins
    const { data: sessionsData, error: sessionsError } = await supabase
      .from('sessions')
      .select(`
        *,
        leads!inner(id, name, status, email, phone),
        projects(id, name, status_id)
      `)
      .eq('organization_id', organizationId)
      .order('session_date', { ascending: false })
      .order('session_time', { ascending: false });

    if (sessionsError) throw sessionsError;

    // Filter out sessions with invalid references or archived projects
    const { data: userData } = await supabase.auth.getUser();
    const userId = userData.user?.id;
    let filteredSessions = sessionsData || [];

    if (userId && organizationId) {
      // Get archived status for filtering
      const { data: archivedStatus } = await supabase
        .from('project_statuses')
        .select('id, name')
        .eq('organization_id', organizationId)
        .ilike('name', 'archived')
        .maybeSingle();

      filteredSessions = filteredSessions.filter(session => {
        // Must have valid lead (inner join ensures this)
        if (!session.leads) return false;

        // If session has a project, check if it's archived
        if (session.project_id && session.projects) {
          if (archivedStatus?.id && session.projects.status_id === archivedStatus.id) {
            return false;
          }
        }

        return true;
      });
    }

    // Process sessions with enhanced data validation
    return filteredSessions.map(session => ({
      ...session,
      lead_name: session.leads?.name || 'Unknown Lead',
      lead_status: session.leads?.status || 'unknown',
      project_name: session.projects?.name || undefined,
      lead: session.leads,
      project: session.projects
    }));
  }

  /**
   * Fetch filtered and sorted sessions
   */
  async fetchFilteredSessions(
    filters?: SessionFilters,
    sort?: SessionSort
  ): Promise<Session[]> {
    let sessions = await this.fetchSessions();

    // Apply filters
    if (filters) {
      if (filters.status && filters.status !== 'all') {
        sessions = sessions.filter(session => session.status === filters.status);
      }

      if (filters.dateRange) {
        sessions = sessions.filter(session => {
          const sessionDate = new Date(session.session_date);
          return sessionDate >= filters.dateRange!.start && 
                 sessionDate < filters.dateRange!.end;
        });
      }

      if (filters.leadId) {
        sessions = sessions.filter(session => session.lead_id === filters.leadId);
      }

      if (filters.projectId) {
        sessions = sessions.filter(session => session.project_id === filters.projectId);
      }

      if (filters.search) {
        const searchLower = filters.search.toLowerCase();
        sessions = sessions.filter(session => 
          session.lead_name?.toLowerCase().includes(searchLower) ||
          session.project_name?.toLowerCase().includes(searchLower) ||
          session.notes?.toLowerCase().includes(searchLower) ||
          session.location?.toLowerCase().includes(searchLower) ||
          session.session_name?.toLowerCase().includes(searchLower)
        );
      }
    }

    // Apply sorting
    if (sort) {
      sessions.sort((a, b) => {
        // For date sorting, always add secondary sort by time
        if (sort.field === 'session_date') {
          const aDate = a.session_date ? new Date(a.session_date).getTime() : 0;
          const bDate = b.session_date ? new Date(b.session_date).getTime() : 0;

          // First compare dates
          if (aDate !== bDate) {
            return sort.direction === 'asc' ? aDate - bDate : bDate - aDate;
          }

          // If dates are equal, sort by time (always ascending for better UX)
          const aTime = a.session_time || '';
          const bTime = b.session_time || '';
          return aTime.localeCompare(bTime);
        }

        // For other fields, use regular sorting
        let aValue: any = a[sort.field];
        let bValue: any = b[sort.field];

        // Handle date values
        if (sort.field === 'created_at') {
          aValue = aValue ? new Date(aValue).getTime() : 0;
          bValue = bValue ? new Date(bValue).getTime() : 0;
        }

        // Handle time values
        if (sort.field === 'session_time') {
          aValue = aValue ? aValue : '';
          bValue = bValue ? bValue : '';
        }

        // Handle string values
        if (typeof aValue === 'string' && typeof bValue === 'string') {
          aValue = aValue.toLowerCase();
          bValue = bValue.toLowerCase();
        }

        if (aValue < bValue) return sort.direction === 'asc' ? -1 : 1;
        if (aValue > bValue) return sort.direction === 'asc' ? 1 : -1;
        return 0;
      });
    }

    return sessions;
  }

  /**
   * Create a new session
   */
  async createSession(data: CreateSessionData): Promise<Session> {
    const organizationId = await this.getOrganizationId();
    if (!organizationId) throw new Error('No organization ID found');

    const user = await this.getAuthenticatedUser();

    const { data: sessionData, error } = await supabase
      .from('sessions')
      .insert({
        ...data,
        organization_id: organizationId,
        user_id: user.id,
      })
      .select()
      .single();

    if (error || !sessionData) throw new Error('Failed to create session');

    // Fetch and return the created session with all related data
    const sessions = await this.fetchSessions();
    const createdSession = sessions.find(s => s.id === sessionData.id);
    if (!createdSession) throw new Error('Failed to fetch created session');

    return createdSession;
  }

  /**
   * Update an existing session
   */
  async updateSession(id: string, data: UpdateSessionData): Promise<Session | null> {
    const { data: result, error } = await supabase
      .from('sessions')
      .update(data)
      .eq('id', id)
      .select()
      .single();

    if (error || !result) return null;

    // Fetch and return updated session with all related data
    const sessions = await this.fetchSessions();
    return sessions.find(session => session.id === id) || null;
  }

  /**
   * Delete a session
   */
  async deleteSession(id: string): Promise<boolean> {
    const { error } = await supabase
      .from('sessions')
      .delete()
      .eq('id', id);

    if (error) throw error;
    return true;
  }

  /**
   * Get sessions by date range
   */
  async getSessionsByDateRange(startDate: Date, endDate: Date): Promise<Session[]> {
    return this.fetchFilteredSessions({
      dateRange: { start: startDate, end: endDate }
    });
  }

  /**
   * Get upcoming sessions
   */
  async getUpcomingSessions(daysAhead: number = 30): Promise<Session[]> {
    const today = new Date();
    const futureDate = new Date(today.getTime() + (daysAhead * 24 * 60 * 60 * 1000));

    return this.fetchFilteredSessions({
      dateRange: { start: today, end: futureDate }
    }, {
      field: 'session_date',
      direction: 'asc'
    });
  }

  /**
   * Get sessions for a specific lead
   */
  async getSessionsForLead(leadId: string): Promise<Session[]> {
    return this.fetchFilteredSessions({ leadId });
  }

  /**
   * Get sessions for a specific project
   */
  async getSessionsForProject(projectId: string): Promise<Session[]> {
    return this.fetchFilteredSessions({ projectId });
  }
}

