  it("derives health status and metrics from onboarding validation", () => {
    jest.isolateModules(() => {
      jest.doMock("@/constants/onboarding", () => ({
        ONBOARDING_STEPS: [
          {
            id: 2,
            title: "Misnumbered Step",
            description: "Broken data",
            route: "",
            buttonText: "",
            duration: "1 min",
          },
        ],
        TOTAL_STEPS: 3,
      }));

      const moduleRef = require("./onboardingValidation");
      const health = moduleRef.getSystemHealthStatus();

      expect(health.status).toBe("error");
      expect(health.version).toBe("V3");
      expect(health.features.productionReady).toBe(false);
      expect(health.metrics).toEqual({
        totalSteps: 3,
        validSteps: 0,
        errorCount: 3,
        warningCount: 1,
      });
    });
  });
});

import React from 'react';
