import { createEmailTemplate, EmailTemplateData } from './enhanced-email-base.ts';
import { createEmailLocalization } from '../../_shared/email-i18n.ts';

// Base interface for all immediate notifications
interface BaseImmediateNotificationData {
  organizationId: string;
  triggeredByUser: {
    name: string;
    id: string;
  };
}

// Project Assignment specific data
export interface ProjectAssignmentData extends BaseImmediateNotificationData {
  type: 'project-assignment';
  project: {
    id: string;
    name: string;
    type?: string;
    status?: string;
    notes?: string;
    leadName?: string;
  };
  assignee: {
    name: string;
    email: string;
  };
}

// Lead Assignment specific data  
export interface LeadAssignmentData extends BaseImmediateNotificationData {
  type: 'lead-assignment';
  lead: {
    id: string;
    name: string;
    status?: string;
    notes?: string;
  };
  assignee: {
    name: string;
    email: string;
  };
}

// Project Milestone specific data
export interface ProjectMilestoneData extends BaseImmediateNotificationData {
  type: 'project-milestone';
  project: {
    id: string;
    name: string;
    type?: string;
    oldStatus: string;
    newStatus: string;
    lifecycle: 'completed' | 'cancelled';
    notes?: string;
    leadName?: string;
  };
  assignee: {
    name: string;
    email: string;
  };
}

// Union type for all notification data
export type ImmediateNotificationData = ProjectAssignmentData | LeadAssignmentData | ProjectMilestoneData;

// Extended email template data
export interface ImmediateNotificationEmailData {
  user: {
    fullName: string;
    email: string;
  };
  business: {
    businessName: string;
    brandColor?: string;
  };
  notificationData: ImmediateNotificationData;
  language?: string;
  localization?: ReturnType<typeof createEmailLocalization>;
  baseUrl?: string;
}

type TranslateFn = (key: string, variables?: Record<string, unknown>) => string;

/**
 * Helper function to adjust color brightness
 */
function adjustBrightness(hex: string, percent: number): string {
  hex = hex.replace('#', '');
  const num = parseInt(hex, 16);
  const r = Math.min(255, Math.max(0, (num >> 16) + percent));
  const g = Math.min(255, Math.max(0, (num >> 8 & 0x00FF) + percent));
  const b = Math.min(255, Math.max(0, (num & 0x0000FF) + percent));

  return `#${((1 << 24) + (r << 16) + (g << 8) + b).toString(16).slice(1)}`;
}

/**
 * Generate the notification header with emoji and title
 */
function generateNotificationHeader(
  data: ImmediateNotificationData,
  brandColor: string,
  t: TranslateFn,
): string {
  let emoji = '';
  let title = '';
  let subtitle = '';

  switch (data.type) {
    case 'project-assignment':
      emoji = 'ðŸ“‹';
      title = t('immediate.header.projectAssignment.title');
      subtitle = t('immediate.header.projectAssignment.subtitle', {
        name: data.triggeredByUser.name,
      });
      break;
    case 'lead-assignment':
      emoji = 'ðŸ‘¤';
      title = t('immediate.header.leadAssignment.title');
      subtitle = t('immediate.header.leadAssignment.subtitle', {
        name: data.triggeredByUser.name,
      });
      break;
    case 'project-milestone':
      emoji = data.project.lifecycle === 'completed' ? 'ðŸŽ‰' : 'âš ï¸';
      title = t('immediate.header.projectMilestone.title');
      subtitle = data.project.lifecycle === 'completed'
        ? t('immediate.header.projectMilestone.subtitleCompleted', {
            name: data.triggeredByUser.name,
          })
        : t('immediate.header.projectMilestone.subtitleCancelled', {
            name: data.triggeredByUser.name,
          });
      break;
  }

  const lighterBrandColor = adjustBrightness(brandColor, 20);

  return `
    <div style="
      background: linear-gradient(135deg, ${brandColor}15, ${lighterBrandColor}10);
      border-radius: 16px;
      padding: 32px;
      margin: 24px 0;
      border-left: 4px solid ${brandColor};
    ">
      <div style="text-align: center; margin-bottom: 24px;">
        <div style="
          font-size: 48px;
          margin-bottom: 8px;
          line-height: 1;
        ">${emoji}</div>
        <h2 style="
          color: #1f2937;
          font-size: 24px;
          font-weight: 700;
          margin: 0 0 8px 0;
          line-height: 1.2;
        ">${title}</h2>
        <p style="
          color: #6b7280;
          font-size: 16px;
          margin: 0;
          line-height: 1.4;
        ">${subtitle}</p>
      </div>
    </div>
  `;
}

/**
 * Generate call-to-action button
 */
function generateCallToAction(
  data: ImmediateNotificationData,
  brandColor: string,
  t: TranslateFn,
  baseUrl = 'https://my.lumiso.app',
): string {
  let buttonText = '';
  let url = '';

  switch (data.type) {
    case 'project-assignment':
    case 'project-milestone':
      buttonText = t('immediate.callToAction.project');
      url = `${baseUrl}/projects/${data.project.id}`;
      break;
    case 'lead-assignment':
      buttonText = t('immediate.callToAction.lead');
      url = `${baseUrl}/leads/${data.lead.id}`;
      break;
  }

  return `
    <div style="text-align: center; margin: 32px 0;">
      <a href="${url}" style="
        display: inline-block;
        background: ${brandColor};
        color: white;
        padding: 12px 24px;
        border-radius: 8px;
        text-decoration: none;
        font-weight: 600;
        font-size: 14px;
        transition: all 0.2s ease;
      ">${buttonText}</a>
    </div>
  `;
}

/**
 * Generate unified immediate notification email
 */
export function generateImmediateNotificationEmail(emailData: ImmediateNotificationEmailData): string {
  const localization =
    emailData.localization ||
    (emailData.localization = createEmailLocalization(emailData.language));
  const t = localization.t;
  const brandColor = emailData.business.brandColor || '#1EB29F';
  const data = emailData.notificationData;
  const baseUrl = emailData.baseUrl || 'https://my.lumiso.app';

  let content = generateNotificationHeader(data, brandColor, t);

  // Add entity-specific details
  switch (data.type) {
    case 'project-assignment':
      content += `
        <div style="
          background: white;
          border-radius: 12px;
          padding: 24px;
          box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
          margin: 24px 0;
        ">
          <h3 style="
            color: #1f2937;
            font-size: 18px;
            font-weight: 600;
            margin: 0 0 16px 0;
          ">${t('immediate.sections.projectDetails')}</h3>
          <div style="color: #374151; font-size: 16px; line-height: 1.6;">
            <strong>${t('common.labels.project')}:</strong> ${data.project.name}<br>
            ${data.project.type ? `<strong>${t('common.labels.type')}:</strong> ${data.project.type}<br>` : ''}
            ${data.project.status ? `<strong>${t('immediate.cards.status')}:</strong> ${data.project.status}<br>` : ''}
            ${data.project.leadName ? `<strong>${t('common.labels.client')}:</strong> ${data.project.leadName}<br>` : ''}
            ${data.project.notes ? `<strong>${t('common.labels.notes')}:</strong> ${data.project.notes}` : ''}
          </div>
        </div>
      `;
      break;

    case 'lead-assignment':
      content += `
        <div style="
          background: white;
          border-radius: 12px;
          padding: 24px;
          box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
          margin: 24px 0;
        ">
          <h3 style="
            color: #1f2937;
            font-size: 18px;
            font-weight: 600;
            margin: 0 0 16px 0;
          ">${t('immediate.sections.leadDetails')}</h3>
          <div style="color: #374151; font-size: 16px; line-height: 1.6;">
            <strong>${t('immediate.cards.lead')}:</strong> ${data.lead.name}<br>
            ${data.lead.status ? `<strong>${t('immediate.cards.status')}:</strong> ${data.lead.status}<br>` : ''}
            ${data.lead.notes ? `<strong>${t('common.labels.notes')}:</strong> ${data.lead.notes}` : ''}
          </div>
        </div>
      `;
      break;

    case 'project-milestone':
      const isCompleted = data.project.lifecycle === 'completed';
      content += `
        <div style="
          background: white;
          border-radius: 12px;
          padding: 24px;
          box-shadow: 0 4px 6px -1px rgba(0, 0, 0, 0.1);
          margin: 24px 0;
        ">
          <h3 style="
            color: #1f2937;
            font-size: 18px;
            font-weight: 600;
            margin: 0 0 16px 0;
          ">${t('immediate.sections.projectUpdate')}</h3>
          <div style="color: #374151; font-size: 16px; line-height: 1.6;">
            <strong>${t('common.labels.project')}:</strong> ${data.project.name}<br>
            ${data.project.type ? `<strong>${t('common.labels.type')}:</strong> ${data.project.type}<br>` : ''}
            ${data.project.leadName ? `<strong>${t('common.labels.client')}:</strong> ${data.project.leadName}<br>` : ''}
            <div style="
              background: ${isCompleted ? '#22c55e' : '#f59e0b'}15;
              padding: 12px;
              border-radius: 8px;
              margin: 16px 0;
              border-left: 4px solid ${isCompleted ? '#22c55e' : '#f59e0b'};
            ">
              <strong>${t('immediate.sections.statusUpdate')}:</strong> ${data.project.oldStatus} â†’ ${data.project.newStatus}
            </div>
            ${data.project.notes ? `<strong>${t('common.labels.notes')}:</strong> ${data.project.notes}` : ''}
          </div>
        </div>
      `;
      break;
  }

  content += generateCallToAction(data, brandColor, t, baseUrl);

  const templateData: EmailTemplateData = {
    userFullName: emailData.user.fullName,
    businessName: emailData.business.businessName,
    brandColor: brandColor,
    baseUrl,
    localization,
  };

  return createEmailTemplate(
    generateSubject(data, t),
    content,
    templateData
  );
}

/**
 * Generate email subject based on notification type
 */
export function generateSubject(data: ImmediateNotificationData, t: TranslateFn): string {
  switch (data.type) {
    case 'project-assignment':
      return t('immediate.subject.projectAssignment', { name: data.project.name });
    case 'lead-assignment':
      return t('immediate.subject.leadAssignment', { name: data.lead.name });
    case 'project-milestone':
      const isCompleted = data.project.lifecycle === 'completed';
      return isCompleted
        ? t('immediate.subject.projectMilestoneCompleted', {
            name: data.project.name,
          })
        : t('immediate.subject.projectMilestoneCancelled', {
            name: data.project.name,
          });
    default:
      return t('immediate.header.projectAssignment.title');
  }
}

import { serve } from "https://deno.land/std@0.190.0/http/server.ts";
import { createClient } from "https://esm.sh/@supabase/supabase-js@2.39.3";
import {
  getErrorMessage,
  getErrorStack,
} from "../_shared/error-utils.ts";

export const corsHeaders = {
  'Access-Control-Allow-Origin': '*',
  'Access-Control-Allow-Headers': 'authorization, x-client-info, apikey, content-type',
};

export const handler = async (req: Request): Promise<Response> => {
  console.log('Session reminders processor started');

  if (req.method === 'OPTIONS') {
    return new Response(null, { headers: corsHeaders });
  }

  const adminSupabase = createClient(
    Deno.env.get('SUPABASE_URL') ?? '',
    Deno.env.get('SUPABASE_SERVICE_ROLE_KEY') ?? ''
  );

  try {
    const processedReminders = await processScheduledReminders(adminSupabase);

    return new Response(JSON.stringify({
      success: true,
      processed_reminders: processedReminders,
      processed_at: new Date().toISOString()
    }), {
      status: 200,
      headers: { ...corsHeaders, 'Content-Type': 'application/json' },
    });

  } catch (error: unknown) {
    console.error('Error processing session reminders:', error);
    return new Response(JSON.stringify({
      error: getErrorMessage(error),
      stack: getErrorStack(error),
      timestamp: new Date().toISOString()
    }), {
      status: 500,
      headers: { ...corsHeaders, 'Content-Type': 'application/json' },
    });
  }
};

export async function processScheduledReminders(supabase: any) {
  console.log('Processing scheduled session reminders');

  // Get reminders that are due to be sent (exact timing, no early processing buffer)
  const now = new Date();

  console.log(`Processing reminders scheduled at or before: ${now.toISOString()}`);

  const { data: dueReminders, error: fetchError } = await supabase
    .from('scheduled_session_reminders')
    .select(`
      *,
      sessions!fk_scheduled_session_reminders_session_id (
        id,
        session_date,
        session_time,
        session_type_id,
        session_types:session_type_id (
          id,
          name,
          duration_minutes
        ),
        location,
        notes,
        organization_id,
        leads!inner (
          id,
          name,
          email,
          phone,
          organization_id
        )
      ),
      workflows!fk_scheduled_session_reminders_workflow_id (
        id,
        name,
        organization_id
      )
    `)
    .eq('status', 'pending')
    .lte('scheduled_for', now.toISOString())
    .order('scheduled_for');

  if (fetchError) {
    console.error('Error fetching due reminders:', fetchError);
    throw fetchError;
  }

  if (!dueReminders || dueReminders.length === 0) {
    console.log('No reminders due for processing');
    return { processed: 0, triggered: 0, failed: 0 };
  }

  console.log(`Found ${dueReminders.length} reminders due for processing`);

  let processed = 0;
  let triggered = 0;
  let failed = 0;

  // Process each reminder
  for (const reminder of dueReminders) {
    try {
      console.log(`Processing reminder ${reminder.id} for session ${reminder.session_id}, type: ${reminder.reminder_type}`);

      // Verify session data before processing
      if (!reminder.sessions || !reminder.sessions.leads) {
        console.error(`Invalid session or lead data for reminder ${reminder.id}`);
        await supabase
          .from('scheduled_session_reminders')
          .update({ 
            status: 'failed',
            error_message: 'Invalid session or lead data',
            processed_at: new Date().toISOString()
          })
          .eq('id', reminder.id);
        failed++;
        processed++;
        continue;
      }

      console.log(
        `Session data: ${reminder.sessions.session_date} ${reminder.sessions.session_time}, Lead: ${reminder.sessions.leads.name}`,
      );

      // Mark reminder as being processed first to prevent duplicate processing
      const { error: updateError } = await supabase
        .from('scheduled_session_reminders')
        .update({ 
          status: 'sent',
          processed_at: new Date().toISOString()
        })
        .eq('id', reminder.id)
        .eq('status', 'pending'); // Only update if still pending

      if (updateError) {
        console.error(`Error updating reminder ${reminder.id}:`, updateError);
        failed++;
        processed++;
        continue;
      }

      // Trigger the workflow executor for this session reminder with explicit session validation
      console.log(`Triggering workflow for session ${reminder.session_id} (${reminder.sessions.session_date} ${reminder.sessions.session_time})`);
      console.log(`Processing reminder type: ${reminder.reminder_type} scheduled for: ${reminder.scheduled_for}`);
      console.log(`Current time: ${now.toISOString()}, Reminder due: ${reminder.scheduled_for}`);

      const { error: triggerError } = await supabase.functions.invoke('workflow-executor', {
        body: {
          action: 'trigger',
          trigger_type: 'session_reminder',
          trigger_entity_type: 'session',
          trigger_entity_id: reminder.session_id, // This MUST match the session in the reminder
          organization_id: reminder.organization_id,
          trigger_data: {
            reminder_type: reminder.reminder_type,
            // Pass the exact session data we retrieved to ensure consistency
            session_data: {
              id: reminder.sessions.id,
              session_date: reminder.sessions.session_date,
              session_time: reminder.sessions.session_time,
              location: reminder.sessions.location,
              notes: reminder.sessions.notes,
              session_type_id: reminder.sessions.session_type_id,
              session_type_name: reminder.sessions.session_types?.name ?? null,
              session_type_duration_minutes: reminder.sessions.session_types?.duration_minutes ?? null
            },
            lead_data: {
              id: reminder.sessions.leads.id,
              name: reminder.sessions.leads.name,
              email: reminder.sessions.leads.email,
              phone: reminder.sessions.leads.phone
            },
            scheduled_reminder_id: reminder.id,
            workflow_id: reminder.workflow_id,
            // Add debug info to ensure correct session is used
            debug_session_validation: {
              expected_session_id: reminder.session_id,
              actual_session_date: reminder.sessions.session_date,
              actual_session_time: reminder.sessions.session_time,
              lead_name: reminder.sessions.leads.name,
              reminder_type: reminder.reminder_type,
              scheduled_for: reminder.scheduled_for
            }
          }
        }
      });

      if (triggerError) {
        const triggerErrorMessage = getErrorMessage(triggerError);
        console.error(`Error triggering workflow for reminder ${reminder.id}:`, triggerError);

        // Mark reminder as failed and record error
        await supabase
          .from('scheduled_session_reminders')
          .update({ 
            status: 'failed',
            error_message: triggerErrorMessage,
            processed_at: new Date().toISOString()
          })
          .eq('id', reminder.id);

        failed++;
      } else {
        console.log(`Successfully triggered workflow for reminder ${reminder.id}`);
        triggered++;
      }

      processed++;

    } catch (error) {
      console.error(`Error processing reminder ${reminder.id}:`, error);

      // Mark reminder as failed
      try {
        await supabase
          .from('scheduled_session_reminders')
          .update({ 
            status: 'failed',
            error_message: getErrorMessage(error),
            processed_at: new Date().toISOString()
          })
          .eq('id', reminder.id);
      } catch (updateError) {
        console.error(`Failed to update reminder ${reminder.id} status:`, updateError);
      }

      failed++;
      processed++;
    }
  }

  // Clean up old reminders
  try {
    const { data: cleanupResult, error: cleanupError } = await supabase.rpc('cleanup_old_session_reminders');
    if (cleanupError) {
      console.error('Error during cleanup:', cleanupError);
    } else {
      console.log(`Cleaned up ${cleanupResult || 0} old reminders`);
    }
  } catch (cleanupError) {
    console.error('Cleanup failed:', cleanupError);
  }

  console.log(`Processed ${processed} reminders: ${triggered} triggered, ${failed} failed`);

  return { processed, triggered, failed };
}

serve(handler);

import { serve } from "https://deno.land/std@0.190.0/http/server.ts";
import { createClient } from "https://esm.sh/@supabase/supabase-js@2.50.0";
import { getErrorMessage } from "../_shared/error-utils.ts";

const corsHeaders = {
  'Access-Control-Allow-Origin': '*',
  'Access-Control-Allow-Headers': 'authorization, x-client-info, apikey, content-type',
};

interface SupabaseClientLike {
  from(table: string): any;
  functions: {
    invoke(name: string, options: { body: Record<string, unknown> }): Promise<{ data?: unknown; error?: Error | null }>;
  };
}

interface HandlerDependencies {
  createClient?: () => SupabaseClientLike;
  getNow?: () => Date;
}

function createSupabaseClient(): SupabaseClientLike {
  return createClient(
    Deno.env.get('SUPABASE_URL') ?? '',
    Deno.env.get('SUPABASE_SERVICE_ROLE_KEY') ?? '',
  ) as unknown as SupabaseClientLike;
}

export const handler = async (
  req: Request,
  deps: HandlerDependencies = {},
): Promise<Response> => {
  if (req.method === 'OPTIONS') {
    return new Response(null, { headers: corsHeaders });
  }

  console.log('Daily notification scheduler started');

  const createClientFn = deps.createClient ?? createSupabaseClient;
  const getNow = deps.getNow ?? (() => new Date());

  try {
    const supabase = createClientFn();

    const now = getNow();
    const currentTime = `${now.getUTCHours().toString().padStart(2, '0')}:${now.getUTCMinutes().toString().padStart(2, '0')}`;

    console.log(`Current time: ${currentTime}`);

    const { data: userSettings, error: userError } = await supabase
      .from('user_settings')
      .select(`
        user_id,
        active_organization_id,
        notification_scheduled_time
      `)
      .eq('notification_global_enabled', true)
      .eq('notification_daily_summary_enabled', true)
      .eq('notification_scheduled_time', currentTime)
      .not('active_organization_id', 'is', null);

    if (userError) {
      console.error('Error fetching user settings:', userError);
      throw userError;
    }

    console.log(`Found ${userSettings?.length || 0} user settings to process`);

    if (!userSettings || userSettings.length === 0) {
      return new Response(JSON.stringify({
        success: true,
        message: `No users scheduled for ${currentTime}`,
        processed: 0,
      }), {
        status: 200,
        headers: { 'Content-Type': 'application/json', ...corsHeaders },
      });
    }

    const userSettingsList = (userSettings ?? []) as Array<{
      user_id: string;
      active_organization_id: string;
      notification_scheduled_time: string;
    }>;

    const orgIds = [...new Set(userSettingsList.map((u) => u.active_organization_id))];
    const { data: orgs, error: orgDetailsError } = await supabase
      .from('organizations')
      .select('id, owner_id, name')
      .in('id', orgIds);

    if (orgDetailsError) {
      console.error('Error fetching organization details:', orgDetailsError);
      throw orgDetailsError;
    }

    console.log(`Found ${orgs?.length || 0} organizations to process`);

    const today = now.toISOString().split('T')[0];
    const scheduledFor = now.toISOString();
    const notifications: Array<Record<string, unknown>> = [];

    const orgsList = (orgs ?? []) as Array<{ id: string; owner_id: string; name: string }>;

    for (const userSetting of userSettingsList) {
      const org = orgsList.find((o) => o.id === userSetting.active_organization_id);
      if (!org) {
        console.log(`Organization ${userSetting.active_organization_id} not found`);
        continue;
      }

      const { data: existingNotification } = await supabase
        .from('notifications')
        .select('id')
        .eq('organization_id', org.id)
        .eq('user_id', userSetting.user_id)
        .eq('notification_type', 'daily-summary')
        .gte('created_at', `${today}T00:00:00Z`)
        .lt('created_at', `${today}T23:59:59Z`)
        .maybeSingle();

      if (existingNotification) {
        console.log(`Daily summary already exists for user ${userSetting.user_id} in organization ${org.id}`);
        continue;
      }

      notifications.push({
        organization_id: org.id,
        user_id: userSetting.user_id,
        notification_type: 'daily-summary',
        delivery_method: 'scheduled',
        scheduled_for: scheduledFor,
        metadata: {
          date: today,
          organization_name: org.name,
        },
        status: 'pending',
      });
    }

    if (notifications.length > 0) {
      const { error: insertError } = await supabase
        .from('notifications')
        .insert(notifications);

      if (insertError) {
        console.error('Error inserting notifications:', insertError);
        throw insertError;
      }

      console.log(`Created ${notifications.length} daily summary notifications`);

      try {
        const { error: processorError } = await supabase.functions.invoke('notification-processor', {
          body: { action: 'process-pending' },
        });

        if (processorError) {
          console.error('Error triggering notification processor:', processorError);
        } else {
          console.log('Successfully triggered notification processor');
        }
      } catch (processorError) {
        console.error('Failed to trigger notification processor:', processorError);
      }
    }

    return new Response(JSON.stringify({
      success: true,
      processed: notifications.length,
      users: userSettingsList.map((u) => u.user_id),
    }), {
      status: 200,
      headers: { 'Content-Type': 'application/json', ...corsHeaders },
    });
  } catch (error: unknown) {
    console.error('Error in schedule-daily-notifications:', error);
    return new Response(JSON.stringify({
      error: getErrorMessage(error),
      success: false,
    }), {
      status: 500,
      headers: { 'Content-Type': 'application/json', ...corsHeaders },
    });
  }
};

if (import.meta.main) {
  serve((req: Request) => handler(req));
}

