import { createClient } from "https://esm.sh/@supabase/supabase-js@2.53.0";

const corsHeaders = {
  "Access-Control-Allow-Origin": "*",
  "Access-Control-Allow-Headers": "authorization, x-client-info, apikey, content-type",
};

const supabaseUrl = Deno.env.get("SUPABASE_URL");
const supabaseServiceKey = Deno.env.get("SUPABASE_SERVICE_ROLE_KEY");

if (!supabaseUrl || !supabaseServiceKey) {
  console.error("Missing Supabase environment configuration for session-types-delete function.");
}

export const SESSION_TYPE_IN_USE_CODE = "SESSION_TYPE_IN_USE";

export interface DeleteSessionTypeResult {
  status: number;
  message?: string;
}

export async function deleteSessionTypeWithClient(
  client: ReturnType<typeof createClient> | any,
  sessionTypeId: string | undefined
): Promise<DeleteSessionTypeResult> {
  if (!sessionTypeId || typeof sessionTypeId !== "string" || sessionTypeId.trim().length === 0) {
    return { status: 400, message: "invalid_session_type_id" };
  }

  const { count, error: countError } = await client
    .from("sessions")
    .select("id", { count: "exact", head: true })
    .eq("session_type_id", sessionTypeId);

  if (countError) {
    const message = countError instanceof Error ? countError.message : String(countError);
    return { status: 500, message };
  }

  if ((count ?? 0) > 0) {
    return { status: 409, message: SESSION_TYPE_IN_USE_CODE };
  }

  const { error: deleteError } = await client
    .from("session_types")
    .delete()
    .eq("id", sessionTypeId);

  if (deleteError) {
    const message = deleteError instanceof Error ? deleteError.message : String(deleteError);
    return { status: 500, message };
  }

  return { status: 200 };
}

const handler = async (req: Request): Promise<Response> => {
  if (req.method === "OPTIONS") {
    return new Response("ok", { headers: corsHeaders });
  }

  try {
    const { session_type_id: sessionTypeId } = await req.json();

    const supabase = createClient(supabaseUrl!, supabaseServiceKey!, {
      auth: { persistSession: false },
    });

    const result = await deleteSessionTypeWithClient(supabase, sessionTypeId);

    if (result.status !== 200) {
      return new Response(
        JSON.stringify({ error: { message: result.message } }),
        { status: result.status, headers: { "Content-Type": "application/json", ...corsHeaders } }
      );
    }

    return new Response(JSON.stringify({ success: true }), {
      status: 200,
      headers: { "Content-Type": "application/json", ...corsHeaders },
    });
  } catch (error) {
    console.error("session-types-delete error", error);
    const message = error instanceof Error ? error.message : String(error);
    return new Response(
      JSON.stringify({ error: { message } }),
      { status: 500, headers: { "Content-Type": "application/json", ...corsHeaders } }
    );
  }
};

if (import.meta.main) {
  serve(handler);
}

import { assertEquals } from "std/testing/asserts.ts";
import {
  handler,
  setSupabaseClientFactoryForTests,
  resetSupabaseClientFactoryForTests,
} from "../simple-daily-notifications/index.ts";

type UsersQueryResult = { data: unknown; error: unknown };

class UsersQuery {
  #result: UsersQueryResult;

  constructor(result: UsersQueryResult) {
    this.#result = result;
  }

  select(_columns?: string) {
    return this;
  }

  eq(_field: string, _value: unknown) {
    return this;
  }

  then<TResult1 = UsersQueryResult, TResult2 = never>(
    onfulfilled?: ((value: UsersQueryResult) => TResult1 | Promise<TResult1>) | undefined,
    onrejected?: ((reason: unknown) => TResult2 | Promise<TResult2>) | undefined,
  ): Promise<TResult1 | TResult2> {
    return Promise.resolve(this.#result).then(onfulfilled, onrejected);
  }
}

Deno.test("handler returns success payload when no users require processing", async () => {
  setSupabaseClientFactoryForTests(() => ({
    from(table: string): any {
      if (table === "user_settings") {
        return new UsersQuery({ data: [], error: null });
      }
      throw new Error(`Unexpected table ${table}`);
    },
  }));

  const response = await handler(new Request("https://example.com", {
    method: "POST",
    body: JSON.stringify({ action: "process" }),
  }));

  const payload = await response.json();
  assertEquals(response.status, 200);
  assertEquals(payload.success, true);
  assertEquals(payload.processed, 0);
  assertEquals(payload.message, "No users with daily summaries enabled");

  resetSupabaseClientFactoryForTests();
});

